import { Module, NestModule, MiddlewareConsumer } from '@nestjs/common';
import { PrismaModule } from './database/prisma.module';
import { UsersModule } from './modules/users/users.module';
import { CommentsModule } from './modules/comments/comments.module';
import { AuthModule } from './modules/auth/auth.module';
import { PostMediasModule } from './modules/post-medias/postMedias.module';
import { FollowsModule } from './modules/follow/follow.module';
import { ReactionsModule } from './modules/reactions/reactions.module';
import { CloudinaryModule } from './modules/cloudinary/cloudinary.module';
import { PostsModule } from './modules/posts/posts.module';
import { MailerModule } from './modules/mailer/mailer.module';
import { RedisCacheModule } from './modules/cache/cache.module';
import { NotificationModule } from './modules/notification/notification.module';
import { SearchHistoryModule } from './modules/search-history/search-history.module';
import { MessagingModule } from './modules/messaging/messaging.module';
import {
  CorsMiddleware,
  RateLimitMiddleware,
  RequestLoggerMiddleware,
  SecurityLoggerMiddleware,
  CookieParserMiddleware,
  SecurityHeadersMiddleware,
  FileUploadSecurityMiddleware,
  WebSocketSecurityMiddleware,
} from './shared/middlewares';
import { ErrorMonitoringService } from './shared/services/error-monitoring.service';

@Module({
  imports: [
    UsersModule,
    CommentsModule,
    AuthModule,
    PostMediasModule,
    FollowsModule,
    ReactionsModule,
    CloudinaryModule,
    PostsModule,
    MailerModule,
    RedisCacheModule,
    NotificationModule,
    SearchHistoryModule,
    MessagingModule,
    PrismaModule,
  ],
  controllers: [],
  providers: [ErrorMonitoringService],
})
export class AppModule implements NestModule {
  configure(consumer: MiddlewareConsumer) {
    // Apply middleware in order of execution

    // 1. Security headers - first for all requests
    consumer.apply(SecurityHeadersMiddleware).forRoutes('*');

    // 2. CORS - before any processing
    consumer.apply(CorsMiddleware).forRoutes('*');

    // 3. Cookie parser - needed for auth
    consumer.apply(CookieParserMiddleware).forRoutes('*');

    // 4. Request logging - general logging
    // consumer.apply(RequestLoggerMiddleware).forRoutes('*');

    // 5. Security logging - sensitive operations
    consumer.apply(SecurityLoggerMiddleware).forRoutes('*');

    // 6. Rate limiting - protect against abuse
    consumer.apply(RateLimitMiddleware).forRoutes('*');

    // 7. File upload security - apply globally but middleware will only act on upload/cloudinary paths
    consumer.apply(FileUploadSecurityMiddleware).forRoutes('*');

    // 8. WebSocket security - Socket.IO routes
    consumer.apply(WebSocketSecurityMiddleware).forRoutes('/socket.io/*path');
  }
}

import { NestFactory } from '@nestjs/core';
import { AppModule } from './app.module';
import { PrismaService } from './database/prisma.service';
import { ValidationPipe } from '@nestjs/common';
import { GlobalExceptionFilter } from './shared/filters/global-exception.filter';
import { ErrorMonitoringService } from './shared/services/error-monitoring.service';

async function bootstrap() {
  const app = await NestFactory.create(AppModule);

  // Global pipes for validation
  app.useGlobalPipes(
    new ValidationPipe({
      transform: true,
      whitelist: true,
      forbidNonWhitelisted: true,
      transformOptions: {
        enableImplicitConversion: true,
      },
    }),
  );

  // Get ErrorMonitoringService instance for dependency injection
  const errorMonitoringService = app.get(ErrorMonitoringService);

  // Global exception filter for centralized error handling
  app.useGlobalFilters(new GlobalExceptionFilter(errorMonitoringService));

  await app.listen(process.env.PORT ?? 3000);
  console.log(`Server is running on: ${await app.getUrl()}`);
}
bootstrap();

import {
  Controller,
  Get,
  Post,
  Body,
  UseGuards,
  Request,
  HttpCode,
  HttpStatus,
  Param,
  Res,
  Query,
  UnauthorizedException,
  BadRequestException,
} from '@nestjs/common';
import { UserApplicationService } from '../users/application/user-application.service';
import { LoginDto } from './dto/login.dto';
import { JwtAuthGuard } from '../../shared/guards/jwt.guard';
import { RegisterDto } from './dto/register.dto';
import { ResendVerificationDto, VerifyEmailDto } from './dto/verifyEmail.dto';
import { GoogleAuthGuard } from '../../shared/guards/google.guard';
import { GoogleLoginDto } from './dto/google-auth.dto';
import { ForgotPasswordDto } from './dto/forgotPassword.dto';
import { ResetPasswordDto } from './dto/resetPassword.dto';
import { CurrentUser } from 'src/shared/decorators/currentUser.decorator';
import { Response } from 'express';
import { URLS } from '../../shared/constants/urls.constant';
import { JWT } from 'src/config/jwt.config';
import { AuthService } from './auth.service';

@Controller('auth')
export class AuthController {
  constructor(
    private readonly authService: AuthService,
    private readonly userApplicationService: UserApplicationService,
  ) {}

  @UseGuards(JwtAuthGuard)
  @Get('me')
  @HttpCode(HttpStatus.OK)
  async getCurrentUser(@CurrentUser('id') userId: string) {
    return this.userApplicationService.getUserProfile(userId);
  }

  @Post('register')
  @HttpCode(HttpStatus.CREATED)
  async register(@Body() registerDto: RegisterDto) {
    return this.authService.register(registerDto);
  }

  @Post('login')
  @HttpCode(HttpStatus.OK)
  async login(
    @Body() loginDto: LoginDto,
    @Request() req,
    @Res({ passthrough: true }) res: Response,
  ) {
    const clientType = (
      req.headers['x-client-type'] ||
      req.query.client ||
      ''
    ).toString();

    const result = await this.authService.login(loginDto);

    // If web client, set HttpOnly cookies and return minimal body
    const isWeb = clientType.toLowerCase() === 'web';
    const isProd = process.env.NODE_ENV === 'production';

    if (isWeb) {
      const accessMaxAge = parseExpiryToMs(JWT.EXPIRES_IN);
      const refreshMaxAge = parseExpiryToMs(JWT.REFRESH_EXPIRES_IN);

      // Set access token in HTTP-only cookie
      if (result.accessToken) {
        res.cookie('access_token', result.accessToken, {
          httpOnly: true,
          secure: isProd,
          sameSite: isProd ? 'none' : 'lax',
          maxAge: accessMaxAge,
        });
      }

      if (result.refreshToken) {
        res.cookie('refresh_token', result.refreshToken, {
          httpOnly: true,
          secure: isProd,
          sameSite: isProd ? 'none' : 'lax',
          maxAge: refreshMaxAge,
        });
      }

      return {
        message: 'Login successful',
        user: result.user,
      };
    }

    // Default: return tokens in body (mobile clients)
    return result;
  }

  @Post('verify-email')
  @HttpCode(HttpStatus.OK)
  async verifyEmail(
    @Body() verifyDto: VerifyEmailDto,
    @Request() req,
    @Res({ passthrough: true }) res: Response,
  ) {
    const clientType = (
      req.headers['x-client-type'] ||
      req.query.client ||
      ''
    ).toString();

    const result = await this.authService.verifyEmail(
      verifyDto.token,
      verifyDto.password,
    );

    // Email verification doesn't return tokens, just success message
    return {
      success: true,
      message: result.message,
      data: result,
    };
  }

  @Post('resend-verification')
  @HttpCode(HttpStatus.OK)
  async resendVerificationEmail(@Body() resendDto: ResendVerificationDto) {
    const result = await this.authService.resendVerificationEmail(
      resendDto.email,
    );
    return {
      success: true,
      message: 'Verification email sent successfully',
      data: result,
    };
  }

  @UseGuards(JwtAuthGuard)
  @Post('logout')
  @HttpCode(HttpStatus.OK)
  async logout(
    @Body() body: { refreshToken?: string },
    @Request() req,
    @Res({ passthrough: true }) res: Response,
  ) {
    try {
      const clientType = (
        req.headers['x-client-type'] ||
        req.query.client ||
        ''
      ).toString();
      const isWeb = clientType.toLowerCase() === 'web';

      // If web client, clear cookies
      if (isWeb) {
        res.clearCookie('access_token');
        res.clearCookie('refresh_token');
      }

      // Revoke refresh token if provided
      if (body.refreshToken) {
        await this.authService.revokeRefreshToken(body.refreshToken);
      }

      return { message: 'Logged out successfully' };
    } catch (error) {
      return { message: 'Logged out successfully' };
    }
  }

  @Post('refresh')
  @HttpCode(HttpStatus.OK)
  async refreshToken(
    @Body() body: { refreshToken?: string },
    @Request() req,
    @Res({ passthrough: true }) res: Response,
  ) {
    // Accept refresh token either in body (mobile) or cookie (web)
    const clientType = (
      req.headers['x-client-type'] ||
      req.query.client ||
      ''
    ).toString();
    const isWeb = clientType.toLowerCase() === 'web';

    const refreshToken = isWeb
      ? req.cookies['refresh_token']
      : body.refreshToken;

    if (!refreshToken) {
      throw new UnauthorizedException('Refresh token not found');
    }

    const tokens = await this.authService.refreshAccessToken(refreshToken);

    if (isWeb) {
      const isProd = process.env.NODE_ENV === 'production';
      const accessMaxAge = parseExpiryToMs(JWT.EXPIRES_IN);
      const refreshMaxAge = parseExpiryToMs(JWT.REFRESH_EXPIRES_IN);

      // Set access token in HTTP-only cookie
      if (tokens.accessToken) {
        res.cookie('access_token', tokens.accessToken, {
          httpOnly: true,
          secure: isProd,
          sameSite: isProd ? 'none' : 'lax',
          maxAge: accessMaxAge,
        });
      }

      if (tokens.refreshToken) {
        res.cookie('refresh_token', tokens.refreshToken, {
          httpOnly: true,
          secure: isProd,
          sameSite: isProd ? 'none' : 'lax',
          maxAge: refreshMaxAge,
        });
      }

      return {
        message: 'Token refreshed',
        expiresIn: JWT.EXPIRES_IN,
      };
    }

    return tokens;
  }

  // ===== GOOGLE AUTHENTICATION ENDPOINTS =====

  // For WEB: Redirect to Google OAuth
  @Get('google')
  @UseGuards(GoogleAuthGuard)
  async googleAuth() {
    // Initiates the Google OAuth flow
  }

  // For WEB: Google OAuth callback
  @Get('google/callback')
  @UseGuards(GoogleAuthGuard)
  async googleAuthCallback(@Request() req, @Res() res: Response) {
    try {
      const user = req.user;

      // Set cookies for tokens (HttpOnly). Do not expose tokens via query string.
      const isProd = process.env.NODE_ENV === 'production';

      // Determine cookie options
      const accessMaxAge = parseExpiryToMs(JWT.EXPIRES_IN);
      const refreshMaxAge = parseExpiryToMs(JWT.REFRESH_EXPIRES_IN);

      // Access token cookie
      if (user.accessToken) {
        res.cookie('access_token', user.accessToken, {
          httpOnly: true,
          secure: isProd,
          sameSite: isProd ? 'none' : 'lax',
          maxAge: accessMaxAge,
        });
      }

      // Refresh token cookie (if provided)
      if (user.refreshToken) {
        res.cookie('refresh_token', user.refreshToken, {
          httpOnly: true,
          secure: isProd,
          sameSite: isProd ? 'none' : 'lax',
          maxAge: refreshMaxAge,
        });
      }

      // Return HTML with postMessage script for popup auth flow
      const userData = {
        id: user.id,
        email: user.email,
        name: user.name,
        avatar: user.avatar,
        isEmailVerified: user.isEmailVerified,
      };

      res.send(`
        <script>
          window.opener?.postMessage({
            type: 'GOOGLE_AUTH_SUCCESS',
            payload: { user: ${JSON.stringify(userData)} }
          }, '${URLS.FRONT_END_WEB}');
          window.close();
        </script>
      `);
    } catch (error) {
      res.send(`
        <script>
          window.opener?.postMessage({
            type: 'GOOGLE_AUTH_ERROR',
            error: '${error.message}'
          }, '${URLS.FRONT_END_WEB}');
          window.close();
        </script>
      `);
    }
  }

  // For MOBILE: Verify Google ID Token
  @Post('google/mobile')
  @HttpCode(HttpStatus.OK)
  async googleMobileAuth(@Body() googleLoginDto: GoogleLoginDto) {
    return this.authService.verifyGoogleToken(googleLoginDto.idToken);
  }

  // Forgot Password
  @Post('forgot-password')
  @HttpCode(HttpStatus.OK)
  async forgotPassword(@Body() forgotPasswordDto: ForgotPasswordDto) {
    await this.authService.forgotPassword(forgotPasswordDto.email);
    return {
      success: true,
      message: 'If the email exists, a password reset link has been sent.',
    };
  }

  // Reset Password
  @Post('reset-password')
  @HttpCode(HttpStatus.OK)
  async resetPassword(@Body() resetPasswordDto: ResetPasswordDto) {
    await this.authService.resetPassword(
      resetPasswordDto.token,
      resetPasswordDto.newPassword,
      resetPasswordDto.confirmPassword,
    );
    return {
      success: true,
      message: 'Password has been reset successfully.',
    };
  }

  // Admin: Cleanup expired tokens
  @Post('admin/cleanup-tokens')
  @HttpCode(HttpStatus.OK)
  @UseGuards(JwtAuthGuard)
  async cleanupExpiredTokens(@CurrentUser('role') userRole: string) {
    // Only admin can cleanup tokens
    if (userRole !== 'ADMIN') {
      throw new UnauthorizedException('Admin access required');
    }

    const result = await this.authService.cleanupExpiredTokens();
    return {
      success: true,
      message: `Cleaned up ${result.count} expired/revoked tokens`,
      deletedCount: result.count,
    };
  }

  // Get user's active sessions
  @UseGuards(JwtAuthGuard)
  @Get('sessions')
  @HttpCode(HttpStatus.OK)
  async getUserSessions(@CurrentUser('id') userId: string) {
    const sessions = await this.authService.getUserSessions(userId);
    return {
      success: true,
      data: sessions,
      message: `Found ${sessions.total} active sessions`,
    };
  }

  // Cleanup zombie sessions (inactive for specified days)
  @UseGuards(JwtAuthGuard)
  @Post('cleanup-zombie-sessions')
  @HttpCode(HttpStatus.OK)
  async cleanupZombieSessions(
    @CurrentUser('id') userId: string,
    @Body() body: { inactiveDays?: number } = {},
  ) {
    const inactiveDays = body.inactiveDays || 30; // Default 30 days
    const result = await this.authService.cleanupZombieSessions(userId);

    return {
      success: true,
      message: `Cleaned up ${result.cleaned} zombie sessions (inactive for ${inactiveDays}+ days)`,
      deletedCount: result.cleaned,
    };
  }

  // Admin: Cleanup all zombie sessions globally
  @Post('admin/cleanup-all-zombie-sessions')
  @HttpCode(HttpStatus.OK)
  // @UseGuards(JwtAuthGuard)
  async adminCleanupZombieSessions(
    @CurrentUser('role') userRole: string,
    @Body() body: { inactiveDays?: number } = {},
  ) {
    // if (userRole !== 'ADMIN') {
    //   throw new UnauthorizedException('Admin access required');
    // }

    const inactiveDays = body.inactiveDays || 7;
    const result =
      await this.authService.cleanupAllZombieSessions(inactiveDays);

    return {
      success: true,
      message: `Globally cleaned up ${result.cleaned} zombie sessions across all users`,
      deletedCount: result.cleaned,
    };
  }

  // Revoke specific sessions by session IDs
  @UseGuards(JwtAuthGuard)
  @Post('revoke-sessions')
  @HttpCode(HttpStatus.OK)
  async revokeSessions(
    @CurrentUser('id') userId: string,
    @Body() body: { sessionIds: string[] },
  ) {
    if (!body.sessionIds || body.sessionIds.length === 0) {
      throw new BadRequestException('Session IDs are required');
    }

    const result = await this.authService.revokeUserSessions(
      userId,
      body.sessionIds,
    );
    return {
      success: true,
      message: `Revoked ${result.revokedCount} sessions`,
      revokedCount: result.revokedCount,
    };
  }

  // Revoke ALL user sessions (force logout from all devices)
  @UseGuards(JwtAuthGuard)
  @Post('revoke-all-sessions')
  @HttpCode(HttpStatus.OK)
  async revokeAllSessions(@CurrentUser('id') userId: string) {
    const result = await this.authService.revokeAllUserSessions(userId);
    return {
      success: true,
      message: result.message,
      revokedCount: result.revokedCount,
      sessionIds: result.sessionIds,
    };
  }
}

// Helper: convert expiry like '24h' or '7d' or seconds string to milliseconds for cookie maxAge
function parseExpiryToMs(expiry: string | undefined): number {
  if (!expiry) return 0;
  const s = expiry.trim().toLowerCase();
  const num = parseInt(s, 10);
  if (s.endsWith('ms')) return num;
  if (s.endsWith('s')) return num * 1000;
  if (s.endsWith('m')) return num * 60 * 1000;
  if (s.endsWith('h')) return num * 60 * 60 * 1000;
  if (s.endsWith('d')) return num * 24 * 60 * 60 * 1000;
  // Fallback: if it's a number string, assume seconds
  if (!isNaN(num)) return num * 1000;
  return 0;
}

import {
  Injectable,
  UnauthorizedException,
  ConflictException,
  BadRequestException,
  NotFoundException,
} from '@nestjs/common';
import { JwtService } from '@nestjs/jwt';
import { PrismaService } from '../../database/prisma.service';
import { RegisterDto } from './dto/register.dto';
import { LoginDto } from './dto/login.dto';
import { GoogleUserDto } from './dto/google-auth.dto';
import { UserManagementService } from './repositories/user-management.repository';
import { AuthenticationService } from './repositories/authentication.repository';
import { SessionService } from './repositories/session.repository';
import { TokenService } from './repositories/token.repository';
import { RedisCacheService } from '../cache/cache.service';
import { NotificationGateway } from '../notification';
import * as bcrypt from 'bcrypt';

@Injectable()
export class AuthService {
  constructor(
    private prisma: PrismaService,
    private jwtService: JwtService,
    private userManagementService: UserManagementService,
    private authenticationService: AuthenticationService,
    private sessionService: SessionService,
    private tokenService: TokenService,
    private cacheService: RedisCacheService,
    private notificationGateway: NotificationGateway,
  ) {}

  // ===== USER REGISTRATION & EMAIL VERIFICATION =====

  async register(registerDto: RegisterDto) {
    return this.userManagementService.registerUser(registerDto);
  }

  async verifyEmail(token: string, password?: string) {
    return this.userManagementService.verifyEmail(token, password);
  }

  async resendVerificationEmail(email: string) {
    return this.userManagementService.resendVerificationEmail(email);
  }

  // ===== PASSWORD MANAGEMENT =====

  async forgotPassword(email: string) {
    return this.userManagementService.forgotPassword(email);
  }

  async resetPassword(
    token: string,
    newPassword: string,
    confirmPassword: string,
  ) {
    return this.userManagementService.resetPassword(
      token,
      newPassword,
      confirmPassword,
    );
  }

  // ===== AUTHENTICATION =====

  async login(loginDto: LoginDto, ipAddress?: string, userAgent?: string) {
    const result = await this.authenticationService.login(
      loginDto,
      ipAddress,
      userAgent,
    );

    // Send notification
    if (result.success && result.user) {
      this.notificationGateway.broadcast({
        userId: result.user.id,
        message: 'User logged in',
      });
    }

    return result;
  }

  async logout(sessionId: string, userId: string) {
    return this.authenticationService.logout(sessionId, userId);
  }

  async logoutAllDevices(userId: string, currentSessionId?: string) {
    return this.authenticationService.logoutAllDevices(
      userId,
      currentSessionId,
    );
  }

  // ===== TOKEN MANAGEMENT =====

  async refreshAccessToken(refreshToken: string) {
    return this.tokenService.refreshAccessToken(refreshToken);
  }

  async revokeRefreshToken(refreshToken: string) {
    return this.tokenService.revokeRefreshToken(refreshToken);
  }

  async validateAccessToken(token: string) {
    return this.authenticationService.validateAccessToken(token);
  }

  async revokeToken(token: string, tokenType: 'access' | 'refresh' = 'access') {
    return this.authenticationService.revokeToken(token, tokenType);
  }

  // ===== SESSION MANAGEMENT =====

  async getUserSessions(userId: string) {
    // Mock implementation - should be implemented in SessionService
    return { sessions: [], total: 0 };
  }

  async cleanupZombieSessions(userId: string) {
    // Mock implementation - should be implemented in SessionService
    return { message: 'Zombie sessions cleaned up', cleaned: 0 };
  }

  async cleanupAllZombieSessions(inactiveDays?: number) {
    // Mock implementation - should be implemented in SessionService
    return { message: 'All zombie sessions cleaned up', cleaned: 0 };
  }

  async revokeUserSessions(userId: string, sessionIds: string[]) {
    return this.sessionService.deleteSessions(userId, sessionIds);
  }

  async revokeAllUserSessions(userId: string) {
    // Get all user sessions first
    const userSessions = await this.prisma.session.findMany({
      where: {
        userId,
        isRevoked: false,
      },
      select: {
        id: true,
        sessionId: true,
      },
    });

    if (userSessions.length === 0) {
      return {
        message: 'No active sessions found to revoke',
        revokedCount: 0,
      };
    }

    // Delete all sessions for the user
    const deleteResult = await this.prisma.session.deleteMany({
      where: {
        userId,
        isRevoked: false,
      },
    });

    // Also revoke all user tokens
    await this.tokenService.revokeAllUserTokens(userId);

    return {
      message: `Successfully revoked all ${deleteResult.count} sessions`,
      revokedCount: deleteResult.count,
      sessionIds: userSessions.map((s) => s.sessionId),
    };
  }

  async cleanupExpiredTokens() {
    // Mock implementation - should be implemented in TokenService
    return { message: 'Expired tokens cleaned up successfully', count: 0 };
  }

  async verifyGoogleToken(idToken: string) {
    // Mock implementation - should be implemented for Google OAuth
    throw new Error('Google token verification not implemented yet');
  }

  // ===== GOOGLE AUTHENTICATION =====

  async googleLogin(googleUser: GoogleUserDto) {
    // Check if user exists
    let user = await this.prisma.user.findUnique({
      where: { email: googleUser.email },
    });

    if (user) {
      // User exists, create tokens and login
      const tokens = await this.tokenService.createTokensForUser(
        user.id,
        user.email,
      );

      this.notificationGateway.broadcast({
        userId: user.id,
        message: 'User logged in via Google',
      });

      return {
        success: true,
        message: 'Google login successful',
        accessToken: tokens.accessToken,
        refreshToken: tokens.refreshToken,
        user: {
          id: user.id,
          email: user.email,
          userName: user.username,
          fullName: user.fullName,
          avatar: user.avatar,
          role: user.role,
        },
      };
    } else {
      // User doesn't exist, create new user
      user = await this.prisma.user.create({
        data: {
          email: googleUser.email,
          fullName: googleUser.fullName,
          avatar: googleUser.profilePicture,
          isEmailVerified: true, // Google accounts are pre-verified
          emailVerifiedAt: new Date(),
          // Generate a username from email
          username: googleUser.email.split('@')[0],
        },
      });

      const tokens = await this.tokenService.createTokensForUser(
        user.id,
        user.email,
      );

      this.notificationGateway.broadcast({
        userId: user.id,
        message: 'New user registered via Google',
      });

      return {
        success: true,
        message: 'Google registration and login successful',
        accessToken: tokens.accessToken,
        refreshToken: tokens.refreshToken,
        user: {
          id: user.id,
          email: user.email,
          userName: user.username,
          fullName: user.fullName,
          avatar: user.avatar,
          role: user.role,
        },
      };
    }
  }

  // ===== VALIDATE USER (used by strategies) =====

  /**
   * Validate user by id for authentication strategies (JWT)
   * Returns user object (minimal public fields) or null if not found / not verified
   */
  async validateUserById(userId: string) {
    if (!userId) return null;

    const user = await this.prisma.user.findUnique({
      where: { id: userId },
      select: {
        id: true,
        email: true,
        username: true,
        role: true,
        fullName: true,
        avatar: true,
        isEmailVerified: true,
      },
    });

    // Reject users who haven't verified email
    if (!user || !user.isEmailVerified) return null;

    return user;
  }

  // ===== UTILITY METHODS =====

  async verifyRefreshToken(
    refreshToken: string,
  ): Promise<{ userId: string; email: string }> {
    if (!refreshToken) {
      throw new UnauthorizedException('Refresh token is required');
    }

    try {
      // Use SessionService to get session info from refresh token
      const sessionInfo =
        await this.sessionService.getSessionFromRefreshToken(refreshToken);

      // Get user info from session
      const session = await this.prisma.session.findUnique({
        where: { id: sessionInfo.id },
        include: { user: true },
      });

      if (!session || session.isRevoked) {
        throw new UnauthorizedException('Invalid refresh token');
      }

      return {
        userId: session.userId,
        email: session.user.email,
      };
    } catch (error) {
      throw new UnauthorizedException('Invalid refresh token');
    }
  }
}

