import { Injectable, ConflictException } from '@nestjs/common';
import { BaseUseCase } from './base.use-case';
import { GoogleAuthRequest } from './auth.dtos';
import { LoginResult } from '../entities';
import { IUserRepository, ITokenRepository } from '../repositories';
import { ROLES } from '../../../../shared/constants/roles.constant';

@Injectable()
export class GoogleAuthUseCase extends BaseUseCase<
  GoogleAuthRequest,
  LoginResult
> {
  constructor(
    private userRepository: IUserRepository,
    private tokenRepository: ITokenRepository,
  ) {
    super();
  }

  async execute(request: GoogleAuthRequest): Promise<LoginResult> {
    const { email, fullName, profilePicture } = request;

    // Check if user exists
    let user = await this.userRepository.findUserByEmail(email);

    if (user) {
      // User exists, generate tokens and return
      const tokens = await this.tokenRepository.createTokensForUser(
        user.id,
        user.email,
        user.role,
      );

      return {
        success: true,
        message: 'Google login successful',
        user: {
          id: user.id,
          email: user.email,
          userName: user.userName,
          fullName: user.fullName,
          avatar: user.avatar,
          role: user.role,
        },
        accessToken: tokens.accessToken,
        refreshToken: tokens.refreshToken,
        sessionId: `google_session_${Date.now()}`, // Temporary session ID
      };
    } else {
      // User doesn't exist, create new user
      let userName = email.split('@')[0]; // Generate username from email

      // Check if username already exists
      const existingUserByUsername =
        await this.userRepository.findUserByUsername(userName);
      if (existingUserByUsername) {
        // Use email prefix with random number if username taken
        const randomSuffix = Math.floor(Math.random() * 1000);
        userName = `${email.split('@')[0]}${randomSuffix}`;
      }

      user = await this.userRepository.createUser({
        email,
        fullName,
        userName,
        avatar: profilePicture,
        isEmailVerified: true, // Google accounts are pre-verified
        role: ROLES.USER,
      });

      const tokens = await this.tokenRepository.createTokensForUser(
        user.id,
        user.email,
        user.role,
      );

      return {
        success: true,
        message: 'Google registration and login successful',
        user: {
          id: user.id,
          email: user.email,
          userName: user.userName,
          fullName: user.fullName,
          avatar: user.avatar,
          role: user.role,
        },
        accessToken: tokens.accessToken,
        refreshToken: tokens.refreshToken,
        sessionId: `google_session_${Date.now()}`, // Temporary session ID
      };
    }
  }
}

import { BadRequestException } from '@nestjs/common';
import { Transform } from 'class-transformer';
import {
  IsEmail,
  IsString,
  IsOptional,
  MinLength,
  MaxLength,
  IsUrl,
  IsDateString,
  Matches,
  IsNotEmpty,
  IsDate,
} from 'class-validator';
import { isValid, parse } from 'date-fns';

export class RegisterDto {
  @IsOptional()
  @IsString()
  @MinLength(3, { message: 'Username must be at least 3 characters long' })
  @MaxLength(30, { message: 'Username must not exceed 30 characters' })
  @Matches(/^[a-z0-9._]+$/, {
    message:
      'Username can only contain lowercase letters, numbers, dot and underscore',
  })
  @Transform(({ value }) =>
    typeof value === 'string' ? value.trim().toLowerCase() : value,
  )
  userName?: string;

  @IsString()
  @IsNotEmpty({ message: 'Full name is required' })
  @MinLength(2, { message: 'Full name must be at least 2 characters long' })
  @MaxLength(50, { message: 'Full name must not exceed 50 characters' })
  fullName: string;

  @IsEmail({}, { message: 'Please provide a valid email address' })
  @IsNotEmpty({ message: 'Email is required' })
  email: string;

  @IsNotEmpty({ message: 'Date of birth is required' })
  @IsDate({ message: 'Date of birth must be a valid date' })
  @Transform(({ value }) => {
    if (!value) return value;
    if (typeof value === 'string') {
      // try ISO first
      const iso = new Date(value);
      if (!isNaN(iso.getTime())) return iso;

      // fallback to dd/MM/yyyy
      const parsedDate = parse(value, 'dd/MM/yyyy', new Date());
      if (!isValid(parsedDate)) {
        throw new BadRequestException(
          'Invalid date format. Use ISO (YYYY-MM-DD) or DD/MM/YYYY.',
        );
      }
      return parsedDate;
    }
    return value;
  })
  dateOfBirth: Date;

  @IsString()
  @IsOptional()
  @MaxLength(10, { message: 'Gender must not exceed 10 characters' })
  gender?: string;

  @IsOptional()
  @IsString()
  @MaxLength(15, { message: 'Phone number must not exceed 15 characters' })
  phoneNumber?: string;

  @IsOptional()
  @IsUrl({}, { message: 'Avatar must be a valid URL' })
  avatar?: string;

  @IsOptional()
  @IsString()
  @MaxLength(500, { message: 'Bio must not exceed 500 characters' })
  bio?: string;

  @IsOptional()
  @IsString()
  @MaxLength(100, { message: 'Location must not exceed 100 characters' })
  location?: string;

  @IsOptional()
  @IsUrl({}, { message: 'Website URL must be a valid URL' })
  @MaxLength(200, { message: 'Website URL must not exceed 200 characters' })
  websiteUrl?: string;
}

