import { Injectable } from '@nestjs/common';
import { JwtService } from '@nestjs/jwt';
import { ITokenRepository } from '../domain/repositories';
import {
  AuthToken,
  TokenPayload,
  AccessTokenData,
  RefreshTokenData,
} from '../domain/entities';
import * as crypto from 'crypto';

@Injectable()
export class TokenRepository implements ITokenRepository {
  constructor(private jwtService: JwtService) {}

  async generateAccessToken(payload: AccessTokenData): Promise<string> {
    return this.jwtService.signAsync(
      {
        userId: payload.userId,
        email: payload.email,
        role: payload.role,
        sessionId: payload.sessionId,
      },
      {
        expiresIn: '15m', // 15 minutes
      },
    );
  }

  async generateRefreshToken(): Promise<string> {
    return crypto.randomBytes(64).toString('hex');
  }

  async createTokensForUser(
    userId: string,
    email: string,
    role: string,
  ): Promise<AuthToken> {
    const sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    const accessToken = await this.generateAccessToken({
      userId,
      email,
      role,
      sessionId,
    });

    const refreshToken = await this.generateRefreshToken();

    return {
      accessToken,
      refreshToken,
      expiresIn: 15 * 60, // 15 minutes in seconds
      tokenType: 'Bearer',
    };
  }

  async validateAccessToken(token: string): Promise<TokenPayload | null> {
    try {
      const payload = await this.jwtService.verifyAsync(token);
      return payload as TokenPayload;
    } catch (error) {
      return null;
    }
  }

  async validateRefreshToken(
    refreshToken: string,
  ): Promise<TokenPayload | null> {
    // For refresh tokens, we need to validate against stored sessions
    // This would typically involve checking against the session repository
    // For now, return null as we need session integration
    return null;
  }

  async refreshAccessToken(
    refreshToken: string,
  ): Promise<{ accessToken: string; refreshToken: string }> {
    // This would typically:
    // 1. Validate the refresh token against session storage
    // 2. Get user info from session
    // 3. Generate new access token
    // 4. Optionally rotate refresh token

    // For now, throw error as this needs session repository integration
    throw new Error('Token refresh requires session repository integration');
  }

  async revokeToken(
    token: string,
    tokenType: 'access' | 'refresh',
  ): Promise<void> {
    // For JWT access tokens, we can't really revoke them without a blacklist
    // For refresh tokens, we would revoke the associated session
    console.log(`Revoking ${tokenType} token: ${token}`);
  }

  async revokeAllUserTokens(userId: string): Promise<void> {
    // This would typically revoke all sessions for the user
    console.log(`Revoking all tokens for user: ${userId}`);
  }

  async cleanupExpiredTokens(): Promise<number> {
    // JWT tokens expire automatically, refresh tokens are cleaned via sessions
    return 0;
  }

  async decodeToken(token: string): Promise<TokenPayload | null> {
    try {
      const decoded = this.jwtService.decode(token);
      return decoded as TokenPayload;
    } catch (error) {
      return null;
    }
  }

  async isTokenExpired(token: string): Promise<boolean> {
    try {
      await this.jwtService.verifyAsync(token);
      return false;
    } catch (error) {
      return true;
    }
  }
}

import {
  Injectable,
  BadRequestException,
  UnauthorizedException,
  NotFoundException,
  ForbiddenException,
} from '@nestjs/common';
import { PrismaService } from '../../../database/prisma.service';
import { JwtService } from '@nestjs/jwt';
import { LoginDto } from '../dto/login.dto';
import { SessionService } from './session.repository';
import { TokenService } from './token.repository';
import { RedisCacheService } from '../../cache/cache.service';
import * as bcrypt from 'bcrypt';

@Injectable()
export class AuthenticationService {
  constructor(
    private prisma: PrismaService,
    private jwtService: JwtService,
    private sessionService: SessionService,
    private tokenService: TokenService,
    private cacheService: RedisCacheService,
  ) {}

  // ===== LOGIN =====

  async login(loginDto: LoginDto, ipAddress?: string, userAgent?: string) {
    const { email, userName, password } = loginDto;

    // Find user by email or username
    const user = await this.prisma.user.findFirst({
      where: {
        OR: [{ email }, ...(userName ? [{ username: userName }] : [])],
      },
      select: {
        id: true,
        email: true,
        username: true,
        fullName: true,
        avatar: true,
        role: true,
        status: true,
        isEmailVerified: true,
        passwordHash: true, // Include password for authentication
        dateOfBirth: true,
        emailVerifiedAt: true,
      },
    });

    if (!user) {
      throw new UnauthorizedException('Invalid credentials');
    }

    // Check if email is verified
    if (!user.isEmailVerified) {
      throw new ForbiddenException(
        'Please verify your email before logging in. Check your email for verification instructions.',
      );
    }

    // Check if user has a password set
    if (!user.passwordHash) {
      throw new BadRequestException(
        'Password not set. Please complete email verification to set your password.',
      );
    }

    // Verify password
    const isPasswordValid = await bcrypt.compare(password, user.passwordHash);
    if (!isPasswordValid) {
      throw new UnauthorizedException('Invalid credentials');
    }

    // Create tokens
    const tokens = await this.createTokensForUser(
      user.id,
      user.email,
      userAgent,
      ipAddress,
    );

    return {
      success: true,
      message: 'Login successful',
      accessToken: tokens.accessToken,
      refreshToken: tokens.refreshToken,
      user: {
        id: user.id,
        email: user.email,
        userName: user.username,
        fullName: user.fullName,
        avatar: user.avatar,
        role: user.role,
        dateOfBirth: user.dateOfBirth,
        isEmailVerified: user.isEmailVerified,
        emailVerifiedAt: user.emailVerifiedAt,
      },
    };
  }

  private async createTokensForUser(
    userId: string,
    email: string,
    userAgent?: string,
    ipAddress?: string,
  ): Promise<{ accessToken: string; refreshToken: string }> {
    const payload = { sub: userId, email };
    const accessToken = this.jwtService.sign(payload);
    const refreshToken = await this.sessionService.createSession(
      userId,
      userAgent,
      ipAddress,
    );

    return { accessToken, refreshToken };
  }

  // ===== LOGOUT =====

  async logout(sessionId: string, userId: string) {
    // Verify session belongs to user
    const session = await this.prisma.session.findFirst({
      where: {
        id: sessionId,
        userId,
        isRevoked: false,
      },
    });

    if (!session) {
      throw new NotFoundException('Session not found');
    }

    // Delete the session
    await this.sessionService.deleteSession(sessionId);

    return { message: 'Logged out successfully' };
  }

  // ===== LOGOUT ALL DEVICES =====

  async logoutAllDevices(userId: string, currentSessionId?: string) {
    const sessions = await this.prisma.session.findMany({
      where: {
        userId,
        isRevoked: false,
        ...(currentSessionId && { id: { not: currentSessionId } }),
      },
    });

    // Delete all sessions
    const sessionIds = sessions.map((session) => session.id);
    await this.sessionService.deleteSessions(userId, sessionIds);

    return {
      message: currentSessionId
        ? 'Logged out from all other devices successfully'
        : 'Logged out from all devices successfully',
      sessionsRevoked: sessions.length,
    };
  }

  // ===== VALIDATE TOKEN =====

  async validateAccessToken(token: string) {
    try {
      const payload = this.jwtService.verify(token);

      // Check if token is blacklisted
      const isBlacklisted = await this.cacheService.get(`blacklist:${token}`);
      if (isBlacklisted) {
        throw new UnauthorizedException('Token has been revoked');
      }

      // Get user
      const user = await this.prisma.user.findUnique({
        where: { id: payload.sub },
        select: {
          id: true,
          email: true,
          username: true,
          fullName: true,
          role: true,
          isEmailVerified: true,
        },
      });

      if (!user) {
        throw new UnauthorizedException('User not found');
      }

      if (!user.isEmailVerified) {
        throw new UnauthorizedException('Email not verified');
      }

      return { user, payload };
    } catch (error) {
      if (error.name === 'TokenExpiredError') {
        throw new UnauthorizedException('Access token expired');
      }
      if (error.name === 'JsonWebTokenError') {
        throw new UnauthorizedException('Invalid access token');
      }
      throw error;
    }
  }

  // ===== HELPER METHODS =====

  private generateDeviceFingerprint(
    ipAddress?: string,
    userAgent?: string,
  ): string {
    const components = [
      ipAddress || 'unknown-ip',
      userAgent || 'unknown-agent',
      Date.now().toString(),
    ];

    return Buffer.from(components.join('|')).toString('base64');
  }

  // ===== REVOKE TOKEN =====

  async revokeToken(token: string, tokenType: 'access' | 'refresh' = 'access') {
    if (tokenType === 'access') {
      // Add to blacklist cache
      const payload = this.jwtService.decode(token);
      if (payload && payload.exp) {
        const ttl = payload.exp - Math.floor(Date.now() / 1000);
        if (ttl > 0) {
          await this.cacheService.set(`blacklist:${token}`, true, ttl);
        }
      }
    } else {
      // Revoke refresh token
      await this.tokenService.revokeRefreshToken(token);
    }

    return { message: 'Token revoked successfully' };
  }
}

import { Injectable, UnauthorizedException } from '@nestjs/common';
import { PrismaService } from '../../../database/prisma.service';
import { JWT } from 'src/config/jwt.config';
import * as crypto from 'crypto';

@Injectable()
export class SessionService {
  constructor(private prisma: PrismaService) {}

  // ===== SESSION GENERATION =====

  generateSessionId(): string {
    return crypto.randomBytes(32).toString('hex');
  }

  generateRefreshToken(sessionId: string): string {
    const payload = { sessionId, timestamp: Date.now() };
    return Buffer.from(JSON.stringify(payload)).toString('base64');
  }

  // ===== SESSION CRUD =====

  async createSession(
    userId: string,
    userAgent?: string,
    ipAddress?: string,
  ): Promise<string> {
    const sessionId = this.generateSessionId();

    // Calculate expiry date from JWT config
    const expiresAt = new Date();
    const expiryTime = JWT.REFRESH_EXPIRES_IN;

    if (expiryTime.endsWith('d')) {
      expiresAt.setDate(expiresAt.getDate() + parseInt(expiryTime));
    } else if (expiryTime.endsWith('h')) {
      expiresAt.setHours(expiresAt.getHours() + parseInt(expiryTime));
    } else {
      expiresAt.setDate(expiresAt.getDate() + 7);
    }

    await this.prisma.session.create({
      data: {
        sessionId,
        userId,
        expiresAt,
        userAgent,
        ipAddress,
      },
    });

    return this.generateRefreshToken(sessionId);
  }

  async getSessionFromRefreshToken(
    refreshToken: string,
  ): Promise<{ sessionId: string; id: string }> {
    try {
      const tokenPayload = JSON.parse(
        Buffer.from(refreshToken, 'base64').toString(),
      );
      const { sessionId } = tokenPayload;

      if (!sessionId) {
        throw new UnauthorizedException('Invalid refresh token format');
      }

      const session = await this.prisma.session.findUnique({
        where: { sessionId },
        select: { id: true, sessionId: true },
      });

      if (!session) {
        throw new UnauthorizedException('Session not found');
      }

      return session;
    } catch (error) {
      throw new UnauthorizedException('Invalid refresh token');
    }
  }

  async verifyAndUpdateSession(
    refreshToken: string,
  ): Promise<{ userId: string; email: string }> {
    if (!refreshToken) {
      throw new UnauthorizedException('Refresh token is required');
    }

    try {
      const tokenPayload = JSON.parse(
        Buffer.from(refreshToken, 'base64').toString(),
      );
      const { sessionId } = tokenPayload;

      if (!sessionId) {
        throw new UnauthorizedException('Invalid refresh token format');
      }

      const session = await this.prisma.session.findUnique({
        where: { sessionId },
        include: { user: true },
      });

      if (!session) {
        throw new UnauthorizedException(
          'Session not found. Please log in again.',
        );
      }

      if (session.isRevoked) {
        throw new UnauthorizedException(
          'Session has been revoked. Please log in again.',
        );
      }

      if (session.expiresAt < new Date()) {
        await this.deleteSession(session.sessionId);
        throw new UnauthorizedException(
          'Session has expired. Please log in again.',
        );
      }

      // Update last used timestamp
      await this.prisma.session.update({
        where: { id: session.id },
        data: { lastUsedAt: new Date() },
      });

      return {
        userId: session.userId,
        email: session.user.email,
      };
    } catch (error) {
      if (error instanceof UnauthorizedException) {
        throw error;
      }
      throw new UnauthorizedException(
        'Invalid refresh token. Please log in again.',
      );
    }
  }

  async rotateRefreshToken(sessionId: string): Promise<string> {
    const newRefreshToken = this.generateRefreshToken(sessionId);

    const expiresAt = new Date();
    const expiryTime = JWT.REFRESH_EXPIRES_IN;

    if (expiryTime.endsWith('d')) {
      expiresAt.setDate(expiresAt.getDate() + parseInt(expiryTime));
    } else if (expiryTime.endsWith('h')) {
      expiresAt.setHours(expiresAt.getHours() + parseInt(expiryTime));
    } else {
      expiresAt.setDate(expiresAt.getDate() + 7);
    }

    await this.prisma.session.update({
      where: { sessionId },
      data: {
        lastUsedAt: new Date(),
        expiresAt: expiresAt,
      },
    });

    return newRefreshToken;
  }

  // ===== SESSION DELETION =====

  async deleteSession(refreshTokenOrSessionId: string): Promise<void> {
    try {
      // Try to parse as refresh token first
      const tokenPayload = JSON.parse(
        Buffer.from(refreshTokenOrSessionId, 'base64').toString(),
      );
      const { sessionId } = tokenPayload;

      if (sessionId) {
        await this.prisma.session.deleteMany({
          where: { sessionId, isRevoked: false },
        });
      }
    } catch (error) {
      // If parsing fails, treat as sessionId directly
      await this.prisma.session.deleteMany({
        where: { sessionId: refreshTokenOrSessionId, isRevoked: false },
      });
    }
  }

  async deleteAllUserSessions(userId: string): Promise<void> {
    await this.prisma.session.deleteMany({
      where: { userId, isRevoked: false },
    });
  }

  async deleteSessions(userId: string, sessionIds: string[]) {
    const deleteResult = await this.prisma.session.deleteMany({
      where: {
        userId,
        sessionId: { in: sessionIds },
        isRevoked: false,
      },
    });

    return { revokedCount: deleteResult.count };
  }
}

import {
  Injectable,
  BadRequestException,
  UnauthorizedException,
} from '@nestjs/common';
import { JwtService } from '@nestjs/jwt';
import { SessionService } from './session.repository';

@Injectable()
export class TokenService {
  constructor(
    private jwtService: JwtService,
    private sessionService: SessionService,
  ) {}

  // ===== ACCESS TOKEN =====

  generateAccessToken(userId: string, email: string): string {
    const payload = { sub: userId, email };
    return this.jwtService.sign(payload);
  }

  // ===== TOKEN PAIR GENERATION =====

  async createTokensForUser(
    userId: string,
    email: string,
    userAgent?: string,
    ipAddress?: string,
  ): Promise<{ accessToken: string; refreshToken: string }> {
    const accessToken = this.generateAccessToken(userId, email);
    const refreshToken = await this.sessionService.createSession(
      userId,
      userAgent,
      ipAddress,
    );

    return { accessToken, refreshToken };
  }

  // ===== TOKEN REFRESH (RTR) =====

  async refreshAccessToken(refreshToken: string) {
    // Verify current session and get user data
    const tokenData =
      await this.sessionService.verifyAndUpdateSession(refreshToken);

    // Get current session info
    const currentSession =
      await this.sessionService.getSessionFromRefreshToken(refreshToken);

    // Generate new access token
    const accessToken = this.generateAccessToken(
      tokenData.userId,
      tokenData.email,
    );

    // Rotate refresh token within same session
    const newRefreshToken = await this.sessionService.rotateRefreshToken(
      currentSession.sessionId,
    );

    return {
      accessToken,
      refreshToken: newRefreshToken,
      userId: tokenData.userId,
      email: tokenData.email,
    };
  }

  // ===== TOKEN REVOCATION =====

  async revokeRefreshToken(refreshToken: string): Promise<void> {
    await this.sessionService.deleteSession(refreshToken);
  }

  async revokeAllUserTokens(userId: string): Promise<void> {
    await this.sessionService.deleteAllUserSessions(userId);
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../database/prisma.service';
import { MailerService } from '../../mailer/mailer.service';
import { JwtService } from '@nestjs/jwt';
import { RegisterDto } from '../dto/register.dto';
import { mailQueue } from 'src/queues/mail.queue';
import * as bcrypt from 'bcrypt';
import * as crypto from 'crypto';
import { IUserRepository } from '../domain/repositories';
import {
  AuthUser,
  AuthUserCreationData,
  AuthUserUpdateData,
} from '../domain/entities';
import {
  ErrorFactory,
  DomainErrors,
  ErrorUtils,
  ValidationException,
} from '../../../shared';

@Injectable()
export class UserManagementService implements IUserRepository {
  constructor(
    private prisma: PrismaService,
    private mailerService: MailerService,
    private jwtService: JwtService,
  ) {}

  // ===== USER REGISTRATION =====

  async registerUser(registerDto: RegisterDto) {
    const { userName, email, fullName } = registerDto;

    // Check existing user
    const existingUser = await this.prisma.user.findFirst({
      where: {
        OR: [{ email }, ...(userName ? [{ username: userName }] : [])],
      },
    });

    if (existingUser) {
      if (existingUser.email === email) {
        // If the email exists but is not verified, instruct user to verify
        if (!existingUser.isEmailVerified) {
          throw ErrorFactory.businessRule(
            'Email already registered but not verified. Please verify your email or request a new verification email.',
            'EMAIL_NOT_VERIFIED',
          );
        }
        throw DomainErrors.userAlreadyExists(email);
      }
      if (userName && existingUser.username === userName) {
        throw ErrorFactory.entityAlreadyExists('Username', userName);
      }
    }

    // Create user WITHOUT password (will be set during email verification)
    const newUser = await this.prisma.user.create({
      data: {
        fullName: fullName?.trim(),
        username: userName as string,
        email,
        passwordHash: null, // No password initially
        dateOfBirth: registerDto.dateOfBirth,
        phoneNumber: registerDto.phoneNumber,
        avatar: registerDto.avatar,
        bio: registerDto.bio,
        location: registerDto.location,
        role: 'USER' as any,
      },
      select: {
        id: true,
        username: true,
        email: true,
        fullName: true,
        avatar: true,
        role: true,
        dateOfBirth: true,
        isEmailVerified: true,
        emailVerifiedAt: true,
        createdAt: true,
        updatedAt: true,
      },
    });

    // Generate email verification token
    const verificationToken = this.generateEmailToken(
      newUser.id,
      newUser.email,
    );

    // Send verification email (using queue)
    await mailQueue.add('send-verification-email', {
      email: newUser.email,
      userName: newUser.username,
      verificationLink: `${process.env.FRONTEND_URL}/verify-email/${verificationToken}`,
    });

    return {
      message:
        'Registration successful. Please check your email for verification.',
      user: newUser,
    };
  }

  // ===== EMAIL VERIFICATION =====

  generateEmailToken(userId: string, email: string): string {
    const payload = {
      sub: userId,
      email,
      type: 'email-verification',
      timestamp: Date.now(),
    };
    return this.jwtService.sign(payload, { expiresIn: '24h' });
  }

  async verifyEmail(token: string, password?: string) {
    try {
      const payload = this.jwtService.verify(token);

      if (payload.type !== 'email-verification') {
        throw ErrorFactory.validation('Invalid token type', {
          token: ['Token is not a valid email verification token'],
        });
      }

      const user = await this.prisma.user.findUnique({
        where: { id: payload.sub },
      });

      if (!user) {
        throw DomainErrors.userNotFound(payload.sub);
      }

      if (user.isEmailVerified) {
        return {
          message: 'Email has already been verified. You can now log in.',
        };
      }

      // Update verification status
      const updateData: any = {
        isEmailVerified: true,
        emailVerifiedAt: new Date(),
      };

      // If password provided during verification, hash and update it
      if (password) {
        updateData.password = await bcrypt.hash(password, 10);
      }

      await this.prisma.user.update({
        where: { id: user.id },
        data: updateData,
      });

      return {
        message: 'Email verified successfully. You can now log in.',
        user: {
          id: user.id,
          email: user.email,
          userName: user.username,
          fullName: user.fullName,
          isEmailVerified: true,
        },
      };
    } catch (error) {
      if (error.name === 'TokenExpiredError') {
        throw ErrorFactory.validation(
          'Verification token has expired. Please request a new one.',
          { token: ['Token has expired'] },
        );
      }
      if (error.name === 'JsonWebTokenError') {
        throw ErrorFactory.validation('Invalid verification token', {
          token: ['Token is invalid'],
        });
      }
      throw error;
    }
  }

  async resendVerificationEmail(email: string) {
    const user = await this.prisma.user.findUnique({
      where: { email },
    });

    if (!user) {
      // Don't reveal if user exists for security
      return {
        message: 'If the email exists, a verification email has been sent.',
      };
    }

    if (user.isEmailVerified) {
      throw ErrorFactory.businessRule(
        'Email is already verified',
        'EMAIL_ALREADY_VERIFIED',
      );
    }

    const verificationToken = this.generateEmailToken(user.id, user.email);

    await mailQueue.add('send-verification-email', {
      email: user.email,
      userName: user.username,
      verificationLink: `${process.env.FRONTEND_URL}/verify-email/${verificationToken}`,
    });

    return { message: 'Verification email sent successfully' };
  }

  // ===== PASSWORD RESET =====

  async forgotPassword(email: string) {
    const user = await this.prisma.user.findUnique({
      where: { email },
    });

    if (!user) {
      // Don't reveal if user exists
      return {
        message: 'If the email exists, a password reset link has been sent.',
      };
    }

    const resetToken = this.jwtService.sign(
      {
        sub: user.id,
        email: user.email,
        type: 'password-reset',
        timestamp: Date.now(),
      },
      { expiresIn: '1h' },
    );

    await mailQueue.add('send-password-reset-email', {
      email: user.email,
      userName: user.username,
      resetLink: `${process.env.FRONTEND_URL}/reset-password?token=${resetToken}`,
    });

    return { message: 'Password reset link sent successfully' };
  }

  async resetPassword(
    token: string,
    newPassword: string,
    confirmPassword: string,
  ) {
    if (newPassword !== confirmPassword) {
      throw ErrorFactory.validation('Passwords do not match', {
        confirmPassword: ['Must match new password'],
      });
    }

    ErrorUtils.validateStringLength(newPassword, 6, 255, 'password');

    try {
      const payload = this.jwtService.verify(token);

      if (payload.type !== 'password-reset') {
        throw ErrorFactory.validation('Invalid token type', {
          token: ['Token is not a valid password reset token'],
        });
      }

      const user = await this.prisma.user.findUnique({
        where: { id: payload.sub },
      });

      if (!user) {
        throw DomainErrors.userNotFound(payload.sub);
      }

      const hashedPassword = await bcrypt.hash(newPassword, 10);

      await this.prisma.user.update({
        where: { id: user.id },
        data: { passwordHash: hashedPassword },
      });

      return {
        message:
          'Password has been reset successfully. Please log in with your new password.',
      };
    } catch (error) {
      if (error.name === 'TokenExpiredError') {
        throw ErrorFactory.validation(
          'Reset token has expired. Please request a new one.',
          { token: ['Token has expired'] },
        );
      }
      if (error.name === 'JsonWebTokenError') {
        throw ErrorFactory.validation('Invalid reset token', {
          token: ['Token is invalid'],
        });
      }
      throw error;
    }
  }

  // ===== IMPLEMENT IUserRepository INTERFACE =====

  async createUser(userData: AuthUserCreationData): Promise<AuthUser> {
    const user = await this.prisma.user.create({
      data: {
        username: userData.userName,
        email: userData.email,
        fullName: userData.fullName,
        passwordHash: userData.password,
        avatar: userData.avatar,
        role: (userData.role as any) || 'USER',
        isEmailVerified: userData.isEmailVerified || false,
        dateOfBirth: new Date(), // Default for now
      },
    });

    return this.mapPrismaUserToAuthUser(user);
  }

  async findUserById(id: string): Promise<AuthUser | null> {
    const user = await this.prisma.user.findUnique({ where: { id } });
    return user ? this.mapPrismaUserToAuthUser(user) : null;
  }

  async findUserByEmail(email: string): Promise<AuthUser | null> {
    const user = await this.prisma.user.findUnique({ where: { email } });
    return user ? this.mapPrismaUserToAuthUser(user) : null;
  }

  async findUserByUsername(username: string): Promise<AuthUser | null> {
    const user = await this.prisma.user.findUnique({ where: { username } });
    return user ? this.mapPrismaUserToAuthUser(user) : null;
  }

  async findUserByEmailOrUsername(
    identifier: string,
  ): Promise<AuthUser | null> {
    const user = await this.prisma.user.findFirst({
      where: {
        OR: [{ email: identifier }, { username: identifier }],
      },
    });
    return user ? this.mapPrismaUserToAuthUser(user) : null;
  }

  async updateUser(
    id: string,
    userData: AuthUserUpdateData,
  ): Promise<AuthUser> {
    const user = await this.prisma.user.update({
      where: { id },
      data: {
        username: userData.userName,
        email: userData.email,
        fullName: userData.fullName,
        passwordHash: userData.password,
        avatar: userData.avatar,
        isEmailVerified: userData.isEmailVerified,
        emailVerifiedAt: userData.emailVerifiedAt,
      },
    });

    return this.mapPrismaUserToAuthUser(user);
  }

  async updateUserPassword(id: string, hashedPassword: string): Promise<void> {
    await this.prisma.user.update({
      where: { id },
      data: { passwordHash: hashedPassword },
    });
  }

  async setEmailVerificationToken(
    userId: string,
    token: string,
  ): Promise<void> {
    // TODO: Add emailVerificationToken field to schema or use Redis
    console.log(
      `Setting email verification token for user ${userId}: ${token}`,
    );
  }

  async verifyEmailByToken(token: string): Promise<AuthUser | null> {
    // This delegates to existing verifyEmail method
    try {
      const result = await this.verifyEmail(token);
      if (result.user) {
        const user = await this.findUserById(result.user.id);
        return user;
      }
    } catch (error) {
      console.error('Error verifying email by token:', error);
    }
    return null;
  }

  async setPasswordResetToken(
    userId: string,
    token: string,
    expiresAt: Date,
  ): Promise<void> {
    // TODO: Add password reset token fields to schema or use Redis
    console.log(
      `Setting password reset token for user ${userId}: ${token}, expires: ${expiresAt}`,
    );
  }

  async findUserByPasswordResetToken(token: string): Promise<AuthUser | null> {
    // TODO: Implement with token storage
    console.log(`Finding user by password reset token: ${token}`);
    return null;
  }

  async clearPasswordResetToken(userId: string): Promise<void> {
    // TODO: Implement with token storage
    console.log(`Clearing password reset token for user: ${userId}`);
  }

  async existsByEmail(email: string): Promise<boolean> {
    const count = await this.prisma.user.count({ where: { email } });
    return count > 0;
  }

  async existsByUsername(username: string): Promise<boolean> {
    const count = await this.prisma.user.count({ where: { username } });
    return count > 0;
  }

  private mapPrismaUserToAuthUser(user: any): AuthUser {
    return {
      id: user.id,
      email: user.email,
      userName: user.username, // Map from Prisma username to entity userName
      fullName: user.fullName,
      password: user.passwordHash, // Map from Prisma passwordHash to entity password
      avatar: user.avatar,
      role: user.role,
      isEmailVerified: user.isEmailVerified,
      emailVerifiedAt: user.emailVerifiedAt,
      createdAt: user.createdAt,
      updatedAt: user.updatedAt,
    };
  }
}

import { ExtractJwt, Strategy } from 'passport-jwt';
import { PassportStrategy } from '@nestjs/passport';
import { Injectable, UnauthorizedException } from '@nestjs/common';
import { AuthService } from '../auth.service';
import { JWT } from 'src/config/jwt.config';
import { Request } from 'express';

@Injectable()
export class JwtStrategy extends PassportStrategy(Strategy) {
  constructor(private authService: AuthService) {
    super({
      jwtFromRequest: ExtractJwt.fromExtractors([
        // First try Bearer token from header (for mobile clients)
        ExtractJwt.fromAuthHeaderAsBearerToken(),
        // Then try cookie (for web clients)
        (request: Request) => {
          const clientType = (
            request.headers['x-client-type'] ||
            request.query.client ||
            ''
          ).toString();
          const isWeb = clientType.toLowerCase() === 'web';

          // Only extract from cookie for web clients
          if (isWeb && request.cookies) {
            return request.cookies['access_token'];
          }
          return null;
        },
      ]),
      ignoreExpiration: false,
      secretOrKey: JWT.SECRET as string,
      passReqToCallback: false,
    });
  }

  async validate(payload: any) {
    const user = await this.authService.validateUserById(payload.sub);
    if (!user) {
      throw new UnauthorizedException();
    }
    return {
      id: user.id,
      email: user.email,
      userName: user.username,
      role: user.role,
      fullName: user.fullName,
    };
  }
}

import { Injectable } from '@nestjs/common';
import { PassportStrategy } from '@nestjs/passport';
import { Strategy, VerifyCallback } from 'passport-google-oauth20';
import { AuthService } from '../auth.service';
import { GOOGLE_CONFIG } from 'src/config/google.config';
import { URLS } from '../../../shared/constants/urls.constant';

@Injectable()
export class GoogleStrategy extends PassportStrategy(Strategy, 'google') {
  constructor(private authService: AuthService) {
    const clientID = GOOGLE_CONFIG.clientID;
    const clientSecret = GOOGLE_CONFIG.clientSecret;
    const callbackURL = URLS.GOOGLE_CALLBACK;

    if (!clientID || !clientSecret) {
      console.warn(
        'Google OAuth not configured. Please set GOOGLE_CLIENT_ID and GOOGLE_CLIENT_SECRET environment variables.',
      );
    }

    super({
      clientID: clientID || 'dummy-client-id',
      clientSecret: clientSecret || 'dummy-client-secret',
      callbackURL: callbackURL || 'http://localhost:3107/auth/google/callback',
      scope: ['email', 'profile'],
    });
  }

  async validate(
    accessToken: string,
    refreshToken: string,
    profile: any,
    done: VerifyCallback,
  ): Promise<any> {
    try {
      const { id, name, emails, photos } = profile;
      const user = {
        googleId: id,
        email: emails[0].value,
        fullName: `${name.givenName} ${name.familyName}`,
        firstName: name.givenName,
        lastName: name.familyName,
        profilePicture: photos[0].value,
        accessToken,
      };

      const result = await this.authService.googleLogin(user);
      done(null, result);
    } catch (error) {
      done(error, false);
    }
  }
}

import { SetMetadata } from '@nestjs/common';
import { CacheConfig, CacheConfigName } from './cache.interfaces';

// Metadata keys for decorators
export const CACHEABLE_KEY = 'cacheable';
export const CACHE_EVICT_KEY = 'cache_evict';
export const CACHE_UPDATE_KEY = 'cache_update';
export const CACHE_WARM_KEY = 'cache_warm';

// Decorator metadata interfaces
export interface CacheableMetadata {
  keyTemplate: string;
  config: CacheConfig | CacheConfigName;
  condition?: string; // Optional condition function
}

export interface CacheEvictMetadata {
  keys: string[]; // Array of key patterns to evict
  allEntries?: boolean; // If true, clear all cache
  beforeInvocation?: boolean; // If true, evict before method execution
}

export interface CacheUpdateMetadata {
  keyTemplate: string;
  evictPatterns?: string[]; // Patterns to evict after update
  config: CacheConfig | CacheConfigName;
}

export interface CacheWarmMetadata {
  keyTemplate: string;
  config: CacheConfig | CacheConfigName;
}

// Cache key template interpolation utility
export function interpolateTemplate(
  template: string,
  args: any[],
  paramNames: string[],
): string {
  let result = template;

  // Replace parameter placeholders like {{id}}, {{userId}}
  paramNames.forEach((paramName, index) => {
    const placeholder = `{{${paramName}}}`;
    if (result.includes(placeholder) && args[index] !== undefined) {
      result = result.replace(
        new RegExp(placeholder.replace(/[{}]/g, '\\$&'), 'g'),
        String(args[index]),
      );
    }
  });

  // Replace numeric placeholders like {{0}}, {{1}}
  args.forEach((arg, index) => {
    const placeholder = `{{${index}}}`;
    if (result.includes(placeholder)) {
      result = result.replace(
        new RegExp(placeholder.replace(/[{}]/g, '\\$&'), 'g'),
        String(arg),
      );
    }
  });

  return result;
}

/**
 * Cacheable decorator - automatically caches method results
 * @param keyTemplate Template for cache key (e.g., "user:{{id}}" or "post:{{0}}")
 * @param config Cache configuration name or object
 * @param condition Optional condition to check before caching
 */
export function Cacheable(
  keyTemplate: string,
  config: CacheConfig | CacheConfigName,
  condition?: string,
) {
  return function (
    target: any,
    propertyName: string,
    descriptor: PropertyDescriptor,
  ) {
    const metadata: CacheableMetadata = {
      keyTemplate,
      config,
      condition,
    };

    SetMetadata(CACHEABLE_KEY, metadata)(target, propertyName, descriptor);

    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      // This will be handled by the interceptor
      return originalMethod.apply(this, args);
    };

    return descriptor;
  };
}

/**
 * CacheEvict decorator - automatically removes cache entries
 * @param keys Array of key patterns to evict
 * @param allEntries If true, clear all cache entries
 * @param beforeInvocation If true, evict before method execution
 */
export function CacheEvict(
  keys: string[],
  allEntries: boolean = false,
  beforeInvocation: boolean = false,
) {
  return function (
    target: any,
    propertyName: string,
    descriptor: PropertyDescriptor,
  ) {
    const metadata: CacheEvictMetadata = {
      keys,
      allEntries,
      beforeInvocation,
    };

    SetMetadata(CACHE_EVICT_KEY, metadata)(target, propertyName, descriptor);

    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      // This will be handled by the interceptor
      return originalMethod.apply(this, args);
    };

    return descriptor;
  };
}

/**
 * CacheUpdate decorator - caches result and evicts related entries
 * @param keyTemplate Template for cache key
 * @param evictPatterns Array of patterns to evict after update
 * @param config Cache configuration
 */
export function CacheUpdate(
  keyTemplate: string,
  evictPatterns: string[] = [],
  config: CacheConfig | CacheConfigName,
) {
  return function (
    target: any,
    propertyName: string,
    descriptor: PropertyDescriptor,
  ) {
    const metadata: CacheUpdateMetadata = {
      keyTemplate,
      evictPatterns,
      config,
    };

    SetMetadata(CACHE_UPDATE_KEY, metadata)(target, propertyName, descriptor);

    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      // This will be handled by the interceptor
      return originalMethod.apply(this, args);
    };

    return descriptor;
  };
}

/**
 * CacheWarm decorator - preloads cache with method result
 * @param keyTemplate Template for cache key
 * @param config Cache configuration
 */
export function CacheWarm(
  keyTemplate: string,
  config: CacheConfig | CacheConfigName,
) {
  return function (
    target: any,
    propertyName: string,
    descriptor: PropertyDescriptor,
  ) {
    const metadata: CacheWarmMetadata = {
      keyTemplate,
      config,
    };

    SetMetadata(CACHE_WARM_KEY, metadata)(target, propertyName, descriptor);

    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      // This will be handled by the interceptor
      return originalMethod.apply(this, args);
    };

    return descriptor;
  };
}

import {
  Injectable,
  NestInterceptor,
  ExecutionContext,
  CallHandler,
  Logger,
} from '@nestjs/common';
import { Observable, of } from 'rxjs';
import { tap } from 'rxjs/operators';
import { Reflector } from '@nestjs/core';
import { RedisCacheService } from './cache.service';
import {
  CACHEABLE_KEY,
  CACHE_EVICT_KEY,
  CACHE_UPDATE_KEY,
  CACHE_WARM_KEY,
  CacheableMetadata,
  CacheEvictMetadata,
  CacheUpdateMetadata,
  CacheWarmMetadata,
  interpolateTemplate,
} from './cache.decorators';

@Injectable()
export class CacheInterceptor implements NestInterceptor {
  private readonly logger = new Logger(CacheInterceptor.name);

  constructor(
    private readonly reflector: Reflector,
    private readonly cacheService: RedisCacheService,
  ) {}

  async intercept(
    context: ExecutionContext,
    next: CallHandler,
  ): Promise<Observable<any>> {
    const handler = context.getHandler();
    const target = context.getClass();

    // Get method parameter names for template interpolation
    const paramNames = this.getParameterNames(handler);
    const args = context.getArgs();

    // Check for cache decorators
    const cacheableMetadata = this.reflector.get<CacheableMetadata>(
      CACHEABLE_KEY,
      handler,
    );
    const cacheEvictMetadata = this.reflector.get<CacheEvictMetadata>(
      CACHE_EVICT_KEY,
      handler,
    );
    const cacheUpdateMetadata = this.reflector.get<CacheUpdateMetadata>(
      CACHE_UPDATE_KEY,
      handler,
    );
    const cacheWarmMetadata = this.reflector.get<CacheWarmMetadata>(
      CACHE_WARM_KEY,
      handler,
    );

    // Handle cache eviction before method execution
    if (cacheEvictMetadata?.beforeInvocation) {
      await this.handleCacheEviction(cacheEvictMetadata, args, paramNames);
    }

    // Handle cacheable operations
    if (cacheableMetadata) {
      return this.handleCacheable(cacheableMetadata, args, paramNames, next);
    }

    // Execute the method
    const result = next.handle();

    return result.pipe(
      tap(async (methodResult) => {
        // Handle cache operations after method execution

        // Cache eviction after execution
        if (cacheEvictMetadata && !cacheEvictMetadata.beforeInvocation) {
          await this.handleCacheEviction(cacheEvictMetadata, args, paramNames);
        }

        // Cache update operations
        if (cacheUpdateMetadata) {
          await this.handleCacheUpdate(
            cacheUpdateMetadata,
            args,
            paramNames,
            methodResult,
          );
        }

        // Cache warm operations
        if (cacheWarmMetadata) {
          await this.handleCacheWarm(
            cacheWarmMetadata,
            args,
            paramNames,
            methodResult,
          );
        }
      }),
    );
  }

  private async handleCacheable(
    metadata: CacheableMetadata,
    args: any[],
    paramNames: string[],
    next: CallHandler,
  ): Promise<Observable<any>> {
    try {
      const cacheKey = interpolateTemplate(
        metadata.keyTemplate,
        args,
        paramNames,
      );

      // Try to get from cache first
      const cachedResult = await this.cacheService.get(
        cacheKey,
        metadata.config,
      );

      if (cachedResult !== null) {
        this.logger.debug(`Cache hit for key: ${cacheKey}`);
        return of(cachedResult);
      }

      // Cache miss - execute method and cache result
      this.logger.debug(`Cache miss for key: ${cacheKey}`);

      return next.handle().pipe(
        tap(async (result) => {
          if (result !== null && result !== undefined) {
            await this.cacheService.set(cacheKey, result, metadata.config);
            this.logger.debug(`Cached result for key: ${cacheKey}`);
          }
        }),
      );
    } catch (error) {
      this.logger.error('Error in cacheable operation', error);
      return next.handle();
    }
  }

  private async handleCacheEviction(
    metadata: CacheEvictMetadata,
    args: any[],
    paramNames: string[],
  ): Promise<void> {
    try {
      if (metadata.allEntries) {
        await this.cacheService.clear();
        this.logger.debug('Cleared all cache entries');
        return;
      }

      for (const keyPattern of metadata.keys) {
        const resolvedPattern = interpolateTemplate(
          keyPattern,
          args,
          paramNames,
        );

        if (resolvedPattern.includes('*')) {
          // Pattern-based eviction
          await this.cacheService.invalidateByPattern(resolvedPattern);
          this.logger.debug(`Evicted cache pattern: ${resolvedPattern}`);
        } else {
          // Single key eviction
          await this.cacheService.del(resolvedPattern);
          this.logger.debug(`Evicted cache key: ${resolvedPattern}`);
        }
      }
    } catch (error) {
      this.logger.error('Error in cache eviction', error);
    }
  }

  private async handleCacheUpdate(
    metadata: CacheUpdateMetadata,
    args: any[],
    paramNames: string[],
    result: any,
  ): Promise<void> {
    try {
      // Cache the updated result
      const cacheKey = interpolateTemplate(
        metadata.keyTemplate,
        args,
        paramNames,
      );
      await this.cacheService.set(cacheKey, result, metadata.config);
      this.logger.debug(`Updated cache for key: ${cacheKey}`);

      // Evict related cache entries
      if (metadata.evictPatterns) {
        for (const pattern of metadata.evictPatterns) {
          const resolvedPattern = interpolateTemplate(
            pattern,
            args,
            paramNames,
          );
          await this.cacheService.invalidateByPattern(resolvedPattern);
          this.logger.debug(
            `Evicted related cache pattern: ${resolvedPattern}`,
          );
        }
      }
    } catch (error) {
      this.logger.error('Error in cache update', error);
    }
  }

  private async handleCacheWarm(
    metadata: CacheWarmMetadata,
    args: any[],
    paramNames: string[],
    result: any,
  ): Promise<void> {
    try {
      const cacheKey = interpolateTemplate(
        metadata.keyTemplate,
        args,
        paramNames,
      );
      await this.cacheService.set(cacheKey, result, metadata.config);
      this.logger.debug(`Warmed cache for key: ${cacheKey}`);
    } catch (error) {
      this.logger.error('Error in cache warming', error);
    }
  }

  private getParameterNames(func: Function): string[] {
    // Extract parameter names from function signature
    const funcStr = func.toString();
    const match = funcStr.match(/\(([^)]*)\)/);

    if (!match || !match[1].trim()) {
      return [];
    }

    return match[1]
      .split(',')
      .map((param) => param.trim().split(/[\s=]/)[0])
      .filter((name) => name && !name.startsWith('...'));
  }
}

import { Injectable } from '@nestjs/common';

// Cache configuration interfaces
export interface CacheConfig {
  ttl?: number; // Time to live in seconds
  prefix?: string; // Key prefix
  tags?: string[]; // Tags for invalidation
  serialize?: boolean; // Whether to serialize data
  compress?: boolean; // Whether to compress data
}

export interface CacheStats {
  hits: number;
  misses: number;
  hitRate: number;
  totalOperations: number;
  lastReset: Date;
}

export enum CacheEvent {
  USER_CREATED = 'user.created',
  USER_UPDATED = 'user.updated',
  USER_DELETED = 'user.deleted',
  POST_CREATED = 'post.created',
  POST_UPDATED = 'post.updated',
  POST_DELETED = 'post.deleted',
  FOLLOW_CREATED = 'follow.created',
  FOLLOW_DELETED = 'follow.deleted',
}

// Predefined cache configurations for common use cases
export const DEFAULT_CACHE_CONFIGS = {
  USER_PROFILE: {
    ttl: 1800, // 30 minutes
    prefix: 'user:profile',
    tags: ['user'],
    serialize: true,
  } as CacheConfig,

  POST: {
    ttl: 600, // 10 minutes
    prefix: 'post',
    tags: ['post'],
    serialize: true,
  } as CacheConfig,

  USER_FEED: {
    ttl: 300, // 5 minutes
    prefix: 'user:feed',
    tags: ['user', 'feed'],
    serialize: true,
    compress: true, // Feeds can be large
  } as CacheConfig,

  POST_LIST: {
    ttl: 600, // 10 minutes
    prefix: 'post:list',
    tags: ['post', 'list'],
    serialize: true,
    compress: true,
  } as CacheConfig,

  USER_LIST: {
    ttl: 900, // 15 minutes
    prefix: 'user:list',
    tags: ['user', 'list'],
    serialize: true,
  } as CacheConfig,

  SEARCH_RESULTS: {
    ttl: 600, // 10 minutes
    prefix: 'search',
    tags: ['search'],
    serialize: true,
    compress: true,
  } as CacheConfig,

  AUTH_STATE: {
    ttl: 300, // 5 minutes
    prefix: 'auth:state',
    tags: ['auth'],
    serialize: true,
  } as CacheConfig,

  NOTIFICATION_LIST: {
    ttl: 180, // 3 minutes
    prefix: 'notifications',
    tags: ['notification'],
    serialize: true,
  } as CacheConfig,
} as const;

export type CacheConfigName = keyof typeof DEFAULT_CACHE_CONFIGS;

import { CacheModule } from '@nestjs/cache-manager';
import { Module } from '@nestjs/common';
import { APP_INTERCEPTOR } from '@nestjs/core';
import { RedisCacheService } from './cache.service';
import { CacheUtils } from './cache.utils';
import { CacheInterceptor } from './cache.interceptor';
import Keyv from 'keyv';
import { CacheableMemory } from 'cacheable';
import { createKeyv } from '@keyv/redis';
import { REDIS } from 'src/config/redis.config';

@Module({
  imports: [
    CacheModule.registerAsync({
      isGlobal: true,
      useFactory: async () => {
        return {
          stores: [
            new Keyv({
              store: new CacheableMemory({ ttl: 60000, lruSize: 5000 }),
            }),
            createKeyv(REDIS.URL),
          ],
        };
      },
    }),
  ],
  providers: [
    RedisCacheService,
    CacheUtils,
    {
      provide: APP_INTERCEPTOR,
      useClass: CacheInterceptor,
    },
  ],
  exports: [RedisCacheService, CacheUtils],
})
export class RedisCacheModule {}

import { CACHE_MANAGER } from '@nestjs/cache-manager';
import { Inject, Injectable, Logger } from '@nestjs/common';
import { Cache } from 'cache-manager';
import * as zlib from 'zlib';
import { promisify } from 'util';
import {
  CacheConfig,
  CacheStats,
  CacheEvent,
  DEFAULT_CACHE_CONFIGS,
  CacheConfigName,
} from './cache.interfaces';

const gzip = promisify(zlib.gzip);
const gunzip = promisify(zlib.gunzip);

@Injectable()
export class RedisCacheService {
  private readonly logger = new Logger(RedisCacheService.name);
  private stats: CacheStats = {
    hits: 0,
    misses: 0,
    hitRate: 0,
    totalOperations: 0,
    lastReset: new Date(),
  };

  constructor(@Inject(CACHE_MANAGER) private readonly cacheManager: Cache) {}

  private getRedisClient() {
    try {
      const stores = (this.cacheManager as any).stores;
      const redisStore =
        stores?.find((store: any) => store.name === 'redis') || stores?.[1];
      const client = redisStore?.getClient?.() || redisStore;

      // Debug: log available methods (only once)
      if (client && !this.loggedMethods) {
        this.loggedMethods = true;
        const methods = Object.getOwnPropertyNames(
          Object.getPrototypeOf(client),
        )
          .filter(
            (name) =>
              typeof client[name] === 'function' &&
              name.toLowerCase().includes('z'),
          )
          .sort();
        this.logger.debug('Available Redis ZSET methods:', methods);
      }

      return client;
    } catch (err) {
      this.logger.error('Error accessing Redis client', err);
      return null;
    }
  }

  private loggedMethods = false;

  // Test method to check Redis connection and methods
  async testRedisConnection(): Promise<void> {
    try {
      const client = this.getRedisClient();
      this.logger.log('Redis client type:', typeof client);
      this.logger.log('Redis client constructor:', client?.constructor?.name);

      if (client) {
        // List all methods available on the client
        const allMethods = Object.getOwnPropertyNames(
          Object.getPrototypeOf(client),
        )
          .filter((name) => typeof client[name] === 'function')
          .sort();
        this.logger.log('All available methods:', allMethods);
      }
    } catch (err) {
      this.logger.error('Error testing Redis connection:', err);
    }
  }

  // Helper methods for configuration and serialization
  getConfig(configName: CacheConfigName | string): CacheConfig {
    if (typeof configName === 'string' && configName in DEFAULT_CACHE_CONFIGS) {
      return DEFAULT_CACHE_CONFIGS[configName as CacheConfigName];
    }
    return { ttl: 300, serialize: true }; // Default config
  }

  private generateCacheKey(key: string, config: CacheConfig): string {
    return config.prefix ? `${config.prefix}:${key}` : key;
  }

  private async serializeData(
    data: any,
    config: CacheConfig,
  ): Promise<string | Buffer> {
    if (!config.serialize) return data;

    const serialized = JSON.stringify(data);

    if (config.compress) {
      try {
        return await gzip(serialized);
      } catch (error) {
        this.logger.warn('Compression failed, using uncompressed data', error);
        return serialized;
      }
    }

    return serialized;
  }

  private async deserializeData<T>(data: any, config: CacheConfig): Promise<T> {
    if (!config.serialize) return data as T;

    try {
      let stringData: string;

      if (config.compress && Buffer.isBuffer(data)) {
        const decompressed = await gunzip(data);
        stringData = decompressed.toString();
      } else {
        stringData = typeof data === 'string' ? data : JSON.stringify(data);
      }

      return JSON.parse(stringData);
    } catch (error) {
      this.logger.error('Failed to deserialize cache data', error);
      return data as T;
    }
  }

  private updateStats(isHit: boolean): void {
    if (isHit) {
      this.stats.hits++;
    } else {
      this.stats.misses++;
    }
    this.stats.totalOperations++;
    this.stats.hitRate = (this.stats.hits / this.stats.totalOperations) * 100;
  }

  // Enhanced get method with config support
  async get<T>(
    key: string,
    config?: CacheConfig | CacheConfigName,
  ): Promise<T | null> {
    try {
      const cacheConfig =
        typeof config === 'string'
          ? this.getConfig(config)
          : config || { ttl: 300 };
      const fullKey = this.generateCacheKey(key, cacheConfig);

      const value = await this.cacheManager.get(fullKey);

      if (value !== null && value !== undefined) {
        this.updateStats(true);
        return await this.deserializeData<T>(value, cacheConfig);
      }

      this.updateStats(false);
      return null;
    } catch (err) {
      this.logger.error(`Error reading cache key=${key}`, err);
      this.updateStats(false);
      return null;
    }
  }

  // Enhanced set method with config support
  async set<T>(
    key: string,
    value: T,
    config?: CacheConfig | CacheConfigName | number,
  ): Promise<void> {
    try {
      let cacheConfig: CacheConfig;

      // Handle backward compatibility with old ttlSeconds parameter
      if (typeof config === 'number') {
        cacheConfig = { ttl: config, serialize: true };
      } else if (typeof config === 'string') {
        cacheConfig = this.getConfig(config);
      } else {
        cacheConfig = config || { ttl: 300, serialize: true };
      }

      const fullKey = this.generateCacheKey(key, cacheConfig);
      const serializedValue = await this.serializeData(value, cacheConfig);

      await this.cacheManager.set(
        fullKey,
        serializedValue,
        cacheConfig.ttl ?? 300,
      );

      // Store tags for invalidation
      if (cacheConfig.tags && cacheConfig.tags.length > 0) {
        await this.addToTagIndex(cacheConfig.tags, fullKey);
      }
    } catch (err) {
      this.logger.error(`Error setting cache key=${key}`, err);
    }
  }

  // Helper methods for tag indexing
  private async addToTagIndex(tags: string[], key: string): Promise<void> {
    try {
      for (const tag of tags) {
        const tagKey = `tag:${tag}`;
        await this.cacheManager.set(`${tagKey}:${key}`, '1', 3600); // 1 hour for tag index
      }
    } catch (err) {
      this.logger.warn('Failed to update tag index', err);
    }
  }

  private async removeFromTagIndex(tags: string[], key: string): Promise<void> {
    try {
      for (const tag of tags) {
        const tagKey = `tag:${tag}:${key}`;
        await this.cacheManager.del(tagKey);
      }
    } catch (err) {
      this.logger.warn('Failed to remove from tag index', err);
    }
  }

  // Enhanced delete method
  async del(
    key: string,
    config?: CacheConfig | CacheConfigName,
  ): Promise<void> {
    try {
      const cacheConfig =
        typeof config === 'string' ? this.getConfig(config) : config;
      const fullKey = cacheConfig
        ? this.generateCacheKey(key, cacheConfig)
        : key;

      await this.cacheManager.del(fullKey);

      // Remove from tag index if tags exist
      if (cacheConfig?.tags) {
        await this.removeFromTagIndex(cacheConfig.tags, fullKey);
      }
    } catch (err) {
      this.logger.error(`Error deleting cache key=${key}`, err);
    }
  }

  // Pattern-based invalidation
  async invalidateByPattern(pattern: string): Promise<void> {
    try {
      const client = this.getRedisClient();
      if (!client) {
        this.logger.warn('Redis client not available for pattern invalidation');
        return;
      }

      // Use SCAN to find keys matching pattern
      const keys: string[] = [];
      let cursor = '0';

      do {
        const result = await client.scan(
          cursor,
          'MATCH',
          pattern,
          'COUNT',
          1000,
        );
        cursor = result[0];
        keys.push(...result[1]);
      } while (cursor !== '0');

      if (keys.length > 0) {
        await client.del(...keys);
        this.logger.debug(
          `Invalidated ${keys.length} keys matching pattern: ${pattern}`,
        );
      }
    } catch (err) {
      this.logger.error(`Error invalidating pattern=${pattern}`, err);
    }
  }

  // Tag-based invalidation
  async invalidateByTag(tag: string): Promise<void> {
    try {
      await this.invalidateByPattern(`tag:${tag}:*`);
    } catch (err) {
      this.logger.error(`Error invalidating tag=${tag}`, err);
    }
  }

  // Event-based invalidation
  async invalidateByEvent(
    event: CacheEvent,
    data: Record<string, any>,
  ): Promise<void> {
    try {
      switch (event) {
        case CacheEvent.USER_UPDATED:
          await this.invalidateByPattern(`user:profile:${data.userId}`);
          await this.invalidateByPattern(`user:feed:${data.userId}:*`);
          break;
        case CacheEvent.POST_CREATED:
        case CacheEvent.POST_UPDATED:
          await this.invalidateByPattern(`user:feed:*`);
          await this.invalidateByPattern(`post:list:*`);
          break;
        case CacheEvent.USER_DELETED:
          await this.invalidateByPattern(`user:*:${data.userId}:*`);
          await this.invalidateByPattern(`*:${data.userId}:*`);
          break;
        default:
          this.logger.warn(`Unknown cache event: ${event}`);
      }
    } catch (err) {
      this.logger.error(`Error handling cache event=${event}`, err);
    }
  }

  // Multiple key operations
  async mget<T>(
    keys: string[],
    config?: CacheConfig | CacheConfigName,
  ): Promise<(T | null)[]> {
    const promises = keys.map((key) => this.get<T>(key, config));
    return Promise.all(promises);
  }

  async mset<T>(
    keyValuePairs: Array<[string, T]>,
    config?: CacheConfig | CacheConfigName,
  ): Promise<void> {
    const promises = keyValuePairs.map(([key, value]) =>
      this.set(key, value, config),
    );
    await Promise.all(promises);
  }

  // Statistics and monitoring
  getStats(): CacheStats {
    return { ...this.stats };
  }

  resetStats(): void {
    this.stats = {
      hits: 0,
      misses: 0,
      hitRate: 0,
      totalOperations: 0,
      lastReset: new Date(),
    };
  }

  async ping(): Promise<boolean> {
    try {
      await this.cacheManager.set('ping', 'pong', 1);
      const result = await this.cacheManager.get('ping');
      await this.cacheManager.del('ping');
      return result === 'pong';
    } catch (err) {
      this.logger.error('Cache ping failed', err);
      return false;
    }
  }

  async clear(): Promise<void> {
    try {
      await this.cacheManager.clear();
      this.resetStats();
    } catch (err) {
      this.logger.error('Error clearing cache', err);
    }
  }

  // ZSET operations for Redis
  async zAdd(key: string, score: number, member: string): Promise<number> {
    try {
      const client = this.getRedisClient();
      if (!client) return 0;

      // Try different method names based on Redis client version
      if (typeof client.zadd === 'function') {
        return await client.zadd(key, score, member);
      } else if (typeof client.zAdd === 'function') {
        return await client.zAdd(key, [{ score, value: member }]);
      } else if (typeof client.ZADD === 'function') {
        return await client.ZADD(key, score, member);
      }

      this.logger.warn(`zAdd method not found on Redis client for key=${key}`);
      return 0;
    } catch (err) {
      this.logger.error(`Error adding to sorted set key=${key}`, err);
      return 0;
    }
  }

  async zRevRangeWithScores(
    key: string,
    start: number,
    stop: number,
  ): Promise<{ value: string; score: number }[]> {
    try {
      const client = this.getRedisClient();
      if (!client) return [];

      let result: any;

      // Try different method names
      if (typeof client.zrevrangebyscore === 'function') {
        result = await client.zrevrangebyscore(
          key,
          '+inf',
          '-inf',
          'WITHSCORES',
          'LIMIT',
          start,
          stop - start + 1,
        );
      } else if (typeof client.zRevRangeWithScores === 'function') {
        result = await client.zRevRangeWithScores(key, start, stop);
      } else if (typeof client.ZREVRANGE === 'function') {
        result = await client.ZREVRANGE(key, start, stop, 'WITHSCORES');
      } else if (typeof client.zrevrange === 'function') {
        result = await client.zrevrange(key, start, stop, 'WITHSCORES');
      } else {
        this.logger.warn(
          `zRevRangeWithScores method not found on Redis client for key=${key}`,
        );
        return [];
      }

      // Handle different result formats
      if (Array.isArray(result)) {
        if (
          result.length > 0 &&
          typeof result[0] === 'object' &&
          'value' in result[0]
        ) {
          // Already in correct format
          return result.map((item: any) => ({
            value: item.value,
            score: item.score,
          }));
        } else {
          // Format: [member1, score1, member2, score2, ...]
          const formatted: { value: string; score: number }[] = [];
          for (let i = 0; i < result.length; i += 2) {
            formatted.push({
              value: result[i],
              score: parseFloat(result[i + 1]),
            });
          }
          return formatted;
        }
      }

      return [];
    } catch (err) {
      this.logger.error(`Error getting sorted set range key=${key}`, err);
      return [];
    }
  }

  async zCard(key: string): Promise<number> {
    try {
      const client = this.getRedisClient();
      if (!client) return 0;

      // Try different method names
      if (typeof client.zcard === 'function') {
        return await client.zcard(key);
      } else if (typeof client.zCard === 'function') {
        return await client.zCard(key);
      } else if (typeof client.ZCARD === 'function') {
        return await client.ZCARD(key);
      }

      this.logger.warn(`zCard method not found on Redis client for key=${key}`);
      return 0;
    } catch (err) {
      this.logger.error(`Error getting sorted set count key=${key}`, err);
      return 0;
    }
  }

  async zRemRangeByRank(
    key: string,
    start: number,
    stop: number,
  ): Promise<number> {
    try {
      const client = this.getRedisClient();
      if (!client) return 0;

      // Try different method names
      if (typeof client.zremrangebyrank === 'function') {
        return await client.zremrangebyrank(key, start, stop);
      } else if (typeof client.zRemRangeByRank === 'function') {
        return await client.zRemRangeByRank(key, start, stop);
      } else if (typeof client.ZREMRANGEBYRANK === 'function') {
        return await client.ZREMRANGEBYRANK(key, start, stop);
      }

      this.logger.warn(
        `zRemRangeByRank method not found on Redis client for key=${key}`,
      );
      return 0;
    } catch (err) {
      this.logger.error(`Error removing sorted set range key=${key}`, err);
      return 0;
    }
  }

  async zRem(key: string, member: string): Promise<number> {
    try {
      const client = this.getRedisClient();
      if (!client) return 0;

      // Try different method names
      if (typeof client.zrem === 'function') {
        return await client.zrem(key, member);
      } else if (typeof client.zRem === 'function') {
        return await client.zRem(key, member);
      } else if (typeof client.ZREM === 'function') {
        return await client.ZREM(key, member);
      }

      this.logger.warn(`zRem method not found on Redis client for key=${key}`);
      return 0;
    } catch (err) {
      this.logger.error(`Error removing from sorted set key=${key}`, err);
      return 0;
    }
  }

  async zScore(key: string, member: string): Promise<number | null> {
    try {
      const client = this.getRedisClient();
      if (!client) return null;

      // Try different method names
      if (typeof client.zscore === 'function') {
        return await client.zscore(key, member);
      } else if (typeof client.zScore === 'function') {
        return await client.zScore(key, member);
      } else if (typeof client.ZSCORE === 'function') {
        return await client.ZSCORE(key, member);
      }

      this.logger.warn(
        `zScore method not found on Redis client for key=${key}`,
      );
      return null;
    } catch (err) {
      this.logger.error(`Error getting sorted set score key=${key}`, err);
      return null;
    }
  }

  async expire(key: string, seconds: number): Promise<boolean> {
    try {
      const client = this.getRedisClient();
      if (!client) return false;

      // Try different method names
      if (typeof client.expire === 'function') {
        return await client.expire(key, seconds);
      } else if (typeof client.EXPIRE === 'function') {
        return await client.EXPIRE(key, seconds);
      } else if (typeof client.pexpire === 'function') {
        return await client.pexpire(key, seconds * 1000); // Convert to milliseconds
      }

      this.logger.warn(
        `expire method not found on Redis client for key=${key}`,
      );
      return false;
    } catch (err) {
      this.logger.error(`Error setting expiry for key=${key}`, err);
      return false;
    }
  }
}

