import {
  Controller,
  Post,
  Delete,
  Param,
  UploadedFile,
  UploadedFiles,
  UseInterceptors,
  BadRequestException,
  Body,
  UseGuards,
} from '@nestjs/common';
import { FileInterceptor, FilesInterceptor } from '@nestjs/platform-express';
import { CloudinaryService } from './cloudinary.service';
import { JwtAuthGuard } from '../../shared/guards/jwt.guard';

@Controller('cloudinary')
@UseGuards(JwtAuthGuard)
export class CloudinaryController {
  constructor(private readonly cloudinaryService: CloudinaryService) {}

  @Post('upload/single')
  @UseInterceptors(
    FileInterceptor('file', {
      fileFilter: (req, file, callback) => {
        if (
          file.mimetype.startsWith('image/') ||
          file.mimetype.startsWith('video/')
        ) {
          callback(null, true);
        } else {
          callback(
            new BadRequestException('Only images and videos are allowed'),
            false,
          );
        }
      },
      limits: {
        fileSize: 50 * 1024 * 1024,
      },
    }),
  )
  async uploadSingle(
    @UploadedFile() file: Express.Multer.File,
    @Body('folder') folder?: string,
  ) {
    if (!file) {
      throw new BadRequestException('File is required');
    }

    const result = await this.cloudinaryService.uploadFile(file, folder);

    return {
      message: 'File uploaded successfully',
      data: {
        publicId: result.public_id,
        url: result.secure_url,
        format: result.format,
        resourceType: result.resource_type,
        bytes: result.bytes,
        width: result.width,
        height: result.height,
        thumbnail: this.cloudinaryService.getThumbnail(result.public_id),
      },
    };
  }

  @Post('upload/multiple')
  @UseInterceptors(
    FilesInterceptor('files', 10, {
      fileFilter: (req, file, callback) => {
        if (
          file.mimetype.startsWith('image/') ||
          file.mimetype.startsWith('video/')
        ) {
          callback(null, true);
        } else {
          callback(
            new BadRequestException('Only images and videos are allowed'),
            false,
          );
        }
      },
      limits: {
        fileSize: 50 * 1024 * 1024, // 50MB per file
      },
    }),
  )
  async uploadMultiple(
    @UploadedFiles() files: Express.Multer.File[],
    @Body('folder') folder?: string,
  ) {
    if (!files || files.length === 0) {
      throw new BadRequestException('At least one file is required');
    }
    const results = await this.cloudinaryService.uploadMultipleFiles(
      files,
      folder,
    );

    return {
      message: 'Files uploaded successfully',
      data: results.map((result) => ({
        publicId: result.public_id,
        url: result.secure_url,
        format: result.format,
        resourceType: result.resource_type,
        bytes: result.bytes,
        width: result.width,
        height: result.height,
        thumbnail: this.cloudinaryService.getThumbnail(result.public_id),
      })),
    };
  }

  @Post('upload/image')
  @UseInterceptors(
    FileInterceptor('image', {
      fileFilter: (req, file, callback) => {
        if (file.mimetype.startsWith('image/')) {
          callback(null, true);
        } else {
          callback(new BadRequestException('Only images are allowed'), false);
        }
      },
      limits: {
        fileSize: 10 * 1024 * 1024, // 10MB for images
      },
    }),
  )
  async uploadImage(
    @UploadedFile() file: Express.Multer.File,
    @Body('folder') folder?: string,
  ) {
    if (!file) {
      throw new BadRequestException('Image file is required');
    }

    const result = await this.cloudinaryService.uploadImage(file, folder);

    return {
      message: 'Image uploaded successfully',
      data: {
        publicId: result.public_id,
        url: result.secure_url,
        format: result.format,
        bytes: result.bytes,
        width: result.width,
        height: result.height,
        thumbnail: this.cloudinaryService.getThumbnail(result.public_id),
      },
    };
  }

  @Post('upload/video')
  @UseInterceptors(
    FileInterceptor('video', {
      fileFilter: (req, file, callback) => {
        if (file.mimetype.startsWith('video/')) {
          callback(null, true);
        } else {
          callback(new BadRequestException('Only videos are allowed'), false);
        }
      },
      limits: {
        fileSize: 100 * 1024 * 1024, // 100MB for videos
      },
    }),
  )
  async uploadVideo(
    @UploadedFile() file: Express.Multer.File,
    @Body('folder') folder?: string,
  ) {
    if (!file) {
      throw new BadRequestException('Video file is required');
    }

    const result = await this.cloudinaryService.uploadVideo(file, folder);

    return {
      message: 'Video uploaded successfully',
      data: {
        publicId: result.public_id,
        url: result.secure_url,
        format: result.format,
        bytes: result.bytes,
        width: result.width,
        height: result.height,
      },
    };
  }

  @Delete(':publicId')
  async deleteFile(@Param('publicId') publicId: string) {
    const result = await this.cloudinaryService.deleteFile(publicId);

    return {
      message: 'File deleted successfully',
      data: result,
    };
  }
}

import { Injectable, BadRequestException } from '@nestjs/common';
import { v2 as cloudinary } from 'cloudinary';
import { Readable } from 'stream';
import { CloudinaryResponse } from './dto/cloudinary.response';
import { CLOUDINARY } from 'src/config/cloudinary.constant';
import { CloudinaryConfig } from 'src/config/cloudinary.config';

@Injectable()
export class CloudinaryService {
  constructor() {
    cloudinary.config(CloudinaryConfig.useFactory());
  }

  async generateSignature(paramsToSign: Record<string, any>): Promise<string> {
    try {
      return cloudinary.utils.api_sign_request(
        paramsToSign,
        CLOUDINARY.API_SECRET || '',
      );
    } catch (error) {
      throw new BadRequestException('Failed to generate signature');
    }
  }

  async uploadImage(
    file: Express.Multer.File,
    folder: string = CLOUDINARY.FOLDER,
  ): Promise<CloudinaryResponse> {
    try {
      return new Promise((resolve, reject) => {
        const uploadStream = cloudinary.uploader.upload_stream(
          {
            folder: folder,
            resource_type: 'image',
            transformation: [
              { width: 1000, height: 1000, crop: 'limit', quality: 'auto' },
            ],
            format: 'webp',
          },
          (error, result) => {
            if (error) {
              reject(new BadRequestException('Image upload failed'));
            } else {
              resolve(result as unknown as CloudinaryResponse);
            }
          },
        );

        const bufferStream = new Readable();
        bufferStream.push(file.buffer);
        bufferStream.push(null);
        bufferStream.pipe(uploadStream);
      });
    } catch (error) {
      throw new BadRequestException('Image upload failed');
    }
  }

  async uploadVideo(
    file: Express.Multer.File,
    folder: string = 'social-media/videos',
  ): Promise<CloudinaryResponse> {
    try {
      return new Promise((resolve, reject) => {
        const uploadStream = cloudinary.uploader.upload_stream(
          {
            folder: folder,
            resource_type: 'video',
            transformation: [
              { width: 1280, height: 720, crop: 'limit', quality: 'auto' },
            ],
            format: 'mp4',
          },
          (error, result) => {
            if (error) {
              reject(new BadRequestException('Video upload failed'));
            } else {
              resolve(result as unknown as CloudinaryResponse);
            }
          },
        );

        const bufferStream = new Readable();
        bufferStream.push(file.buffer);
        bufferStream.push(null);
        bufferStream.pipe(uploadStream);
      });
    } catch (error) {
      throw new BadRequestException('Video upload failed');
    }
  }

  async uploadFile(
    file: Express.Multer.File,
    folder: string = CLOUDINARY.FOLDER,
  ): Promise<CloudinaryResponse> {
    const isImage = file.mimetype.startsWith('image/');
    const isVideo = file.mimetype.startsWith('video/');

    if (isImage) {
      return this.uploadImage(file, `${folder}/images`);
    } else if (isVideo) {
      return this.uploadVideo(file, `${folder}/videos`);
    } else {
      throw new BadRequestException('Only images and videos are allowed');
    }
  }

  async uploadMultipleFiles(
    files: Express.Multer.File[],
    folder: string = CLOUDINARY.FOLDER,
  ): Promise<CloudinaryResponse[]> {
    console.log('Service received files:', files.length);
    const uploadPromises = files.map((file) => this.uploadFile(file, folder));
    return Promise.all(uploadPromises);
  }

  async deleteFile(publicId: string): Promise<any> {
    try {
      return await cloudinary.uploader.destroy(publicId);
    } catch (error) {
      throw new BadRequestException('Failed to delete file');
    }
  }

  async deleteMultipleFiles(publicIds: string[]): Promise<any> {
    try {
      return await cloudinary.api.delete_resources(publicIds);
    } catch (error) {
      throw new BadRequestException('Failed to delete files');
    }
  }

  // Get optimized URL for different sizes
  getOptimizedUrl(
    publicId: string,
    options: {
      width?: number;
      height?: number;
      quality?: string;
      format?: string;
    } = {},
  ): string {
    return cloudinary.url(publicId, {
      width: options.width || 'auto',
      height: options.height || 'auto',
      crop: 'fill',
      quality: options.quality || 'auto',
      format: options.format || 'auto',
    });
  }

  getThumbnail(publicId: string, size: number = 150): string {
    return cloudinary.url(publicId, {
      width: size,
      height: size,
      crop: 'fill',
      quality: 'auto',
      format: 'webp',
    });
  }
}

