}

export class CommentEntity extends Entity<string> {
  private _content: string;
  private _authorId: string;
  private _postId: string;
  private _parentId?: string;
  private _createdAt: Date;
  private _updatedAt: Date;
  private _isDeleted: boolean = false;

  constructor(props: CommentProps) {
    super(props.id || randomUUID());
    this._content = props.content;
    this._authorId = props.authorId;
    this._postId = props.postId;
    this._parentId = props.parentId;
    this._createdAt = props.createdAt || new Date();
    this._updatedAt = props.updatedAt || new Date();

    this.validate();
    this.addDomainEvent(
      new CommentCreatedEvent({
        id: this.id,
        content: this._content,
        authorId: this._authorId,
        postId: this._postId,
        parentId: this._parentId,
        createdAt: this._createdAt,
        updatedAt: this._updatedAt,
      }),
    );
  }

  // Getters
  get content(): string {
    return this._content;
  }

  get authorId(): string {
    return this._authorId;
  }

  get postId(): string {
    return this._postId;
  }

  get parentId(): string | undefined {
    return this._parentId;
  }

  get createdAt(): Date {
    return this._createdAt;
  }

  get updatedAt(): Date {
    return this._updatedAt;
  }

  get isDeleted(): boolean {
    return this._isDeleted;
  }

  get isReply(): boolean {
    return !!this._parentId;
  }

  // Business methods
  public updateContent(newContent: string, userId: string): void {
    if (this._authorId !== userId) {
      throw new InvalidCommentException(
        'Only comment author can update content',
      );
    }

    if (this._isDeleted) {
      throw new InvalidCommentException('Cannot update deleted comment');
    }

    this.validateContent(newContent);

    const oldContent = this._content;
    this._content = newContent.trim();
    this._updatedAt = new Date();

    this.addDomainEvent(
      new CommentUpdatedEvent(
        {
          id: this.id,
          content: this._content,
          authorId: this._authorId,
          postId: this._postId,
          parentId: this._parentId,
          createdAt: this._createdAt,
          updatedAt: this._updatedAt,
        },
        { oldContent, newContent: this._content },
      ),
    );
  }

  public delete(userId: string, isAuthorOrAdmin: boolean = false): void {
    if (!isAuthorOrAdmin && this._authorId !== userId) {
      throw new InvalidCommentException(
        'Only comment author or admin can delete comment',
      );
    }

    if (this._isDeleted) {
      throw new InvalidCommentException('Comment is already deleted');
    }

    this._isDeleted = true;
    this._updatedAt = new Date();

    this.addDomainEvent(
      new CommentDeletedEvent({
        id: this.id,
        content: this._content,
        authorId: this._authorId,
        postId: this._postId,
        parentId: this._parentId,
        createdAt: this._createdAt,
        updatedAt: this._updatedAt,
      }),
    );
  }

  public addReaction(userId: string, reactionType: ReactionType): void {
    if (this._isDeleted) {
      throw new InvalidCommentException(
        'Cannot add reaction to deleted comment',
      );
    }

    this.addDomainEvent(
      new CommentReactionAddedEvent(this.id, userId, reactionType),
    );
  }

  public removeReaction(userId: string, reactionType: ReactionType): void {
    this.addDomainEvent(
      new CommentReactionRemovedEvent(this.id, userId, reactionType),
    );
  }

  // Static factory methods
  public static create(
    props: Omit<CommentProps, 'id' | 'createdAt' | 'updatedAt'>,
  ): CommentEntity {
    return new CommentEntity(props);
  }

  public static createReply(
    props: Omit<CommentProps, 'id' | 'createdAt' | 'updatedAt'>,
    maxDepth: number = 3,
  ): CommentEntity {
    if (!props.parentId) {
      throw new InvalidCommentException('Reply must have a parent comment');
    }

    // Note: Depth validation should be done by domain service with repository access
    return new CommentEntity(props);
  }

  public static update(
    existingComment: CommentEntity,
    updateProps: { content?: string },
  ): CommentEntity {
    const updatedEntity = new CommentEntity({
      id: existingComment.id,
      content: updateProps.content ?? existingComment.content,
      authorId: existingComment.authorId,
      postId: existingComment.postId,
      parentId: existingComment.parentId,
      createdAt: existingComment.createdAt,
      updatedAt: new Date(),
    });

    updatedEntity.addDomainEvent(
      new CommentUpdatedEvent(
        {
          id: updatedEntity.id,
          content: updatedEntity.content,
          authorId: updatedEntity.authorId,
          postId: updatedEntity.postId,
          parentId: updatedEntity.parentId,
          createdAt: updatedEntity.createdAt,
          updatedAt: updatedEntity.updatedAt,
        },
        { content: updateProps.content },
      ),
    );

    return updatedEntity;
  }

  public static fromPersistence(props: CommentProps): CommentEntity {
    const entity = Object.create(CommentEntity.prototype);
    Entity.call(entity, props.id);
    entity._content = props.content;
    entity._authorId = props.authorId;
    entity._postId = props.postId;
    entity._parentId = props.parentId;
    entity._createdAt = props.createdAt;
    entity._updatedAt = props.updatedAt;
    entity._isDeleted = false;
    return entity;
  }

  public toPlainObject(): CommentProps {
    return {
      id: this.id,
      content: this._content,
      authorId: this._authorId,
      postId: this._postId,
      parentId: this._parentId,
      createdAt: this._createdAt,
      updatedAt: this._updatedAt,
    };
  }

  private validate(): void {
    if (!this._authorId || this._authorId.trim() === '') {
      throw new InvalidCommentException('Author ID is required');
    }

    if (!this._postId || this._postId.trim() === '') {
      throw new InvalidCommentException('Post ID is required');
    }

    this.validateContent(this._content);
  }

  private validateContent(content: string): void {
    if (!content || content.trim() === '') {
      throw new CommentContentException('Comment content cannot be empty');
    }

    if (content.trim().length < 1) {
      throw new CommentContentException('Comment content is too short');
    }

    if (content.trim().length > 1000) {
      throw new CommentContentException(
        'Comment content is too long (max 1000 characters)',
      );
    }

    // Additional content validation rules can be added here
    const trimmedContent = content.trim();
    if (trimmedContent !== content) {
      // This is just a validation, the actual trimming is done in business methods
    }
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../../database/prisma.service';
import { CommentRepository } from '../../domain/repositories/comment.repository';
import { CommentEntity } from '../../domain/comment.entity';
import { CommentFactory } from '../../domain/factories/comment.factory';

@Injectable()
export class PrismaCommentRepository implements CommentRepository {
  constructor(private readonly prisma: PrismaService) {}

  private mapToEntity(prismaComment: any): CommentEntity {
    return CommentFactory.fromPersistence({
      id: prismaComment.id,
      content: prismaComment.content,
      authorId: prismaComment.authorId,
      postId: prismaComment.postId,
      parentId: prismaComment.parentId || undefined,
      createdAt: prismaComment.createdAt,
      updatedAt: prismaComment.updatedAt,
    });
  }

  async save(comment: CommentEntity): Promise<CommentEntity> {
    const data = {
      id: comment.id,
      content: comment.content,
      authorId: comment.authorId,
      postId: comment.postId,
      parentId: comment.parentId,
      createdAt: comment.createdAt,
      updatedAt: comment.updatedAt,
    };

    const savedComment = await this.prisma.comment.upsert({
      where: { id: comment.id },
      update: data,
      create: data,
    });

    return this.mapToEntity(savedComment);
  }

  async update(comment: CommentEntity): Promise<CommentEntity> {
    const updatedComment = await this.prisma.comment.update({
      where: { id: comment.id },
      data: {
        content: comment.content,
        updatedAt: comment.updatedAt,
      },
    });

    return this.mapToEntity(updatedComment);
  }

  async findById(id: string): Promise<CommentEntity | null> {
    const comment = await this.prisma.comment.findUnique({
      where: { id },
    });

    if (!comment) {
      return null;
    }

    return this.mapToEntity(comment);
  }

  async findByPostId(
    postId: string,
    page: number,
    limit: number,
  ): Promise<{
    items: CommentEntity[];
    total: number;
    page: number;
    limit: number;
    totalPages: number;
  }> {
    const skip = (page - 1) * limit;

    const [comments, total] = await Promise.all([
      this.prisma.comment.findMany({
        where: {
          postId,
          parentId: null, // Top-level comments only
        },
        orderBy: { createdAt: 'desc' },
        skip,
        take: limit,
      }),
      this.prisma.comment.count({
        where: {
          postId,
          parentId: null,
        },
      }),
    ]);

    const entities = comments.map((comment) => this.mapToEntity(comment));

    return {
      items: entities,
      total,
      page,
      limit,
      totalPages: Math.ceil(total / limit),
    };
  }

  async findRepliesByCommentId(
    commentId: string,
    page: number,
    limit: number,
  ): Promise<{
    items: CommentEntity[];
    total: number;
    page: number;
    limit: number;
    totalPages: number;
  }> {
    const skip = (page - 1) * limit;

    const [replies, total] = await Promise.all([
      this.prisma.comment.findMany({
        where: { parentId: commentId },
        orderBy: { createdAt: 'asc' },
        skip,
        take: limit,
      }),
      this.prisma.comment.count({
        where: { parentId: commentId },
      }),
    ]);

    const entities = replies.map((comment) => this.mapToEntity(comment));

    return {
      items: entities,
      total,
      page,
      limit,
      totalPages: Math.ceil(total / limit),
    };
  }

  async findByAuthorId(
    authorId: string,
    page: number,
    limit: number,
  ): Promise<{
    items: CommentEntity[];
    total: number;
    page: number;
    limit: number;
    totalPages: number;
  }> {
    const skip = (page - 1) * limit;

    const [comments, total] = await Promise.all([
      this.prisma.comment.findMany({
        where: { authorId },
        orderBy: { createdAt: 'desc' },
        skip,
        take: limit,
      }),
      this.prisma.comment.count({
        where: { authorId },
      }),
    ]);

    const entities = comments.map((comment) => this.mapToEntity(comment));

    return {
      items: entities,
      total,
      page,
      limit,
      totalPages: Math.ceil(total / limit),
    };
  }

  async deleteById(id: string): Promise<void> {
    await this.prisma.comment.delete({
      where: { id },
    });
  }

  async exists(id: string): Promise<boolean> {
    const count = await this.prisma.comment.count({
      where: { id },
    });
    return count > 0;
  }

  async countByPostId(postId: string): Promise<number> {
    return this.prisma.comment.count({
      where: { postId },
    });
  }

  async countRepliesByCommentId(commentId: string): Promise<number> {
    return this.prisma.comment.count({
      where: { parentId: commentId },
    });
  }

  async getCommentDepth(commentId: string): Promise<number> {
    let depth = 0;
    let currentComment = await this.prisma.comment.findUnique({
      where: { id: commentId },
      select: { parentId: true },
    });

    while (currentComment?.parentId) {
      depth++;
      currentComment = await this.prisma.comment.findUnique({
        where: { id: currentComment.parentId },
        select: { parentId: true },
      });
    }

    return depth;
  }

  async findTopLevelCommentsByPostId(
    postId: string,
    page: number,
    limit: number,
    sortBy: 'newest' | 'oldest' | 'popular' = 'newest',
  ): Promise<{
    items: CommentEntity[];
    total: number;
    page: number;
    limit: number;
    totalPages: number;
  }> {
    const skip = (page - 1) * limit;

    let orderBy: any = { createdAt: 'desc' };
    if (sortBy === 'oldest') {
      orderBy = { createdAt: 'asc' };
    } else if (sortBy === 'popular') {
      // This would require reaction counts in a separate table
      orderBy = { createdAt: 'desc' }; // Fallback to newest for now
    }

    const [comments, total] = await Promise.all([
      this.prisma.comment.findMany({
        where: {
          postId,
          parentId: null,
        },
        orderBy,
        skip,
        take: limit,
      }),
      this.prisma.comment.count({
        where: {
          postId,
          parentId: null,
        },
      }),
    ]);

    const entities = comments.map((comment) => this.mapToEntity(comment));

    return {
      items: entities,
      total,
      page,
      limit,
      totalPages: Math.ceil(total / limit),
    };
  }

  async findCommentThread(rootCommentId: string): Promise<CommentEntity[]> {
    // This would be a recursive query to get all nested replies
    const comments = await this.prisma.$queryRaw<any[]>`
      WITH RECURSIVE comment_tree AS (
        SELECT id, content, "authorId", "postId", "parentId", "createdAt", "updatedAt", 0 as depth
        FROM "Comment"
        WHERE id = ${rootCommentId}

        UNION ALL

        SELECT c.id, c.content, c."authorId", c."postId", c."parentId", c."createdAt", c."updatedAt", ct.depth + 1
        FROM "Comment" c
        INNER JOIN comment_tree ct ON c."parentId" = ct.id
      )
      SELECT * FROM comment_tree
      ORDER BY depth, "createdAt" ASC
    `;

    return comments.map((comment) => this.mapToEntity(comment));
  }

  async softDelete(id: string): Promise<void> {
    await this.prisma.comment.update({
      where: { id },
      data: {
        content: '[deleted]',
        updatedAt: new Date(),
      },
    });
  }

  async hasUserReacted(
    commentId: string,
    userId: string,
    reactionType: string,
  ): Promise<boolean> {
    // TODO: Implement when CommentReaction table is added to schema
    // For now, always return false
    return false;
  }

  async addReaction(
    commentId: string,
    userId: string,
    reactionType: string,
  ): Promise<void> {
    // TODO: Implement when CommentReaction table is added to schema
    // For now, do nothing
  }

  async removeReaction(
    commentId: string,
    userId: string,
    reactionType: string,
  ): Promise<void> {
    // TODO: Implement when CommentReaction table is added to schema
    // For now, do nothing
  }

  async getReactionCounts(commentId: string): Promise<Record<string, number>> {
    // TODO: Implement when CommentReaction table is added to schema
    // For now, return empty counts
    return {};
  }

  async deleteByPostId(postId: string): Promise<void> {
    await this.prisma.comment.deleteMany({
      where: { postId },
    });
  }
}

import { FollowEntity } from '../../domain/follow.entity';
