  async sendBulkEmails(emails: CreateMailerDto[]) {
    const results: Array<
      | {
          email: string;
          success: true;
          result: { success: boolean; messageId: any; message: string };
        }
      | { email: string; success: false; error: string }
    > = [];

    for (const emailData of emails) {
      try {
        const result = await this.sendEmail(emailData);
        results.push({ email: emailData.to, success: true, result });

        // Add delay to avoid rate limiting
        await new Promise((resolve) => setTimeout(resolve, 100));
      } catch (error) {
        results.push({
          email: emailData.to,
          success: false,
          error: error.message,
        });
      }
    }

    return results;
  }

  async testConnection() {
    try {
      await this.transporter.verify();
      return { success: true, message: 'Email service is working correctly' };
    } catch (error) {
      this.logger.error('Email connection test failed:', error);
      return {
        success: false,
        message: 'Email service connection failed',
        error: error.message,
      };
    }
  }
}

import {
  Injectable,
  NotFoundException,
  ForbiddenException,
} from '@nestjs/common';
import { Conversation, ConversationType } from '../domain/conversation.entity';
import { ConversationRepository } from './repositories.interface';
import {
  ConversationId,
  UserId,
  ConversationParticipant,
  ConversationTitle,
} from '../domain/value-objects';
import { ParticipantRole, ConversationStatus } from '../domain/enums';

export interface CreateConversationRequest {
  participants: string[];
  type: ConversationType;
  metadata?: {
    title?: string;
    description?: string;
  };
  initiatorId: string;
}

export interface GetConversationsRequest {
  userId: string;
  limit?: number;
  offset?: number;
}

@Injectable()
export class ConversationUseCases {
  constructor(
    private readonly conversationRepository: ConversationRepository,
  ) {}

  async createConversation(
    request: CreateConversationRequest,
  ): Promise<Conversation> {
    // Validate participants include the initiator
    if (!request.participants.includes(request.initiatorId)) {
      request.participants.push(request.initiatorId);
    }

    // For private conversations, ensure only 2 participants
    if (
      request.type === ConversationType.PRIVATE &&
      request.participants.length !== 2
    ) {
      throw new Error('Private conversations must have exactly 2 participants');
    }

    // Check if private conversation already exists
    if (request.type === ConversationType.PRIVATE) {
      const existingConversation =
        await this.conversationRepository.existsWithParticipants(
          request.participants,
        );

      if (existingConversation) {
        throw new Error(
          'Private conversation already exists between these users',
        );
      }
    }

    // Create conversation participants
    const participants = request.participants.map((participantId) =>
      ConversationParticipant.create(
        UserId.fromString(participantId),
        new Date(),
        participantId === request.initiatorId
          ? ParticipantRole.ADMIN
          : ParticipantRole.MEMBER,
      ),
    );

    const now = new Date();
    const conversationProps = {
      id: ConversationId.create(),
      participants,
      type: request.type,
      createdBy: UserId.fromString(request.initiatorId),
      createdAt: now,
      lastActivityAt: now,
      status: ConversationStatus.ACTIVE,
      ...(request.metadata?.title && {
        title: ConversationTitle.create(request.metadata.title),
      }),
    };

    // Create conversation
    const conversation = Conversation.create(conversationProps);

    await this.conversationRepository.create(conversation);
    return conversation;
  }

  async getConversationById(
    conversationId: string,
    userId: string,
  ): Promise<Conversation> {
    const conversation =
      await this.conversationRepository.findById(conversationId);

    if (!conversation) {
      throw new NotFoundException('Conversation not found');
    }

    if (!conversation.isParticipant(UserId.fromString(userId))) {
      throw new ForbiddenException(
        'User is not a participant in this conversation',
      );
    }

    return conversation;
  }

  async getUserConversations(
    request: GetConversationsRequest,
  ): Promise<Conversation[]> {
    return this.conversationRepository.findByUserId(request.userId);
  }

  async addParticipantToConversation(
    conversationId: string,
    participantId: string,
    requesterId: string,
  ): Promise<void> {
    const conversation = await this.getConversationById(
      conversationId,
      requesterId,
    );

    if (conversation.isPrivateConversation()) {
      throw new Error('Cannot add participants to private conversations');
    }

    const updatedConversation = conversation.addParticipant(
      UserId.fromString(participantId),
      new Date(),
      ParticipantRole.MEMBER,
    );
    await this.conversationRepository.update(updatedConversation);
  }

  async removeParticipantFromConversation(
    conversationId: string,
    participantId: string,
    requesterId: string,
  ): Promise<void> {
    const conversation = await this.getConversationById(
      conversationId,
      requesterId,
    );

    if (conversation.isPrivateConversation()) {
      throw new Error('Cannot remove participants from private conversations');
    }

    // Users can only remove themselves or be removed by group admins (future feature)
    if (participantId !== requesterId) {
      throw new ForbiddenException(
        'Can only remove yourself from group conversations',
      );
    }

    const updatedConversation = conversation.removeParticipant(
      UserId.fromString(participantId),
      new Date(),
    );
    await this.conversationRepository.update(updatedConversation);
  }

  async updateConversationMetadata(
    conversationId: string,
    metadata: { title?: string; description?: string },
    requesterId: string,
  ): Promise<void> {
    const conversation = await this.getConversationById(
      conversationId,
      requesterId,
    );

    let updatedConversation = conversation;

    // Update title if provided
    if (metadata.title !== undefined) {
      updatedConversation = updatedConversation.updateTitle(
        ConversationTitle.create(metadata.title),
      );
    }

    // Note: description is not currently supported in ConversationSettings
    // If needed, it could be added to ConversationSettings interface

    await this.conversationRepository.update(updatedConversation);
  }

  async deleteConversation(
    conversationId: string,
    requesterId: string,
  ): Promise<void> {
    const conversation = await this.getConversationById(
      conversationId,
      requesterId,
    );

    // For now, any participant can delete the conversation
    // In future, might want to add admin checks for group conversations
    await this.conversationRepository.delete(conversationId);
  }
}

import {
  Injectable,
  NotFoundException,
  ForbiddenException,
} from '@nestjs/common';
import { Message, MessageType, MessageContent } from '../domain/message.entity';
import { MessageStatus } from '../domain/enums';
import { Conversation } from '../domain/conversation.entity';
import {
  MessageRepository,
  ConversationRepository,
} from './repositories.interface';
import {
  ConversationId,
  MessageId,
  UserId,
  ConversationParticipant,
} from '../domain/value-objects';
import { WebSocketGatewayService } from '../../../shared/websocket';
import { WebSocketEvent, WebSocketNamespace } from '../../../shared/websocket';

export interface SendMessageRequest {
  conversationId: string;
  senderId: string;
  content: MessageContent;
  type: MessageType;
}

export interface GetMessagesRequest {
  conversationId: string;
  userId: string;
  limit?: number;
  offset?: number;
}

export interface MarkMessagesAsReadRequest {
  conversationId: string;
  userId: string;
  messageIds?: string[]; // If not provided, mark all unread messages as read
}

@Injectable()
export class MessageUseCases {
  constructor(
    private readonly messageRepository: MessageRepository,
    private readonly conversationRepository: ConversationRepository,
    private readonly webSocketGateway: WebSocketGatewayService,
  ) {}

  async sendMessage(request: SendMessageRequest): Promise<Message> {
    // Verify user is participant in conversation
    const conversation = await this.conversationRepository.findById(
      request.conversationId,
    );

    if (!conversation) {
      throw new NotFoundException('Conversation not found');
    }

    if (!conversation.isParticipant(UserId.fromString(request.senderId))) {
      throw new ForbiddenException(
        'User is not a participant in this conversation',
      );
    }

    // Create message
    const message = Message.create({
      id: MessageId.create(),
      conversationId: ConversationId.fromString(request.conversationId),
      senderId: UserId.fromString(request.senderId),
      content: request.content,
      type: request.type,
      status: MessageStatus.PENDING,
      sentAt: new Date(),
    });

    // Save message
    await this.messageRepository.create(message);

    // Emit real-time message to all participants
    await this.emitMessageToParticipants(
      message,
      this.extractParticipantIds(conversation.participants),
    );

    // Emit conversation updated event
    await this.emitConversationUpdate(
      request.conversationId,
      this.extractParticipantIds(conversation.participants),
    );

    return message;
  }

  async getMessages(request: GetMessagesRequest): Promise<Message[]> {
    // Verify user is participant in conversation
    const conversation = await this.conversationRepository.findById(
      request.conversationId,
    );

    if (!conversation) {
      throw new NotFoundException('Conversation not found');
    }

    if (!conversation.isParticipant(UserId.fromString(request.userId))) {
      throw new ForbiddenException(
        'User is not a participant in this conversation',
      );
    }

    return this.messageRepository.findByConversationId(
      request.conversationId,
      request.limit,
      request.offset,
    );
  }

  async getMessageById(messageId: string, userId: string): Promise<Message> {
    const message = await this.messageRepository.findById(messageId);

    if (!message) {
      throw new NotFoundException('Message not found');
    }

    // Verify user is participant in the conversation
    const conversation = await this.conversationRepository.findById(
      message.conversationId.value,
    );

    if (
      !conversation ||
      !conversation.isParticipant(UserId.fromString(userId))
    ) {
      throw new ForbiddenException(
        'User is not a participant in this conversation',
      );
    }

    return message;
  }

  async markMessagesAsRead(request: MarkMessagesAsReadRequest): Promise<void> {
    // Verify user is participant in conversation
    const conversation = await this.conversationRepository.findById(
      request.conversationId,
    );

    if (!conversation) {
      throw new NotFoundException('Conversation not found');
    }

    if (!conversation.isParticipant(UserId.fromString(request.userId))) {
      throw new ForbiddenException(
        'User is not a participant in this conversation',
      );
    }

    let messageIds = request.messageIds;

    // If no specific messages provided, get all unread messages
    if (!messageIds || messageIds.length === 0) {
      const unreadMessages =
        await this.messageRepository.findUnreadByConversationId(
          request.conversationId,
          request.userId,
        );
      messageIds = unreadMessages.map((msg) => msg.id.value);
    }

    if (messageIds && messageIds.length > 0) {
      await this.messageRepository.markAsRead(messageIds, request.userId);

      // Emit read receipts to other participants
      await this.emitReadReceipts(
        messageIds,
        request.userId,
        this.extractParticipantIds(conversation.participants),
      );
    }
  }

  async markMessagesAsDelivered(messageIds: string[]): Promise<void> {
    if (messageIds.length > 0) {
      await this.messageRepository.markAsDelivered(messageIds);
    }
  }

  async editMessage(
    messageId: string,
    newContent: MessageContent,
    userId: string,
  ): Promise<Message> {
    const message = await this.getMessageById(messageId, userId);

    // Only sender can edit their message
    if (!message.senderId || message.senderId.value !== userId) {
      throw new ForbiddenException('Can only edit your own messages');
    }

    // Cannot edit deleted messages
    if (message.isDeleted()) {
      throw new Error('Cannot edit deleted messages');
    }

    message.editContent(newContent);
    await this.messageRepository.update(message);

    // Emit message updated event
    const conversation = await this.conversationRepository.findById(
      message.conversationId.value,
    );
    if (conversation) {
      await this.emitMessageUpdate(
        message,
        this.extractParticipantIds(conversation.participants),
      );
    }

    return message;
  }

  async deleteMessage(messageId: string, userId: string): Promise<void> {
    const message = await this.getMessageById(messageId, userId);

    // Only sender can delete their message
    if (!message.senderId || message.senderId.value !== userId) {
      throw new ForbiddenException('Can only delete your own messages');
    }

    message.deleteMessage();
    await this.messageRepository.update(message);

    // Emit message deleted event
    const conversation = await this.conversationRepository.findById(
      message.conversationId.value,
    );
    if (conversation) {
      await this.emitMessageDelete(
        message,
        this.extractParticipantIds(conversation.participants),
      );
    }
  }

  async addReactionToMessage(
    messageId: string,
    emoji: string,
    userId: string,
  ): Promise<void> {
    const message = await this.getMessageById(messageId, userId);

    message.addReaction(emoji, UserId.fromString(userId));
    await this.messageRepository.update(message);

    // Emit reaction added event
    const conversation = await this.conversationRepository.findById(
      message.conversationId.value,
    );
    if (conversation) {
      await this.emitReactionUpdate(
        message,
        this.extractParticipantIds(conversation.participants),
      );
    }
  }

  async removeReactionFromMessage(
    messageId: string,
    emoji: string,
    userId: string,
  ): Promise<void> {
    const message = await this.getMessageById(messageId, userId);

    message.removeReaction(emoji, UserId.fromString(userId));
    await this.messageRepository.update(message);

    // Emit reaction removed event
    const conversation = await this.conversationRepository.findById(
      message.conversationId.value,
    );
    if (conversation) {
      await this.emitReactionUpdate(
        message,
        this.extractParticipantIds(conversation.participants),
      );
    }
  }

  async searchMessages(
    query: string,
    userId: string,
    limit?: number,
  ): Promise<Message[]> {
    return this.messageRepository.searchMessages(query, userId, limit);
  }

  async getUserUnreadMessages(userId: string): Promise<Message[]> {
    return this.messageRepository.findUnreadByUserId(userId);
  }

  // Real-time event emission methods
  private async emitMessageToParticipants(
    message: Message,
    participants: string[],
  ): Promise<void> {
    for (const participantId of participants) {
      if (participantId !== message.senderId?.value) {
        await this.webSocketGateway.emitToUser(
          participantId,
          WebSocketEvent.MESSAGE_RECEIVED,
          {
            message: this.serializeMessage(message),
            conversationId: message.conversationId.value,
          },
          WebSocketNamespace.MESSAGING,
        );
      }
    }

    // Emit to sender as well for confirmation
    if (message.senderId) {
      await this.webSocketGateway.emitToUser(
        message.senderId.value,
        WebSocketEvent.MESSAGE_SENT,
        {
          message: this.serializeMessage(message),
          conversationId: message.conversationId.value,
        },
        WebSocketNamespace.MESSAGING,
      );
    }
  }

  private async emitConversationUpdate(
    conversationId: string,
    participants: string[],
  ): Promise<void> {
    for (const participantId of participants) {
      await this.webSocketGateway.emitToUser(
        participantId,
        WebSocketEvent.CONVERSATION_UPDATED,
        { conversationId },
        WebSocketNamespace.MESSAGING,
      );
    }
  }

  private async emitMessageUpdate(
    message: Message,
    participants: string[],
  ): Promise<void> {
    for (const participantId of participants) {
      await this.webSocketGateway.emitToUser(
        participantId,
        WebSocketEvent.MESSAGE_RECEIVED, // Reuse for updates
        {
          message: this.serializeMessage(message),
          conversationId: message.conversationId.value,
          isUpdate: true,
        },
        WebSocketNamespace.MESSAGING,
      );
    }
  }

  private async emitMessageDelete(
    message: Message,
    participants: string[],
  ): Promise<void> {
    for (const participantId of participants) {
      await this.webSocketGateway.emitToUser(
        participantId,
        WebSocketEvent.MESSAGE_RECEIVED, // Reuse for deletions
        {
          message: this.serializeMessage(message),
          conversationId: message.conversationId.value,
          isDeleted: true,
        },
        WebSocketNamespace.MESSAGING,
      );
    }
  }

  private async emitReactionUpdate(
    message: Message,
    participants: string[],
  ): Promise<void> {
    for (const participantId of participants) {
      await this.webSocketGateway.emitToUser(
        participantId,
        WebSocketEvent.MESSAGE_RECEIVED, // Reuse for reactions
        {
          message: this.serializeMessage(message),
          conversationId: message.conversationId.value,
          isReactionUpdate: true,
        },
        WebSocketNamespace.MESSAGING,
      );
    }
  }

  private async emitReadReceipts(
    messageIds: string[],
    userId: string,
    participants: string[],
  ): Promise<void> {
    for (const participantId of participants) {
      if (participantId !== userId) {
        await this.webSocketGateway.emitToUser(
          participantId,
          WebSocketEvent.MESSAGE_READ,
          {
            messageIds,
            readByUserId: userId,
            readAt: new Date(),
          },
          WebSocketNamespace.MESSAGING,
        );
      }
    }
  }

  private extractParticipantIds(
    participants: ConversationParticipant[],
  ): string[] {
    return participants.map((participant) => participant.userId.value);
  }

  private serializeMessage(message: Message): any {
    return {
      id: message.id,
      conversationId: message.conversationId,
      senderId: message.senderId,
      content: message.content,
      type: message.type,
      status: message.status,
      createdAt: message.createdAt,
      updatedAt: message.updatedAt,
      deliveredAt: message.deliveredAt,
      readAt: message.readAt,
      metadata: message.metadata,
    };
  }
}

import {
  ConversationId,
  UserId,
  ConversationTitle,
  ConversationParticipant,
} from './value-objects';
import { ConversationType, ConversationStatus, ParticipantRole } from './enums';
import { Entity } from '../../../shared/domain';

export { ConversationType };

export interface ConversationProps {
  id: ConversationId;
  type: ConversationType;
  participants: ConversationParticipant[];
  title?: ConversationTitle;
  description?: string;
  avatarUrl?: string;
  createdBy: UserId;
  createdAt: Date;
  updatedAt: Date;
  lastMessageAt?: Date;
  lastActivityAt: Date;
  status: ConversationStatus;
  settings?: ConversationSettings;
  unreadCount?: number;
}

export interface ConversationSettings {
  isEncrypted?: boolean;
  allowNotifications?: boolean;
  muteUntil?: Date;
  autoDeleteMessages?: boolean;
  autoDeleteDuration?: number; // in hours
  maxParticipants?: number;
  allowInviteLinks?: boolean;
  adminOnlyMessaging?: boolean;
}

export class Conversation extends Entity<ConversationId> {
  private constructor(private readonly props: ConversationProps) {
    super(props.id);
  }

  public static create(
    props: Omit<ConversationProps, 'updatedAt'>,
  ): Conversation {
    return new Conversation({
      ...props,
      updatedAt: props.createdAt,
    });
  }

  public static fromPrimitives(data: {
    id: string;
    type: string;
    participants: Array<{
      userId: string;
      joinedAt: string;
      role?: string;
      leftAt?: string;
    }>;
    title?: string;
    createdBy: string;
    createdAt: string;
    updatedAt: string;
    lastMessageAt?: string;
    isArchived?: boolean;
    settings?: ConversationSettings;
  }): Conversation {
    return new Conversation({
      id: ConversationId.fromString(data.id),
      type: data.type as ConversationType,
      participants: data.participants.map((p) =>
        ConversationParticipant.create(
          UserId.fromString(p.userId),
          new Date(p.joinedAt),
          (p.role as ParticipantRole) || ParticipantRole.MEMBER,
          p.leftAt ? new Date(p.leftAt) : undefined,
        ),
      ),
      title: data.title ? ConversationTitle.create(data.title) : undefined,
      createdBy: UserId.fromString(data.createdBy),
      createdAt: new Date(data.createdAt),
      updatedAt: new Date(data.updatedAt),
      lastMessageAt: data.lastMessageAt
        ? new Date(data.lastMessageAt)
        : undefined,
      lastActivityAt: new Date(),
      status: ConversationStatus.ACTIVE,
      settings: data.settings,
    });
  }

  // Getters
  public get id(): ConversationId {
    return this.props.id;
  }

  public get type(): ConversationType {
    return this.props.type;
  }

  public get participants(): ConversationParticipant[] {
    return [...this.props.participants];
  }

  public get activeParticipants(): ConversationParticipant[] {
    return this.props.participants.filter((p) => p.isActive);
  }

  public get title(): ConversationTitle | undefined {
    return this.props.title;
  }

  public get createdBy(): UserId {
    return this.props.createdBy;
  }

  public get createdAt(): Date {
    return this.props.createdAt;
  }

  public get updatedAt(): Date {
    return this.props.updatedAt;
  }

  public get lastMessageAt(): Date | undefined {
    return this.props.lastMessageAt;
  }

  public get lastActivityAt(): Date {
    return this.props.lastActivityAt;
  }

  public get status(): ConversationStatus {
    return this.props.status;
  }

  public get description(): string | undefined {
    return this.props.description;
  }

  public get avatarUrl(): string | undefined {
    return this.props.avatarUrl;
  }

  public get unreadCount(): number {
    return this.props.unreadCount || 0;
  }

  public isActive(): boolean {
    return this.props.status === ConversationStatus.ACTIVE;
  }

  public isArchived(): boolean {
    return this.props.status === ConversationStatus.ARCHIVED;
  }

  public isDeleted(): boolean {
    return this.props.status === ConversationStatus.DELETED;
  }

  public isMuted(): boolean {
    return this.props.status === ConversationStatus.MUTED;
  }

  public get settings(): ConversationSettings | undefined {
    return this.props.settings;
  }

  // Business Logic Methods
  public isParticipant(userId: UserId): boolean {
    return this.activeParticipants.some((p) => p.userId.equals(userId));
  }

  public getParticipant(userId: UserId): ConversationParticipant | undefined {
    return this.props.participants.find((p) => p.userId.equals(userId));
  }

  public addParticipant(
    userId: UserId,
    joinedAt: Date,
    role: string = 'member',
  ): Conversation {
    if (this.isParticipant(userId)) {
      throw new Error('User is already a participant');
    }

    const newParticipant = ConversationParticipant.create(
      userId,
      joinedAt,
      role as ParticipantRole,
    );
    const updatedParticipants = [...this.props.participants, newParticipant];

    return new Conversation({
      ...this.props,
      participants: updatedParticipants,
      updatedAt: new Date(),
    });
  }

  public removeParticipant(userId: UserId, leftAt: Date): Conversation {
    const participantIndex = this.props.participants.findIndex((p) =>
      p.userId.equals(userId),
    );

    if (participantIndex === -1) {
      throw new Error('User is not a participant');
    }

    const participant = this.props.participants[participantIndex];
    const updatedParticipant = participant.leave(leftAt);

    const updatedParticipants = [...this.props.participants];
    updatedParticipants[participantIndex] = updatedParticipant;

    return new Conversation({
      ...this.props,
      participants: updatedParticipants,
      updatedAt: new Date(),
    });
  }

  public updateTitle(title: ConversationTitle): Conversation {
    if (this.type === ConversationType.PRIVATE) {
      throw new Error('Cannot set title for private conversations');
    }

    return new Conversation({
      ...this.props,
      title,
      updatedAt: new Date(),
    });
  }

  public updateSettings(settings: Partial<ConversationSettings>): Conversation {
    return new Conversation({
      ...this.props,
      settings: {
        ...this.props.settings,
        ...settings,
      },
      updatedAt: new Date(),
    });
  }

  public archive(): Conversation {
    return new Conversation({
      ...this.props,
      status: ConversationStatus.ARCHIVED,
      updatedAt: new Date(),
    });
  }

  public unarchive(): Conversation {
    return new Conversation({
      ...this.props,
      status: ConversationStatus.ACTIVE,
      updatedAt: new Date(),
    });
  }

  public updateLastMessageAt(lastMessageAt: Date): Conversation {
    return new Conversation({
      ...this.props,
      lastMessageAt,
      updatedAt: new Date(),
    });
  }

  public isPrivateConversation(): boolean {
    return this.props.type === ConversationType.PRIVATE;
  }

  public updateMetadata(metadata: Partial<ConversationSettings>): Conversation {
    return this.updateSettings(metadata);
  }

  public canUserPerformAction(
    userId: UserId,
    action: 'read' | 'write' | 'admin',
  ): boolean {
    const participant = this.getParticipant(userId);

    if (!participant || !participant.isActive) {
      return false;
    }

    switch (action) {
      case 'read':
      case 'write':
        return true;
      case 'admin':
        return participant.role === 'admin' || this.createdBy.equals(userId);
      default:
        return false;
    }
  }

  // Serialization
  public toPrimitives(): {
    id: string;
    type: string;
    participants: Array<{
      userId: string;
      joinedAt: string;
      role: string;
      leftAt?: string;
    }>;
    title?: string;
    createdBy: string;
    createdAt: string;
    updatedAt: string;
    lastMessageAt?: string;
    isArchived: boolean;
    settings?: ConversationSettings;
  } {
    return {
      id: this.props.id.value,
      type: this.props.type,
      participants: this.props.participants.map((p) => ({
        userId: p.userId.value,
        joinedAt: p.joinedAt.toISOString(),
        role: p.role,
        leftAt: p.leftAt?.toISOString(),
      })),
      title: this.props.title?.value,
      createdBy: this.props.createdBy.value,
      createdAt: this.props.createdAt.toISOString(),
      updatedAt: this.props.updatedAt.toISOString(),
      lastMessageAt: this.props.lastMessageAt?.toISOString(),
      isArchived: this.props.status === ConversationStatus.ARCHIVED,
      settings: this.props.settings,
    };
  }
}

