import {
  ConversationId,
  MessageId,
  UserId,
  MessageContent,
  MessageAttachment,
  MessageMetadata,
  MessageLocation,
} from './value-objects';
import { MessageType, MessageStatus } from './enums';
import { Entity } from '../../../shared/domain';

export { MessageType, MessageContent };

export interface MessageProps {
  id: MessageId;
  conversationId: ConversationId;
  senderId: UserId | null; // null for system messages
  content: MessageContent;
  type: MessageType;
  status: MessageStatus;
  sentAt: Date;
  deliveredAt?: Date;
  readAt?: Date;
  editedAt?: Date;
  deletedAt?: Date;
  attachments?: MessageAttachment[];
  location?: MessageLocation;
  replyToMessageId?: MessageId;
  metadata?: MessageMetadata;
  createdAt: Date;
  updatedAt: Date;
}

export class Message extends Entity<MessageId> {
  private constructor(private readonly props: MessageProps) {
    super(props.id);
  }

  public static create(
    props: Omit<
      MessageProps,
      | 'deliveredAt'
      | 'readAt'
      | 'editedAt'
      | 'deletedAt'
      | 'metadata'
      | 'createdAt'
      | 'updatedAt'
    >,
  ): Message {
    const now = new Date();

    // Validate content for text messages
    if (props.type === MessageType.TEXT && !props.content) {
      throw new Error('Text messages must have content');
    }

    // Validate attachment for media messages
    const mediaTypes = [
      MessageType.IMAGE,
      MessageType.VIDEO,
      MessageType.AUDIO,
      MessageType.DOCUMENT,
      MessageType.GIF,
      MessageType.STICKER,
    ];
    if (
      mediaTypes.includes(props.type) &&
      (!props.attachments || props.attachments.length === 0)
    ) {
      throw new Error('Media messages must have at least one attachment');
    }

    // Validate location for location messages
    if (props.type === MessageType.LOCATION && !props.location) {
      throw new Error('Location messages must have location data');
    }

    // System messages can have null sender
    if (props.type !== MessageType.SYSTEM && !props.senderId) {
      throw new Error('Non-system messages must have a sender');
    }

    const messageProps: MessageProps = {
      ...props,
      metadata: MessageMetadata.create(),
      createdAt: now,
      updatedAt: now,
    };

    return new Message(messageProps);
  }

  public static fromPrimitives(data: {
    id: string;
    conversationId: string;
    senderId: string | null;
    content: string;
    type: string;
    status: string;
    sentAt: string;
    deliveredAt?: string;
    readAt?: string;
    editedAt?: string;
    attachmentUrl?: string;
    replyToMessageId?: string;
    reactions?: Array<{ emoji: string; userIds: string[] }>;
  }): Message {
    // Convert reactions to Map
    const reactionsMap = new Map<string, UserId[]>();
    if (data.reactions) {
      data.reactions.forEach((r) => {
        reactionsMap.set(
          r.emoji,
          r.userIds.map((id) => UserId.fromString(id)),
        );
      });
    }

    const metadata = MessageMetadata.create(
      data.deliveredAt ? new Date(data.deliveredAt) : undefined,
      data.readAt ? new Date(data.readAt) : undefined,
      data.editedAt ? new Date(data.editedAt) : undefined,
      data.replyToMessageId
        ? MessageId.fromString(data.replyToMessageId)
        : undefined,
      reactionsMap,
    );

    return new Message({
      id: MessageId.fromString(data.id),
      conversationId: ConversationId.fromString(data.conversationId),
      senderId: data.senderId ? UserId.fromString(data.senderId) : null,
      content: MessageContent.create(data.content),
      type: data.type as MessageType,
      status: data.status as MessageStatus,
      sentAt: new Date(data.sentAt),
      deliveredAt: data.deliveredAt ? new Date(data.deliveredAt) : undefined,
      readAt: data.readAt ? new Date(data.readAt) : undefined,
      editedAt: data.editedAt ? new Date(data.editedAt) : undefined,
      attachments: data.attachmentUrl
        ? [{ url: data.attachmentUrl } as MessageAttachment]
        : undefined,
      replyToMessageId: data.replyToMessageId
        ? MessageId.fromString(data.replyToMessageId)
        : undefined,
      metadata,
      createdAt: new Date(),
      updatedAt: new Date(),
    });
  }

  // Getters
  public get id(): MessageId {
    return this.props.id;
  }

  public get conversationId(): ConversationId {
    return this.props.conversationId;
  }

  public get senderId(): UserId | null {
    return this.props.senderId;
  }

  public get content(): MessageContent {
    return this.props.content;
  }

  public get type(): MessageType {
    return this.props.type;
  }

  public get status(): MessageStatus {
    return this.props.status;
  }

  public get sentAt(): Date {
    return this.props.sentAt;
  }

  public get deliveredAt(): Date | undefined {
    return this.props.deliveredAt;
  }

  public get readAt(): Date | undefined {
    return this.props.readAt;
  }

  public get editedAt(): Date | undefined {
    return this.props.editedAt;
  }

  public get attachmentUrl(): string | undefined {
    return this.props.attachments?.[0]?.url;
  }

  public get replyToMessageId(): MessageId | undefined {
    return this.props.replyToMessageId;
  }

  public get metadata(): MessageMetadata | undefined {
    return this.props.metadata;
  }

  public get isSystemMessage(): boolean {
    return (
      this.props.type === MessageType.SYSTEM || this.props.senderId === null
    );
  }

  public get isDelivered(): boolean {
    return !!this.props.deliveredAt;
  }

  public get isRead(): boolean {
    return !!this.props.readAt;
  }

  public get isEdited(): boolean {
    return !!this.props.editedAt;
  }

  public get hasAttachment(): boolean {
    return !!this.props.attachments && this.props.attachments.length > 0;
  }

  public get isReply(): boolean {
    return !!this.props.replyToMessageId;
  }

  // Business Logic Methods
  public markAsDelivered(deliveredAt: Date): Message {
    if (this.props.status === MessageStatus.FAILED) {
      throw new Error('Cannot mark failed message as delivered');
    }

    if (this.props.deliveredAt) {
      return this; // Already delivered
    }

    return new Message({
      ...this.props,
      status: MessageStatus.DELIVERED,
      deliveredAt,
      metadata: this.props.metadata?.markAsDelivered(deliveredAt),
    });
  }

  public markAsRead(readAt: Date): Message {
    if (this.props.status === MessageStatus.FAILED) {
      throw new Error('Cannot mark failed message as read');
    }

    if (!this.props.deliveredAt) {
      throw new Error('Cannot mark message as read before it is delivered');
    }

    if (this.props.readAt) {
      return this; // Already read
    }

    return new Message({
      ...this.props,
      status: MessageStatus.READ,
      readAt,
      metadata: this.props.metadata?.markAsRead(readAt),
    });
  }

  public markAsFailed(): Message {
    return new Message({
      ...this.props,
      status: MessageStatus.FAILED,
    });
  }

  public edit(newContent: string, editedAt: Date): Message {
    if (this.props.type === MessageType.SYSTEM) {
      throw new Error('Cannot edit system messages');
    }

    if (this.props.sentAt && editedAt < this.props.sentAt) {
      throw new Error('Edit timestamp cannot be before send timestamp');
    }

    return new Message({
      ...this.props,
      content: MessageContent.create(newContent),
      editedAt,
      metadata: this.props.metadata?.markAsEdited(editedAt),
    });
  }

  public addReaction(emoji: string, userId: UserId): Message {
    if (!emoji || emoji.trim().length === 0) {
      throw new Error('Emoji cannot be empty');
    }

    const updatedMetadata =
      this.props.metadata?.addReaction(emoji, userId) ||
      MessageMetadata.create().addReaction(emoji, userId);

    return new Message({
      ...this.props,
      metadata: updatedMetadata,
    });
  }

  public removeReaction(emoji: string, userId: UserId): Message {
    if (!this.props.metadata) {
      return this; // No reactions to remove
    }

    const updatedMetadata = this.props.metadata.removeReaction(emoji, userId);

    return new Message({
      ...this.props,
      metadata: updatedMetadata,
    });
  }

  public getReactions(): Map<string, UserId[]> {
    return this.props.metadata?.reactions || new Map();
  }

  public getUserReactions(userId: UserId): string[] {
    const reactions = this.getReactions();
    const userReactions: string[] = [];

    reactions.forEach((userIds, emoji) => {
      if (userIds.some((id) => id.equals(userId))) {
        userReactions.push(emoji);
      }
    });

    return userReactions;
  }

  public getReactionCount(emoji: string): number {
    const reactions = this.getReactions();
    return reactions.get(emoji)?.length || 0;
  }

  public canBeEditedBy(userId: UserId): boolean {
    if (this.props.type === MessageType.SYSTEM) {
      return false;
    }

    return this.props.senderId?.equals(userId) || false;
  }

  public canBeDeletedBy(userId: UserId): boolean {
    return this.canBeEditedBy(userId);
  }

  public isEditableWithinTimeLimit(timeLimitHours: number = 24): boolean {
    if (!this.props.sentAt) {
      return false;
    }

    const timeLimitMs = timeLimitHours * 60 * 60 * 1000;
    const now = new Date();
    return now.getTime() - this.props.sentAt.getTime() <= timeLimitMs;
  }

  public get createdAt(): Date {
    return this.props.sentAt;
  }

  public get updatedAt(): Date {
    return this.props.editedAt || this.props.sentAt;
  }

  public isDeleted(): boolean {
    return (
      this.props.status === MessageStatus.FAILED &&
      this.props.content.value === '[deleted]'
    );
  }

  public editContent(newContent: MessageContent): Message {
    if (!this.canBeEditedBy(this.props.senderId!)) {
      throw new Error('Message cannot be edited');
    }

    if (this.isDeleted()) {
      throw new Error('Cannot edit a deleted message');
    }

    return new Message({
      ...this.props,
      content: newContent,
      editedAt: new Date(),
    });
  }

  public deleteMessage(): Message {
    return new Message({
      ...this.props,
      content: MessageContent.create('[deleted]'),
      status: MessageStatus.FAILED,
      editedAt: new Date(),
    });
  }

  // Serialization
  public toPrimitives(): {
    id: string;
    conversationId: string;
    senderId: string | null;
    content: string;
    type: string;
    status: string;
    sentAt: string;
    deliveredAt?: string;
    readAt?: string;
    editedAt?: string;
    attachmentUrl?: string;
    replyToMessageId?: string;
    reactions: Array<{ emoji: string; userIds: string[] }>;
  } {
    const reactions = this.getReactions();
    const reactionsArray = Array.from(reactions.entries()).map(
      ([emoji, userIds]) => ({
        emoji,
        userIds: userIds.map((id) => id.value),
      }),
    );

    return {
      id: this.props.id.value,
      conversationId: this.props.conversationId.value,
      senderId: this.props.senderId?.value || null,
      content: this.props.content.value,
      type: this.props.type,
      status: this.props.status,
      sentAt: this.props.sentAt.toISOString(),
      deliveredAt: this.props.deliveredAt?.toISOString(),
      readAt: this.props.readAt?.toISOString(),
      editedAt: this.props.editedAt?.toISOString(),
      attachmentUrl: this.attachmentUrl,
      replyToMessageId: this.props.replyToMessageId?.value,
      reactions: reactionsArray,
    };
  }
}

import { Injectable } from '@nestjs/common';
import { Conversation } from './conversation.entity';
import { Message } from './message.entity';
import {
  ConversationId,
  MessageId,
  UserId,
  ConversationParticipant,
  ConversationTitle,
  MessageContent,
} from './value-objects';
import {
  ConversationType,
  MessageType,
  MessageStatus,
  ConversationStatus,
  ParticipantRole,
} from './enums';

@Injectable()
export class ConversationDomainService {
  public createPrivateConversation(
    participantIds: [UserId, UserId],
    createdBy: UserId,
  ): Conversation {
    if (participantIds.length !== 2) {
      throw new Error('Private conversation must have exactly 2 participants');
    }

    if (participantIds[0].equals(participantIds[1])) {
      throw new Error('Cannot create conversation with the same user');
    }

    const conversationId = ConversationId.create();
    const participants = participantIds.map((userId) =>
      ConversationParticipant.create(userId, new Date()),
    );

    return Conversation.create({
      id: conversationId,
      type: ConversationType.PRIVATE,
      participants,
      createdBy,
      createdAt: new Date(),
      lastActivityAt: new Date(),
      status: ConversationStatus.ACTIVE,
    });
  }

  public createGroupConversation(
    title: string,
    participantIds: UserId[],
    createdBy: UserId,
  ): Conversation {
    if (participantIds.length < 2) {
      throw new Error('Group conversation must have at least 2 participants');
    }

    if (participantIds.length > 100) {
      throw new Error(
        'Group conversation cannot have more than 100 participants',
      );
    }

    // Ensure creator is included in participants
    if (!participantIds.some((id) => id.equals(createdBy))) {
      participantIds.push(createdBy);
    }

    const conversationId = ConversationId.create();
    const participants = participantIds.map((userId) =>
      ConversationParticipant.create(
        userId,
        new Date(),
        userId.equals(createdBy)
          ? ParticipantRole.ADMIN
          : ParticipantRole.MEMBER,
      ),
    );

    return Conversation.create({
      id: conversationId,
      type: ConversationType.GROUP,
      title: ConversationTitle.create(title),
      participants,
      createdBy,
      createdAt: new Date(),
      lastActivityAt: new Date(),
      status: ConversationStatus.ACTIVE,
    });
  }

  public addParticipant(
    conversation: Conversation,
    userId: UserId,
    addedBy: UserId,
  ): Conversation {
    if (conversation.type === ConversationType.PRIVATE) {
      throw new Error('Cannot add participants to private conversation');
    }

    if (!conversation.isParticipant(addedBy)) {
      throw new Error('Only participants can add new members');
    }

    if (conversation.isParticipant(userId)) {
      throw new Error('User is already a participant');
    }

    return conversation.addParticipant(userId, new Date());
  }

  public removeParticipant(
    conversation: Conversation,
    userId: UserId,
    removedBy: UserId,
  ): Conversation {
    if (conversation.type === ConversationType.PRIVATE) {
      throw new Error('Cannot remove participants from private conversation');
    }

    if (!conversation.isParticipant(removedBy) && !removedBy.equals(userId)) {
      throw new Error('Only participants can remove members');
    }

    if (!conversation.isParticipant(userId)) {
      throw new Error('User is not a participant');
    }

    return conversation.removeParticipant(userId, new Date());
  }

  public canUserAccessConversation(
    conversation: Conversation,
    userId: UserId,
  ): boolean {
    return conversation.isParticipant(userId);
  }

  public canUserSendMessage(
    conversation: Conversation,
    userId: UserId,
  ): boolean {
    return conversation.isParticipant(userId);
  }
}

@Injectable()
export class MessageDomainService {
  public createTextMessage(
    conversationId: ConversationId,
    senderId: UserId,
    content: string,
  ): Message {
    if (!content || content.trim().length === 0) {
      throw new Error('Message content cannot be empty');
    }

    return Message.create({
      id: MessageId.create(),
      conversationId,
      senderId,
      type: MessageType.TEXT,
      content: MessageContent.create(content),
      status: MessageStatus.PENDING,
      sentAt: new Date(),
    });
  }

  public createMediaMessage(
    conversationId: ConversationId,
    senderId: UserId,
    type: MessageType,
    attachmentUrl: string,
    content?: string,
  ): Message {
    if (
      ![
        MessageType.IMAGE,
        MessageType.VIDEO,
        MessageType.AUDIO,
        MessageType.DOCUMENT,
      ].includes(type)
    ) {
      throw new Error('Invalid media message type');
    }

    if (!attachmentUrl) {
      throw new Error('Attachment URL is required for media messages');
    }

    return Message.create({
      id: MessageId.create(),
      conversationId,
      senderId,
      type,
      content: MessageContent.create(content || ''),
      attachments: attachmentUrl ? [{ url: attachmentUrl } as any] : undefined,
      status: MessageStatus.PENDING,
      sentAt: new Date(),
    });
  }

  public createSystemMessage(
    conversationId: ConversationId,
    content: string,
  ): Message {
    return Message.create({
      id: MessageId.create(),
      conversationId,
      senderId: null, // System messages don't have a sender
      type: MessageType.SYSTEM,
      content: MessageContent.create(content),
      status: MessageStatus.SENT,
      sentAt: new Date(),
    });
  }

  public createReplyMessage(
    conversationId: ConversationId,
    senderId: UserId,
    content: string,
    replyToMessageId: MessageId,
  ): Message {
    if (!content || content.trim().length === 0) {
      throw new Error('Reply content cannot be empty');
    }

    return Message.create({
      id: MessageId.create(),
      conversationId,
      senderId,
      type: MessageType.TEXT,
      content: MessageContent.create(content),
      status: MessageStatus.PENDING,
      sentAt: new Date(),
      replyToMessageId,
    });
  }

  public markAsDelivered(message: Message, deliveredAt: Date): Message {
    if (message.status === MessageStatus.FAILED) {
      throw new Error('Cannot mark failed message as delivered');
    }

    return message.markAsDelivered(deliveredAt);
  }

  public markAsRead(message: Message, readAt: Date): Message {
    if (message.status === MessageStatus.FAILED) {
      throw new Error('Cannot mark failed message as read');
    }

    if (!message.deliveredAt) {
      throw new Error('Cannot mark message as read before it is delivered');
    }

    return message.markAsRead(readAt);
  }

  public editMessage(
    message: Message,
    newContent: string,
    editedAt: Date,
  ): Message {
    if (message.type === MessageType.SYSTEM) {
      throw new Error('Cannot edit system messages');
    }

    if (!newContent || newContent.trim().length === 0) {
      throw new Error('Edited content cannot be empty');
    }

    if (message.sentAt && editedAt < message.sentAt) {
      throw new Error('Edit timestamp cannot be before send timestamp');
    }

    return message.edit(newContent, editedAt);
  }

  public addReaction(message: Message, emoji: string, userId: UserId): Message {
    if (!emoji || emoji.trim().length === 0) {
      throw new Error('Emoji cannot be empty');
    }

    return message.addReaction(emoji, userId);
  }

  public removeReaction(
    message: Message,
    emoji: string,
    userId: UserId,
  ): Message {
    return message.removeReaction(emoji, userId);
  }

  public canUserEditMessage(message: Message, userId: UserId): boolean {
    return message.senderId?.equals(userId) || false;
  }

  public canUserDeleteMessage(message: Message, userId: UserId): boolean {
    return message.senderId?.equals(userId) || false;
  }

  public isMessageExpired(
    message: Message,
    expirationHours: number = 24,
  ): boolean {
    if (!message.sentAt) {
      return false;
    }

    const expirationTime = new Date(
      message.sentAt.getTime() + expirationHours * 60 * 60 * 1000,
    );
    return new Date() > expirationTime;
  }
}

@Injectable()
export class MessagingValidationService {
  public validateConversationAccess(
    conversation: Conversation,
    userId: UserId,
    operation: 'read' | 'write' | 'admin',
  ): void {
    if (!conversation.isParticipant(userId)) {
      throw new Error('User does not have access to this conversation');
    }

    if (operation === 'admin') {
      const participant = conversation.getParticipant(userId);
      if (!participant || participant.role !== 'admin') {
        throw new Error('User does not have admin privileges');
      }
    }
  }

  public validateMessageOperation(
    message: Message,
    userId: UserId,
    operation: 'edit' | 'delete' | 'react',
  ): void {
    switch (operation) {
      case 'edit':
      case 'delete':
        if (!message.senderId?.equals(userId)) {
          throw new Error('Only message sender can perform this operation');
        }
        if (message.type === MessageType.SYSTEM) {
          throw new Error('Cannot modify system messages');
        }
        break;
      case 'react':
        // Anyone in the conversation can react
        break;
      default:
        throw new Error('Unknown operation');
    }
  }

  public validateMessageContent(content: string, type: MessageType): void {
    if (
      type === MessageType.TEXT &&
      (!content || content.trim().length === 0)
    ) {
      throw new Error('Text message content cannot be empty');
    }

    if (content && content.length > 10000) {
      throw new Error('Message content cannot exceed 10000 characters');
    }
  }

  public validateConversationParticipants(
    participants: UserId[],
    type: ConversationType,
  ): void {
    if (type === ConversationType.PRIVATE && participants.length !== 2) {
      throw new Error('Private conversation must have exactly 2 participants');
    }

    if (type === ConversationType.GROUP && participants.length < 2) {
      throw new Error('Group conversation must have at least 2 participants');
    }

    if (participants.length > 100) {
      throw new Error('Conversation cannot have more than 100 participants');
    }

    // Check for duplicate participants
    const uniqueParticipants = new Set(participants.map((p) => p.value));
    if (uniqueParticipants.size !== participants.length) {
      throw new Error('Duplicate participants are not allowed');
    }
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../../database/prisma.service';
import { IConversationRepository } from '../../domain/repositories';
import { Conversation } from '../../domain/conversation.entity';
import { ConversationId, UserId } from '../../domain/value-objects';
import { ConversationType } from '../../domain/enums';

@Injectable()
export class ConversationRepositoryImpl implements IConversationRepository {
  constructor(private readonly prisma: PrismaService) {}

  async save(conversation: Conversation): Promise<void> {
    const data = conversation.toPrimitives();

    // Map domain model to current schema
    await this.prisma.conversation.create({
      data: {
        id: data.id,
        name: data.title,
        isGroup: data.type === ConversationType.GROUP,
        createdAt: new Date(data.createdAt),
        updatedAt: new Date(data.updatedAt),
        participants: {
          createMany: {
            data: data.participants.map((p) => ({
              userId: p.userId,
              role: p.role,
              joinedAt: new Date(p.joinedAt),
              lastReadAt: null, // Will be updated later
            })),
          },
        },
      },
    });
  }

  async findById(id: ConversationId): Promise<Conversation | null> {
    const conversation = await this.prisma.conversation.findUnique({
      where: { id: id.value },
      include: {
        participants: {
          include: {
            user: true,
          },
        },
      },
    });

    if (!conversation) {
      return null;
    }

    // Map current schema to domain model
    const firstParticipant = conversation.participants[0];
    return Conversation.fromPrimitives({
      id: conversation.id,
      type: conversation.isGroup
        ? ConversationType.GROUP
        : ConversationType.PRIVATE,
      participants: conversation.participants.map((p) => ({
        userId: p.userId,
        joinedAt: p.joinedAt.toISOString(),
        role: p.role,
        leftAt: undefined, // Current schema doesn't support leftAt
      })),
      title: conversation.name || undefined,
      createdBy: firstParticipant?.userId || '', // Fallback
      createdAt: conversation.createdAt.toISOString(),
      updatedAt: conversation.updatedAt.toISOString(),
      lastMessageAt: undefined, // Current schema doesn't support this
      isArchived: false, // Current schema doesn't support this
      settings: undefined, // Current schema doesn't support this
    });
  }

  async findByParticipants(participantIds: UserId[]): Promise<Conversation[]> {
    const userIdValues = participantIds.map((id) => id.value);

    const conversations = await this.prisma.conversation.findMany({
      where: {
        participants: {
          every: {
            userId: { in: userIdValues },
          },
        },
      },
      include: {
        participants: {
          include: {
            user: true,
          },
        },
      },
    });

    return this.mapToConversations(conversations);
  }

  async findByParticipant(participantId: UserId): Promise<Conversation[]> {
    const conversations = await this.prisma.conversation.findMany({
      where: {
        participants: {
          some: {
            userId: participantId.value,
          },
        },
      },
      include: {
        participants: {
          include: {
            user: true,
          },
        },
      },
      orderBy: {
        updatedAt: 'desc',
      },
    });

    return this.mapToConversations(conversations);
  }

  async update(conversation: Conversation): Promise<void> {
    const data = conversation.toPrimitives();

    // Update conversation - only update fields supported by current schema
    await this.prisma.conversation.update({
      where: { id: data.id },
      data: {
        name: data.title,
        updatedAt: new Date(data.updatedAt),
      },
    });

    // Update participants - delete existing and recreate
    await this.prisma.userConversation.deleteMany({
      where: { conversationId: data.id },
    });

    await this.prisma.userConversation.createMany({
      data: data.participants.map((p) => ({
        conversationId: data.id,
        userId: p.userId,
        role: p.role,
        joinedAt: new Date(p.joinedAt),
        lastReadAt: null,
      })),
    });
  }

  async delete(id: ConversationId): Promise<void> {
    await this.prisma.conversation.delete({
      where: { id: id.value },
    });
  }

  async exists(id: ConversationId): Promise<boolean> {
    const count = await this.prisma.conversation.count({
      where: { id: id.value },
    });
    return count > 0;
  }

  private mapToConversations(conversations: any[]): Conversation[] {
    return conversations.map((conversation) => {
      const firstParticipant = conversation.participants[0];
      return Conversation.fromPrimitives({
        id: conversation.id,
        type: conversation.isGroup
          ? ConversationType.GROUP
          : ConversationType.PRIVATE,
        participants: conversation.participants.map((p: any) => ({
          userId: p.userId,
          joinedAt: p.joinedAt.toISOString(),
          role: p.role,
          leftAt: undefined, // Current schema doesn't support leftAt
        })),
        title: conversation.name || undefined,
        createdBy: firstParticipant?.userId || '',
        createdAt: conversation.createdAt.toISOString(),
        updatedAt: conversation.updatedAt.toISOString(),
        lastMessageAt: undefined, // Current schema doesn't support this
        isArchived: false, // Current schema doesn't support this
        settings: undefined, // Current schema doesn't support this
      });
    });
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../../database/prisma.service';
import { IMessageRepository } from '../../domain/repositories';
import { Message } from '../../domain/message.entity';
import { ConversationId, MessageId, UserId } from '../../domain/value-objects';
import { MessageType, MessageStatus } from '../../domain/enums';

@Injectable()
export class MessageRepositoryImpl implements IMessageRepository {
  constructor(private readonly prisma: PrismaService) {}

  async save(message: Message): Promise<void> {
    const data = message.toPrimitives();

    // Skip system messages (senderId is null) as current schema requires senderId
    if (!data.senderId) {
      throw new Error('Current schema does not support system messages');
    }

    await this.prisma.message.create({
      data: {
        id: data.id,
        content: data.content,
        messageType: data.type,
        senderId: data.senderId,
        conversationId: data.conversationId,
        createdAt: new Date(data.sentAt),
      },
    });
  }

  async findById(id: MessageId): Promise<Message | null> {
    const message = await this.prisma.message.findUnique({
      where: { id: id.value },
      include: {
        sender: true,
        conversation: true,
      },
    });

    if (!message) {
      return null;
    }

    return this.mapToMessage(message);
  }

  async findByConversationId(
    conversationId: ConversationId,
    limit: number = 50,
    offset: number = 0,
  ): Promise<Message[]> {
    const messages = await this.prisma.message.findMany({
      where: {
        conversationId: conversationId.value,
      },
      include: {
        sender: true,
        conversation: true,
      },
      orderBy: {
        createdAt: 'desc',
      },
      take: limit,
      skip: offset,
    });

    return messages.map(this.mapToMessage);
  }

  async findByConversationIdPaginated(
    conversationId: ConversationId,
    cursor?: MessageId,
    limit: number = 50,
  ): Promise<{
    messages: Message[];
    hasMore: boolean;
    nextCursor?: MessageId;
  }> {
    const messages = await this.prisma.message.findMany({
      where: {
        conversationId: conversationId.value,
        ...(cursor && {
          id: {
            lt: cursor.value,
          },
        }),
      },
      include: {
        sender: true,
        conversation: true,
      },
      orderBy: {
        createdAt: 'desc',
      },
      take: limit + 1, // Take one extra to check if there are more
    });

    const hasMore = messages.length > limit;
    const resultMessages = hasMore ? messages.slice(0, -1) : messages;
    const nextCursor =
      hasMore && resultMessages.length > 0
        ? MessageId.fromString(resultMessages[resultMessages.length - 1].id)
        : undefined;

    return {
      messages: resultMessages.map(this.mapToMessage),
      hasMore,
      nextCursor,
    };
  }

  async update(message: Message): Promise<void> {
    const data = message.toPrimitives();

    await this.prisma.message.update({
      where: { id: data.id },
      data: {
        content: data.content,
        messageType: data.type,
        isRead: data.status === MessageStatus.READ,
      },
    });
  }

  async delete(id: MessageId): Promise<void> {
    await this.prisma.message.delete({
      where: { id: id.value },
    });
  }

  async markAsDelivered(
    messageId: MessageId,
    deliveredAt: Date,
  ): Promise<void> {
    // Current schema doesn't support delivered status, so we just update the read status
    await this.prisma.message.update({
      where: { id: messageId.value },
      data: {
        // Current schema only has isRead, so we can't mark as delivered separately
      },
    });
  }

  async markAsRead(messageId: MessageId, readAt: Date): Promise<void> {
    await this.prisma.message.update({
      where: { id: messageId.value },
      data: {
        isRead: true,
      },
    });
  }

  async countUnreadMessages(
    conversationId: ConversationId,
    userId: UserId,
  ): Promise<number> {
    return await this.prisma.message.count({
      where: {
        conversationId: conversationId.value,
        senderId: {
          not: userId.value,
        },
        isRead: false,
      },
    });
  }

  async getLastMessage(
    conversationId: ConversationId,
  ): Promise<Message | null> {
    const message = await this.prisma.message.findFirst({
      where: {
        conversationId: conversationId.value,
      },
      include: {
        sender: true,
        conversation: true,
      },
      orderBy: {
        createdAt: 'desc',
      },
    });

    if (!message) {
      return null;
    }

    return this.mapToMessage(message);
  }

  async exists(id: MessageId): Promise<boolean> {
    const count = await this.prisma.message.count({
      where: { id: id.value },
    });
    return count > 0;
  }

  private mapToMessage(message: any): Message {
    return Message.fromPrimitives({
      id: message.id,
      conversationId: message.conversationId,
      senderId: message.senderId,
      content: message.content,
      type: message.messageType as MessageType,
      status: message.isRead ? MessageStatus.READ : MessageStatus.SENT,
      sentAt: message.createdAt.toISOString(),
      deliveredAt: undefined, // Current schema doesn't support this
      readAt: message.isRead ? message.createdAt.toISOString() : undefined,
      editedAt: undefined, // Current schema doesn't support this
      attachmentUrl: undefined, // Current schema doesn't support this
      replyToMessageId: undefined, // Current schema doesn't support this
      reactions: [], // Current schema doesn't support this
    });
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../../database/prisma.service';
import {
  IMessagingUnitOfWork,
  IConversationRepository,
  IMessageRepository,
} from '../../domain/repositories';
import { ConversationRepositoryImpl } from './conversation.repository';
import { MessageRepositoryImpl } from './message.repository';

@Injectable()
export class MessagingUnitOfWorkImpl implements IMessagingUnitOfWork {
  private _conversations: IConversationRepository;
  private _messages: IMessageRepository;

  constructor(private readonly prisma: PrismaService) {
    this._conversations = new ConversationRepositoryImpl(this.prisma);
    this._messages = new MessageRepositoryImpl(this.prisma);
  }

  get conversations(): IConversationRepository {
    return this._conversations;
  }

  get messages(): IMessageRepository {
    return this._messages;
  }

  async commit(): Promise<void> {
    // In a real implementation, this would commit a database transaction
    // For now, we'll use Prisma's implicit transactions
  }

  async rollback(): Promise<void> {
    // In a real implementation, this would rollback a database transaction
    // For now, we'll throw an error to indicate rollback
    throw new Error(
      'Transaction rollback not implemented with current Prisma setup',
    );
  }
}

import {
  Controller,
  Get,
  Post,
  Put,
  Delete,
  Body,
  Param,
  Query,
  UseGuards,
  Request,
  HttpStatus,
  BadRequestException,
  NotFoundException,
  ForbiddenException,
} from '@nestjs/common';
import {
  ApiTags,
  ApiOperation,
  ApiResponse,
  ApiBearerAuth,
  ApiParam,
} from '@nestjs/swagger';
import { ConversationUseCases } from '../../application/use-cases/conversation.use-cases';
import { MessagingApplicationServices } from '../../application/services/messaging-application.services';
import {
  CreatePrivateConversationDto,
  CreateGroupConversationDto,
  UpdateConversationTitleDto,
  AddParticipantDto,
  RemoveParticipantDto,
  GetConversationsQueryDto,
  ConversationResponseDto,
  ConversationWithLastMessageDto,
} from '../dto/conversation.dto';

@ApiTags('Conversations')
@ApiBearerAuth()
@Controller('conversations')
export class ConversationController {
  constructor(
    private readonly conversationUseCases: ConversationUseCases,
    private readonly messagingServices: MessagingApplicationServices,
  ) {}

  @Post('private')
  @ApiOperation({ summary: 'Create a private conversation' })
  @ApiResponse({
    status: HttpStatus.CREATED,
    description: 'Private conversation created successfully',
    schema: {
      type: 'object',
      properties: { conversationId: { type: 'string' } },
    },
  })
  @ApiResponse({
    status: HttpStatus.BAD_REQUEST,
    description: 'Invalid input or conversation already exists',
  })
  async createPrivateConversation(
    @Body() dto: CreatePrivateConversationDto,
    @Request() req: any,
  ) {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      // Validate that current user is one of the participants
      if (!dto.participantIds.includes(userId)) {
        throw new ForbiddenException(
          'You must be a participant in the conversation',
        );
      }

      const conversationId =
        await this.conversationUseCases.createPrivateConversation({
          participantIds: dto.participantIds,
          createdBy: userId,
        });

      return { conversationId };
    } catch (error) {
      if (error.message.includes('already exists')) {
        throw new BadRequestException(
          'Private conversation already exists between these users',
        );
      }
      throw error;
    }
  }

  @Post('group')
  @ApiOperation({ summary: 'Create a group conversation' })
  @ApiResponse({
    status: HttpStatus.CREATED,
    description: 'Group conversation created successfully',
    schema: {
      type: 'object',
      properties: { conversationId: { type: 'string' } },
    },
  })
  @ApiResponse({
    status: HttpStatus.BAD_REQUEST,
    description: 'Invalid input',
  })
  async createGroupConversation(
    @Body() dto: CreateGroupConversationDto,
    @Request() req: any,
  ) {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      const conversationId = await this.messagingServices.createGroupChat(
        dto.title,
        userId,
        dto.participantIds,
      );

      return { conversationId };
    } catch (error) {
      throw new BadRequestException(error.message);
    }
  }

  @Get()
  @ApiOperation({ summary: 'Get user conversations with last messages' })
  @ApiResponse({
    status: HttpStatus.OK,
    description: 'Conversations retrieved successfully',
    type: [ConversationWithLastMessageDto],
  })
  async getUserConversations(
    @Query() query: GetConversationsQueryDto,
    @Request() req: any,
  ): Promise<ConversationWithLastMessageDto[]> {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      return await this.messagingServices.getUserConversationsWithLastMessage(
        userId,
      );
    } catch (error) {
      throw new BadRequestException(error.message);
    }
  }

  @Get(':id')
  @ApiOperation({ summary: 'Get conversation by ID' })
  @ApiParam({ name: 'id', description: 'Conversation ID' })
  @ApiResponse({
    status: HttpStatus.OK,
    description: 'Conversation retrieved successfully',
    type: ConversationResponseDto,
  })
  @ApiResponse({
    status: HttpStatus.NOT_FOUND,
    description: 'Conversation not found',
  })
  @ApiResponse({
    status: HttpStatus.FORBIDDEN,
    description: 'Access denied',
  })
  async getConversationById(
    @Param('id') id: string,
    @Request() req: any,
  ): Promise<ConversationResponseDto> {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      const conversation = await this.conversationUseCases.getConversationById(
        id,
        userId,
      );
      if (!conversation) {
        throw new NotFoundException('Conversation not found or access denied');
      }

      return conversation.toPrimitives() as ConversationResponseDto;
    } catch (error) {
      if (error.message.includes('not found')) {
        throw new NotFoundException(error.message);
      }
      if (error.message.includes('access')) {
        throw new ForbiddenException(error.message);
      }
      throw error;
    }
  }

  @Put(':id/title')
  @ApiOperation({ summary: 'Update conversation title' })
  @ApiParam({ name: 'id', description: 'Conversation ID' })
  @ApiResponse({
    status: HttpStatus.OK,
    description: 'Conversation title updated successfully',
  })
  @ApiResponse({
    status: HttpStatus.NOT_FOUND,
    description: 'Conversation not found',
  })
  @ApiResponse({
    status: HttpStatus.FORBIDDEN,
    description: 'Access denied',
  })
  async updateConversationTitle(
    @Param('id') id: string,
    @Body() dto: UpdateConversationTitleDto,
    @Request() req: any,
  ) {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      await this.conversationUseCases.updateConversationTitle({
        conversationId: id,
        title: dto.title,
        updatedBy: userId,
      });

      return { message: 'Conversation title updated successfully' };
    } catch (error) {
      if (error.message.includes('not found')) {
        throw new NotFoundException(error.message);
      }
      if (
        error.message.includes('access') ||
        error.message.includes('privileges')
      ) {
        throw new ForbiddenException(error.message);
      }
      throw error;
    }
  }

  @Post(':id/participants')
  @ApiOperation({ summary: 'Add participant to conversation' })
  @ApiParam({ name: 'id', description: 'Conversation ID' })
  @ApiResponse({
    status: HttpStatus.OK,
    description: 'Participant added successfully',
  })
  @ApiResponse({
    status: HttpStatus.NOT_FOUND,
    description: 'Conversation not found',
  })
  @ApiResponse({
    status: HttpStatus.FORBIDDEN,
    description: 'Access denied or insufficient privileges',
  })
  async addParticipant(
    @Param('id') id: string,
    @Body() dto: AddParticipantDto,
    @Request() req: any,
  ) {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      await this.conversationUseCases.addParticipant({
        conversationId: id,
        userId: dto.userId,
        addedBy: userId,
      });

      return { message: 'Participant added successfully' };
    } catch (error) {
      if (error.message.includes('not found')) {
        throw new NotFoundException(error.message);
      }
      if (
        error.message.includes('access') ||
        error.message.includes('privileges')
      ) {
        throw new ForbiddenException(error.message);
      }
      throw new BadRequestException(error.message);
    }
  }

  @Delete(':id/participants/:userId')
  @ApiOperation({ summary: 'Remove participant from conversation' })
  @ApiParam({ name: 'id', description: 'Conversation ID' })
  @ApiParam({ name: 'userId', description: 'User ID to remove' })
  @ApiResponse({
    status: HttpStatus.OK,
    description: 'Participant removed successfully',
  })
  @ApiResponse({
    status: HttpStatus.NOT_FOUND,
    description: 'Conversation not found',
  })
  @ApiResponse({
    status: HttpStatus.FORBIDDEN,
    description: 'Access denied or insufficient privileges',
  })
  async removeParticipant(
    @Param('id') id: string,
    @Param('userId') participantUserId: string,
    @Request() req: any,
  ) {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      await this.conversationUseCases.removeParticipant({
        conversationId: id,
        userId: participantUserId,
        removedBy: userId,
      });

      return { message: 'Participant removed successfully' };
    } catch (error) {
      if (error.message.includes('not found')) {
        throw new NotFoundException(error.message);
      }
      if (
        error.message.includes('access') ||
        error.message.includes('privileges')
      ) {
        throw new ForbiddenException(error.message);
      }
      throw new BadRequestException(error.message);
    }
  }

  @Post(':id/leave')
  @ApiOperation({ summary: 'Leave group conversation' })
  @ApiParam({ name: 'id', description: 'Conversation ID' })
  @ApiResponse({
    status: HttpStatus.OK,
    description: 'Left conversation successfully',
  })
  @ApiResponse({
    status: HttpStatus.NOT_FOUND,
    description: 'Conversation not found',
  })
  async leaveConversation(@Param('id') id: string, @Request() req: any) {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      await this.messagingServices.leaveGroupConversation(id, userId);
      return { message: 'Left conversation successfully' };
    } catch (error) {
      if (error.message.includes('not found')) {
        throw new NotFoundException(error.message);
      }
      throw new BadRequestException(error.message);
    }
  }

  @Post(':id/archive')
  @ApiOperation({ summary: 'Archive conversation' })
  @ApiParam({ name: 'id', description: 'Conversation ID' })
  @ApiResponse({
    status: HttpStatus.OK,
    description: 'Conversation archived successfully',
  })
  async archiveConversation(@Param('id') id: string, @Request() req: any) {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      await this.conversationUseCases.archiveConversation(id, userId);
      return { message: 'Conversation archived successfully' };
    } catch (error) {
      if (error.message.includes('not found')) {
        throw new NotFoundException(error.message);
      }
      if (error.message.includes('access')) {
        throw new ForbiddenException(error.message);
      }
      throw error;
    }
  }

  @Post(':id/unarchive')
  @ApiOperation({ summary: 'Unarchive conversation' })
  @ApiParam({ name: 'id', description: 'Conversation ID' })
  @ApiResponse({
    status: HttpStatus.OK,
    description: 'Conversation unarchived successfully',
  })
  async unarchiveConversation(@Param('id') id: string, @Request() req: any) {
    try {
      const userId = req.user?.id || req.user?.sub;
      if (!userId) {
        throw new BadRequestException('User not authenticated');
      }

      await this.conversationUseCases.unarchiveConversation(id, userId);
      return { message: 'Conversation unarchived successfully' };
    } catch (error) {
      if (error.message.includes('not found')) {
        throw new NotFoundException(error.message);
      }
      if (error.message.includes('access')) {
        throw new ForbiddenException(error.message);
      }
      throw error;
    }
  }
}

import {
  Controller,
  Get,
  Post,
  Put,
  Delete,
  Body,
  Param,
