} from '@nestjs/common';
import {
  ApiTags,
  ApiOperation,
  ApiResponse,
  ApiBearerAuth,
  ApiParam,
} from '@nestjs/swagger';
import { MessageUseCases } from '../../application/use-cases/message.use-cases';
import { MessagingApplicationServices } from '../../application/services/messaging-application.services';
import {
  SendTextMessageDto,
  SendMediaMessageDto,
