import { Injectable, Inject } from '@nestjs/common';
import { INotificationDomainRepository } from '../../domain/repositories/notification-domain-repository.interface';
import { NotificationDomainService } from '../../domain/services/notification-domain.service';
import { NOTIFICATION_REPOSITORY_TOKEN } from './create-notification.use-case';

export interface CleanupResult {
  deletedCount: number;
  message: string;
}

/**
 * Use case for cleaning up old notifications
 * Used for maintenance and storage optimization
 */
@Injectable()
export class NotificationCleanupUseCase {
  constructor(
    private readonly notificationDomainService: NotificationDomainService,
    @Inject(NOTIFICATION_REPOSITORY_TOKEN)
    private readonly notificationRepository: INotificationDomainRepository,
  ) {}

  /**
   * Clean up old read notifications for a specific user
   */
  async cleanupUserReadNotifications(
    userId: string,
    olderThanDays: number = 30,
  ): Promise<CleanupResult> {
    const deletedCount =
      await this.notificationRepository.deleteReadNotifications(
        userId,
        olderThanDays,
      );

    return {
      deletedCount,
      message: `Deleted ${deletedCount} read notifications older than ${olderThanDays} days for user ${userId}`,
    };
  }

  /**
   * Clean up all old notifications system-wide (admin operation)
   */
  async cleanupSystemOldNotifications(
    olderThanDays: number = 90,
  ): Promise<CleanupResult> {
    const deletedCount =
      await this.notificationRepository.deleteOldNotifications(olderThanDays);

    return {
      deletedCount,
      message: `System cleanup: Deleted ${deletedCount} notifications older than ${olderThanDays} days`,
    };
  }

  /**
   * Clean up notifications related to a deleted entity
   */
  async cleanupNotificationsByEntity(
    entityId: string,
    entityType: any, // NotificationEntityType
    userId?: string,
  ): Promise<CleanupResult> {
    // Find notifications to be deleted first (for counting)
    const notificationsToDelete =
      await this.notificationRepository.findByEntity(
        entityId,
        entityType,
        userId,
      );

    // Delete the notifications
    await this.notificationRepository.deleteByEntity(
      entityId,
      entityType,
      userId,
    );

    const deletedCount = notificationsToDelete.length;
    const userScope = userId ? `for user ${userId}` : 'system-wide';

    return {
      deletedCount,
      message: `Deleted ${deletedCount} notifications related to ${entityType} ${entityId} ${userScope}`,
    };
  }

  /**
   * Batch cleanup for multiple users (admin operation)
   */
  async batchCleanupUsers(
    userIds: string[],
    olderThanDays: number = 30,
  ): Promise<CleanupResult[]> {
    const results: CleanupResult[] = [];

    for (const userId of userIds) {
      try {
        const result = await this.cleanupUserReadNotifications(
          userId,
          olderThanDays,
        );
        results.push(result);
      } catch (error) {
        results.push({
          deletedCount: 0,
          message: `Failed to cleanup notifications for user ${userId}: ${error.message}`,
        });
      }
    }

    return results;
  }

  /**
   * Get cleanup statistics before performing cleanup
   */
  async getCleanupStats(
    userId?: string,
    olderThanDays: number = 30,
  ): Promise<{
    totalNotifications: number;
    readNotifications: number;
    oldReadNotifications: number;
    estimatedCleanupCount: number;
  }> {
    const filters = userId ? { userId } : {};

    // Get all notifications
    const allResult = await this.notificationRepository.findAll({
      ...filters,
      page: 1,
      limit: 100000, // Large number to get all
    });

    // Get read notifications
    const readResult = await this.notificationRepository.findAll({
      ...filters,
      isRead: true,
      page: 1,
      limit: 100000,
    });

    // Calculate old read notifications
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - olderThanDays);

    const oldReadNotifications = readResult.notifications.filter(
      (notification) => notification.createdAt < cutoffDate,
    );

    return {
      totalNotifications: allResult.total,
      readNotifications: readResult.total,
      oldReadNotifications: oldReadNotifications.length,
      estimatedCleanupCount: oldReadNotifications.length,
    };
  }
}

import { Injectable, Inject } from '@nestjs/common';
import { INotificationDomainRepository } from '../../domain/repositories/notification-domain-repository.interface';
import { NotificationDomainService } from '../../domain/services/notification-domain.service';
import {
  NotificationNotFoundException,
  UnauthorizedNotificationAccessException,
} from '../../domain/notification.exceptions';
import {
  UpdateNotificationDto,
  NotificationResponseDto,
} from '../dto/notification.dto';
import { NOTIFICATION_REPOSITORY_TOKEN } from './create-notification.use-case';

/**
 * Use case for updating a notification
 */
@Injectable()
export class UpdateNotificationUseCase {
  constructor(
    private readonly notificationDomainService: NotificationDomainService,
    @Inject(NOTIFICATION_REPOSITORY_TOKEN)
    private readonly notificationRepository: INotificationDomainRepository,
  ) {}

  async execute(
    notificationId: string,
    userId: string,
    dto: UpdateNotificationDto,
  ): Promise<NotificationResponseDto> {
    // Find notification
    const notification =
      await this.notificationRepository.findById(notificationId);

    if (!notification) {
      throw new NotificationNotFoundException(notificationId);
    }

    // Validate access permissions
    this.notificationDomainService.validateNotificationAccess(
      notification,
      userId,
    );

    // Update notification content if provided
    if (dto.title || dto.content) {
      notification.updateContent(
        dto.title || notification.title,
        dto.content || notification.content,
      );
    }

    // Update read status if provided
    if (dto.isRead !== undefined) {
      if (dto.isRead && !notification.isRead) {
        notification.markAsRead();
      } else if (!dto.isRead && notification.isRead) {
        notification.markAsUnread();
      }
    }

    // Save changes
    const updatedNotification =
      await this.notificationRepository.save(notification);

    // Convert to response DTO
    return this.mapToResponseDto(updatedNotification);
  }

  private mapToResponseDto(notification: any): NotificationResponseDto {
    const priority = this.notificationDomainService.getNotificationPriority(
      notification.type,
    );

    return {
      id: notification.id,
      type: notification.type,
      title: notification.title,
      content: notification.content,
      userId: notification.userId,
      isRead: notification.isRead,
      entityId: notification.entityId,
      entityType: notification.entityType,
      createdAt: notification.createdAt,
      priority,
    };
  }
}

import { Injectable } from '@nestjs/common';
import {
  NotificationEntity,
  NotificationType,
  NotificationEntityType,
} from '../notification.entity';
import {
  NotificationNotFoundException,
  UnauthorizedNotificationAccessException,
  NotificationBulkOperationException,
} from '../notification.exceptions';

/**
 * Domain service for complex notification business logic
 */
@Injectable()
export class NotificationDomainService {
  /**
   * Validates if a user can access a notification
   */
  validateNotificationAccess(
    notification: NotificationEntity,
    userId: string,
  ): void {
    if (!notification.isOwnedBy(userId)) {
      throw new UnauthorizedNotificationAccessException(
        notification.id,
        userId,
      );
    }
  }

  /**
   * Validates bulk operation permissions
   */
  validateBulkOperationPermissions(
    notifications: NotificationEntity[],
    userId: string,
  ): void {
    const unauthorizedNotifications = notifications.filter(
      (notification) => !notification.isOwnedBy(userId),
    );

    if (unauthorizedNotifications.length > 0) {
      const unauthorizedIds = unauthorizedNotifications.map((n) => n.id);
      throw new UnauthorizedNotificationAccessException(
        `Multiple notifications: ${unauthorizedIds.join(', ')}`,
        userId,
      );
    }
  }

  /**
   * Determines notification priority based on type
   */
  getNotificationPriority(type: NotificationType): 'high' | 'medium' | 'low' {
    const highPriorityTypes = [
      NotificationType.MESSAGE,
      NotificationType.FRIEND_REQUEST,
    ];

    const mediumPriorityTypes = [
      NotificationType.COMMENT,
      NotificationType.POST_MENTION,
      NotificationType.COMMENT_MENTION,
    ];

    if (highPriorityTypes.includes(type)) {
      return 'high';
    }

    if (mediumPriorityTypes.includes(type)) {
      return 'medium';
    }

    return 'low';
  }

  /**
   * Checks if notifications should be grouped together
   */
  shouldGroupNotifications(
    notification1: NotificationEntity,
    notification2: NotificationEntity,
  ): boolean {
    // Group notifications of the same type for the same entity
    return (
      notification1.type === notification2.type &&
      notification1.entityId === notification2.entityId &&
      notification1.entityType === notification2.entityType &&
      this.areNotificationsRecent(notification1, notification2)
    );
  }

  /**
   * Checks if notifications are recent enough to be grouped
   */
  private areNotificationsRecent(
    notification1: NotificationEntity,
    notification2: NotificationEntity,
    maxHoursDifference: number = 24,
  ): boolean {
    const timeDifference = Math.abs(
      notification1.createdAt.getTime() - notification2.createdAt.getTime(),
    );
    const maxDifference = maxHoursDifference * 60 * 60 * 1000; // Convert to milliseconds

    return timeDifference <= maxDifference;
  }

  /**
   * Generates notification content based on type and context
   */
  generateNotificationContent(
    type: NotificationType,
    context: {
      actorName?: string;
      entityName?: string;
      customMessage?: string;
    },
  ): { title: string; content: string } {
    switch (type) {
      case NotificationType.LIKE:
        return {
          title: 'New Like',
          content: `${context.actorName} liked your ${context.entityName || 'post'}`,
        };

      case NotificationType.COMMENT:
        return {
          title: 'New Comment',
          content: `${context.actorName} commented on your ${context.entityName || 'post'}`,
        };

      case NotificationType.FOLLOW:
        return {
          title: 'New Follower',
          content: `${context.actorName} started following you`,
        };

      case NotificationType.MESSAGE:
        return {
          title: 'New Message',
          content: `${context.actorName} sent you a message`,
        };

      case NotificationType.POST_MENTION:
        return {
          title: 'You were mentioned',
          content: `${context.actorName} mentioned you in a post`,
        };

      case NotificationType.COMMENT_MENTION:
        return {
          title: 'You were mentioned',
          content: `${context.actorName} mentioned you in a comment`,
        };

      case NotificationType.FRIEND_REQUEST:
        return {
          title: 'Friend Request',
          content: `${context.actorName} sent you a friend request`,
        };

      case NotificationType.BIRTHDAY:
        return {
          title: 'Birthday Reminder',
          content: `It's ${context.actorName}'s birthday today!`,
        };

      case NotificationType.POST_SHARE:
        return {
          title: 'Post Shared',
          content: `${context.actorName} shared your ${context.entityName || 'post'}`,
        };

      case NotificationType.SYSTEM:
        return {
          title: 'System Notification',
          content:
            context.customMessage || 'You have a new system notification',
        };

      default:
        return {
          title: 'New Notification',
          content: context.customMessage || 'You have a new notification',
        };
    }
  }

  /**
   * Determines if notification should be sent in real-time
   */
  shouldSendRealTime(type: NotificationType): boolean {
    const realTimeTypes = [
      NotificationType.MESSAGE,
      NotificationType.FRIEND_REQUEST,
      NotificationType.COMMENT,
      NotificationType.POST_MENTION,
      NotificationType.COMMENT_MENTION,
    ];

    return realTimeTypes.includes(type);
  }

  /**
   * Determines if notification should be sent via email
   */
  shouldSendEmail(type: NotificationType): boolean {
    const emailTypes = [
      NotificationType.FRIEND_REQUEST,
      NotificationType.MESSAGE,
      NotificationType.SYSTEM,
    ];

    return emailTypes.includes(type);
  }

  /**
   * Determines if notification should be sent via push notification
   */
  shouldSendPush(type: NotificationType): boolean {
    // Most notifications should be sent via push, except system ones
    return type !== NotificationType.SYSTEM;
  }

  /**
   * Calculates notification digest summary
   */
  createNotificationDigest(notifications: NotificationEntity[]): {
    totalCount: number;
    unreadCount: number;
    typeBreakdown: Record<NotificationType, number>;
    priorityBreakdown: Record<'high' | 'medium' | 'low', number>;
  } {
    const digest = {
      totalCount: notifications.length,
      unreadCount: notifications.filter((n) => !n.isRead).length,
      typeBreakdown: {} as Record<NotificationType, number>,
      priorityBreakdown: { high: 0, medium: 0, low: 0 },
    };

    notifications.forEach((notification) => {
      // Count by type
      if (!digest.typeBreakdown[notification.type]) {
        digest.typeBreakdown[notification.type] = 0;
      }
      digest.typeBreakdown[notification.type]++;

      // Count by priority
      const priority = this.getNotificationPriority(notification.type);
      digest.priorityBreakdown[priority]++;
    });

    return digest;
  }
}

import { Injectable, Inject } from '@nestjs/common';
import {
  INotificationRepository,
  CreateNotificationDto,
  UpdateNotificationDto,
  NotificationResponseDto,
  NotificationListResponseDto,
  NotificationStatsDto,
  NotificationQueryDto,
} from '../../application';
import {
  INotificationDomainRepository,
  NotificationDomainService,
  NotificationFactory,
} from '../../domain';

export const NOTIFICATION_REPOSITORY_TOKEN = 'NOTIFICATION_REPOSITORY';

/**
 * Application-layer repository implementation that adapts domain repository
 * This acts as an adapter between the application and domain layers
 */
@Injectable()
export class NotificationApplicationRepository
  implements INotificationRepository
{
  constructor(
    @Inject(NOTIFICATION_REPOSITORY_TOKEN)
    private readonly domainRepository: INotificationDomainRepository,
    private readonly notificationFactory: NotificationFactory,
    private readonly domainService: NotificationDomainService,
  ) {}

  async create(dto: CreateNotificationDto): Promise<NotificationResponseDto> {
    // Create domain entity using factory
    const notification = this.notificationFactory.createNotification({
      type: dto.type,
      userId: dto.userId,
      entityId: dto.entityId,
      entityType: dto.entityType,
      customTitle: dto.title,
      customContent: dto.content,
    });

    // Save using domain repository
    const savedNotification = await this.domainRepository.save(notification);

    // Convert to application DTO
    return this.mapToResponseDto(savedNotification);
  }

  async findById(id: string): Promise<NotificationResponseDto | null> {
    const notification = await this.domainRepository.findById(id);

    if (!notification) {
      return null;
    }

    return this.mapToResponseDto(notification);
  }

  async update(
    id: string,
    dto: UpdateNotificationDto,
  ): Promise<NotificationResponseDto> {
    const notification = await this.domainRepository.findById(id);

    if (!notification) {
      throw new Error(`Notification with id ${id} not found`);
    }

    // Update using domain methods
    if (dto.title || dto.content) {
      notification.updateContent(
        dto.title || notification.title,
        dto.content || notification.content,
      );
    }

    if (dto.isRead !== undefined) {
      if (dto.isRead && !notification.isRead) {
        notification.markAsRead();
      } else if (!dto.isRead && notification.isRead) {
        notification.markAsUnread();
      }
    }

    // Save changes
    const updatedNotification = await this.domainRepository.save(notification);

    return this.mapToResponseDto(updatedNotification);
  }

  async delete(id: string): Promise<void> {
    await this.domainRepository.delete(id);
  }

  async exists(id: string): Promise<boolean> {
    return await this.domainRepository.exists(id);
  }

  async findByUserId(
    userId: string,
    query: NotificationQueryDto,
  ): Promise<NotificationListResponseDto> {
    const result = await this.domainRepository.findByUserId(userId, {
      isRead: query.isRead,
      type: query.type,
      entityType: query.entityType,
      page: query.page || 1,
      limit: query.limit || 20,
      sortBy: query.sortBy || 'newest',
    });

    const notifications = result.notifications.map((notification) =>
      this.mapToResponseDto(notification),
    );

    const totalPages = Math.ceil(result.total / (query.limit || 20));

    return {
      notifications,
      total: result.total,
      unreadCount: result.unreadCount,
      page: query.page || 1,
      limit: query.limit || 20,
      totalPages,
    };
  }

  async findAll(
    query: NotificationQueryDto & { userId?: string },
  ): Promise<NotificationListResponseDto> {
    const result = await this.domainRepository.findAll({
      userId: query.userId,
      isRead: query.isRead,
      type: query.type,
      entityType: query.entityType,
      entityId: query.entityId,
      page: query.page || 1,
      limit: query.limit || 20,
      sortBy: query.sortBy || 'newest',
    });

    const notifications = result.notifications.map((notification) =>
      this.mapToResponseDto(notification),
    );

    const totalPages = Math.ceil(result.total / (query.limit || 20));

    return {
      notifications,
      total: result.total,
      unreadCount: 0, // Not applicable for general findAll
      page: query.page || 1,
      limit: query.limit || 20,
      totalPages,
    };
  }

  async markAsReadBulk(
    notificationIds: string[],
    userId: string,
  ): Promise<void> {
    await this.domainRepository.markAsReadBulk(notificationIds, userId);
  }

  async markAsUnreadBulk(
    notificationIds: string[],
    userId: string,
  ): Promise<void> {
    await this.domainRepository.markAsUnreadBulk(notificationIds, userId);
  }

  async deleteBulk(notificationIds: string[], userId: string): Promise<void> {
    await this.domainRepository.deleteBulk(notificationIds, userId);
  }

  async getStats(userId: string): Promise<NotificationStatsDto> {
    const result = await this.domainRepository.findByUserId(userId, {
      page: 1,
      limit: 10000, // Get all for stats
    });

    // Use domain service to create digest
    const digest = this.domainService.createNotificationDigest(
      result.notifications,
    );

    return {
      totalCount: digest.totalCount,
      unreadCount: digest.unreadCount,
      typeBreakdown: digest.typeBreakdown,
      priorityBreakdown: digest.priorityBreakdown,
    };
  }

  async getUnreadCount(userId: string): Promise<number> {
    return await this.domainRepository.getUnreadCount(userId);
  }

  async getLatestNotifications(
    userId: string,
    sinceTimestamp: Date,
    limit?: number,
  ): Promise<NotificationResponseDto[]> {
    const notifications = await this.domainRepository.getLatestNotifications(
      userId,
      sinceTimestamp,
      limit,
    );

    return notifications.map((notification) =>
      this.mapToResponseDto(notification),
    );
  }

  async cleanupOldNotifications(olderThanDays: number): Promise<number> {
    return await this.domainRepository.deleteOldNotifications(olderThanDays);
  }

  async cleanupReadNotifications(
    userId: string,
    olderThanDays: number,
  ): Promise<number> {
    return await this.domainRepository.deleteReadNotifications(
      userId,
      olderThanDays,
    );
  }

  private mapToResponseDto(notification: any): NotificationResponseDto {
    const priority = this.domainService.getNotificationPriority(
      notification.type,
    );

    return {
      id: notification.id,
      type: notification.type,
      title: notification.title,
      content: notification.content,
      userId: notification.userId,
      isRead: notification.isRead,
      entityId: notification.entityId,
      entityType: notification.entityType,
      createdAt: notification.createdAt,
      priority,
    };
  }
}

