import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../../database/prisma.service';
import {
  NotificationEntity,
  NotificationType,
  NotificationEntityType,
  INotificationDomainRepository,
} from '../../domain';

/**
 * Prisma implementation of the notification domain repository
 */
@Injectable()
export class NotificationRepository implements INotificationDomainRepository {
  constructor(private readonly prisma: PrismaService) {}

  async save(notification: NotificationEntity): Promise<NotificationEntity> {
    const data = {
      id: notification.id,
      type: notification.type,
      title: notification.title,
      content: notification.content,
      userId: notification.userId,
      isRead: notification.isRead,
      entityId: notification.entityId,
      entityType: notification.entityType,
      createdAt: notification.createdAt,
    };

    const savedNotification = await this.prisma.notification.upsert({
      where: { id: notification.id },
      create: data,
      update: {
        type: data.type,
        title: data.title,
        content: data.content,
        isRead: data.isRead,
        entityId: data.entityId,
        entityType: data.entityType,
      },
    });

    return this.mapToDomainEntity(savedNotification);
  }

  async findById(id: string): Promise<NotificationEntity | null> {
    const notification = await this.prisma.notification.findUnique({
      where: { id },
    });

    if (!notification) {
      return null;
    }

    return this.mapToDomainEntity(notification);
  }

  async delete(id: string): Promise<void> {
    await this.prisma.notification.delete({
      where: { id },
    });
  }

  async exists(id: string): Promise<boolean> {
    const count = await this.prisma.notification.count({
      where: { id },
    });
    return count > 0;
  }

  async findByUserId(
    userId: string,
    filters: {
      isRead?: boolean;
      type?: NotificationType;
      entityType?: NotificationEntityType;
      page: number;
      limit: number;
      sortBy?: 'newest' | 'oldest';
    },
  ): Promise<{
    notifications: NotificationEntity[];
    total: number;
    unreadCount: number;
  }> {
    const where: any = { userId };

    if (filters.isRead !== undefined) {
      where.isRead = filters.isRead;
    }

    if (filters.type) {
      where.type = filters.type;
    }

    if (filters.entityType) {
      where.entityType = filters.entityType;
    }

    const orderBy = {
      createdAt:
        filters.sortBy === 'oldest' ? ('asc' as const) : ('desc' as const),
    };

    const skip = (filters.page - 1) * filters.limit;

    const [notifications, total, unreadCount] = await Promise.all([
      this.prisma.notification.findMany({
        where,
        orderBy,
        skip,
        take: filters.limit,
      }),
      this.prisma.notification.count({ where }),
      this.prisma.notification.count({
        where: { userId, isRead: false },
      }),
    ]);

    return {
      notifications: notifications.map((n) => this.mapToDomainEntity(n)),
      total,
      unreadCount,
    };
  }

  async findAll(filters: {
    userId?: string;
    isRead?: boolean;
    type?: NotificationType;
    entityType?: NotificationEntityType;
    entityId?: string;
    page: number;
    limit: number;
    sortBy?: 'newest' | 'oldest';
  }): Promise<{
    notifications: NotificationEntity[];
    total: number;
  }> {
    const where: any = {};

    if (filters.userId) {
      where.userId = filters.userId;
    }

    if (filters.isRead !== undefined) {
      where.isRead = filters.isRead;
    }

    if (filters.type) {
      where.type = filters.type;
    }

    if (filters.entityType) {
      where.entityType = filters.entityType;
    }

    if (filters.entityId) {
      where.entityId = filters.entityId;
    }

    const orderBy = {
      createdAt:
        filters.sortBy === 'oldest' ? ('asc' as const) : ('desc' as const),
    };

    const skip = (filters.page - 1) * filters.limit;

    const [notifications, total] = await Promise.all([
      this.prisma.notification.findMany({
        where,
        orderBy,
        skip,
        take: filters.limit,
      }),
      this.prisma.notification.count({ where }),
    ]);

    return {
      notifications: notifications.map((n) => this.mapToDomainEntity(n)),
      total,
    };
  }

  async findByIds(ids: string[]): Promise<NotificationEntity[]> {
    const notifications = await this.prisma.notification.findMany({
      where: {
        id: { in: ids },
      },
    });

    return notifications.map((n) => this.mapToDomainEntity(n));
  }

  async markAsReadBulk(
    notificationIds: string[],
    userId: string,
  ): Promise<void> {
    await this.prisma.notification.updateMany({
      where: {
        id: { in: notificationIds },
        userId, // Ensure user owns the notifications
      },
      data: {
        isRead: true,
      },
    });
  }

  async markAsUnreadBulk(
    notificationIds: string[],
    userId: string,
  ): Promise<void> {
    await this.prisma.notification.updateMany({
      where: {
        id: { in: notificationIds },
        userId, // Ensure user owns the notifications
      },
      data: {
        isRead: false,
      },
    });
  }

  async deleteBulk(notificationIds: string[], userId: string): Promise<void> {
    await this.prisma.notification.deleteMany({
      where: {
        id: { in: notificationIds },
        userId, // Ensure user owns the notifications
      },
    });
  }

  async findByEntity(
    entityId: string,
    entityType: NotificationEntityType,
    userId?: string,
  ): Promise<NotificationEntity[]> {
    const where: any = {
      entityId,
      entityType,
    };

    if (userId) {
      where.userId = userId;
    }

    const notifications = await this.prisma.notification.findMany({
      where,
      orderBy: { createdAt: 'desc' },
    });

    return notifications.map((n) => this.mapToDomainEntity(n));
  }

  async deleteByEntity(
    entityId: string,
    entityType: NotificationEntityType,
    userId?: string,
  ): Promise<void> {
    const where: any = {
      entityId,
      entityType,
    };

    if (userId) {
      where.userId = userId;
    }

    await this.prisma.notification.deleteMany({ where });
  }

  async getUnreadCount(userId: string): Promise<number> {
    return await this.prisma.notification.count({
      where: {
        userId,
        isRead: false,
      },
    });
  }

  async getUnreadCountByType(
    userId: string,
    type: NotificationType,
  ): Promise<number> {
    return await this.prisma.notification.count({
      where: {
        userId,
        type,
        isRead: false,
      },
    });
  }

  async deleteOldNotifications(olderThanDays: number): Promise<number> {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - olderThanDays);

    const result = await this.prisma.notification.deleteMany({
      where: {
        createdAt: {
          lt: cutoffDate,
        },
      },
    });

    return result.count;
  }

  async deleteReadNotifications(
    userId: string,
    olderThanDays: number,
  ): Promise<number> {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - olderThanDays);

    const result = await this.prisma.notification.deleteMany({
      where: {
        userId,
        isRead: true,
        createdAt: {
          lt: cutoffDate,
        },
      },
    });

    return result.count;
  }

  async findUserNotificationPreferences(userId: string): Promise<{
    enabledTypes: NotificationType[];
    mutedEntityIds: string[];
  } | null> {
    // This would typically be stored in a separate preferences table
    // For now, return default preferences
    return {
      enabledTypes: Object.values(NotificationType),
      mutedEntityIds: [],
    };
  }

  async getLatestNotifications(
    userId: string,
    sinceTimestamp: Date,
    limit: number = 50,
  ): Promise<NotificationEntity[]> {
    const notifications = await this.prisma.notification.findMany({
      where: {
        userId,
        createdAt: {
          gt: sinceTimestamp,
        },
      },
      orderBy: { createdAt: 'desc' },
      take: limit,
    });

    return notifications.map((n) => this.mapToDomainEntity(n));
  }

  private mapToDomainEntity(prismaNotification: any): NotificationEntity {
    return NotificationEntity.fromPersistence({
      id: prismaNotification.id,
      type: prismaNotification.type as NotificationType,
      title: prismaNotification.title,
      content: prismaNotification.content,
      userId: prismaNotification.userId,
      isRead: prismaNotification.isRead,
      entityId: prismaNotification.entityId,
      entityType: prismaNotification.entityType as NotificationEntityType,
      createdAt: prismaNotification.createdAt,
    });
  }
}

import { Injectable } from '@nestjs/common';
import { NotificationType } from '../../domain';

export interface EmailNotificationPayload {
  to: string;
  subject: string;
  content: string;
  type: NotificationType;
  metadata?: Record<string, any>;
}

export interface IEmailNotificationService {
  sendNotificationEmail(payload: EmailNotificationPayload): Promise<boolean>;
  sendBulkNotificationEmails(
    payloads: EmailNotificationPayload[],
  ): Promise<boolean[]>;
}

/**
 * Mock implementation of email notification service
 * In production, this would integrate with services like SendGrid, AWS SES, etc.
 */
@Injectable()
export class EmailNotificationService implements IEmailNotificationService {
  async sendNotificationEmail(
    payload: EmailNotificationPayload,
  ): Promise<boolean> {
    try {
      console.log(`[EMAIL] Sending notification email to ${payload.to}`);
      console.log(`[EMAIL] Subject: ${payload.subject}`);
      console.log(`[EMAIL] Type: ${payload.type}`);
      console.log(`[EMAIL] Content: ${payload.content}`);

      // In production, integrate with actual email service
      // await this.emailProvider.send(payload);

      // Simulate email sending delay
      await new Promise((resolve) => setTimeout(resolve, 100));

      return true;
    } catch (error) {
      console.error(
        `[EMAIL] Failed to send notification email to ${payload.to}:`,
        error,
      );
      return false;
    }
  }

  async sendBulkNotificationEmails(
    payloads: EmailNotificationPayload[],
  ): Promise<boolean[]> {
    console.log(
      `[EMAIL] Sending bulk notification emails (${payloads.length} emails)`,
    );

    const results = await Promise.allSettled(
      payloads.map((payload) => this.sendNotificationEmail(payload)),
    );

    return results.map((result) =>
      result.status === 'fulfilled' ? result.value : false,
    );
  }

  /**
   * Generate email template based on notification type
   */
  generateEmailTemplate(
    type: NotificationType,
    context: {
      recipientName?: string;
      actorName?: string;
      entityName?: string;
      appUrl?: string;
    },
  ): { subject: string; content: string } {
    const {
      recipientName = 'User',
      actorName = 'Someone',
      entityName = 'item',
      appUrl = '#',
    } = context;

    switch (type) {
      case NotificationType.FRIEND_REQUEST:
        return {
          subject: `${actorName} sent you a friend request`,
          content: `
            Hi ${recipientName},

            ${actorName} has sent you a friend request on our platform.

            Click here to view and respond: ${appUrl}/friends/requests

            Best regards,
            The Social Media Team
          `,
        };

      case NotificationType.MESSAGE:
        return {
          subject: `New message from ${actorName}`,
          content: `
            Hi ${recipientName},

            You have received a new message from ${actorName}.

            Click here to read the message: ${appUrl}/messages

            Best regards,
            The Social Media Team
          `,
        };

      case NotificationType.SYSTEM:
        return {
          subject: 'Important system notification',
          content: `
            Hi ${recipientName},

            You have received an important system notification.

            Click here to view: ${appUrl}/notifications

            Best regards,
            The Social Media Team
          `,
        };

      default:
        return {
          subject: 'New notification',
          content: `
            Hi ${recipientName},

            You have a new notification on our platform.

            Click here to view: ${appUrl}/notifications

            Best regards,
            The Social Media Team
          `,
        };
    }
  }
}

import { Injectable } from '@nestjs/common';
import { NotificationType } from '../../domain';

export interface PushNotificationPayload {
  userId: string;
  title: string;
  body: string;
  type: NotificationType;
  data?: Record<string, any>;
  badge?: number;
  sound?: string;
  icon?: string;
}

export interface IPushNotificationService {
  sendPushNotification(payload: PushNotificationPayload): Promise<boolean>;
  sendBulkPushNotifications(
    payloads: PushNotificationPayload[],
  ): Promise<boolean[]>;
  registerDeviceToken(
    userId: string,
    deviceToken: string,
    platform: 'ios' | 'android' | 'web',
  ): Promise<void>;
  unregisterDeviceToken(userId: string, deviceToken: string): Promise<void>;
}

/**
 * Mock implementation of push notification service
 * In production, this would integrate with Firebase Cloud Messaging, Apple Push Notification Service, etc.
 */
@Injectable()
export class PushNotificationService implements IPushNotificationService {
  private readonly deviceTokens = new Map<
    string,
    { token: string; platform: string }[]
  >();

  async sendPushNotification(
    payload: PushNotificationPayload,
  ): Promise<boolean> {
    try {
      const userTokens = this.deviceTokens.get(payload.userId) || [];

      if (userTokens.length === 0) {
        console.log(`[PUSH] No device tokens found for user ${payload.userId}`);
        return true; // Not an error, just no devices to notify
      }

      console.log(
        `[PUSH] Sending push notification to ${userTokens.length} devices for user ${payload.userId}`,
      );
      console.log(`[PUSH] Title: ${payload.title}`);
      console.log(`[PUSH] Body: ${payload.body}`);
      console.log(`[PUSH] Type: ${payload.type}`);

      // In production, send to actual push notification services
      for (const deviceToken of userTokens) {
        await this.sendToDevice(
          deviceToken.token,
          deviceToken.platform,
          payload,
        );
      }

      return true;
    } catch (error) {
      console.error(
        `[PUSH] Failed to send push notification to user ${payload.userId}:`,
        error,
      );
      return false;
    }
  }

  async sendBulkPushNotifications(
    payloads: PushNotificationPayload[],
  ): Promise<boolean[]> {
    console.log(
      `[PUSH] Sending bulk push notifications (${payloads.length} notifications)`,
    );

    const results = await Promise.allSettled(
      payloads.map((payload) => this.sendPushNotification(payload)),
    );

    return results.map((result) =>
      result.status === 'fulfilled' ? result.value : false,
    );
  }

  async registerDeviceToken(
    userId: string,
    deviceToken: string,
    platform: 'ios' | 'android' | 'web',
  ): Promise<void> {
    const userTokens = this.deviceTokens.get(userId) || [];

    // Remove existing token if it exists
    const existingIndex = userTokens.findIndex((t) => t.token === deviceToken);
    if (existingIndex >= 0) {
      userTokens.splice(existingIndex, 1);
    }

    // Add new token
    userTokens.push({ token: deviceToken, platform });
    this.deviceTokens.set(userId, userTokens);

    console.log(
      `[PUSH] Registered device token for user ${userId}, platform: ${platform}`,
    );
  }

  async unregisterDeviceToken(
    userId: string,
    deviceToken: string,
  ): Promise<void> {
    const userTokens = this.deviceTokens.get(userId) || [];
    const filteredTokens = userTokens.filter((t) => t.token !== deviceToken);

    if (filteredTokens.length === 0) {
      this.deviceTokens.delete(userId);
    } else {
      this.deviceTokens.set(userId, filteredTokens);
    }

    console.log(`[PUSH] Unregistered device token for user ${userId}`);
  }

  private async sendToDevice(
    deviceToken: string,
    platform: string,
    payload: PushNotificationPayload,
  ): Promise<void> {
    // Simulate different platform handling
    console.log(
      `[PUSH] Sending to ${platform} device: ${deviceToken.substring(0, 10)}...`,
    );

    // Simulate network delay
    await new Promise((resolve) => setTimeout(resolve, 50));

    // In production, this would make actual API calls to:
    // - Firebase Cloud Messaging for Android/Web
    // - Apple Push Notification Service for iOS
    // - Windows Notification Service for Windows
  }

  /**
   * Get notification priority based on type
   */
  getNotificationPriority(type: NotificationType): 'high' | 'normal' | 'low' {
    const highPriorityTypes = [
      NotificationType.MESSAGE,
      NotificationType.FRIEND_REQUEST,
      NotificationType.SYSTEM,
    ];

    const normalPriorityTypes = [
      NotificationType.COMMENT,
      NotificationType.POST_MENTION,
      NotificationType.COMMENT_MENTION,
    ];

    if (highPriorityTypes.includes(type)) {
      return 'high';
    }

    if (normalPriorityTypes.includes(type)) {
      return 'normal';
    }

    return 'low';
  }

  /**
   * Generate push notification payload from notification data
   */
  generatePushPayload(
    userId: string,
    type: NotificationType,
    title: string,
    content: string,
    metadata?: Record<string, any>,
  ): PushNotificationPayload {
    return {
      userId,
      title,
      body: content,
      type,
      data: {
        type,
        ...metadata,
      },
      badge: 1, // This would typically be the actual unread count
      sound: this.getNotificationSound(type),
      icon: this.getNotificationIcon(type),
    };
  }

  private getNotificationSound(type: NotificationType): string {
    switch (type) {
      case NotificationType.MESSAGE:
        return 'message.wav';
      case NotificationType.FRIEND_REQUEST:
        return 'friend_request.wav';
      case NotificationType.SYSTEM:
        return 'system.wav';
      default:
        return 'default.wav';
    }
  }

  private getNotificationIcon(type: NotificationType): string {
    switch (type) {
      case NotificationType.LIKE:
        return 'ic_like';
      case NotificationType.COMMENT:
        return 'ic_comment';
      case NotificationType.FOLLOW:
        return 'ic_follow';
      case NotificationType.MESSAGE:
        return 'ic_message';
      case NotificationType.FRIEND_REQUEST:
        return 'ic_friend_request';
      default:
        return 'ic_notification';
    }
  }
}

import { Injectable } from '@nestjs/common';
import { NotificationResponseDto } from '../../application';
import { NotificationType } from '../../domain';

export interface RealTimeNotificationPayload {
  userId: string;
  notification: NotificationResponseDto;
  type:
    | 'notification_created'
    | 'notification_updated'
    | 'notification_deleted'
    | 'bulk_read';
  timestamp: Date;
}

export interface IRealtimeNotificationService {
  sendToUser(
    userId: string,
    payload: RealTimeNotificationPayload,
  ): Promise<boolean>;
  sendToMultipleUsers(
    userIds: string[],
    payload: Omit<RealTimeNotificationPayload, 'userId'>,
  ): Promise<boolean[]>;
  subscribeUser(userId: string, socketId: string): Promise<void>;
  unsubscribeUser(userId: string, socketId: string): Promise<void>;
  broadcastUnreadCount(userId: string, count: number): Promise<boolean>;
}

/**
 * Real-time notification service using WebSocket/Server-Sent Events
 * This service manages real-time communication with connected clients
 */
@Injectable()
export class RealtimeNotificationService
  implements IRealtimeNotificationService
{
  private readonly userConnections = new Map<string, Set<string>>();

  async sendToUser(
    userId: string,
    payload: RealTimeNotificationPayload,
  ): Promise<boolean> {
    const userSockets = this.userConnections.get(userId);

    if (!userSockets || userSockets.size === 0) {
      console.log(`[REALTIME] No active connections for user ${userId}`);
      return true; // Not an error, user is just offline
    }

    try {
      console.log(
        `[REALTIME] Sending notification to ${userSockets.size} connections for user ${userId}`,
      );
      console.log(`[REALTIME] Type: ${payload.type}`);
      console.log(`[REALTIME] Notification ID: ${payload.notification.id}`);

      // In production, this would emit to actual WebSocket connections
      for (const socketId of userSockets) {
        await this.emitToSocket(socketId, 'notification', payload);
      }

      return true;
    } catch (error) {
      console.error(
        `[REALTIME] Failed to send notification to user ${userId}:`,
        error,
      );
      return false;
    }
  }

  async sendToMultipleUsers(
    userIds: string[],
    payload: Omit<RealTimeNotificationPayload, 'userId'>,
  ): Promise<boolean[]> {
    console.log(
      `[REALTIME] Broadcasting notification to ${userIds.length} users`,
    );

    const results = await Promise.allSettled(
      userIds.map((userId) => this.sendToUser(userId, { ...payload, userId })),
    );

    return results.map((result) =>
      result.status === 'fulfilled' ? result.value : false,
    );
  }

  async subscribeUser(userId: string, socketId: string): Promise<void> {
    const userSockets = this.userConnections.get(userId) || new Set();
    userSockets.add(socketId);
    this.userConnections.set(userId, userSockets);

    console.log(`[REALTIME] User ${userId} connected with socket ${socketId}`);
    console.log(`[REALTIME] Total connections for user: ${userSockets.size}`);

    // Send initial data when user connects
    await this.sendInitialData(userId, socketId);
  }

  async unsubscribeUser(userId: string, socketId: string): Promise<void> {
    const userSockets = this.userConnections.get(userId);

    if (userSockets) {
      userSockets.delete(socketId);

      if (userSockets.size === 0) {
        this.userConnections.delete(userId);
        console.log(`[REALTIME] User ${userId} fully disconnected`);
      } else {
        console.log(
          `[REALTIME] User ${userId} disconnected socket ${socketId}. Remaining connections: ${userSockets.size}`,
        );
      }
    }
  }

  async broadcastUnreadCount(userId: string, count: number): Promise<boolean> {
    const userSockets = this.userConnections.get(userId);

    if (!userSockets || userSockets.size === 0) {
      return true; // User is offline
    }

    try {
      console.log(
        `[REALTIME] Broadcasting unread count ${count} to user ${userId}`,
      );

      for (const socketId of userSockets) {
        await this.emitToSocket(socketId, 'unread_count', {
          count,
          timestamp: new Date(),
        });
      }

      return true;
    } catch (error) {
      console.error(
        `[REALTIME] Failed to broadcast unread count to user ${userId}:`,
        error,
      );
      return false;
    }
  }

  /**
   * Send typing indicator for message notifications
   */
  async sendTypingIndicator(
    userId: string,
    conversationId: string,
    isTyping: boolean,
  ): Promise<boolean> {
    const userSockets = this.userConnections.get(userId);

    if (!userSockets || userSockets.size === 0) {
      return true;
    }

    try {
      for (const socketId of userSockets) {
        await this.emitToSocket(socketId, 'typing_indicator', {
          conversationId,
          isTyping,
          timestamp: new Date(),
        });
      }

      return true;
    } catch (error) {
      console.error(`[REALTIME] Failed to send typing indicator:`, error);
      return false;
    }
  }

  /**
   * Send presence update (online/offline status)
   */
  async broadcastPresenceUpdate(
    userId: string,
    status: 'online' | 'offline' | 'away',
    toUserIds: string[],
  ): Promise<boolean[]> {
    console.log(
      `[REALTIME] Broadcasting presence update for user ${userId} to ${toUserIds.length} users`,
    );

    const results = await Promise.allSettled(
      toUserIds.map(async (targetUserId) => {
        const userSockets = this.userConnections.get(targetUserId);

        if (!userSockets || userSockets.size === 0) {
          return true;
        }

        for (const socketId of userSockets) {
          await this.emitToSocket(socketId, 'presence_update', {
            userId,
            status,
            timestamp: new Date(),
          });
        }

        return true;
      }),
    );

    return results.map((result) =>
      result.status === 'fulfilled' ? result.value : false,
    );
  }

  /**
   * Get currently connected users count
   */
  getConnectedUsersCount(): number {
    return this.userConnections.size;
  }

  /**
   * Get connection count for a specific user
   */
  getUserConnectionCount(userId: string): number {
    const userSockets = this.userConnections.get(userId);
    return userSockets ? userSockets.size : 0;
  }

  /**
   * Check if user is currently connected
   */
  isUserConnected(userId: string): boolean {
    const userSockets = this.userConnections.get(userId);
    return userSockets ? userSockets.size > 0 : false;
  }

  private async emitToSocket(
    socketId: string,
    event: string,
    data: any,
  ): Promise<void> {
    // In production, this would emit to actual WebSocket connection
    console.log(`[REALTIME] Emit to socket ${socketId}: ${event}`, data);

    // Simulate network delay
    await new Promise((resolve) => setTimeout(resolve, 10));
  }

  private async sendInitialData(
    userId: string,
    socketId: string,
  ): Promise<void> {
    try {
      // Send unread count when user connects
      // This would typically fetch from the notification service
      await this.emitToSocket(socketId, 'initial_data', {
        userId,
        connected: true,
        timestamp: new Date(),
      });
    } catch (error) {
      console.error(
        `[REALTIME] Failed to send initial data to user ${userId}:`,
        error,
      );
    }
  }
}

import {
  Controller,
  Get,
  Post,
  Body,
  Patch,
  Param,
  Delete,
  Query,
  HttpCode,
  HttpStatus,
