import {
  WebSocketGateway,
  WebSocketServer,
  OnGatewayConnection,
  OnGatewayDisconnect,
  SubscribeMessage,
  MessageBody,
  ConnectedSocket,
} from '@nestjs/websockets';
import { Server, Socket } from 'socket.io';
import { Injectable, Logger } from '@nestjs/common';
import { JwtService } from '@nestjs/jwt';
import Redis from 'ioredis';
import { NotificationApplicationService } from '../../application/notification-application.service';

/**
 * Presentation layer WebSocket Gateway for real-time notifications
 * Handles WebSocket connections and real-time notification delivery
 */
@Injectable()
@WebSocketGateway({ namespace: '/notifications', cors: true })
export class NotificationGateway
  implements OnGatewayConnection, OnGatewayDisconnect
{
  @WebSocketServer()
  server: Server;

  private readonly redisSubscriber = new Redis(process.env.REDIS_URL as string);
  private logger = new Logger('NotificationGateway');

  constructor(
    private readonly notificationApplicationService: NotificationApplicationService,
    private readonly jwtService: JwtService,
  ) {}

  afterInit(server: Server) {
    this.server = server;
    this.redisSubscriber.subscribe('notifications', (err) => {
      if (err) {
        this.logger.error(
          'Failed to subscribe to Redis channel: notifications',
          err,
        );
      } else {
        this.logger.log('Subscribed to Redis channel: notifications');
      }
    });

    this.redisSubscriber.on('message', (channel, message) => {
      this.logger.debug(`Received message from ${channel}: ${message}`);
      if (channel === 'notifications') {
        try {
          const { targetUserId, notification } = JSON.parse(message);

          if (!this.server) {
            this.logger.warn('Socket server not ready; skipping notify');
            return;
          }

          this.notifyUser(targetUserId, notification);
        } catch (error) {
          this.logger.error('Failed to parse notification message', error);
        }
      }
    });
  }

  handleConnection(client: Socket) {
    // Authenticate websocket handshake via token in query or headers
    const query = client.handshake.query as any;
    const token =
      query?.token || client.handshake.headers?.authorization || null;

    let userId: string | null = null;
    let email: string | null = null;

    if (token) {
      try {
        // support 'Bearer <token>' or raw token
        const raw = (token as string).startsWith('Bearer')
          ? (token as string).split(' ')[1]
          : (token as string);
        const payload = this.jwtService.verify(raw);
        // assume payload.sub contains user id
        userId = payload?.sub;
        email = payload?.email;
      } catch (err) {
        this.logger.warn(`Invalid token on websocket connect: ${client.id}`);
      }
    }

    if (userId) {
      client.join(`user:${userId}`);
      this.logger.log(
        `Client ${email} authenticated and joined room user:${userId} (socket ${client.id})`,
      );
    } else {
      this.logger.log(
        `Client connected without valid auth (socket ${client.id})`,
      );
    }
  }

  handleDisconnect(client: Socket) {
    this.logger.log(`Client disconnected: ${client.id}`);
  }

  /**
   * Client can acknowledge notification (mark as read)
   */
  @SubscribeMessage('ack')
  async handleAck(
    @MessageBody() data: { notificationId: string; userId: string },
    @ConnectedSocket() client: Socket,
  ) {
    const { notificationId, userId } = data || {};
    if (!notificationId || !userId) {
      return { success: false, error: 'Missing notificationId or userId' };
    }

    try {
      await this.notificationApplicationService.markAsRead(
        notificationId,
        userId,
      );
      return { success: true };
    } catch (err) {
      this.logger.error('Failed to ack notification', err);
      return { success: false, error: 'Failed to mark notification as read' };
    }
  }

  /**
   * Client requests real-time notifications
   */
  @SubscribeMessage('getNotifications')
  async handleGetNotifications(
    @MessageBody() data: { userId: string; since?: string; limit?: number },
    @ConnectedSocket() client: Socket,
  ) {
    const { userId, since, limit = 50 } = data || {};
    if (!userId) {
      return { success: false, error: 'Missing userId' };
    }

    try {
      const sinceDate = since
        ? new Date(since)
        : new Date(Date.now() - 24 * 60 * 60 * 1000); // Last 24 hours
      const notifications =
        await this.notificationApplicationService.getLatestNotifications(
          userId,
          sinceDate,
          limit,
        );

      return { success: true, notifications };
    } catch (err) {
      this.logger.error('Failed to get notifications', err);
      return { success: false, error: 'Failed to retrieve notifications' };
    }
  }

  /**
   * Client requests unread count
   */
  @SubscribeMessage('getUnreadCount')
  async handleGetUnreadCount(
    @MessageBody() data: { userId: string },
    @ConnectedSocket() client: Socket,
  ) {
    const { userId } = data || {};
    if (!userId) {
      return { success: false, error: 'Missing userId' };
    }

    try {
      const count =
        await this.notificationApplicationService.getUnreadCount(userId);
      return { success: true, count };
    } catch (err) {
      this.logger.error('Failed to get unread count', err);
      return { success: false, error: 'Failed to retrieve unread count' };
    }
  }

  /**
   * Emit notification to a specific user by userId
   */
  async notifyUser(userId: string, payload: any): Promise<void> {
    try {
      const room = `user:${userId}`;
      this.server.to(room).emit('notification', payload);
      this.logger.debug(`Notification sent to user ${userId}`);
    } catch (error) {
      this.logger.error(`Failed to notify user ${userId}`, error);
    }
  }

  /**
   * Emit notification to multiple users (array of userIds)
   */
  async notifyUsers(userIds: string[], payload: any): Promise<void> {
    if (!Array.isArray(userIds) || userIds.length === 0) return;

    try {
      // Emit to each user's room. Looping is simple and reliable.
      for (const uid of userIds) {
        const room = `user:${uid}`;
        this.server.to(room).emit('notification', payload);
      }
      this.logger.debug(`Notification sent to ${userIds.length} users`);
    } catch (error) {
      this.logger.error('Failed to notify multiple users', error);
    }
  }

  /**
   * Notify users in parallel with limited concurrency to avoid blocking
   */
  async notifyUsersParallel(
    userIds: string[],
    payload: any,
    concurrency = 20,
  ): Promise<void> {
    if (!Array.isArray(userIds) || userIds.length === 0) return;

    try {
      const chunks: string[][] = [];
      for (let i = 0; i < userIds.length; i += concurrency) {
        chunks.push(userIds.slice(i, i + concurrency));
      }

      for (const chunk of chunks) {
        // emit for this chunk in parallel
        await Promise.all(
          chunk.map((uid) =>
            this.server.to(`user:${uid}`).emit('notification', payload),
          ),
        );
        // Small pause to prevent overwhelming the system
        if (chunks.length > 1) {
          await new Promise((resolve) => setTimeout(resolve, 10));
        }
      }
      this.logger.debug(
        `Notification sent to ${userIds.length} users in parallel`,
      );
    } catch (error) {
      this.logger.error('Failed to notify users in parallel', error);
    }
  }

  /**
   * Broadcast to all connected clients
   */
  async broadcast(payload: any): Promise<void> {
    try {
      this.server.emit('notification', payload);
      this.logger.debug('Notification broadcasted to all connected clients');
    } catch (error) {
      this.logger.error('Failed to broadcast notification', error);
    }
  }

  /**
   * Send unread count update to specific user
   */
  async sendUnreadCountUpdate(userId: string, count: number): Promise<void> {
    try {
      const room = `user:${userId}`;
      this.server.to(room).emit('unreadCountUpdate', { count });
      this.logger.debug(`Unread count update sent to user ${userId}: ${count}`);
    } catch (error) {
      this.logger.error(
        `Failed to send unread count update to user ${userId}`,
        error,
      );
    }
  }

  /**
   * Send notification stats update to specific user
   */
  async sendStatsUpdate(userId: string, stats: any): Promise<void> {
    try {
      const room = `user:${userId}`;
      this.server.to(room).emit('statsUpdate', stats);
      this.logger.debug(`Stats update sent to user ${userId}`);
    } catch (error) {
      this.logger.error(`Failed to send stats update to user ${userId}`, error);
    }
  }
}

import { Processor, WorkerHost } from '@nestjs/bullmq';
import { Logger } from '@nestjs/common';
import { Job } from 'bullmq';
import Redis from 'ioredis';
import { NotificationApplicationService } from '../../application/notification-application.service';
import { PrismaService } from '../../../../database/prisma.service';
import { NotificationType } from '../../domain/notification.entity';

interface NotificationJobData {
  reactorId: string;
  targetUserId: string;
  type: string;
  content?: string;
  entityId?: string;
  entityType?: string;
  actorUserId?: string;
  relatedEntityId?: string;
  relatedEntityType?: string;
  title?: string;
  message?: string;
  metadata?: Record<string, any>;
}

/**
 * Presentation layer processor for handling notification queue jobs
 * Processes background notification tasks and publishes real-time events
 */
@Processor('notification-queue')
export class NotificationProcessor extends WorkerHost {
  private readonly logger = new Logger(NotificationProcessor.name);
  private readonly redisClient = new Redis(
    process.env.REDIS_URL || 'redis://localhost:6379',
  );

  constructor(
    private readonly notificationApplicationService: NotificationApplicationService,
    private readonly prismaService: PrismaService,
  ) {
    super();
  }

  async process(job: Job<NotificationJobData, any, string>): Promise<any> {
    this.logger.log(
      `[Job ${job.id}] Started processing job with name: ${job.name}`,
    );

    try {
      const jobData = job.data;

      const jobId = job.id || 'unknown';

      switch (job.name) {
        case 'create-notification':
          await this.processCreateNotification(jobId, jobData);
          break;
        case 'reaction-notification':
          await this.processReactionNotification(jobId, jobData);
          break;
        case 'follow-notification':
          await this.processFollowNotification(jobId, jobData);
          break;
        case 'comment-notification':
          await this.processCommentNotification(jobId, jobData);
          break;
        case 'message-notification':
          await this.processMessageNotification(jobId, jobData);
          break;
        case 'bulk-notification':
          await this.processBulkNotification(jobId, jobData);
          break;
        default:
          this.logger.warn(`[Job ${jobId}] Unknown job name: ${job.name}`);
          break;
      }

      this.logger.log(`[Job ${job.id}] Successfully processed`);
    } catch (error) {
      this.logger.error(
        `[Job ${job.id}] Failed with error: ${error.message}`,
        error.stack,
      );
      // Nm li  BullMQ c th th li job nu cn
      throw error;
    }
  }

  /**
   * Process generic notification creation
   */
  private async processCreateNotification(
    jobId: string | number,
    data: NotificationJobData,
  ): Promise<void> {
    const {
      targetUserId,
      type,
      title,
      message,
      actorUserId,
      relatedEntityId,
      relatedEntityType,
      metadata,
    } = data;

    if (!targetUserId || !type || !title || !message) {
      this.logger.warn(
        `[Job ${jobId}] Missing required fields for notification creation`,
      );
      return;
    }

    // To thng bo trong database
    const notification =
      await this.notificationApplicationService.createNotification({
        userId: targetUserId,
        type: type as any,
        title,
        content: message,
      });

    // Publish s kin qua Redis cho real-time updates
    await this.publishNotificationEvent(targetUserId, notification);

    this.logger.log(
      `[Job ${jobId}] Generic notification created for user ${targetUserId}`,
    );
  }

  /**
   * Process reaction notification (like, heart, etc.)
   */
  private async processReactionNotification(
    jobId: string | number,
    data: NotificationJobData,
  ): Promise<void> {
    const { reactorId, targetUserId, type, entityId, entityType } = data;

    // Tm np thng tin cn thit t c s d liu  m bo tnh nht qun
    const reactor = await this.prismaService.user.findUnique({
      where: { id: reactorId },
      select: { id: true, fullName: true, username: true },
    });

    if (!reactor) {
      this.logger.warn(
        `[Job ${jobId}] Reactor with ID ${reactorId} not found. Skipping notification.`,
      );
      return;
    }

    // Xy dng tiu  v ni dung thng bo
    const title = `New ${type} on your ${entityType}`;
    const message = `${reactor.fullName} reacted to your ${entityType}`;

    // To thng bo trong database
    const notification =
      await this.notificationApplicationService.createNotification({
        userId: targetUserId,
        type: NotificationType.LIKE,
        title,
        content: message,
        entityId,
        entityType: entityType as any,
      });

    // Publish s kin qua Redis
    await this.publishNotificationEvent(targetUserId, notification);

    this.logger.log(
      `[Job ${jobId}] Reaction notification created for user ${targetUserId}`,
    );
  }

  /**
   * Process follow notification
   */
  private async processFollowNotification(
    jobId: string | number,
    data: NotificationJobData,
  ): Promise<void> {
    const { actorUserId, targetUserId } = data;

    const follower = await this.prismaService.user.findUnique({
      where: { id: actorUserId },
      select: { id: true, fullName: true, username: true },
    });

    if (!follower) {
      this.logger.warn(
        `[Job ${jobId}] Follower with ID ${actorUserId} not found`,
      );
      return;
    }

    const notification =
      await this.notificationApplicationService.createNotification({
        userId: targetUserId,
        type: NotificationType.FOLLOW,
        title: 'New follower',
        content: `${follower.fullName} started following you`,
      });

    await this.publishNotificationEvent(targetUserId, notification);

    this.logger.log(
      `[Job ${jobId}] Follow notification created for user ${targetUserId}`,
    );
  }

  /**
   * Process comment notification
   */
  private async processCommentNotification(
    jobId: string | number,
    data: NotificationJobData,
  ): Promise<void> {
    const { actorUserId, targetUserId, entityId, entityType, content } = data;

    const commenter = await this.prismaService.user.findUnique({
      where: { id: actorUserId },
      select: { id: true, fullName: true, username: true },
    });

    if (!commenter) {
      this.logger.warn(
        `[Job ${jobId}] Commenter with ID ${actorUserId} not found`,
      );
      return;
    }

    const notification =
      await this.notificationApplicationService.createNotification({
        userId: targetUserId,
        type: NotificationType.COMMENT,
        title: 'New comment',
        content: `${commenter.fullName} commented on your ${entityType}`,
        entityId,
        entityType: entityType as any,
      });

    await this.publishNotificationEvent(targetUserId, notification);

    this.logger.log(
      `[Job ${jobId}] Comment notification created for user ${targetUserId}`,
    );
  }

  /**
   * Process message notification
   */
  private async processMessageNotification(
    jobId: string | number,
    data: NotificationJobData,
  ): Promise<void> {
    const { actorUserId, targetUserId, content } = data;

    const sender = await this.prismaService.user.findUnique({
      where: { id: actorUserId },
      select: { id: true, fullName: true, username: true },
    });

    if (!sender) {
      this.logger.warn(
        `[Job ${jobId}] Sender with ID ${actorUserId} not found`,
      );
      return;
    }

    const notification =
      await this.notificationApplicationService.createNotification({
        userId: targetUserId,
        type: NotificationType.MESSAGE,
        title: 'New message',
        content: `${sender.fullName} sent you a message`,
      });

    await this.publishNotificationEvent(targetUserId, notification);

    this.logger.log(
      `[Job ${jobId}] Message notification created for user ${targetUserId}`,
    );
  }

  /**
   * Process bulk notifications
   */
  private async processBulkNotification(
    jobId: string | number,
    data: NotificationJobData & { targetUserIds?: string[] },
  ): Promise<void> {
    const {
      targetUserIds,
      type,
      title,
      message,
      actorUserId,
      relatedEntityId,
      relatedEntityType,
      metadata,
    } = data;

    if (
      !targetUserIds ||
      !Array.isArray(targetUserIds) ||
      targetUserIds.length === 0
    ) {
      this.logger.warn(
        `[Job ${jobId}] No target users provided for bulk notification`,
      );
      return;
    }

    // Process in batches to avoid overwhelming the database
    const batchSize = 50;
    for (let i = 0; i < targetUserIds.length; i += batchSize) {
      const batch = targetUserIds.slice(i, i + batchSize);

      await Promise.all(
        batch.map(async (userId) => {
          try {
            const notification =
              await this.notificationApplicationService.createNotification({
                userId,
                type: type as any,
                title: title || 'New notification',
                content: message || 'You have a new notification',
              });

            await this.publishNotificationEvent(userId, notification);
          } catch (error) {
            this.logger.error(
              `[Job ${jobId}] Failed to create notification for user ${userId}`,
              error,
            );
          }
        }),
      );
    }

    this.logger.log(
      `[Job ${jobId}] Bulk notifications created for ${targetUserIds.length} users`,
    );
  }

  /**
   * Publish notification event to Redis for real-time updates
   */
  private async publishNotificationEvent(
    targetUserId: string,
    notification: any,
  ): Promise<void> {
    try {
      await this.redisClient.publish(
        'notifications',
        JSON.stringify({ targetUserId, notification }),
      );
      this.logger.debug(
        `Published notification event for user ${targetUserId}`,
      );
    } catch (error) {
      this.logger.error(
        `Failed to publish notification event for user ${targetUserId}`,
        error,
      );
    }
  }
}

import { Injectable, Inject } from '@nestjs/common';
import { PostMediaRepository } from '../../domain/repositories/post-media.repository';
import { PostMediaDomainService } from '../../domain/services/post-media-domain.service';
import { PostMediaFactory } from '../../domain/factories/post-media.factory';
import { PostMediaEntity } from '../../domain/post-media.entity';
import { PostMediaUploadFailedException } from '../../domain/post-media.exceptions';

export interface UploadPostMediasCommand {
  files: Express.Multer.File[];
  postId: string;
  userId: string;
  maxMediaPerPost?: number;
}

@Injectable()
export class UploadPostMediasUseCase {
  constructor(
    @Inject('POST_MEDIA_REPOSITORY')
    private readonly postMediaRepository: PostMediaRepository,
    private readonly postMediaDomainService: PostMediaDomainService,
  ) {}

  async execute(command: UploadPostMediasCommand): Promise<PostMediaEntity[]> {
    try {
      // Validate files
      if (!command.files || command.files.length === 0) {
        throw new PostMediaUploadFailedException(
          'At least one file is required',
        );
      }

      // Validate file types
      for (const file of command.files) {
        PostMediaFactory.validateAndGetTypeFromMimetype(file.mimetype);
      }

      // Use domain service to handle the upload logic
      const uploadedMedias = await this.postMediaDomainService.uploadPostMedias(
        command.files,
        command.postId,
        command.userId,
        command.maxMediaPerPost || 10,
      );

      return uploadedMedias;
    } catch (error) {
      if (error instanceof PostMediaUploadFailedException) {
        throw error;
      }
      throw new PostMediaUploadFailedException(
        error.message || 'Unknown upload error',
      );
    }
  }
}

import {
  PostMediaEntity,
  PostMediaType,
  PostMediaProps,
} from '../post-media.entity';
import {
  InvalidPostMediaException,
  InvalidMediaTypeException,
} from '../post-media.exceptions';

export interface CreatePostMediaParams {
  url: string;
  type: PostMediaType | string;
  postId: string;
  order: number;
}

export interface PostMediaFromPersistenceParams extends PostMediaProps {
  id: string;
  createdAt: Date;
  updatedAt: Date;
}

export class PostMediaFactory {
  /**
   * Create a new post media entity
   */
  static create(params: CreatePostMediaParams): PostMediaEntity {
    // Validate and normalize type
    const normalizedType = this.normalizeMediaType(params.type);

    // Validate required fields
    if (!params.url?.trim()) {
      throw new InvalidPostMediaException('URL is required');
    }

    if (!params.postId?.trim()) {
      throw new InvalidPostMediaException('Post ID is required');
    }

    if (params.order < 1) {
      throw new InvalidPostMediaException('Order must be greater than 0');
    }

    return PostMediaEntity.create({
      url: params.url.trim(),
      type: normalizedType,
      postId: params.postId.trim(),
      order: params.order,
    });
  }

  /**
   * Create multiple post media entities from uploaded files
   */
  static createFromUploadedFiles(
    uploadedFiles: Array<{ url: string; type: string; resourceType?: string }>,
    postId: string,
    startOrder: number = 1,
  ): PostMediaEntity[] {
    return uploadedFiles.map((file, index) => {
      const type = this.normalizeMediaType(file.resourceType || file.type);

      return this.create({
        url: file.url,
        type,
        postId,
        order: startOrder + index,
      });
    });
  }

  /**
   * Reconstruct entity from database data
   */
  static fromPersistence(
    data: PostMediaFromPersistenceParams,
  ): PostMediaEntity {
    return PostMediaEntity.fromPersistence({
      id: data.id,
      url: data.url,
      type: this.normalizeMediaType(data.type),
      postId: data.postId,
      order: data.order,
      createdAt: data.createdAt,
      updatedAt: data.updatedAt,
    });
  }

  /**
   * Create entities from array of persistence data
   */
  static fromPersistenceArray(
    dataArray: PostMediaFromPersistenceParams[],
  ): PostMediaEntity[] {
    return dataArray.map((data) => this.fromPersistence(data));
  }

  /**
   * Normalize media type string to enum
   */
  private static normalizeMediaType(
    type: PostMediaType | string,
  ): PostMediaType {
    if (typeof type === 'string') {
      const normalizedType = type.toLowerCase();

      switch (normalizedType) {
        case 'image':
        case PostMediaType.IMAGE:
          return PostMediaType.IMAGE;
        case 'video':
        case PostMediaType.VIDEO:
          return PostMediaType.VIDEO;
        default:
          throw new InvalidMediaTypeException(`Invalid media type: ${type}`);
      }
    }

    if (Object.values(PostMediaType).includes(type)) {
      return type;
    }

    throw new InvalidMediaTypeException(`Invalid media type: ${type}`);
  }

  /**
   * Validate file type from mimetype
   */
  static validateAndGetTypeFromMimetype(mimetype: string): PostMediaType {
    if (mimetype.startsWith('image/')) {
      return PostMediaType.IMAGE;
    }

    if (mimetype.startsWith('video/')) {
      return PostMediaType.VIDEO;
    }

    throw new InvalidMediaTypeException(`Unsupported file type: ${mimetype}`);
  }
}

import { Entity } from '../../../shared/domain/entity.base';
import { randomUUID } from 'crypto';

import {
  PostMediaCreatedEvent,
  PostMediaDeletedEvent,
  PostMediaUpdatedEvent,
} from './post-media.events';
import {
  InvalidMediaOrderException,
  InvalidMediaTypeException,
  InvalidPostMediaException,
} from './post-media.exceptions';

export enum PostMediaType {
  IMAGE = 'image',
  VIDEO = 'video',
}

export interface PostMediaProps {
  id?: string;
  url: string;
  type: PostMediaType;
  postId: string;
  order: number;
  createdAt?: Date;
  updatedAt?: Date;
}

export class PostMediaEntity extends Entity<string> {
  private _url: string;
  private _type: PostMediaType;
  private _postId: string;
  private _order: number;
  private _createdAt: Date;
  private _updatedAt: Date;

  constructor(props: PostMediaProps) {
    super(props.id || randomUUID());
    this._url = props.url;
    this._type = props.type;
    this._postId = props.postId;
    this._order = props.order;
    this._createdAt = props.createdAt || new Date();
    this._updatedAt = props.updatedAt || new Date();

    this.validate();
    this.addDomainEvent(
      new PostMediaCreatedEvent({
        id: this._id,
        postId: this._postId,
        url: this._url,
        type: this._type,
        order: this._order,
      }),
    );
  }

  // Getters - id is inherited from base Entity class

  get url(): string {
    return this._url;
  }

  get type(): PostMediaType {
    return this._type;
  }

  get postId(): string {
    return this._postId;
  }

  get order(): number {
    return this._order;
  }

  get createdAt(): Date {
    return this._createdAt;
  }

  get updatedAt(): Date {
    return this._updatedAt;
  }

  // Business methods
  public updateUrl(newUrl: string, userId: string): void {
    if (!newUrl || newUrl.trim() === '') {
      throw new InvalidPostMediaException('Media URL cannot be empty');
    }

    const oldUrl = this._url;
    this._url = newUrl;
    this._updatedAt = new Date();

    this.addDomainEvent(
      new PostMediaUpdatedEvent(
        {
          id: this.id,
          postId: this._postId,
          url: this._url,
          type: this._type,
          order: this._order,
        },
        { oldUrl, newUrl },
      ),
    );
  }

  public updateOrder(newOrder: number, userId: string): void {
    if (newOrder < 1) {
      throw new InvalidMediaOrderException(
        'Media order must be greater than 0',
      );
    }

    const oldOrder = this._order;
    this._order = newOrder;
    this._updatedAt = new Date();

    this.addDomainEvent(
      new PostMediaUpdatedEvent(
        {
          id: this.id,
          postId: this._postId,
          url: this._url,
          type: this._type,
          order: this._order,
        },
        { oldOrder, newOrder },
      ),
    );
  }

  public delete(userId: string): void {
    this.addDomainEvent(
      new PostMediaDeletedEvent({
        id: this.id,
        postId: this._postId,
        url: this._url,
        type: this._type,
        order: this._order,
      }),
    );
  }

  public static create(
    props: Omit<PostMediaProps, 'id' | 'createdAt' | 'updatedAt'>,
  ): PostMediaEntity {
    return new PostMediaEntity(props);
  }

  public static fromPersistence(props: PostMediaProps): PostMediaEntity {
    const entity = Object.create(PostMediaEntity.prototype);
    Entity.call(entity, props.id);
    entity._url = props.url;
    entity._type = props.type;
    entity._postId = props.postId;
    entity._order = props.order;
    entity._createdAt = props.createdAt;
    entity._updatedAt = props.updatedAt;
    return entity;
  }

  public toPlainObject(): PostMediaProps {
    return {
      id: this.id,
      url: this._url,
      type: this._type,
      postId: this._postId,
      order: this._order,
      createdAt: this._createdAt,
      updatedAt: this._updatedAt,
    };
  }

  private validate(): void {
    if (!this._url || this._url.trim() === '') {
      throw new InvalidPostMediaException('Media URL is required');
    }

    if (!this._postId || this._postId.trim() === '') {
      throw new InvalidPostMediaException('Post ID is required');
    }

    if (!Object.values(PostMediaType).includes(this._type)) {
      throw new InvalidMediaTypeException(`Invalid media type: ${this._type}`);
    }

    if (this._order < 1) {
      throw new InvalidMediaOrderException(
        'Media order must be greater than 0',
      );
    }
  }
}

import { Injectable, Inject } from '@nestjs/common';
import { PostMediaEntity, PostMediaType } from '../post-media.entity';
import { PostMediaRepository } from '../repositories/post-media.repository';
import {
  PostMediaLimitExceededException,
  PostMediaDuplicateException,
  InvalidMediaTypeException,
} from '../post-media.exceptions';
import {
  POST_MEDIA_REPOSITORY,
  CLOUDINARY_SERVICE,
  POST_SERVICE,
} from '../../tokens';

export interface UploadedFile {
  url: string;
  type: string;
  resourceType: string;
}

export interface CloudinaryService {
  uploadMultipleFiles(
    files: Express.Multer.File[],
    folder: string,
  ): Promise<UploadedFile[]>;
  generateSignature(params: any): Promise<string>;
}

export interface PostService {
  exists(postId: string): Promise<boolean>;
  belongsToUser(postId: string, userId: string): Promise<boolean>;
}

@Injectable()
export class PostMediaDomainService {
  constructor(
    @Inject('POST_MEDIA_REPOSITORY')
    private readonly postMediaRepository: PostMediaRepository,
    @Inject(CLOUDINARY_SERVICE)
    private readonly cloudinaryService: CloudinaryService,
    @Inject('POST_SERVICE')
    private readonly postService: PostService,
  ) {}

  async uploadPostMedias(
    files: Express.Multer.File[],
    postId: string,
    userId: string,
    maxMediaPerPost: number = 10,
  ): Promise<PostMediaEntity[]> {
    // Validate post exists and user has permission
    const postExists = await this.postService.exists(postId);
    if (!postExists) {
      throw new Error('Post not found');
    }

    const userOwnsPost = await this.postService.belongsToUser(postId, userId);
    if (!userOwnsPost) {
      throw new Error('You can only add media to your own posts');
    }

    // Check media limit
    const currentMediaCount =
      await this.postMediaRepository.countByPostId(postId);
    if (currentMediaCount + files.length > maxMediaPerPost) {
      throw new PostMediaLimitExceededException(maxMediaPerPost);
    }

    // Upload files to cloud storage
    const uploadedFiles = await this.cloudinaryService.uploadMultipleFiles(
      files,
      `SocialMedia/posts/${postId}`,
    );

    // Create post media entities
    const postMediaEntities = uploadedFiles.map((file, index) => {
      const mediaType = this.mapResourceTypeToPostMediaType(
        file.resourceType || file.type,
      );

      return PostMediaEntity.create({
        url: file.url,
        type: mediaType,
        postId: postId,
        order: currentMediaCount + index + 1,
      });
    });

    // Check for duplicate URLs
    const urls = postMediaEntities.map((media) => media.url);
    const existingMedias = await this.postMediaRepository.findByUrls(urls);
    if (existingMedias.length > 0) {
      throw new PostMediaDuplicateException(existingMedias[0].url);
    }

    // Save to repository
    return await this.postMediaRepository.saveMany(postMediaEntities);
  }

  async reorderPostMedias(
    postId: string,
    userId: string,
    newOrders: Array<{ id: string; order: number }>,
  ): Promise<void> {
    // Validate post ownership
    const userOwnsPost = await this.postService.belongsToUser(postId, userId);
    if (!userOwnsPost) {
      throw new Error('You can only reorder media on your own posts');
    }

    // Validate all media belong to the post
    const postMedias = await this.postMediaRepository.findByPostId(postId);
    const postMediaIds = postMedias.map((media) => media.id);

    for (const orderItem of newOrders) {
      if (!postMediaIds.includes(orderItem.id)) {
        throw new Error(
          `Media with id ${orderItem.id} does not belong to post ${postId}`,
        );
      }
    }

    // Update orders
    await this.postMediaRepository.updateOrdersByPostId(postId, newOrders);
  }

  async generateCloudinarySignature(
    folder: string = 'SocialMedia/posts',
  ): Promise<{
    signature: string;
    timestamp: number;
    folder: string;
    apiKey: string;
    cloudName: string;
  }> {
    const timestamp = Math.floor(Date.now() / 1000);
    const signature = await this.cloudinaryService.generateSignature({
      timestamp,
      folder,
    });

    return {
      signature,
      timestamp,
      folder,
      apiKey: process.env.CLOUDINARY_API_KEY || '',
      cloudName: process.env.CLOUDINARY_CLOUD_NAME || '',
    };
  }

  private mapResourceTypeToPostMediaType(resourceType: string): PostMediaType {
    switch (resourceType.toLowerCase()) {
      case 'image':
        return PostMediaType.IMAGE;
      case 'video':
        return PostMediaType.VIDEO;
      default:
        throw new InvalidMediaTypeException(
          `Unsupported resource type: ${resourceType}`,
        );
    }
  }
}

import { Injectable } from '@nestjs/common';
import { CloudinaryService } from '../../cloudinary/cloudinary.service';
import {
  CloudinaryService as DomainCloudinaryService,
  UploadedFile,
} from '../domain/services/post-media-domain.service';

@Injectable()
export class CloudinaryAdapter implements DomainCloudinaryService {
  constructor(private readonly cloudinaryService: CloudinaryService) {}

  async uploadMultipleFiles(
    files: Express.Multer.File[],
    folder: string,
  ): Promise<UploadedFile[]> {
    const uploadedFiles = await this.cloudinaryService.uploadMultipleFiles(
      files,
      folder,
    );

    return uploadedFiles.map((file) => ({
      url: file.secure_url,
      type: file.resource_type,
      resourceType: file.resource_type,
    }));
  }

  async generateSignature(params: any): Promise<string> {
    return await this.cloudinaryService.generateSignature(params);
  }
}

import {
  Controller,
  Get,
  Post,
  Body,
  Patch,
  Param,
  Delete,
  UseGuards,
  UseInterceptors,
  UploadedFiles,
  BadRequestException,
  Query,
  UseFilters,
  ParseIntPipe,
  HttpCode,
  HttpStatus,
  Inject,
} from '@nestjs/common';
import { FilesInterceptor } from '@nestjs/platform-express';
import { JwtAuthGuard } from '../../../shared/guards/jwt.guard';
import { CurrentUser } from '../../../shared/decorators/currentUser.decorator';
import { MulterExceptionFilter } from '../../../shared/filters/multer-exception.filter';
import { PostMediaApplicationService } from '../application/interfaces/post-media-application.interface';
import {
  UploadPostMediaDto,
  UpdatePostMediaDto,
  ReorderPostMediaDto,
  GetPostMediasDto,
} from '../application/dto/post-media.dto';

@Controller('post-medias')
@UseGuards(JwtAuthGuard)
export class PostMediasController {
  constructor(
    @Inject('POST_MEDIA_APPLICATION_SERVICE')
    private readonly postMediaApplicationService: PostMediaApplicationService,
  ) {}

  @Post('upload/:postId')
  @UseFilters(MulterExceptionFilter)
  @UseInterceptors(
    FilesInterceptor('files', 10, {
      fileFilter: (req, file, callback) => {
        if (
          file.mimetype.startsWith('image/') ||
          file.mimetype.startsWith('video/')
        ) {
          callback(null, true);
        } else {
          callback(
            new BadRequestException('Only images and videos are allowed'),
            false,
          );
        }
      },
      limits: {
        fileSize: 50 * 1024 * 1024, // 50MB
      },
    }),
  )
  async uploadMultiple(
    @UploadedFiles() files: Express.Multer.File[],
    @Param('postId') postId: string,
    @CurrentUser('id') userId: string,
  ) {
    if (!files || files.length === 0) {
      throw new BadRequestException('At least one file is required');
    }

    const uploadDto = new UploadPostMediaDto();
    uploadDto.postId = postId;

    const results = await this.postMediaApplicationService.uploadPostMedias(
      files,
      uploadDto,
      userId,
    );

    return {
      message: 'Media uploaded successfully',
      data: results,
    };
  }

  @Get()
  async findAll(@Query() query: GetPostMediasDto) {
    const result =
      await this.postMediaApplicationService.getAllPostMedias(query);

    return {
      message: 'Post medias retrieved successfully',
      data: result.items,
      pagination: result.pagination,
    };
  }

  @Get('post/:postId')
  async findByPost(@Param('postId') postId: string) {
    const medias =
      await this.postMediaApplicationService.getPostMediasByPostId(postId);

    return {
      message: 'Post medias retrieved successfully',
      data: medias,
    };
  }

  @Get('signature')
  async getSignature(@Query('folder') folder?: string) {
    const signature =
      await this.postMediaApplicationService.generateCloudinarySignature(
        folder,
      );

    return {
      message: 'Signature generated successfully',
      data: signature,
    };
  }

  @Get(':id')
  async findOne(@Param('id') id: string) {
    const media = await this.postMediaApplicationService.getPostMediaById(id);

    return {
      message: 'Post media retrieved successfully',
      data: media,
    };
  }

  @Patch(':id')
  async update(
    @Param('id') id: string,
    @Body() updatePostMediaDto: UpdatePostMediaDto,
    @CurrentUser('id') userId: string,
  ) {
    const result = await this.postMediaApplicationService.updatePostMedia(
      id,
      updatePostMediaDto,
      userId,
    );

    return {
      message: 'Media updated successfully',
      data: result,
    };
  }

  @Post('reorder')
  @HttpCode(HttpStatus.NO_CONTENT)
  async reorderMedias(
    @Body() reorderDto: ReorderPostMediaDto,
    @CurrentUser('id') userId: string,
  ) {
    await this.postMediaApplicationService.reorderPostMedias(
      reorderDto,
      userId,
    );
  }

  @Delete(':id')
  @HttpCode(HttpStatus.NO_CONTENT)
  async remove(@Param('id') id: string, @CurrentUser('id') userId: string) {
    await this.postMediaApplicationService.deletePostMedia(id, userId);
  }

  @Delete('post/:postId/all')
  @HttpCode(HttpStatus.NO_CONTENT)
  async removeAllFromPost(
    @Param('postId') postId: string,
    @CurrentUser('id') userId: string,
  ) {
    const medias =
      await this.postMediaApplicationService.getPostMediasByPostId(postId);

    // Delete each media (this will trigger proper authorization checks)
    await Promise.all(
      medias.map((media) =>
        this.postMediaApplicationService.deletePostMedia(media.id, userId),
      ),
    );
  }
}

