    private readonly getUserFeedUseCase: GetUserFeedUseCase,

    // Reaction use cases
    private readonly addReactionUseCase: AddReactionUseCase,
    private readonly removeReactionUseCase: RemoveReactionUseCase,
    private readonly toggleReactionUseCase: ToggleReactionUseCase,

    // Comment use cases
    private readonly addCommentUseCase: AddCommentUseCase,
    private readonly updateCommentUseCase: UpdateCommentUseCase,
    private readonly deleteCommentUseCase: DeleteCommentUseCase,
  ) {}

  // ===== POST MANAGEMENT =====

  async createPost(
    authorId: string,
    dto: CreatePostDto,
  ): Promise<PostResponseDto> {
    const result = await this.createPostUseCase.execute(authorId, dto);
    return this.enrichPostResponse(result);
  }

  async updatePost(
    postId: string,
    userId: string,
    dto: UpdatePostDto,
  ): Promise<PostResponseDto> {
    const result = await this.updatePostUseCase.execute(postId, userId, dto);
    return this.enrichPostResponse(result);
  }

  async deletePost(
    postId: string,
    userId: string,
    userRole?: string,
  ): Promise<void> {
    return this.deletePostUseCase.execute(postId, userId, userRole);
  }

  // ===== POST RETRIEVAL =====

  async getPostById(
    postId: string,
    viewerId?: string,
    isFollowing?: boolean,
  ): Promise<PostDetailResponseDto> {
    const result = await this.getPostByIdUseCase.execute(
      postId,
      viewerId,
      isFollowing,
    );
    return this.enrichPostDetailResponse(result);
  }

  async getPosts(
    query: GetPostsQueryDto,
    viewerId?: string,
  ): Promise<PostListResponseDto> {
    const result = await this.getPostsUseCase.execute(query, viewerId);

    // Enrich each post with user information
    const enrichedPosts = await Promise.all(
      result.posts.map((post) => this.enrichPostResponse(post)),
    );

    return {
      ...result,
      posts: enrichedPosts,
    };
  }

  async getUserFeed(
    userId: string,
    dto: GetFeedDto,
  ): Promise<PostListResponseDto> {
    const result = await this.getUserFeedUseCase.execute(
      userId,
      dto.page || 1,
      dto.limit || 10,
    );

    // Enrich each post with user information
    const enrichedPosts = await Promise.all(
      result.posts.map((post) => this.enrichPostResponse(post)),
    );

    return {
      ...result,
      posts: enrichedPosts,
    };
  }

  // ===== REACTIONS =====

  async addReaction(
    postId: string,
    userId: string,
    dto: CreateReactionDto,
  ): Promise<void> {
    return this.addReactionUseCase.execute(postId, userId, dto);
  }

  async removeReaction(postId: string, userId: string): Promise<void> {
    return this.removeReactionUseCase.execute(postId, userId);
  }

  async toggleReaction(
    postId: string,
    userId: string,
    dto: CreateReactionDto,
  ): Promise<{
    action: 'added' | 'removed' | 'changed';
    reactionType?: string;
  }> {
    return this.toggleReactionUseCase.execute(postId, userId, dto);
  }

  // ===== COMMENTS =====

  async addComment(
    postId: string,
    authorId: string,
    dto: CreateCommentDto,
  ): Promise<PostCommentResponseDto> {
    const result = await this.addCommentUseCase.execute(postId, authorId, dto);
    return this.enrichCommentResponse(result);
  }

  async updateComment(
    postId: string,
    commentId: string,
    userId: string,
    dto: UpdateCommentDto,
  ): Promise<PostCommentResponseDto> {
    const result = await this.updateCommentUseCase.execute(
      postId,
      commentId,
      userId,
      dto,
    );
    return this.enrichCommentResponse(result);
  }

  async deleteComment(
    postId: string,
    commentId: string,
    userId: string,
    userRole?: string,
  ): Promise<void> {
    return this.deleteCommentUseCase.execute(
      postId,
      commentId,
      userId,
      userRole,
    );
  }

  // ===== PRIVATE HELPER METHODS =====

  /**
   * Enriches post response with additional user information
   * In a real implementation, this would fetch user data from UserApplicationService
   */
  private async enrichPostResponse(
    post: PostResponseDto,
  ): Promise<PostResponseDto> {
    // TODO: Integrate with UserApplicationService to fetch user details
    // For now, return the post as-is
    return {
      ...post,
      author: {
        ...post.author,
        fullName: 'User ' + post.author.id.substring(0, 8), // Placeholder
        username: '@user' + post.author.id.substring(0, 4), // Placeholder
      },
    };
  }

  /**
   * Enriches post detail response with additional user information
   */
  private async enrichPostDetailResponse(
    post: PostDetailResponseDto,
  ): Promise<PostDetailResponseDto> {
    // TODO: Integrate with UserApplicationService to fetch user details
    const enrichedComments = post.comments.map((comment) => ({
      ...comment,
      authorFullName: 'User ' + comment.authorId.substring(0, 8), // Placeholder
    }));

    const enrichedReactions = post.reactions.map((reaction) => ({
      ...reaction,
      userFullName: 'User ' + reaction.userId.substring(0, 8), // Placeholder
    }));

    return {
      ...post,
      author: {
        ...post.author,
        fullName: 'User ' + post.author.id.substring(0, 8), // Placeholder
        username: '@user' + post.author.id.substring(0, 4), // Placeholder
      },
      comments: enrichedComments,
      reactions: enrichedReactions,
    };
  }

  /**
   * Enriches comment response with additional user information
   */
  private async enrichCommentResponse(
    comment: PostCommentResponseDto,
  ): Promise<PostCommentResponseDto> {
    // TODO: Integrate with UserApplicationService to fetch user details
    return {
      ...comment,
      authorFullName: 'User ' + comment.authorId.substring(0, 8), // Placeholder
    };
  }
}

import {
  Injectable,
  Inject,
  NotFoundException,
  ForbiddenException,
} from '@nestjs/common';
import { randomUUID } from 'crypto';
import { PostEntity } from '../../domain/post.entity';
import { PostDomainService } from '../../domain/services/post-domain.service';
import { IPostDomainRepository } from '../../domain/repositories/post-domain-repository.interface';
import { UpdatePostDto, PostResponseDto } from '../dto/post.dto';
import { POST_REPOSITORY_TOKEN } from './create-post.use-case';

/**
 * Use case for updating an existing post
 */
@Injectable()
export class UpdatePostUseCase {
  constructor(
    private readonly postDomainService: PostDomainService,
    @Inject(POST_REPOSITORY_TOKEN)
    private readonly postRepository: IPostDomainRepository,
  ) {}

  async execute(
    postId: string,
    userId: string,
    dto: UpdatePostDto,
  ): Promise<PostResponseDto> {
    // Find existing post
    const post = await this.postRepository.findById(postId);
    if (!post) {
      throw new NotFoundException(`Post with ID ${postId} not found`);
    }

    // Validate user permissions
    this.postDomainService.validatePostEdit(post, userId);

    // Update post content if provided
    if (dto.content !== undefined) {
      const hashtags =
        dto.hashtags || this.postDomainService.extractHashtags(dto.content);
      post.updateContent(dto.content, hashtags);
    }

    // Update privacy if provided
    if (dto.privacy !== undefined) {
      post.changePrivacy(dto.privacy);
    }

    // Update media if provided
    if (dto.media !== undefined) {
      // Clear existing media and add new ones
      post.clearMedia();
      dto.media.forEach((mediaData) => {
        const media = {
          id: randomUUID(),
          url: mediaData.url,
          type: mediaData.type,
          order: mediaData.order,
        };
        post.addMedia(media);
      });
    }

    // Save updated post
    const updatedPost = await this.postRepository.save(post);

    // Convert to response DTO
    return this.mapToResponseDto(updatedPost);
  }

  private mapToResponseDto(post: PostEntity): PostResponseDto {
    return {
      id: post.id,
      content: post.content,
      privacy: post.privacy,
      author: {
        id: post.authorId,
        fullName: '', // Will be populated by application service
        username: '',
        avatar: undefined,
      },
      media: post.media.map((m) => ({
        id: m.id,
        url: m.url,
        type: m.type,
        order: m.order,
      })),
      hashtags: post.hashtags,
      likesCount: post.reactions.length,
      commentsCount: post.comments.length,
      createdAt: post.createdAt,
      updatedAt: post.updatedAt,
    };
  }
}

import { Entity } from '../../../shared/domain/entity.base';
import { randomUUID } from 'crypto';
import {
  PostCreatedEvent,
  PostUpdatedEvent,
  PostDeletedEvent,
  PostLikedEvent,
  PostUnlikedEvent,
  PostCommentedEvent,
} from './post.events';
import {
  InvalidPostContentException,
  PostNotFoundException,
  UnauthorizedPostActionException,
  PostAlreadyLikedException,
  PostNotLikedException,
  EmptyPostContentException,
} from './post.exceptions';

// Simple DomainException class for now
class DomainException extends Error {
  constructor(message: string) {
    super(message);
    this.name = 'DomainException';
  }
}

export enum PostPrivacy {
  PUBLIC = 'public',
  FOLLOWERS = 'followers',
  PRIVATE = 'private',
}

export enum ReactionType {
  LIKE = 'like',
  LOVE = 'love',
  LAUGH = 'laugh',
  ANGRY = 'angry',
  SAD = 'sad',
}

export interface PostMedia {
  id: string;
  url: string;
  type: 'image' | 'video';
  order: number;
}

export interface PostReaction {
  id: string;
  type: ReactionType;
  userId: string;
  createdAt: Date;
}

export interface PostComment {
  id: string;
  content: string;
  authorId: string;
  parentId?: string;
  createdAt: Date;
  updatedAt: Date;
}

export interface PostProps {
  id?: string;
  content?: string;
  privacy: PostPrivacy;
  authorId: string;
  media?: PostMedia[];
  reactions?: PostReaction[];
  comments?: PostComment[];
  hashtags?: string[];
  createdAt?: Date;
  updatedAt?: Date;
}

export class PostEntity extends Entity<string> {
  private _content?: string;
  private _privacy: PostPrivacy;
  private _authorId: string;
  private _media: PostMedia[] = [];
  private _reactions: PostReaction[] = [];
  private _comments: PostComment[] = [];
  private _hashtags: string[] = [];
  private _createdAt: Date;
  private _updatedAt: Date;

  constructor(props: PostProps) {
    super(props.id || randomUUID());
    this._content = props.content;
    this._privacy = props.privacy;
    this._authorId = props.authorId;
    this._media = props.media || [];
    this._reactions = props.reactions || [];
    this._comments = props.comments || [];
    this._hashtags = props.hashtags || [];
    this._createdAt = props.createdAt || new Date();
    this._updatedAt = props.updatedAt || new Date();

    this.validate();

    if (!props.id) {
      this.addDomainEvent(
        new PostCreatedEvent(
          this.id,
          this._authorId,
          this._content,
          this._privacy,
        ),
      );
    }
  }

  // Getters
  get content(): string | undefined {
    return this._content;
  }

  get privacy(): PostPrivacy {
    return this._privacy;
  }

  get authorId(): string {
    return this._authorId;
  }

  get media(): PostMedia[] {
    return [...this._media];
  }

  get reactions(): PostReaction[] {
    return [...this._reactions];
  }

  get comments(): PostComment[] {
    return [...this._comments];
  }

  get hashtags(): string[] {
    return [...this._hashtags];
  }

  get createdAt(): Date {
    return this._createdAt;
  }

  get updatedAt(): Date {
    return this._updatedAt;
  }

  get likesCount(): number {
    return this._reactions.filter((r) => r.type === ReactionType.LIKE).length;
  }

  get commentsCount(): number {
    return this._comments.length;
  }

  // Domain methods
  updateContent(newContent?: string, hashtags?: string[]): void {
    if (
      newContent !== undefined &&
      newContent.trim() === '' &&
      (!this._media || this._media.length === 0)
    ) {
      throw new EmptyPostContentException();
    }

    const oldContent = this._content;
    this._content = newContent;
    this._hashtags = hashtags || this.extractHashtags(newContent);
    this._updatedAt = new Date();

    this.validate();

    this.addDomainEvent(
      new PostUpdatedEvent(this.id, this._authorId, oldContent, newContent),
    );
  }

  changePrivacy(newPrivacy: PostPrivacy): void {
    if (this._privacy !== newPrivacy) {
      this._privacy = newPrivacy;
      this.validate();
    }
  }

  addMedia(media: PostMedia): void {
    const exists = this._media.find((m) => m.id === media.id);
    if (!exists) {
      this._media.push(media);
      this._media.sort((a, b) => a.order - b.order);
    }
  }

  removeMedia(mediaId: string): void {
    this._media = this._media.filter((m) => m.id !== mediaId);
  }

  clearMedia(): void {
    this._media = [];
  }

  addReaction(userId: string, type: ReactionType): void {
    const existingReaction = this._reactions.find((r) => r.userId === userId);

    if (existingReaction) {
      if (existingReaction.type === type) {
        throw new PostAlreadyLikedException();
      }
      // Update existing reaction type
      existingReaction.type = type;
    } else {
      const reaction: PostReaction = {
        id: this.generateId(),
        type,
        userId,
        createdAt: new Date(),
      };
      this._reactions.push(reaction);
    }

    this.addDomainEvent(new PostLikedEvent(this.id, userId, type));
  }

  removeReaction(userId: string): void {
    const reactionIndex = this._reactions.findIndex((r) => r.userId === userId);

    if (reactionIndex === -1) {
      throw new PostNotLikedException();
    }

    const reaction = this._reactions[reactionIndex];
    this._reactions.splice(reactionIndex, 1);

    this.addDomainEvent(new PostUnlikedEvent(this.id, userId, reaction.type));
  }

  addComment(
    commentId: string,
    content: string,
    authorId: string,
    parentId?: string,
  ): void {
    const comment: PostComment = {
      id: commentId,
      content,
      authorId,
      parentId,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    this._comments.push(comment);

    this.addDomainEvent(
      new PostCommentedEvent(this.id, commentId, authorId, content),
    );
  }

  removeComment(commentId: string): void {
    this._comments = this._comments.filter((c) => c.id !== commentId);
  }

  updateComment(commentId: string, content: string): void {
    const comment = this._comments.find((c) => c.id === commentId);
    if (comment) {
      comment.content = content;
      comment.updatedAt = new Date();
      this._updatedAt = new Date();
    }
  }

  canBeViewedBy(viewerId: string): boolean {
    switch (this._privacy) {
      case PostPrivacy.PUBLIC:
        return true;
      case PostPrivacy.PRIVATE:
        return this._authorId === viewerId;
      case PostPrivacy.FOLLOWERS:
        // This would need to check if viewer follows author
        // For now, just check if viewer is author
        return this._authorId === viewerId;
      default:
        return false;
    }
  }

  canBeEditedBy(userId: string): boolean {
    return this._authorId === userId;
  }

  canBeDeletedBy(userId: string): boolean {
    return this._authorId === userId;
  }

  delete(): void {
    this.addDomainEvent(new PostDeletedEvent(this.id, this._authorId));
  }

  private validate(): void {
    if (!this._authorId) {
      throw new InvalidPostContentException('Author ID is required');
    }

    if (!this._content && (!this._media || this._media.length === 0)) {
      throw new EmptyPostContentException();
    }

    if (this._content && this._content.length > 2000) {
      throw new InvalidPostContentException(
        'Post content exceeds maximum length',
      );
    }

    if (!Object.values(PostPrivacy).includes(this._privacy)) {
      throw new InvalidPostContentException('Invalid privacy setting');
    }
  }

  private extractHashtags(content?: string): string[] {
    if (!content) return [];

    const hashtags = content.match(/#\w+/g) || [];
    return hashtags.map((tag) => tag.toLowerCase());
  }

  private generateId(): string {
    return randomUUID();
  }

  // Static factory methods
  static createNew(
    props: Omit<PostProps, 'id' | 'createdAt' | 'updatedAt'>,
  ): PostEntity {
    return new PostEntity(props);
  }

  static fromPersistence(props: PostProps): PostEntity {
    return new PostEntity(props);
  }

  // Convert to persistence format
  toPersistence() {
    return {
      id: this.id,
      content: this._content,
      privacy: this._privacy,
      authorId: this._authorId,
      media: this._media,
      reactions: this._reactions,
      comments: this._comments,
      hashtags: this._hashtags,
      createdAt: this._createdAt,
      updatedAt: this._updatedAt,
    };
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../database/prisma.service';
import { PostEntity, PostPrivacy, ReactionType } from '../domain/post.entity';
import { PostFactory } from '../domain/factories/post.factory';
import { IPostDomainRepository } from '../domain/repositories/post-domain-repository.interface';

/**
 * Prisma implementation of Post repository
 * Handles persistence operations using Prisma ORM
 */
@Injectable()
export class PostPrismaRepository implements IPostDomainRepository {
  constructor(
    private readonly prisma: PrismaService,
    private readonly postFactory: PostFactory,
  ) {}

  async save(post: PostEntity): Promise<PostEntity> {
    const data = {
      id: post.id,
      content: post.content,
      privacy: post.privacy,
      authorId: post.authorId,
      createdAt: post.createdAt,
      updatedAt: post.updatedAt,
    };

    // Upsert the post
    const savedPost = await this.prisma.post.upsert({
      where: { id: post.id },
      create: data,
      update: {
        content: data.content,
        privacy: data.privacy,
        updatedAt: data.updatedAt,
      },
      include: {
        author: true,
        reactions: true,
        comments: {
          include: {
            author: true,
          },
        },
        postMedia: true,
        hashtags: {
          include: {
            hashtag: true,
          },
        },
      },
    });

    // Handle media - delete existing and create new ones
    await this.prisma.postMedia.deleteMany({
      where: { postId: post.id },
    });

    if (post.media.length > 0) {
      await this.prisma.postMedia.createMany({
        data: post.media.map((media) => ({
          id: media.id,
          postId: post.id,
          url: media.url,
          type: media.type,
          order: media.order,
        })),
      });
    }

    // Handle hashtags - delete existing relationships and create new ones
    await this.prisma.postHashtag.deleteMany({
      where: { postId: post.id },
    });

    if (post.hashtags.length > 0) {
      for (const hashtagName of post.hashtags) {
        // Ensure hashtag exists
        const hashtag = await this.prisma.hashtag.upsert({
          where: { name: hashtagName },
          create: { name: hashtagName },
          update: {},
        });

        // Create relationship
        await this.prisma.postHashtag.create({
          data: {
            postId: post.id,
            hashtagId: hashtag.id,
          },
        });
      }
    }

    // Handle reactions - sync reactions
    const existingReactions = await this.prisma.reaction.findMany({
      where: { postId: post.id },
    });

    const currentReactionIds = post.reactions.map((r) => r.id);
    const existingReactionIds = existingReactions.map((r) => r.id);

    // Delete removed reactions
    const reactionsToDelete = existingReactionIds.filter(
      (id) => !currentReactionIds.includes(id),
    );
    if (reactionsToDelete.length > 0) {
      await this.prisma.reaction.deleteMany({
        where: { id: { in: reactionsToDelete } },
      });
    }

    // Create new reactions
    const newReactions = post.reactions.filter(
      (r) => !existingReactionIds.includes(r.id),
    );
    if (newReactions.length > 0) {
      await this.prisma.reaction.createMany({
        data: newReactions.map((reaction) => ({
          id: reaction.id,
          type: reaction.type,
          postId: post.id,
          reactorId: reaction.userId,
          createdAt: reaction.createdAt,
        })),
      });
    }

    // Handle comments - sync comments
    const existingComments = await this.prisma.comment.findMany({
      where: { postId: post.id },
    });

    const currentCommentIds = post.comments.map((c) => c.id);
    const existingCommentIds = existingComments.map((c) => c.id);

    // Delete removed comments
    const commentsToDelete = existingCommentIds.filter(
      (id) => !currentCommentIds.includes(id),
    );
    if (commentsToDelete.length > 0) {
      await this.prisma.comment.deleteMany({
        where: { id: { in: commentsToDelete } },
      });
    }

    // Upsert comments
    for (const comment of post.comments) {
      await this.prisma.comment.upsert({
        where: { id: comment.id },
        create: {
          id: comment.id,
          content: comment.content,
          postId: post.id,
          authorId: comment.authorId,
          parentId: comment.parentId,
          createdAt: comment.createdAt,
          updatedAt: comment.updatedAt,
        },
        update: {
          content: comment.content,
          updatedAt: comment.updatedAt,
        },
      });
    }

    // Return reconstructed entity
    const savedEntity = await this.findById(post.id);
    if (!savedEntity) {
      throw new Error('Failed to retrieve saved post');
    }
    return savedEntity;
  }

  async findById(id: string): Promise<PostEntity | null> {
    const post = await this.prisma.post.findUnique({
      where: { id },
      include: {
        author: true,
        reactions: {
          include: {
            reactor: true,
          },
        },
        comments: {
          include: {
            author: true,
          },
          orderBy: {
            createdAt: 'asc',
          },
        },
        postMedia: {
          orderBy: {
            order: 'asc',
          },
        },
        hashtags: {
          include: {
            hashtag: true,
          },
        },
      },
    });

    if (!post) {
      return null;
    }

    return this.mapToEntity(post);
  }

  async findAll(filters: {
    authorId?: string;
    privacy?: string;
    hashtag?: string;
    search?: string;
    page: number;
    limit: number;
    sortBy?: 'newest' | 'oldest' | 'most_liked' | 'most_commented';
  }): Promise<{ posts: PostEntity[]; total: number }> {
    const where: any = {};

    if (filters.authorId) {
      where.authorId = filters.authorId;
    }

    if (filters.privacy) {
      where.privacy = filters.privacy;
    }

    if (filters.hashtag) {
      where.hashtags = {
        some: {
          hashtag: {
            name: filters.hashtag,
          },
        },
      };
    }

    if (filters.search) {
      where.content = {
        contains: filters.search,
        mode: 'insensitive',
      };
    }

    // Calculate sorting
    let orderBy: any = { createdAt: 'desc' };
    switch (filters.sortBy) {
      case 'oldest':
        orderBy = { createdAt: 'asc' };
        break;
      case 'most_liked':
        orderBy = { reactions: { _count: 'desc' } };
        break;
      case 'most_commented':
        orderBy = { comments: { _count: 'desc' } };
        break;
    }

    const [posts, total] = await Promise.all([
      this.prisma.post.findMany({
        where,
        include: {
          author: true,
          reactions: {
            include: {
              reactor: true,
            },
          },
          comments: {
            include: {
              author: true,
            },
          },
          postMedia: {
            orderBy: {
              order: 'asc',
            },
          },
          hashtags: {
            include: {
              hashtag: true,
            },
          },
        },
        orderBy,
        skip: (filters.page - 1) * filters.limit,
        take: filters.limit,
      }),
      this.prisma.post.count({ where }),
    ]);

    return {
      posts: posts.map((post) => this.mapToEntity(post)),
      total,
    };
  }

  async findByAuthorId(
    authorId: string,
    page: number,
    limit: number,
  ): Promise<{ posts: PostEntity[]; total: number }> {
    return this.findAll({
      authorId,
      page,
      limit,
    });
  }

  async delete(id: string): Promise<void> {
    await this.prisma.post.delete({
      where: { id },
    });
  }

  async exists(id: string): Promise<boolean> {
    const post = await this.prisma.post.findUnique({
      where: { id },
      select: { id: true },
    });
    return !!post;
  }

  async findByIds(ids: string[]): Promise<PostEntity[]> {
    const posts = await this.prisma.post.findMany({
      where: {
        id: { in: ids },
      },
      include: {
        author: true,
        reactions: {
          include: {
            reactor: true,
          },
        },
        comments: {
          include: {
            author: true,
          },
        },
        postMedia: {
          orderBy: {
            order: 'asc',
          },
        },
        hashtags: {
          include: {
            hashtag: true,
          },
        },
      },
    });

    return posts.map((post) => this.mapToEntity(post));
  }

  async getUserFeed(
    userId: string,
    page: number,
    limit: number,
  ): Promise<{ posts: PostEntity[]; total: number }> {
    // Get posts from followed users + user's own posts
    const [posts, total] = await Promise.all([
      this.prisma.post.findMany({
        where: {
          OR: [
            { authorId: userId },
            {
              author: {
                followers: {
                  some: {
                    followerId: userId,
                  },
                },
              },
            },
          ],
          privacy: {
            in: ['public', 'followers'],
          },
        },
        include: {
          author: true,
          reactions: {
            include: {
              reactor: true,
            },
          },
          comments: {
            include: {
              author: true,
            },
          },
          postMedia: {
            orderBy: {
              order: 'asc',
            },
          },
          hashtags: {
            include: {
              hashtag: true,
            },
          },
        },
        orderBy: { createdAt: 'desc' },
        skip: (page - 1) * limit,
        take: limit,
      }),
      this.prisma.post.count({
        where: {
          OR: [
            { authorId: userId },
            {
              author: {
                followers: {
                  some: {
                    followerId: userId,
                  },
                },
              },
            },
          ],
          privacy: {
            in: ['public', 'followers'],
          },
        },
      }),
    ]);

    return {
      posts: posts.map((post) => this.mapToEntity(post)),
      total,
    };
  }

  async getTrendingPosts(
    page: number,
    limit: number,
  ): Promise<{ posts: PostEntity[]; total: number }> {
    // Get posts with high engagement in the last 24 hours
    const oneDayAgo = new Date();
    oneDayAgo.setDate(oneDayAgo.getDate() - 1);

    const [posts, total] = await Promise.all([
      this.prisma.post.findMany({
        where: {
          privacy: 'public',
          createdAt: {
            gte: oneDayAgo,
          },
        },
        include: {
          author: true,
          reactions: {
            include: {
              reactor: true,
            },
          },
          comments: {
            include: {
              author: true,
            },
          },
          postMedia: {
            orderBy: {
              order: 'asc',
            },
          },
          hashtags: {
            include: {
              hashtag: true,
            },
          },
          _count: {
            select: {
              reactions: true,
              comments: true,
            },
          },
        },
        orderBy: [
          { reactions: { _count: 'desc' } },
          { comments: { _count: 'desc' } },
          { createdAt: 'desc' },
        ],
        skip: (page - 1) * limit,
        take: limit,
      }),
      this.prisma.post.count({
        where: {
          privacy: 'public',
          createdAt: {
            gte: oneDayAgo,
          },
        },
      }),
    ]);

    return {
      posts: posts.map((post) => this.mapToEntity(post)),
      total,
    };
  }

  async getPostStats(postId: string): Promise<{
    likesCount: number;
    commentsCount: number;
  }> {
    const stats = await this.prisma.post.findUnique({
      where: { id: postId },
      select: {
        _count: {
          select: {
            reactions: true,
            comments: true,
          },
        },
      },
    });

    return {
      likesCount: stats?._count.reactions || 0,
      commentsCount: stats?._count.comments || 0,
    };
  }

  private mapToEntity(data: any): PostEntity {
    return this.postFactory.reconstitute({
      id: data.id,
      content: data.content,
      authorId: data.authorId,
      privacy: data.privacy as PostPrivacy,
      hashtags: data.hashtags.map((h: any) => h.hashtag.name),
      media: data.postMedia.map((m: any) => ({
        id: m.id,
        url: m.url,
        type: m.type as 'image' | 'video',
        order: m.order,
      })),
      reactions: data.reactions.map((r: any) => ({
        id: r.id,
        userId: r.reactorId,
        type: r.type as ReactionType,
        createdAt: r.createdAt,
      })),
      comments: data.comments.map((c: any) => ({
        id: c.id,
        content: c.content,
        authorId: c.authorId,
        parentId: c.parentId,
        createdAt: c.createdAt,
        updatedAt: c.updatedAt,
      })),
      createdAt: data.createdAt,
      updatedAt: data.updatedAt,
    });
  }
}

