import { Injectable, Inject } from '@nestjs/common';
import { ReactionRepository } from '../../domain/repositories/reaction.repository';
import { ReactionFactory } from '../../domain/factories/reaction.factory';
import { ReactionDomainService } from '../../domain/services/reaction-domain.service';
import { CreateReactionDto } from '../dto/reaction.dto';
import { CreateReactionResponseDto } from '../dto/reaction-response.dto';
import { ReactionMapper } from '../mappers/reaction.mapper';
import {
  ExternalPostService,
  ExternalCommentService,
  NotificationService,
} from '../interfaces/external-services.interface';
import {
  EXTERNAL_POST_SERVICE,
  EXTERNAL_COMMENT_SERVICE,
  NOTIFICATION_SERVICE,
} from '../interfaces/tokens';
import {
  InvalidReactionTargetException,
  PostNotFoundException,
  CommentNotFoundException,
} from '../../domain/reaction.exceptions';

@Injectable()
export class CreateReactionUseCase {
  constructor(
    private readonly reactionRepository: ReactionRepository,
    private readonly reactionFactory: ReactionFactory,
    private readonly reactionDomainService: ReactionDomainService,
    @Inject(EXTERNAL_POST_SERVICE)
    private readonly postService: ExternalPostService,
    @Inject(EXTERNAL_COMMENT_SERVICE)
    private readonly commentService: ExternalCommentService,
    @Inject(NOTIFICATION_SERVICE)
    private readonly notificationService: NotificationService,
  ) {}

  async execute(
    dto: CreateReactionDto,
    userId: string,
  ): Promise<CreateReactionResponseDto> {
    const { postId, commentId, type } = dto;

    // Validate target
    if (!postId && !commentId) {
      throw new InvalidReactionTargetException();
    }

    let targetId: string;
    let targetType: 'post' | 'comment';
    let targetAuthorId: string;
    let targetContent: string;

    if (postId) {
      const post = await this.postService.findById(postId);
      if (!post) {
        throw new PostNotFoundException(postId);
      }
      targetId = postId;
      targetType = 'post';
      targetAuthorId = post.authorId;
      targetContent = post.content;
    } else {
      const comment = await this.commentService.findById(commentId!);
      if (!comment) {
        throw new CommentNotFoundException(commentId!);
      }
      targetId = commentId!;
      targetType = 'comment';
      targetAuthorId = comment.authorId;
      targetContent = comment.content;
    }

    // Create or update reaction
    const { reaction, isNew } =
      await this.reactionDomainService.createOrUpdateReaction(
        type.toUpperCase() as any,
        userId,
        targetId,
        targetType,
      );

    // Send notification if it's a new reaction and not self-reaction
    if (isNew && targetAuthorId !== userId) {
      try {
        await this.notificationService.createReactionNotification({
          reactorId: userId,
          targetUserId: targetAuthorId,
          entityId: targetId,
          entityType: targetType,
          content: targetContent,
        });
      } catch (error) {
        // Log error but don't fail the reaction creation
        console.error('Failed to create reaction notification:', error);
      }
    }

    return {
      message: isNew
        ? 'Reaction created successfully'
        : 'Reaction updated successfully',
      reacted: true,
      reaction: ReactionMapper.toResponseDto(reaction),
      isNew,
    };
  }
}

import { Injectable } from '@nestjs/common';
import { ReactionEntity, ReactionType } from '../reaction.entity';
import { ReactionRepository } from '../repositories/reaction.repository';
import {
  InvalidReactionTargetException,
  PostNotFoundException,
  CommentNotFoundException,
  ReactionNotFoundException,
  UnauthorizedReactionException,
} from '../reaction.exceptions';

@Injectable()
export class ReactionDomainService {
  constructor(private readonly reactionRepository: ReactionRepository) {}

  async createOrUpdateReaction(
    type: ReactionType,
    reactorId: string,
    targetId: string,
    targetType: 'post' | 'comment',
  ): Promise<{ reaction: ReactionEntity; isNew: boolean }> {
    // Check if user already has a reaction to this target
    const existingReaction = await this.reactionRepository.findByUserAndTarget(
      reactorId,
      targetId,
      targetType,
    );

    if (existingReaction) {
      // Update existing reaction
      existingReaction.updateType(type);
      const updatedReaction =
        await this.reactionRepository.save(existingReaction);
      return { reaction: updatedReaction, isNew: false };
    }

    // Create new reaction
    const newReaction = ReactionEntity.createNew(
      type,
      reactorId,
      targetId,
      targetType,
    );
    const savedReaction = await this.reactionRepository.save(newReaction);
    return { reaction: savedReaction, isNew: true };
  }

  async validateReactionOwnership(
    reactionId: string,
    userId: string,
  ): Promise<ReactionEntity> {
    const reaction = await this.reactionRepository.findById(reactionId);

    if (!reaction) {
      throw new ReactionNotFoundException(reactionId);
    }

    if (!reaction.isOwnedBy(userId)) {
      throw new UnauthorizedReactionException();
    }

    return reaction;
  }

  async deleteReaction(reactionId: string, userId: string): Promise<void> {
    await this.validateReactionOwnership(reactionId, userId);
    await this.reactionRepository.delete(reactionId);
  }

  async getUserReactionStatus(
    targetId: string,
    userId: string,
    targetType: 'post' | 'comment',
  ): Promise<{
    reacted: boolean;
    reactionType?: ReactionType;
    reactionId?: string;
  }> {
    const reaction = await this.reactionRepository.findByUserAndTarget(
      userId,
      targetId,
      targetType,
    );

    if (!reaction) {
      return { reacted: false };
    }

    return {
      reacted: true,
      reactionType: reaction.type,
      reactionId: reaction.id,
    };
  }

  async validateTargetExists(
    targetId: string,
    targetType: 'post' | 'comment',
  ): Promise<void> {
    // This method should be implemented with proper validation
    // For now, we'll assume the validation happens at the application layer
    // through the use of external services
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../database/prisma.service';
import {
  ReactionRepository,
  FindReactionsOptions,
  ReactionWithReactor,
  PostReactionsResult,
  ReactionStatusResult,
} from '../domain/repositories/reaction.repository';
import { ReactionEntity } from '../domain/reaction.entity';
import { ReactionFactory } from '../domain/factories/reaction.factory';

@Injectable()
export class PrismaReactionRepository implements ReactionRepository {
  constructor(
    private readonly prisma: PrismaService,
    private readonly reactionFactory: ReactionFactory,
  ) {}

  async save(reaction: ReactionEntity): Promise<ReactionEntity> {
    const data = {
      type: reaction.type,
      reactorId: reaction.reactorId,
      postId: reaction.postId,
      commentId: reaction.commentId,
    };

    if (reaction.id) {
      // Update existing
      const updated = await this.prisma.reaction.update({
        where: { id: reaction.id },
        data,
      });

      return this.reactionFactory.createFromPrimitive({
        ...updated,
        updatedAt: updated.createdAt, // Fallback since Prisma model might not have updatedAt
      });
    } else {
      // Create new
      const created = await this.prisma.reaction.create({
        data,
      });

      return this.reactionFactory.createFromPrimitive({
        ...created,
        updatedAt: created.createdAt,
      });
    }
  }

  async findById(id: string): Promise<ReactionEntity | null> {
    const reaction = await this.prisma.reaction.findUnique({
      where: { id },
    });

    if (!reaction) {
      return null;
    }

    return this.reactionFactory.createFromPrimitive({
      ...reaction,
      updatedAt: reaction.createdAt,
    });
  }

  async findByUserAndTarget(
    userId: string,
    targetId: string,
    targetType: 'post' | 'comment',
  ): Promise<ReactionEntity | null> {
    const where =
      targetType === 'post'
        ? { reactorId: userId, postId: targetId }
        : { reactorId: userId, commentId: targetId };

    const reaction = await this.prisma.reaction.findFirst({
      where,
    });

    if (!reaction) {
      return null;
    }

    return this.reactionFactory.createFromPrimitive({
      ...reaction,
      updatedAt: reaction.createdAt,
    });
  }

  async findAll(options?: FindReactionsOptions): Promise<ReactionEntity[]> {
    const where: any = {};

    if (options?.postId) where.postId = options.postId;
    if (options?.commentId) where.commentId = options.commentId;
    if (options?.reactorId) where.reactorId = options.reactorId;

    if (options?.targetType === 'post') {
      where.postId = { not: null };
    } else if (options?.targetType === 'comment') {
      where.commentId = { not: null };
    }

    const reactions = await this.prisma.reaction.findMany({
      where,
      orderBy: { createdAt: 'desc' },
      take: options?.limit,
      skip: options?.offset,
    });

    return reactions.map((reaction) =>
      this.reactionFactory.createFromPrimitive({
        ...reaction,
        updatedAt: reaction.createdAt,
      }),
    );
  }

  async findAllWithReactor(
    options?: FindReactionsOptions,
  ): Promise<ReactionWithReactor[]> {
    const where: any = {};

    if (options?.postId) where.postId = options.postId;
    if (options?.commentId) where.commentId = options.commentId;
    if (options?.reactorId) where.reactorId = options.reactorId;

    if (options?.targetType === 'post') {
      where.postId = { not: null };
    } else if (options?.targetType === 'comment') {
      where.commentId = { not: null };
    }

    const reactions = await this.prisma.reaction.findMany({
      where,
      include: {
        reactor: {
          select: {
            id: true,
            fullName: true,
            avatar: true,
          },
        },
      },
      orderBy: { createdAt: 'desc' },
      take: options?.limit,
      skip: options?.offset,
    });

    return reactions.map((reaction) => ({
      reaction: this.reactionFactory.createFromPrimitive({
        id: reaction.id,
        type: reaction.type,
        reactorId: reaction.reactorId,
        postId: reaction.postId,
        commentId: reaction.commentId,
        createdAt: reaction.createdAt,
        updatedAt: reaction.createdAt,
      }),
      reactor: reaction.reactor,
    }));
  }

  async delete(id: string): Promise<void> {
    await this.prisma.reaction.delete({
      where: { id },
    });
  }

  async getPostReactions(postId: string): Promise<PostReactionsResult> {
    const reactions = await this.prisma.reaction.findMany({
      where: { postId },
      include: {
        reactor: {
          select: {
            id: true,
            fullName: true,
            avatar: true,
          },
        },
      },
      orderBy: { createdAt: 'desc' },
    });

    return {
      postId,
      totalReactions: reactions.length,
      reactions: reactions.map((reaction) => ({
        reaction: this.reactionFactory.createFromPrimitive({
          id: reaction.id,
          type: reaction.type,
          reactorId: reaction.reactorId,
          postId: reaction.postId,
          commentId: reaction.commentId,
          createdAt: reaction.createdAt,
          updatedAt: reaction.createdAt,
        }),
        reactor: reaction.reactor,
      })),
    };
  }

  async getReactionStatus(
    targetId: string,
    userId: string,
    targetType: 'post' | 'comment',
  ): Promise<ReactionStatusResult> {
    const where =
      targetType === 'post'
        ? { reactorId: userId, postId: targetId }
        : { reactorId: userId, commentId: targetId };

    const reaction = await this.prisma.reaction.findFirst({
      where,
    });

    return {
      targetId,
      userId,
      reacted: !!reaction,
      reactionId: reaction?.id || null,
      reactionType: reaction?.type || null,
    };
  }

  async countByTarget(
    targetId: string,
    targetType: 'post' | 'comment',
  ): Promise<number> {
    const where =
      targetType === 'post' ? { postId: targetId } : { commentId: targetId };

    return this.prisma.reaction.count({
      where,
    });
  }
}

