import { Injectable, Logger } from '@nestjs/common';
import { ApiResponse } from '../../../shared/common/interfaces/api-response.interface';
import {
  GetSearchHistoryUseCase,
  AddSearchEntryUseCase,
  RemoveSearchEntryUseCase,
  ClearSearchHistoryUseCase,
} from './use-cases';
import {
  SearchHistoryResponseDto,
  SearchHistoryEntryDto,
  AddSearchEntryDto,
} from './dto/search-history.dto';

@Injectable()
export class SearchHistoryApplicationService {
  private readonly logger = new Logger(SearchHistoryApplicationService.name);

  constructor(
    private readonly getSearchHistoryUseCase: GetSearchHistoryUseCase,
    private readonly addSearchEntryUseCase: AddSearchEntryUseCase,
    private readonly removeSearchEntryUseCase: RemoveSearchEntryUseCase,
    private readonly clearSearchHistoryUseCase: ClearSearchHistoryUseCase,
  ) {}

  /**
   * Get search history for a user with populated user data
   */
  async getSearchHistory(
    currentUserId: string,
  ): Promise<ApiResponse<SearchHistoryResponseDto>> {
    try {
      const { searchHistory } = await this.getSearchHistoryUseCase.execute({
        userId: currentUserId,
      });

      if (!searchHistory || searchHistory.isEmpty) {
        return {
          success: true,
          message: 'Search history retrieved successfully',
          data: {
            history: [],
            total: 0,
          },
        };
      }

      // Convert domain entities to DTOs
      const historyItems: SearchHistoryEntryDto[] = searchHistory.entries.map(
        (entry) => ({
          id: entry.id,
          searchedUserId: entry.searchedUserId,
          searchedAt: entry.searchedAt.toISOString(),
          user: {
            id: entry.searchedUserProfile?.id || entry.searchedUserId,
            userName: entry.searchedUserProfile?.userName || '',
            fullName: entry.searchedUserProfile?.fullName || '',
            avatar: entry.searchedUserProfile?.avatar || null,
          },
        }),
      );

      return {
        success: true,
        message: 'Search history retrieved successfully',
        data: {
          history: historyItems,
          total: historyItems.length,
        },
      };
    } catch (error) {
      this.logger.error(
        `Failed to get search history for user ${currentUserId}:`,
        error,
      );
      return {
        success: false,
        message: 'Failed to retrieve search history',
        data: {
          history: [],
          total: 0,
        },
      };
    }
  }

  /**
   * Add a searched user to current user's search history
   */
  async addToSearchHistory(
    currentUserId: string,
    dto: AddSearchEntryDto,
    searchedUserProfile?: {
      id: string;
      userName: string;
      fullName: string;
      avatar: string | null;
    },
  ): Promise<ApiResponse<null>> {
    try {
      // Don't add self to search history
      if (currentUserId === dto.searchedUserId) {
        return {
          success: true,
          message: 'Cannot add yourself to search history',
          data: null,
        };
      }

      await this.addSearchEntryUseCase.execute({
        userId: currentUserId,
        searchedUserId: dto.searchedUserId,
        searchedUserProfile,
      });

      return {
        success: true,
        message: 'User added to search history successfully',
        data: null,
      };
    } catch (error) {
      this.logger.error(
        `Failed to add user to search history: ${error.message}`,
        error,
      );
      return {
        success: false,
        message: 'Failed to add user to search history',
        data: null,
      };
    }
  }

  /**
   * Remove a user from search history
   */
  async removeFromSearchHistory(
    currentUserId: string,
    searchedUserId: string,
  ): Promise<ApiResponse<null>> {
    try {
      await this.removeSearchEntryUseCase.execute({
        userId: currentUserId,
        searchedUserId,
      });

      return {
        success: true,
        message: 'User removed from search history successfully',
        data: null,
      };
    } catch (error) {
      this.logger.error(
        `Failed to remove user from search history: ${error.message}`,
        error,
      );
      return {
        success: false,
        message: 'Failed to remove user from search history',
        data: null,
      };
    }
  }

  /**
   * Clear all search history for a user
   */
  async clearSearchHistory(currentUserId: string): Promise<ApiResponse<null>> {
    try {
      await this.clearSearchHistoryUseCase.execute({
        userId: currentUserId,
      });

      return {
        success: true,
        message: 'Search history cleared successfully',
        data: null,
      };
    } catch (error) {
      this.logger.error(
        `Failed to clear search history for user ${currentUserId}:`,
        error,
      );
      return {
        success: false,
        message: 'Failed to clear search history',
        data: null,
      };
    }
  }
}

import { Injectable } from '@nestjs/common';
import { SearchHistory } from '../search-history.entity';
import {
  InvalidUserIdException,
  SearchHistoryLimitExceededException,
} from '../search-history.exceptions';

/**
 * Domain service for SearchHistory business logic
 * Encapsulates complex domain rules that don't belong to a single entity
 */
@Injectable()
export class SearchHistoryDomainService {
  private readonly MAX_ENTRIES = 20;
  private readonly DUPLICATE_THRESHOLD_MS = 1000 * 60 * 5; // 5 minutes

  /**
   * Validates if a user can add a search entry
   */
  canAddSearchEntry(
    searchHistory: SearchHistory,
    searchedUserId: string,
  ): boolean {
    // Cannot add self to search history
    if (searchHistory.userId === searchedUserId) {
      return false;
    }

    return true;
  }

  /**
   * Validates if a user can view another user's search history
   */
  canViewSearchHistory(
    searchHistory: SearchHistory,
    viewerId: string,
  ): boolean {
    // Only the owner can view their search history
    return searchHistory.userId === viewerId;
  }

  /**
   * Validates if a user can modify search history
   */
  canModifySearchHistory(
    searchHistory: SearchHistory,
    userId: string,
  ): boolean {
    return searchHistory.userId === userId;
  }

  /**
   * Checks if adding an entry would exceed the limit
   */
  wouldExceedLimit(searchHistory: SearchHistory): boolean {
    return searchHistory.entriesCount >= this.MAX_ENTRIES;
  }

  /**
   * Checks if a search entry is considered a duplicate within the threshold
   */
  isDuplicateWithinThreshold(
    searchHistory: SearchHistory,
    searchedUserId: string,
    searchTime: Date,
  ): boolean {
    const existingEntry = searchHistory.entries.find(
      (entry) => entry.searchedUserId === searchedUserId,
    );

    if (!existingEntry) {
      return false;
    }

    const timeDiff = searchTime.getTime() - existingEntry.searchedAt.getTime();
    return timeDiff < this.DUPLICATE_THRESHOLD_MS;
  }

  /**
   * Gets the most frequently searched users from search history
   */
  getMostSearchedUsers(
    searchHistory: SearchHistory,
    limit: number = 5,
  ): string[] {
    const userCounts = new Map<string, number>();

    // Count occurrences of each searched user
    searchHistory.entries.forEach((entry) => {
      const count = userCounts.get(entry.searchedUserId) || 0;
      userCounts.set(entry.searchedUserId, count + 1);
    });

    // Sort by count and return top users
    return Array.from(userCounts.entries())
      .sort(([, countA], [, countB]) => countB - countA)
      .slice(0, limit)
      .map(([userId]) => userId);
  }

  /**
   * Gets recent search patterns for analytics
   */
  getSearchPatterns(searchHistory: SearchHistory): {
    totalSearches: number;
    uniqueUsers: number;
    averageSearchesPerDay: number;
    mostActiveDay: string | null;
  } {
    const entries = searchHistory.entries;
    const uniqueUsers = new Set(entries.map((e) => e.searchedUserId)).size;

    if (entries.length === 0) {
      return {
        totalSearches: 0,
        uniqueUsers: 0,
        averageSearchesPerDay: 0,
        mostActiveDay: null,
      };
    }

    // Group searches by date
    const searchesByDate = new Map<string, number>();
    entries.forEach((entry) => {
      const date = entry.searchedAt.toISOString().split('T')[0];
      const count = searchesByDate.get(date) || 0;
      searchesByDate.set(date, count + 1);
    });

    const days = searchesByDate.size;
    const averageSearchesPerDay = days > 0 ? entries.length / days : 0;

    // Find most active day
    let mostActiveDay: string | null = null;
    let maxSearches = 0;
    searchesByDate.forEach((count, date) => {
      if (count > maxSearches) {
        maxSearches = count;
        mostActiveDay = date;
      }
    });

    return {
      totalSearches: entries.length,
      uniqueUsers,
      averageSearchesPerDay: Math.round(averageSearchesPerDay * 100) / 100,
      mostActiveDay,
    };
  }

  /**
   * Validates search history business rules
   */
  validateSearchHistory(searchHistory: SearchHistory): void {
    if (searchHistory.entriesCount > this.MAX_ENTRIES) {
      throw new SearchHistoryLimitExceededException(
        `Search history cannot exceed ${this.MAX_ENTRIES} entries`,
      );
    }
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../../database/prisma.service';
import {
  SearchHistory,
  SearchHistoryEntry,
} from '../../domain/search-history.entity';
import { SearchHistoryRepository } from '../../domain/repositories/search-history.repository';

@Injectable()
export class PrismaSearchHistoryRepository extends SearchHistoryRepository {
  constructor(private readonly prisma: PrismaService) {
    super();
  }

  async findByUserId(userId: string): Promise<SearchHistory | null> {
    const searchHistoryData = await this.prisma.searchHistory.findUnique({
      where: { userId },
      include: {
        entries: {
          include: {
            searchedUser: {
              select: {
                id: true,
                username: true,
                fullName: true,
                avatar: true,
              },
            },
          },
          orderBy: {
            searchedAt: 'desc',
          },
        },
      },
    });

    if (!searchHistoryData) {
      return null;
    }

    return this.toDomainEntity(searchHistoryData);
  }

  async save(searchHistory: SearchHistory): Promise<SearchHistory> {
    const snapshot = searchHistory.toSnapshot();

    // Use upsert to handle both create and update
    const savedData = await this.prisma.searchHistory.upsert({
      where: { userId: snapshot.userId },
      create: {
        id: snapshot.id!,
        userId: snapshot.userId,
        createdAt: snapshot.createdAt!,
        updatedAt: snapshot.updatedAt!,
        entries: {
          create: snapshot.entries.map((entry) => ({
            id: entry.id,
            searchedUserId: entry.searchedUserId,
            searchedAt: entry.searchedAt,
          })),
        },
      },
      update: {
        updatedAt: snapshot.updatedAt!,
        entries: {
          // Delete existing entries and create new ones
          deleteMany: {},
          create: snapshot.entries.map((entry) => ({
            id: entry.id,
            searchedUserId: entry.searchedUserId,
            searchedAt: entry.searchedAt,
          })),
        },
      },
      include: {
        entries: {
          include: {
            searchedUser: {
              select: {
                id: true,
                username: true,
                fullName: true,
                avatar: true,
              },
            },
          },
          orderBy: {
            searchedAt: 'desc',
          },
        },
      },
    });

    return this.toDomainEntity(savedData);
  }

  async deleteByUserId(userId: string): Promise<void> {
    await this.prisma.searchHistory.delete({
      where: { userId },
    });
  }

  async existsByUserId(userId: string): Promise<boolean> {
    const count = await this.prisma.searchHistory.count({
      where: { userId },
    });
    return count > 0;
  }

  async getEntriesCountByUserId(userId: string): Promise<number> {
    const searchHistory = await this.prisma.searchHistory.findUnique({
      where: { userId },
      include: {
        _count: {
          select: { entries: true },
        },
      },
    });

    return searchHistory?._count?.entries ?? 0;
  }

  async findManyByUserIds(userIds: string[]): Promise<SearchHistory[]> {
    const searchHistories = await this.prisma.searchHistory.findMany({
      where: {
        userId: {
          in: userIds,
        },
      },
      include: {
        entries: {
          include: {
            searchedUser: {
              select: {
                id: true,
                username: true,
                fullName: true,
                avatar: true,
              },
            },
          },
          orderBy: {
            searchedAt: 'desc',
          },
        },
      },
    });

    return searchHistories.map((data) => this.toDomainEntity(data));
  }

  private toDomainEntity(data: any): SearchHistory {
    const entries: SearchHistoryEntry[] = data.entries.map((entry: any) => ({
      id: entry.id,
      searchedUserId: entry.searchedUserId,
      searchedAt: entry.searchedAt,
      searchedUserProfile: entry.searchedUser
        ? {
            id: entry.searchedUser.id,
            userName: entry.searchedUser.username,
            fullName: entry.searchedUser.fullName,
            avatar: entry.searchedUser.avatar,
          }
        : undefined,
    }));

    return SearchHistory.fromPersistence({
      id: data.id,
      userId: data.userId,
      entries,
      createdAt: data.createdAt,
      updatedAt: data.updatedAt,
    });
  }
}

