import { User, UserRole, UserStatus } from '../user.entity';
import {
  UserId,
  UserEmail,
  Username,
  Password,
  UserProfile,
} from '../value-objects';

export interface CreateUserRequest {
  username: string;
  email: string;
  password?: string;
  googleId?: string;
  profile: {
    fullName: string;
    bio?: string;
    dateOfBirth?: Date;
    avatar?: string;
    websiteUrl?: string;
    location?: string;
    phoneNumber?: string;
    gender?: string;
  };
  role?: UserRole;
}

export interface CreateUserFromGoogleRequest {
  googleId: string;
  email: string;
  profile: {
    fullName: string;
    avatar?: string;
  };
}

/**
 * User Factory
 * Handles complex user creation logic and ensures domain invariants
 */
export class UserFactory {
  /**
   * Create a new user with email/password
   */
  static async createUser(request: CreateUserRequest): Promise<User> {
    // Create value objects
    const userId = UserId.generate();
    const email = UserEmail.create(request.email);
    const username = Username.create(request.username);
    const userProfile = new UserProfile(request.profile);

    let password: Password | undefined;
    if (request.password) {
      password = await Password.createFromPlainText(request.password);
    }

    // Create user with proper encapsulation
    const user = new User(
      userId.getValue(),
      username.getValue(),
      email.getValue(),
      userProfile,
      {
        passwordHash: password?.getValue(),
        role: request.role || UserRole.USER,
        status: UserStatus.ACTIVE,
        isEmailVerified: false,
        createdAt: new Date(),
        updatedAt: new Date(),
      },
    );

    return user;
  }

  /**
   * Create user from Google OAuth
   */
  static createUserFromGoogle(request: CreateUserFromGoogleRequest): User {
    // Create value objects
    const userId = UserId.generate();
    const email = UserEmail.create(request.email);

    // Generate username from email local part
    const emailLocal = email.getLocalPart();
    const baseUsername = emailLocal.replace(/[^a-z0-9]/g, '');
    const username = Username.create(baseUsername);

    const userProfile = new UserProfile({
      fullName: request.profile.fullName,
      avatar: request.profile.avatar,
    });

    const user = new User(
      userId.getValue(),
      username.getValue(),
      email.getValue(),
      userProfile,
      {
        googleId: request.googleId,
        role: UserRole.USER,
        status: UserStatus.ACTIVE,
        isEmailVerified: true, // Google accounts are pre-verified
        emailVerifiedAt: new Date(),
        createdAt: new Date(),
        updatedAt: new Date(),
      },
    );

    return user;
  }

  /**
   * Reconstruct user from database data
   */
  static reconstructUser(data: {
    id: string;
    username: string;
    email: string;
    passwordHash?: string;
    googleId?: string;
    role: UserRole;
    status: UserStatus;
    isEmailVerified: boolean;
    emailVerifiedAt?: Date;
    createdAt: Date;
    updatedAt: Date;
    lastProfileUpdate?: Date;
    profile: any; // Raw profile data from DB
    followingIds?: string[];
    followerIds?: string[];
  }): User {
    // Validate and create value objects
    const userId = UserId.create(data.id);
    const email = UserEmail.create(data.email);
    const username = Username.create(data.username);
    const userProfile = new UserProfile(data.profile);

    const user = new User(
      userId.getValue(),
      username.getValue(),
      email.getValue(),
      userProfile,
      {
        passwordHash: data.passwordHash,
        googleId: data.googleId,
        role: data.role,
        status: data.status,
        isEmailVerified: data.isEmailVerified,
        emailVerifiedAt: data.emailVerifiedAt,
        createdAt: data.createdAt,
        updatedAt: data.updatedAt,
        lastProfileUpdate: data.lastProfileUpdate,
      },
    );

    // Set follow relationships
    if (data.followingIds || data.followerIds) {
      user.setFollowingAndFollowers(
        data.followingIds || [],
        data.followerIds || [],
      );
    }

    return user;
  }

  /**
   * Create admin user
   */
  static async createAdminUser(request: CreateUserRequest): Promise<User> {
    const adminRequest = {
      ...request,
      role: UserRole.ADMIN,
    };

    const user = await UserFactory.createUser(adminRequest);

    // Admin users are automatically verified
    user.verifyEmail();

    return user;
  }
}

import { User } from '../user.entity';
import { IUserRepository } from '../repositories';
import { UserId, UserEmail, Username } from '../value-objects';
import {
  UsernameAlreadyExistsException,
  EmailAlreadyExistsException,
} from '../user.exceptions';

/**
 * User Domain Service
 * Handles complex business logic that doesn't belong to a single aggregate
 */
export class UserDomainService {
  constructor(private readonly userRepository: IUserRepository) {}

  /**
   * Check if user can be created with given email and username
   */
  async validateUserUniqueness(
    email: UserEmail,
    username: Username,
  ): Promise<void> {
    const [existingUserByEmail, existingUserByUsername] = await Promise.all([
      this.userRepository.findByEmail(email),
      this.userRepository.findByUsername(username),
    ]);

    if (existingUserByEmail) {
      throw new EmailAlreadyExistsException(email.getValue());
    }

    if (existingUserByUsername) {
      throw new UsernameAlreadyExistsException(username.getValue());
    }
  }

  /**
   * Validate follow business rules
   */
  async validateFollowRules(follower: User, followee: User): Promise<void> {
    // Business rule: User cannot follow themselves
    if (follower.id === followee.id) {
      throw new Error('User cannot follow themselves');
    }

    // Business rule: Check if already following
    if (follower.isFollowing(followee.id)) {
      throw new Error(`Already following user ${followee.username}`);
    }

    // Business rule: Check if followee account is active
    if (followee.status !== 'ACTIVE') {
      throw new Error('Cannot follow inactive user account');
    }

    // Business rule: Check follower limits (example: max 5000 following)
    if (follower.followingCount >= 5000) {
      throw new Error('Maximum following limit reached');
    }
  }

  /**
   * Validate unfollow business rules
   */
  validateUnfollowRules(follower: User, followee: User): void {
    // Business rule: Cannot unfollow if not following
    if (!follower.isFollowing(followee.id)) {
      throw new Error(`Not following user ${followee.username}`);
    }
  }

  /**
   * Calculate user interaction score (for recommendations, etc.)
   */
  calculateUserInteractionScore(user1: User, user2: User): number {
    let score = 0;

    // Mutual followers boost
    const mutualFollowersCount = this.calculateMutualFollowersCount(
      user1,
      user2,
    );
    score += mutualFollowersCount * 2;

    // Same creation timeframe (users who joined around same time)
    const daysDifference =
      Math.abs(user1.createdAt.getTime() - user2.createdAt.getTime()) /
      (1000 * 60 * 60 * 24);

    if (daysDifference <= 30) {
      score += 5;
    }

    return score;
  }

  /**
   * Check if users have mutual followers
   */
  private calculateMutualFollowersCount(user1: User, user2: User): number {
    const user1Followers = new Set(user1.followerIds);
    const user2Followers = new Set(user2.followerIds);

    let mutualCount = 0;
    for (const followerId of user1Followers) {
      if (user2Followers.has(followerId)) {
        mutualCount++;
      }
    }

    return mutualCount;
  }

  /**
   * Generate username suggestions based on display name
   */
  async generateUsernameSuggestions(displayName: string): Promise<string[]> {
    const baseUsername = this.sanitizeDisplayNameForUsername(displayName);
    const suggestions: string[] = [];

    // Try base username
    if (
      !(await this.userRepository.existsByUsername(
        Username.create(baseUsername),
      ))
    ) {
      suggestions.push(baseUsername);
    }

    // Generate variations
    for (let i = 1; i <= 5; i++) {
      const variation = `${baseUsername}${i}`;
      if (
        !(await this.userRepository.existsByUsername(
          Username.create(variation),
        ))
      ) {
        suggestions.push(variation);
      }
    }

    // Generate with year
    const currentYear = new Date().getFullYear();
    const withYear = `${baseUsername}${currentYear}`;
    if (
      !(await this.userRepository.existsByUsername(Username.create(withYear)))
    ) {
      suggestions.push(withYear);
    }

    return suggestions.slice(0, 3); // Return top 3 suggestions
  }

  /**
   * Sanitize display name to create a valid username
   */
  private sanitizeDisplayNameForUsername(displayName: string): string {
    return displayName
      .toLowerCase()
      .replace(/[^a-z0-9]/g, '') // Remove special characters
      .substring(0, 20); // Limit length
  }

  /**
   * Check if user can update profile (rate limiting)
   */
  canUpdateProfile(user: User): boolean {
    if (!user.lastProfileUpdate) {
      return true;
    }

    // Allow profile update once per day
    const daysSinceLastUpdate =
      (Date.now() - user.lastProfileUpdate.getTime()) / (1000 * 60 * 60 * 24);
    return daysSinceLastUpdate >= 1;
  }
}

import {
  BusinessRuleException,
  EntityAlreadyExistsException,
  ValidationException,
} from '../../../shared/exceptions/domain.exception';

/**
 * Exception thrown when user tries to follow themselves
 */
export class CannotFollowSelfException extends BusinessRuleException {
  constructor() {
    super('User cannot follow themselves', 'CANNOT_FOLLOW_SELF');
  }
}

/**
 * Exception thrown when user tries to follow someone they already follow
 */
export class AlreadyFollowingUserException extends BusinessRuleException {
  constructor(followeeUsername: string) {
    super(
      `Already following user ${followeeUsername}`,
      'ALREADY_FOLLOWING_USER',
    );
  }
}

/**
 * Exception thrown when user tries to unfollow someone they don't follow
 */
export class NotFollowingUserException extends BusinessRuleException {
  constructor(followeeUsername: string) {
    super(`Not following user ${followeeUsername}`, 'NOT_FOLLOWING_USER');
  }
}

/**
 * Exception thrown when username is already taken
 */
export class UsernameAlreadyExistsException extends EntityAlreadyExistsException {
  constructor(username: string) {
    super('User', username);
  }
}

/**
 * Exception thrown when email is already registered
 */
export class EmailAlreadyExistsException extends EntityAlreadyExistsException {
  constructor(email: string) {
    super('User email', email);
  }
}

/**
 * Exception thrown when user account is not active
 */
export class UserAccountInactiveException extends BusinessRuleException {
  constructor() {
    super('User account is inactive', 'USER_ACCOUNT_INACTIVE');
  }
}

/**
 * Exception thrown when user email is not verified for actions requiring verification
 */
export class EmailNotVerifiedException extends BusinessRuleException {
  constructor() {
    super(
      'Email must be verified to perform this action',
      'EMAIL_NOT_VERIFIED',
    );
  }
}

/**
 * Exception thrown when user tries to update profile too frequently
 */
export class ProfileUpdateTooFrequentException extends BusinessRuleException {
  constructor(nextAllowedUpdate: Date) {
    super(
      `Profile can be updated again after ${nextAllowedUpdate.toISOString()}`,
      'PROFILE_UPDATE_TOO_FREQUENT',
    );
  }
}

import { ValueObject } from '../../../../shared/domain/value-object.base';
import { ValidationException } from '../../../../shared/exceptions/domain.exception';

/**
 * UserId Value Object
 * Ensures user ID validity and type safety
 */
export class UserId extends ValueObject<string> {
  constructor(value: string) {
    super(value);
  }

  protected validateInvariants(value: string): void {
    if (!value || value.trim().length === 0) {
      throw new ValidationException('User ID cannot be empty');
    }

    if (value.length > 36) {
      throw new ValidationException('User ID cannot exceed 36 characters');
    }

    // UUID format validation (optional - depends on your ID strategy)
    const uuidRegex =
      /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
    if (!uuidRegex.test(value)) {
      throw new ValidationException('User ID must be a valid UUID format');
    }
  }

  /**
   * Factory method to create UserId from string
   */
  static create(value: string): UserId {
    return new UserId(value);
  }

  /**
   * Generate new random UserId
   */
  static generate(): UserId {
    const { randomUUID } = require('crypto');
    return new UserId(randomUUID());
  }

  protected isEqual(other: ValueObject<string>): boolean {
    return this.getValue() === other.getValue();
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../../database/prisma.service';
import {
  IUserRepository,
  User,
  UserFactory,
  UserId,
  UserEmail,
  Username,
} from '../domain';

/**
 * Prisma implementation of UserRepository
 * Updated to work with new schema structure
 */
@Injectable()
export class UserPrismaRepository implements IUserRepository {
  constructor(private readonly prisma: PrismaService) {}

  async save(user: User): Promise<void> {
    const userData = this.mapToDataModel(user);

    await this.prisma.user.upsert({
      where: { id: user.id },
      update: userData,
      create: userData,
    });
  }

  async findById(id: UserId): Promise<User | null> {
    const userData = await this.prisma.user.findUnique({
      where: { id: id.getValue() },
      include: {
        followers: { select: { followerId: true } },
        following: { select: { followingId: true } },
      },
    });

    if (!userData) {
      return null;
    }

    return this.mapToDomainModel(userData);
  }

  async findByEmail(email: UserEmail): Promise<User | null> {
    const userData = await this.prisma.user.findUnique({
      where: { email: email.getValue() },
      include: {
        followers: { select: { followerId: true } },
        following: { select: { followingId: true } },
      },
    });

    if (!userData) {
      return null;
    }

    return this.mapToDomainModel(userData);
  }

  async findByUsername(username: Username): Promise<User | null> {
    const userData = await this.prisma.user.findUnique({
      where: { username: username.getValue() }, // Updated to use 'userName'
      include: {
        followers: { select: { followerId: true } },
        following: { select: { followingId: true } },
      },
    });

    if (!userData) {
      return null;
    }

    return this.mapToDomainModel(userData);
  }

  async findByGoogleId(googleId: string): Promise<User | null> {
    const userData = await this.prisma.user.findUnique({
      where: { googleId },
      include: {
        followers: { select: { followerId: true } },
        following: { select: { followingId: true } },
      },
    });

    if (!userData) {
      return null;
    }

    return this.mapToDomainModel(userData);
  }

  async existsByEmail(email: UserEmail): Promise<boolean> {
    const count = await this.prisma.user.count({
      where: { email: email.getValue() },
    });
    return count > 0;
  }

  async existsByUsername(username: Username): Promise<boolean> {
    const count = await this.prisma.user.count({
      where: { username: username.getValue() }, // Updated to use 'username'
    });
    return count > 0;
  }

  async deleteById(id: UserId): Promise<void> {
    await this.prisma.user.delete({
      where: { id: id.getValue() },
    });
  }

  async findByIds(ids: UserId[]): Promise<User[]> {
    const userIds = ids.map((id) => id.getValue());
    const usersData = await this.prisma.user.findMany({
      where: { id: { in: userIds } },
      include: {
        followers: { select: { followerId: true } },
        following: { select: { followingId: true } },
      },
    });

    return usersData.map((userData) => this.mapToDomainModel(userData));
  }

  async searchUsers(
    query: string,
    limit = 20,
    offset = 0,
  ): Promise<{
    users: User[];
    total: number;
  }> {
    const whereCondition = {
      OR: [
        { username: { contains: query, mode: 'insensitive' as const } }, // Updated field name
        { fullName: { contains: query, mode: 'insensitive' as const } }, // Direct field access
      ],
    };

    const [usersData, total] = await Promise.all([
      this.prisma.user.findMany({
        where: whereCondition,
        include: {
          followers: { select: { followerId: true } },
          following: { select: { followingId: true } },
        },
        skip: offset,
        take: limit,
        orderBy: { createdAt: 'desc' },
      }),
      this.prisma.user.count({ where: whereCondition }),
    ]);

    return {
      users: usersData.map((userData) => this.mapToDomainModel(userData)),
      total,
    };
  }

  async getFollowers(
    userId: UserId,
    limit = 20,
    offset = 0,
  ): Promise<{
    users: User[];
    total: number;
  }> {
    const [followData, total] = await Promise.all([
      this.prisma.follow.findMany({
        where: { followingId: userId.getValue() },
        include: {
          follower: {
            include: {
              followers: { select: { followerId: true } },
              following: { select: { followingId: true } },
            },
          },
        },
        skip: offset,
        take: limit,
        orderBy: { createdAt: 'desc' },
      }),
      this.prisma.follow.count({
        where: { followingId: userId.getValue() },
      }),
    ]);

    return {
      users: followData.map((follow) => this.mapToDomainModel(follow.follower)),
      total,
    };
  }

  async getFollowing(
    userId: UserId,
    limit = 20,
    offset = 0,
  ): Promise<{
    users: User[];
    total: number;
  }> {
    const [followData, total] = await Promise.all([
      this.prisma.follow.findMany({
        where: { followerId: userId.getValue() },
        include: {
          following: {
            include: {
              followers: { select: { followerId: true } },
              following: { select: { followingId: true } },
            },
          },
        },
        skip: offset,
        take: limit,
        orderBy: { createdAt: 'desc' },
      }),
      this.prisma.follow.count({
        where: { followerId: userId.getValue() },
      }),
    ]);

    return {
      users: followData.map((follow) =>
        this.mapToDomainModel(follow.following),
      ),
      total,
    };
  }

  async getMutualFollowers(userId1: UserId, userId2: UserId): Promise<User[]> {
    // Get users who follow both user1 and user2
    const mutualFollowsData = await this.prisma.user.findMany({
      where: {
        following: {
          some: {
            followingId: {
              in: [userId1.getValue(), userId2.getValue()],
            },
          },
        },
      },
      include: {
        followers: { select: { followerId: true } },
        following: { select: { followingId: true } },
      },
    });

    return mutualFollowsData.map((userData) => this.mapToDomainModel(userData));
  }

  async getUserStats(userId: UserId): Promise<{
    followersCount: number;
    followingCount: number;
    postsCount: number;
  }> {
    const [followersCount, followingCount, postsCount] = await Promise.all([
      this.prisma.follow.count({
        where: { followingId: userId.getValue() },
      }),
      this.prisma.follow.count({
        where: { followerId: userId.getValue() },
      }),
      this.prisma.post.count({
        where: { authorId: userId.getValue() },
      }),
    ]);

    return {
      followersCount,
      followingCount,
      postsCount,
    };
  }

  async updateFollowRelationship(
    followerId: UserId,
    followeeId: UserId,
    isFollowing: boolean,
  ): Promise<void> {
    if (isFollowing) {
      await this.prisma.follow.upsert({
        where: {
          followerId_followingId: {
            followerId: followerId.getValue(),
            followingId: followeeId.getValue(),
          },
        },
        update: {},
        create: {
          followerId: followerId.getValue(),
          followingId: followeeId.getValue(),
        },
      });
    } else {
      await this.prisma.follow.deleteMany({
        where: {
          followerId: followerId.getValue(),
          followingId: followeeId.getValue(),
        },
      });
    }
  }

  private mapToDomainModel(userData: any): User {
    return UserFactory.reconstructUser({
      id: userData.id,
      username: userData.username, // Updated field name
      email: userData.email,
      passwordHash: userData.passwordHash, // Updated field name
      googleId: userData.googleId,
      role: userData.role,
      status: userData.status, // New field
      isEmailVerified: userData.isEmailVerified,
      emailVerifiedAt: userData.emailVerifiedAt,
      createdAt: userData.createdAt,
      updatedAt: userData.updatedAt,
      lastProfileUpdate: userData.lastProfileUpdate, // New field
      profile: {
        fullName: userData.fullName,
        bio: userData.bio,
        avatar: userData.avatar,
        location: userData.location,
        websiteUrl: userData.websiteUrl,
        dateOfBirth: userData.dateOfBirth,
        phoneNumber: userData.phoneNumber,
        gender: userData.gender,
      },
      followingIds: userData.following?.map((f: any) => f.followingId) || [],
      followerIds: userData.followers?.map((f: any) => f.followerId) || [],
    });
  }

  private mapToDataModel(user: User): any {
    return {
      id: user.id,
      username: user.username, // Updated field name
      email: user.email,
      passwordHash: user.passwordHash, // Need to add getter in User entity
      googleId: user.googleId,
      role: user.role,
      status: user.status, // New field
      isEmailVerified: user.isEmailVerified,
      emailVerifiedAt: user.emailVerifiedAt,
      updatedAt: user.updatedAt,
      lastProfileUpdate: user.lastProfileUpdate,
      // Profile fields are now direct on User model
      fullName: user.profile.fullName,
      bio: user.profile.bio,
      avatar: user.profile.avatar,
      location: user.profile.location,
      websiteUrl: user.profile.websiteUrl,
      dateOfBirth: user.profile.dateOfBirth,
      phoneNumber: user.profile.phoneNumber,
      gender: user.profile.gender,
    };
  }
}

