import { DomainEvent } from './domain-event.base';

/**
 * Base Entity class for all domain entities
 * Provides common functionality for entity lifecycle and domain events
 */
export abstract class Entity<T> {
  protected readonly _id: T;
  private _domainEvents: DomainEvent[] = [];

  constructor(id: T) {
    this._id = id;
  }

  get id(): T {
    return this._id;
  }

  get domainEvents(): DomainEvent[] {
    return this._domainEvents;
  }

  /**
   * Add a domain event to be published later
   */
  protected addDomainEvent(domainEvent: DomainEvent): void {
    this._domainEvents.push(domainEvent);
  }

  /**
   * Clear all domain events
   */
  public clearEvents(): void {
    this._domainEvents = [];
  }

  /**
   * Mark events as committed (called after successful persistence)
   */
  public markEventsAsCommitted(): void {
    this._domainEvents.forEach((event) => event.markAsCommitted());
  }

  /**
   * Check if entity is equal to another entity
   */
  public equals(object?: Entity<T>): boolean {
    if (object === null || object === undefined) {
      return false;
    }

    if (this === object) {
      return true;
    }

    return this._id === object._id;
  }

  /**
   * Get string representation of entity
   */
  public toString(): string {
    return `${this.constructor.name}(${this._id})`;
  }
}

import {
  ValidationException,
  BusinessRuleException,
  EntityNotFoundException,
  EntityAlreadyExistsException,
  UnauthorizedException,
  ForbiddenException,
  DatabaseException,
  ExternalServiceException,
  FileOperationException,
  RateLimitException,
  ConfigurationException,
} from './domain.exception';

/**
 * Utility class for creating domain exceptions with proper context
 * This provides a fluent interface for exception creation
 */
export class ErrorFactory {
  /**
   * Create a validation exception
   */
  static validation(
    message: string,
    errors: Record<string, string[]> = {},
  ): ValidationException {
    return new ValidationException(message, errors);
  }

  /**
   * Create a business rule exception
   */
  static businessRule(message: string, code?: string): BusinessRuleException {
    return new BusinessRuleException(message, code);
  }

  /**
   * Create an entity not found exception
   */
  static entityNotFound(
    entityName: string,
    identifier: string | number,
  ): EntityNotFoundException {
    return new EntityNotFoundException(entityName, identifier);
  }

  /**
   * Create an entity already exists exception
   */
  static entityAlreadyExists(
    entityName: string,
    identifier: string | number,
  ): EntityAlreadyExistsException {
    return new EntityAlreadyExistsException(entityName, identifier);
  }

  /**
   * Create an unauthorized exception
   */
  static unauthorized(message?: string, code?: string): UnauthorizedException {
    return new UnauthorizedException(message, code);
  }

  /**
   * Create a forbidden exception
   */
  static forbidden(message?: string, code?: string): ForbiddenException {
    return new ForbiddenException(message, code);
  }

  /**
   * Create a database exception
   */
  static database(
    operation: string,
    message: string,
    table?: string,
    code?: string,
  ): DatabaseException {
    return new DatabaseException(operation, message, table, code);
  }

  /**
   * Create an external service exception
   */
  static externalService(
    service: string,
    message: string,
    operation?: string,
    code?: string,
  ): ExternalServiceException {
    return new ExternalServiceException(service, message, operation, code);
  }

  /**
   * Create a file operation exception
   */
  static fileOperation(
    operation: string,
    message: string,
    fileName?: string,
    fileSize?: number,
    code?: string,
  ): FileOperationException {
    return new FileOperationException(
      operation,
      message,
      fileName,
      fileSize,
      code,
    );
  }

  /**
   * Create a rate limit exception
   */
  static rateLimit(
    limit: number,
    windowMs: number,
    resetTime?: Date,
    message?: string,
    code?: string,
  ): RateLimitException {
    return new RateLimitException(limit, windowMs, resetTime, message, code);
  }

  /**
   * Create a configuration exception
   */
  static configuration(
    configKey: string,
    message: string,
    code?: string,
  ): ConfigurationException {
    return new ConfigurationException(configKey, message, code);
  }
}

/**
 * Utility class for common error scenarios in domain services
 */
export class DomainErrors {
  /**
   * User not found error
   */
  static userNotFound(userId: string): EntityNotFoundException {
    return ErrorFactory.entityNotFound('User', userId);
  }

  /**
   * Post not found error
   */
  static postNotFound(postId: string): EntityNotFoundException {
    return ErrorFactory.entityNotFound('Post', postId);
  }

  /**
   * Comment not found error
   */
  static commentNotFound(commentId: string): EntityNotFoundException {
    return ErrorFactory.entityNotFound('Comment', commentId);
  }

  /**
   * User already exists error
   */
  static userAlreadyExists(email: string): EntityAlreadyExistsException {
    return ErrorFactory.entityAlreadyExists('User', email);
  }

  /**
   * Invalid credentials error
   */
  static invalidCredentials(): UnauthorizedException {
    return ErrorFactory.unauthorized(
      'Invalid credentials',
      'INVALID_CREDENTIALS',
    );
  }

  /**
   * Access token expired error
   */
  static accessTokenExpired(): UnauthorizedException {
    return ErrorFactory.unauthorized(
      'Access token has expired',
      'ACCESS_TOKEN_EXPIRED',
    );
  }

  /**
   * Insufficient permissions error
   */
  static insufficientPermissions(action: string): ForbiddenException {
    return ErrorFactory.forbidden(
      `You don't have permission to ${action}`,
      'INSUFFICIENT_PERMISSIONS',
    );
  }

  /**
   * Invalid file type error
   */
  static invalidFileType(allowedTypes: string[]): FileOperationException {
    return ErrorFactory.fileOperation(
      'upload',
      `Invalid file type. Allowed types: ${allowedTypes.join(', ')}`,
      undefined,
      undefined,
      'INVALID_FILE_TYPE',
    );
  }

  /**
   * File too large error
   */
  static fileTooLarge(
    maxSize: number,
    actualSize: number,
    fileName?: string,
  ): FileOperationException {
    return ErrorFactory.fileOperation(
      'upload',
      `File is too large. Maximum size: ${maxSize} bytes, actual size: ${actualSize} bytes`,
      fileName,
      actualSize,
      'FILE_TOO_LARGE',
    );
  }

  /**
   * External service unavailable error
   */
  static externalServiceUnavailable(
    serviceName: string,
  ): ExternalServiceException {
    return ErrorFactory.externalService(
      serviceName,
      `${serviceName} service is currently unavailable`,
      'connection',
      'SERVICE_UNAVAILABLE',
    );
  }

  /**
   * Rate limit exceeded error
   */
  static rateLimitExceeded(
    limit: number,
    windowMs: number,
  ): RateLimitException {
    const resetTime = new Date(Date.now() + windowMs);
    return ErrorFactory.rateLimit(
      limit,
      windowMs,
      resetTime,
      `Rate limit exceeded. Maximum ${limit} requests per ${windowMs / 1000} seconds`,
    );
  }

  /**
   * Content validation errors
   */
  static contentTooLong(field: string, maxLength: number): ValidationException {
    return ErrorFactory.validation(`${field} is too long`, {
      [field]: [`Maximum length is ${maxLength} characters`],
    });
  }

  static contentEmpty(field: string): ValidationException {
    return ErrorFactory.validation(`${field} cannot be empty`, {
      [field]: ['This field is required'],
    });
  }

  static invalidFormat(field: string, format: string): ValidationException {
    return ErrorFactory.validation(`Invalid ${field} format`, {
      [field]: [`Must be a valid ${format}`],
    });
  }

  /**
   * Business rule violations
   */
  static cannotPerformAction(
    action: string,
    reason: string,
  ): BusinessRuleException {
    return ErrorFactory.businessRule(
      `Cannot ${action}: ${reason}`,
      'ACTION_NOT_ALLOWED',
    );
  }

  static resourceInUse(
    resourceType: string,
    resourceId: string,
  ): BusinessRuleException {
    return ErrorFactory.businessRule(
      `${resourceType} ${resourceId} is currently in use and cannot be modified`,
      'RESOURCE_IN_USE',
    );
  }

  static dependencyNotMet(dependency: string): BusinessRuleException {
    return ErrorFactory.businessRule(
      `Required dependency not met: ${dependency}`,
      'DEPENDENCY_NOT_MET',
    );
  }
}

/**
 * Utility functions for error handling in application services
 */
export class ErrorUtils {
  /**
   * Assert that a condition is true, throw exception if false
   */
  static assert(condition: boolean, exception: Error): void {
    if (!condition) {
      throw exception;
    }
  }

  /**
   * Assert that a value is not null or undefined
   */
  static assertExists<T>(value: T | null | undefined, exception: Error): T {
    if (value == null) {
      throw exception;
    }
    return value;
  }

  /**
   * Handle async operations with proper error context
   */
  static async handleAsync<T>(
    operation: () => Promise<T>,
    errorContext: { operation: string; resource?: string },
  ): Promise<T> {
    try {
      return await operation();
    } catch (error) {
      if (error instanceof Error) {
        // Re-throw domain exceptions as-is
        if (error.name.includes('Exception')) {
          throw error;
        }

        // Wrap unknown errors as database exceptions
        throw ErrorFactory.database(
          errorContext.operation,
          `Failed to ${errorContext.operation}${errorContext.resource ? ` ${errorContext.resource}` : ''}: ${error.message}`,
        );
      }

      throw ErrorFactory.database(
        errorContext.operation,
        `Unknown error during ${errorContext.operation}`,
      );
    }
  }

  /**
   * Safely parse JSON with error handling
   */
  static safeJsonParse<T>(json: string, defaultValue: T): T {
    try {
      return JSON.parse(json);
    } catch (error) {
      return defaultValue;
    }
  }

  /**
   * Validate array length
   */
  static validateArrayLength(
    array: any[],
    minLength: number,
    maxLength: number,
    fieldName: string,
  ): void {
    if (array.length < minLength) {
      throw ErrorFactory.validation(
        `${fieldName} must have at least ${minLength} items`,
        { [fieldName]: [`Minimum ${minLength} items required`] },
      );
    }

    if (array.length > maxLength) {
      throw ErrorFactory.validation(
        `${fieldName} can have at most ${maxLength} items`,
        { [fieldName]: [`Maximum ${maxLength} items allowed`] },
      );
    }
  }

  /**
   * Validate string length
   */
  static validateStringLength(
    value: string,
    minLength: number,
    maxLength: number,
    fieldName: string,
  ): void {
    if (value.length < minLength) {
      throw ErrorFactory.validation(`${fieldName} is too short`, {
        [fieldName]: [`Minimum length is ${minLength} characters`],
      });
    }

    if (value.length > maxLength) {
      throw ErrorFactory.validation(`${fieldName} is too long`, {
        [fieldName]: [`Maximum length is ${maxLength} characters`],
      });
    }
  }

  /**
   * Validate email format
   */
  static validateEmail(email: string): void {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      throw DomainErrors.invalidFormat('email', 'email address');
    }
  }

  /**
   * Validate URL format
   */
  static validateUrl(url: string, fieldName: string = 'url'): void {
    try {
      new URL(url);
    } catch {
      throw DomainErrors.invalidFormat(fieldName, 'URL');
    }
  }
}

import {
  ExceptionFilter,
  Catch,
  ArgumentsHost,
  HttpException,
  HttpStatus,
  Logger,
  Injectable,
} from '@nestjs/common';
import { ErrorMonitoringService } from '../services/error-monitoring.service';
import { Request, Response } from 'express';
import { Prisma } from '@prisma/client';
import { MulterError } from 'multer';
import { JsonWebTokenError, TokenExpiredError } from 'jsonwebtoken';
import {
  DomainException,
  ValidationException,
  EntityNotFoundException,
  UnauthorizedException,
  ForbiddenException,
  BusinessRuleException,
  DatabaseException,
  ExternalServiceException,
  RateLimitException,
  ConfigurationException,
  FileOperationException,
} from '../exceptions/domain.exception';
import {
  IErrorResponse,
  IErrorInfo,
  ErrorSeverity,
  ErrorCategory,
  IErrorContext,
} from '../exceptions/error-response.interface';

@Injectable()
@Catch()
export class GlobalExceptionFilter implements ExceptionFilter {
  private readonly logger = new Logger(GlobalExceptionFilter.name);

  constructor(
    private readonly errorMonitoringService: ErrorMonitoringService,
  ) {}

  catch(exception: unknown, host: ArgumentsHost): void {
    const ctx = host.switchToHttp();
    const response = ctx.getResponse<Response>();
    const request = ctx.getRequest<Request>();

    const errorInfo = this.buildErrorInfo(exception, request);

    // Log error based on severity
    this.logError(errorInfo);

    // Send response
    response.status(errorInfo.response.statusCode).json(errorInfo.response);
  }

  private buildErrorInfo(exception: unknown, request: Request): IErrorInfo {
    const context = this.extractContext(request);
    const requestId = this.generateRequestId();

    let errorResponse: IErrorResponse;
    let severity: ErrorSeverity;
    let category: ErrorCategory;
    let shouldReport = true;

    if (exception instanceof DomainException) {
      ({ errorResponse, severity, category } = this.handleDomainException(
        exception,
        request,
        requestId,
      ));
      shouldReport =
        severity === ErrorSeverity.HIGH || severity === ErrorSeverity.CRITICAL;
    } else if (exception instanceof HttpException) {
      ({ errorResponse, severity, category } = this.handleHttpException(
        exception,
        request,
        requestId,
      ));
      shouldReport = exception.getStatus() >= 500;
    } else if (exception instanceof MulterError) {
      ({ errorResponse, severity, category } = this.handleMulterError(
        exception,
        request,
        requestId,
      ));
      shouldReport = false;
    } else if (exception instanceof Prisma.PrismaClientKnownRequestError) {
      ({ errorResponse, severity, category } = this.handlePrismaError(
        exception,
        request,
        requestId,
      ));
      shouldReport = true;
    } else if (
      exception instanceof JsonWebTokenError ||
      exception instanceof TokenExpiredError
    ) {
      ({ errorResponse, severity, category } = this.handleJwtError(
        exception,
        request,
        requestId,
      ));
      shouldReport = false;
    } else {
      ({ errorResponse, severity, category } = this.handleUnknownError(
        exception,
        request,
        requestId,
      ));
      shouldReport = true;
    }

    return {
      response: errorResponse,
      severity,
      category,
      context,
      originalError:
        exception instanceof Error ? exception : new Error(String(exception)),
      shouldReport,
    };
  }

  private handleDomainException(
    exception: DomainException,
    request: Request,
    requestId: string,
  ): {
    errorResponse: IErrorResponse;
    severity: ErrorSeverity;
    category: ErrorCategory;
  } {
    let category: ErrorCategory;
    let severity: ErrorSeverity;

    // Determine category and severity based on exception type
    if (exception instanceof ValidationException) {
      category = ErrorCategory.VALIDATION;
      severity = ErrorSeverity.LOW;
    } else if (exception instanceof EntityNotFoundException) {
      category = ErrorCategory.BUSINESS_LOGIC;
      severity = ErrorSeverity.LOW;
    } else if (exception instanceof UnauthorizedException) {
      category = ErrorCategory.AUTHENTICATION;
      severity = ErrorSeverity.MEDIUM;
    } else if (exception instanceof ForbiddenException) {
      category = ErrorCategory.AUTHORIZATION;
      severity = ErrorSeverity.MEDIUM;
    } else if (exception instanceof BusinessRuleException) {
      category = ErrorCategory.BUSINESS_LOGIC;
      severity = ErrorSeverity.MEDIUM;
    } else if (exception instanceof DatabaseException) {
      category = ErrorCategory.DATABASE;
      severity = ErrorSeverity.HIGH;
    } else if (exception instanceof ExternalServiceException) {
      category = ErrorCategory.EXTERNAL_SERVICE;
      severity = ErrorSeverity.HIGH;
    } else if (exception instanceof RateLimitException) {
      category = ErrorCategory.RATE_LIMIT;
      severity = ErrorSeverity.LOW;
    } else if (exception instanceof ConfigurationException) {
      category = ErrorCategory.CONFIGURATION;
      severity = ErrorSeverity.CRITICAL;
    } else if (exception instanceof FileOperationException) {
      category = ErrorCategory.FILE_OPERATION;
      severity = ErrorSeverity.MEDIUM;
    } else {
      category = ErrorCategory.BUSINESS_LOGIC;
      severity = ErrorSeverity.MEDIUM;
    }

    const errorResponse: IErrorResponse = {
      statusCode: exception.statusCode,
      message: exception.message,
      code: exception.code,
      error: exception.constructor.name,
      timestamp: new Date().toISOString(),
      path: request.url,
      requestId,
    };

    // Add validation errors if present
    if (
      exception instanceof ValidationException &&
      exception.validationErrors
    ) {
      errorResponse.validationErrors = exception.validationErrors;
    }

    // Add debug info in development
    if (process.env.NODE_ENV === 'development') {
      errorResponse.details = exception.getDetails();
      errorResponse.stack = exception.stack;
    }

    return { errorResponse, severity, category };
  }

  private handleHttpException(
    exception: HttpException,
    request: Request,
    requestId: string,
  ): {
    errorResponse: IErrorResponse;
    severity: ErrorSeverity;
    category: ErrorCategory;
  } {
    const status = exception.getStatus();
    const response = exception.getResponse();

    let category: ErrorCategory;
    let severity: ErrorSeverity;

    if (status >= 500) {
      category = ErrorCategory.SYSTEM;
      severity = ErrorSeverity.HIGH;
    } else if (status === 401) {
      category = ErrorCategory.AUTHENTICATION;
      severity = ErrorSeverity.MEDIUM;
    } else if (status === 403) {
      category = ErrorCategory.AUTHORIZATION;
      severity = ErrorSeverity.MEDIUM;
    } else if (status === 429) {
      category = ErrorCategory.RATE_LIMIT;
      severity = ErrorSeverity.LOW;
    } else {
      category = ErrorCategory.VALIDATION;
      severity = ErrorSeverity.LOW;
    }

    const errorResponse: IErrorResponse = {
      statusCode: status,
      message:
        typeof response === 'string'
          ? response
          : (response as any)?.message || exception.message,
      code: this.getHttpErrorCode(status),
      error: exception.constructor.name,
      timestamp: new Date().toISOString(),
      path: request.url,
      requestId,
    };

    if (process.env.NODE_ENV === 'development') {
      errorResponse.stack = exception.stack;
    }

    return { errorResponse, severity, category };
  }

  private handleMulterError(
    exception: MulterError,
    request: Request,
    requestId: string,
  ): {
    errorResponse: IErrorResponse;
    severity: ErrorSeverity;
    category: ErrorCategory;
  } {
    let message: string;
    let statusCode: number;

    switch (exception.code) {
      case 'LIMIT_FILE_SIZE':
        message = 'File too large. Max allowed size is 50 MB.';
        statusCode = HttpStatus.PAYLOAD_TOO_LARGE;
        break;
      case 'LIMIT_UNEXPECTED_FILE':
        message = 'Unexpected file field.';
        statusCode = HttpStatus.BAD_REQUEST;
        break;
      case 'LIMIT_FILE_COUNT':
        message = 'Too many files uploaded.';
        statusCode = HttpStatus.BAD_REQUEST;
        break;
      default:
        message = exception.message || 'File upload error';
        statusCode = HttpStatus.BAD_REQUEST;
    }

    const errorResponse: IErrorResponse = {
      statusCode,
      message,
      code: `MULTER_${exception.code}`,
      error: 'MulterError',
      timestamp: new Date().toISOString(),
      path: request.url,
      requestId,
    };

    return {
      errorResponse,
      severity: ErrorSeverity.LOW,
      category: ErrorCategory.FILE_OPERATION,
    };
  }

  private handlePrismaError(
    exception: Prisma.PrismaClientKnownRequestError,
    request: Request,
    requestId: string,
  ): {
    errorResponse: IErrorResponse;
    severity: ErrorSeverity;
    category: ErrorCategory;
  } {
    let message: string;
    let statusCode: number;
    let code: string;

    switch (exception.code) {
      case 'P2002':
        message = 'A record with this value already exists';
        statusCode = HttpStatus.CONFLICT;
        code = 'UNIQUE_CONSTRAINT_VIOLATION';
        break;
      case 'P2025':
        message = 'Record not found';
        statusCode = HttpStatus.NOT_FOUND;
        code = 'RECORD_NOT_FOUND';
        break;
      case 'P2003':
        message = 'Foreign key constraint failed';
        statusCode = HttpStatus.BAD_REQUEST;
        code = 'FOREIGN_KEY_CONSTRAINT';
        break;
      case 'P2014':
        message =
          'The change you are trying to make would violate the required relation';
        statusCode = HttpStatus.BAD_REQUEST;
        code = 'REQUIRED_RELATION_VIOLATION';
        break;
      default:
        message = 'Database operation failed';
        statusCode = HttpStatus.INTERNAL_SERVER_ERROR;
        code = 'DATABASE_ERROR';
    }

    const errorResponse: IErrorResponse = {
      statusCode,
      message,
      code,
      error: 'DatabaseError',
      timestamp: new Date().toISOString(),
      path: request.url,
      requestId,
    };

    if (process.env.NODE_ENV === 'development') {
      errorResponse.details = {
        prismaCode: exception.code,
        prismaMessage: exception.message,
      };
    }

    return {
      errorResponse,
      severity: ErrorSeverity.HIGH,
      category: ErrorCategory.DATABASE,
    };
  }

  private handleJwtError(
    exception: JsonWebTokenError | TokenExpiredError,
    request: Request,
    requestId: string,
  ): {
    errorResponse: IErrorResponse;
    severity: ErrorSeverity;
    category: ErrorCategory;
  } {
    let message: string;
    let code: string;

    if (exception instanceof TokenExpiredError) {
      message = 'Token has expired';
      code = 'TOKEN_EXPIRED';
    } else {
      message = 'Invalid token';
      code = 'INVALID_TOKEN';
    }

    const errorResponse: IErrorResponse = {
      statusCode: HttpStatus.UNAUTHORIZED,
      message,
      code,
      error: 'AuthenticationError',
      timestamp: new Date().toISOString(),
      path: request.url,
      requestId,
    };

    return {
      errorResponse,
      severity: ErrorSeverity.MEDIUM,
      category: ErrorCategory.AUTHENTICATION,
    };
  }

  private handleUnknownError(
    exception: unknown,
    request: Request,
    requestId: string,
  ): {
    errorResponse: IErrorResponse;
    severity: ErrorSeverity;
    category: ErrorCategory;
  } {
    const error =
      exception instanceof Error ? exception : new Error(String(exception));

    const errorResponse: IErrorResponse = {
      statusCode: HttpStatus.INTERNAL_SERVER_ERROR,
      message: 'An unexpected error occurred',
      code: 'INTERNAL_SERVER_ERROR',
      error: 'InternalServerError',
      timestamp: new Date().toISOString(),
      path: request.url,
      requestId,
    };

    if (process.env.NODE_ENV === 'development') {
      errorResponse.message = error.message;
      errorResponse.stack = error.stack;
    }

    return {
      errorResponse,
      severity: ErrorSeverity.CRITICAL,
      category: ErrorCategory.SYSTEM,
    };
  }

  private extractContext(request: Request): IErrorContext {
    return {
      userId: (request as any).user?.id || (request as any).user?.sub,
      ipAddress: request.ip || request.connection.remoteAddress,
      userAgent: request.get('user-agent'),
      method: request.method,
      url: request.url,
      // Don't log sensitive data like passwords
      requestBody: this.sanitizeRequestBody(request.body),
      headers: this.sanitizeHeaders(request.headers),
    };
  }

  private sanitizeRequestBody(body: any): any {
    if (!body) return undefined;

    const sanitized = { ...body };
    const sensitiveFields = ['password', 'token', 'secret', 'key', 'auth'];

    for (const field of sensitiveFields) {
      if (sanitized[field]) {
        sanitized[field] = '[REDACTED]';
      }
    }

    return sanitized;
  }

  private sanitizeHeaders(headers: any): Record<string, string> {
    const sanitized = { ...headers };
    const sensitiveHeaders = ['authorization', 'cookie', 'x-api-key'];

    for (const header of sensitiveHeaders) {
      if (sanitized[header]) {
        sanitized[header] = '[REDACTED]';
      }
    }

    return sanitized;
  }

  private getHttpErrorCode(status: number): string {
    const codes: Record<number, string> = {
      400: 'BAD_REQUEST',
      401: 'UNAUTHORIZED',
      403: 'FORBIDDEN',
      404: 'NOT_FOUND',
      409: 'CONFLICT',
      422: 'UNPROCESSABLE_ENTITY',
      429: 'TOO_MANY_REQUESTS',
      500: 'INTERNAL_SERVER_ERROR',
      502: 'BAD_GATEWAY',
      503: 'SERVICE_UNAVAILABLE',
    };

    return codes[status] || 'UNKNOWN_ERROR';
  }

  private generateRequestId(): string {
    return `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private logError(errorInfo: IErrorInfo): void {
    const { response, severity, category, context, originalError } = errorInfo;

    const logMessage = `[${category.toUpperCase()}] ${response.message}`;
    const logContext = {
      requestId: response.requestId,
      statusCode: response.statusCode,
      code: response.code,
      path: response.path,
      userId: context.userId,
      ip: context.ipAddress,
      userAgent: context.userAgent,
      method: context.method,
    };

    switch (severity) {
      case ErrorSeverity.LOW:
        this.logger.warn(logMessage, logContext);
        break;
      case ErrorSeverity.MEDIUM:
        this.logger.error(logMessage, logContext);
        break;
      case ErrorSeverity.HIGH:
      case ErrorSeverity.CRITICAL:
        this.logger.error(logMessage, {
          ...logContext,
          stack: originalError?.stack,
          originalError: originalError?.message,
        });
        break;
    }

    // Send to monitoring service for reportable errors
    if (errorInfo.shouldReport) {
      // Fire and forget - don't wait for monitoring to complete
      this.reportToMonitoringService(errorInfo).catch(() => {
        // Error already logged in reportToMonitoringService
      });
    }
  }

  private async reportToMonitoringService(
    errorInfo: IErrorInfo,
  ): Promise<void> {
    try {
      await this.errorMonitoringService.reportError(errorInfo);
    } catch (error) {
      // Don't let monitoring errors affect the main response
      this.logger.error('Failed to report error to monitoring service', {
        requestId: errorInfo.response.requestId,
        monitoringError: error.message,
      });
    }
  }
}

import {
  ExceptionFilter,
  Catch,
  ArgumentsHost,
  HttpStatus,
} from '@nestjs/common';
import { MulterError } from 'multer';

@Catch()
export class MulterExceptionFilter implements ExceptionFilter {
  catch(exception: any, host: ArgumentsHost) {
    const ctx = host.switchToHttp();
    const response = ctx.getResponse();
    const request = ctx.getRequest();

    // Detect Multer errors thrown by the multer middleware
    if (exception instanceof MulterError || exception?.name === 'MulterError') {
      const code = exception.code;
      // Customize messages per Multer error code
      if (code === 'LIMIT_FILE_SIZE') {
        const message = 'File too large. Max allowed size is 50 MB.';
        return response.status(HttpStatus.PAYLOAD_TOO_LARGE).json({
          statusCode: HttpStatus.PAYLOAD_TOO_LARGE,
          message,
          error: 'PayloadTooLarge',
        });
      }

      if (code === 'LIMIT_UNEXPECTED_FILE') {
        const message = 'Unexpected file field.';
        return response.status(HttpStatus.BAD_REQUEST).json({
          statusCode: HttpStatus.BAD_REQUEST,
          message,
          error: 'BadRequest',
        });
      }

      // Generic multer error fallback
      const fallbackMessage = exception.message || 'File upload error';
      return response.status(HttpStatus.BAD_REQUEST).json({
        statusCode: HttpStatus.BAD_REQUEST,
        message: fallbackMessage,
        error: 'BadRequest',
      });
    }

    // Not a Multer error  rethrow to let other filters/handlers deal with it
    throw exception;
  }
}

import {
  Injectable,
  ExecutionContext,
  UnauthorizedException,
} from '@nestjs/common';
import { Reflector } from '@nestjs/core';
import { AuthGuard } from '@nestjs/passport';
import { JWT } from 'src/config/jwt.config';
import { SKIP_GUARDS_KEY } from 'src/shared/decorators/skipGuard.decorator';

@Injectable()
export class JwtAuthGuard extends AuthGuard('jwt') {
  constructor(private reflector: Reflector) {
    super();
  }

  canActivate(context: ExecutionContext) {
    const skipGuards = this.reflector.getAllAndOverride<boolean>(
      SKIP_GUARDS_KEY,
      [context.getHandler(), context.getClass()],
    );

    if (skipGuards) {
      return true;
    }

    const request = context.switchToHttp().getRequest();
    const authHeader = request.headers.authorization;
    const clientType = (
      request.headers['x-client-type'] ||
      request.query.client ||
      ''
    ).toString();
    const isWeb = clientType.toLowerCase() === 'web';

    // For web clients, check for token in cookie if no Authorization header
    if (isWeb && (!authHeader || !authHeader.startsWith('Bearer '))) {
      const accessTokenCookie = request.cookies?.['access_token'];
      if (!accessTokenCookie) {
        throw new UnauthorizedException(
          'Access token not found in cookies or Authorization header',
        );
      }
    } else if (!isWeb && (!authHeader || !authHeader.startsWith('Bearer '))) {
      // For non-web clients, require Authorization header
      throw new UnauthorizedException(
        'Authorization header missing or invalid',
      );
    }

    return super.canActivate(context);
  }

  handleRequest(err: any, user: any, info: any) {
    if (err || !user) {
      throw err || new UnauthorizedException('JWT validation failed');
    }
    return user;
  }
}

import { Injectable, CanActivate, ExecutionContext } from '@nestjs/common';
import { Reflector } from '@nestjs/core';

import { ROLES_KEY } from '../decorators/roles.decorator';
import { ROLES } from '../constants/roles.constant';

@Injectable()
export class RolesGuard implements CanActivate {
  constructor(private reflector: Reflector) {}

  canActivate(context: ExecutionContext): boolean {
    const requiredRoles = this.reflector.getAllAndOverride<ROLES[]>(ROLES_KEY, [
      context.getHandler(),
      context.getClass(),
    ]);

    if (!requiredRoles) {
      return true;
    }

    const { user } = context.switchToHttp().getRequest();

    return requiredRoles.some((role) => user.role?.includes(role));
  }
}

import { Injectable, NestMiddleware } from '@nestjs/common';
import { Request, Response, NextFunction } from 'express';

@Injectable()
export class CookieParserMiddleware implements NestMiddleware {
  use(req: Request, res: Response, next: NextFunction) {
    // Parse cookies from Cookie header
    req.cookies = this.parseCookies(req.headers.cookie);

    // Add helper methods to response for setting secure cookies
    this.addCookieHelpers(res);

    next();
  }

  private parseCookies(cookieHeader?: string): { [key: string]: string } {
    const cookies: { [key: string]: string } = {};

    if (!cookieHeader) {
      return cookies;
    }

    cookieHeader.split(';').forEach((cookie) => {
      const [name, ...rest] = cookie.split('=');
      const value = rest.join('=');

      if (name && value) {
        cookies[name.trim()] = decodeURIComponent(value.trim());
      }
    });

    return cookies;
  }

  private addCookieHelpers(res: Response) {
    // Helper to set secure auth cookies
    res.setAuthCookie = function (
      name: string,
      value: string,
      options: {
        maxAge?: number;
        httpOnly?: boolean;
        secure?: boolean;
        sameSite?: 'strict' | 'lax' | 'none';
        domain?: string;
        path?: string;
      } = {},
    ) {
      const isProd = process.env.NODE_ENV === 'production';

      const defaultOptions = {
        httpOnly: true,
        secure: isProd,
        sameSite: isProd ? ('none' as const) : ('lax' as const),
        path: '/',
        ...options,
      };

      let cookieString = `${name}=${encodeURIComponent(value)}`;

      if (defaultOptions.maxAge) {
        cookieString += `; Max-Age=${Math.floor(defaultOptions.maxAge / 1000)}`;
      }

      if (defaultOptions.domain) {
        cookieString += `; Domain=${defaultOptions.domain}`;
      }

      if (defaultOptions.path) {
        cookieString += `; Path=${defaultOptions.path}`;
      }

      if (defaultOptions.httpOnly) {
        cookieString += '; HttpOnly';
      }

      if (defaultOptions.secure) {
        cookieString += '; Secure';
      }

      if (defaultOptions.sameSite) {
        cookieString += `; SameSite=${defaultOptions.sameSite}`;
      }

      this.setHeader('Set-Cookie', cookieString);
      return this;
    };

    // Helper to clear auth cookies
    res.clearAuthCookie = function (
      name: string,
      options: {
        domain?: string;
        path?: string;
      } = {},
    ) {
      const isProd = process.env.NODE_ENV === 'production';

      let cookieString = `${name}=; Max-Age=0`;

      if (options.domain) {
        cookieString += `; Domain=${options.domain}`;
      }

      if (options.path) {
        cookieString += `; Path=${options.path}`;
      } else {
        cookieString += '; Path=/';
      }

      cookieString += '; HttpOnly';

      if (isProd) {
        cookieString += '; Secure; SameSite=none';
      } else {
        cookieString += '; SameSite=lax';
      }

      const existingCookies = this.getHeaders()['set-cookie'] || [];
      const cookiesArray = Array.isArray(existingCookies)
        ? existingCookies
        : [existingCookies];
      cookiesArray.push(cookieString);

      this.setHeader('Set-Cookie', cookiesArray);
      return this;
    };
  }
}

// Extend Express types to include our custom methods
declare global {
  namespace Express {
    interface Request {
      cookies: { [key: string]: string };
    }

    interface Response {
      setAuthCookie(
        name: string,
        value: string,
        options?: {
          maxAge?: number;
          httpOnly?: boolean;
          secure?: boolean;
          sameSite?: 'strict' | 'lax' | 'none';
          domain?: string;
          path?: string;
        },
      ): Response;

      clearAuthCookie(
        name: string,
        options?: {
          domain?: string;
          path?: string;
        },
      ): Response;
    }
  }
}

import { Injectable, NestMiddleware, Logger } from '@nestjs/common';
import { Request, Response, NextFunction } from 'express';

@Injectable()
export class RequestLoggerMiddleware implements NestMiddleware {
  private readonly logger = new Logger('HTTP');

  use(req: Request, res: Response, next: NextFunction) {
    const startTime = Date.now();
    const { method, originalUrl, ip, headers } = req;

    // Get user agent and other useful info
    const userAgent = headers['user-agent'] || 'Unknown';
    const contentLength = headers['content-length'] || '0';

    // Get user ID if authenticated (from JWT payload)
    const userId = this.extractUserIdFromRequest(req);

    // Log request start
    this.logger.log(
      ` ${method} ${originalUrl} - IP: ${ip} - User: ${userId || 'Anonymous'} - UA: ${userAgent.substring(0, 50)}...`,
    );

    // Override end method to log response
    const originalSend = res.send;
    res.send = function (data) {
      const duration = Date.now() - startTime;
      const statusCode = res.statusCode;
      const contentLength =
        res.get('content-length') || Buffer.byteLength(data || '', 'utf8');

      // Determine log level based on status code
      const logLevel =
        statusCode >= 400 ? 'error' : statusCode >= 300 ? 'warn' : 'log';

      // Create log message
      const logMessage = ` ${method} ${originalUrl} - ${statusCode} - ${duration}ms - ${contentLength} bytes - User: ${userId || 'Anonymous'}`;

      // Log errors with more detail
      if (statusCode >= 400) {
        try {
          const errorData = JSON.parse(data);
          this.logger[logLevel](
            `${logMessage} - Error: ${errorData.message || 'Unknown error'}`,
          );
        } catch {
          this.logger[logLevel](logMessage);
        }
      } else {
        this.logger[logLevel](logMessage);
      }

      return originalSend.call(this, data);
    }.bind(res);

    // Handle response errors
    res.on('error', (error) => {
      const duration = Date.now() - startTime;
      this.logger.error(
        ` ${method} ${originalUrl} - ERROR - ${duration}ms - User: ${userId || 'Anonymous'} - ${error.message}`,
      );
    });

    next();
  }

  private extractUserIdFromRequest(req: Request): string | null {
    try {
      // Check if user is attached by JWT guard
      if (req.user && typeof req.user === 'object' && 'id' in req.user) {
        return (req.user as any).id;
      }

      // Try to extract from Authorization header (if needed)
      const authHeader = req.headers.authorization;
      if (authHeader && authHeader.startsWith('Bearer ')) {
        // Could decode JWT here if needed, but usually req.user is set by guards
        return null;
      }

      return null;
    } catch (error) {
      return null;
    }
  }
}

// Additional middleware for sensitive operations logging
@Injectable()
export class SecurityLoggerMiddleware implements NestMiddleware {
  private readonly logger = new Logger('SECURITY');

  use(req: Request, res: Response, next: NextFunction) {
    const { method, originalUrl, ip, headers } = req;
    const userAgent = headers['user-agent'] || 'Unknown';

    // Log security-sensitive operations
    if (this.isSensitiveOperation(originalUrl, method)) {
      const userId = this.extractUserIdFromRequest(req);

      this.logger.warn(
        ` SENSITIVE OPERATION: ${method} ${originalUrl} - IP: ${ip} - User: ${userId || 'Anonymous'} - UA: ${userAgent}`,
      );

      // Log request body for sensitive operations (excluding passwords)
      if (req.body && Object.keys(req.body).length > 0) {
        const sanitizedBody = this.sanitizeRequestBody(req.body);
        this.logger.warn(` Request Body: ${JSON.stringify(sanitizedBody)}`);
      }
    }

    next();
  }

  private isSensitiveOperation(url: string, method: string): boolean {
    const sensitivePatterns = [
      '/auth/login',
      '/auth/register',
      '/auth/refresh',
      '/users/.*/password',
      '/admin/',
      '/auth/logout',
    ];

    return sensitivePatterns.some(
      (pattern) =>
        url.includes(pattern) ||
        (method === 'DELETE' && url.includes('/users/')) ||
        (method === 'PATCH' && url.includes('/users/')),
    );
  }

  private sanitizeRequestBody(body: any): any {
    const sensitiveFields = [
      'password',
      'confirmPassword',
      'oldPassword',
      'newPassword',
      'token',
    ];
    const sanitized = { ...body };

    sensitiveFields.forEach((field) => {
      if (sanitized[field]) {
        sanitized[field] = '[REDACTED]';
      }
    });

    return sanitized;
  }

  private extractUserIdFromRequest(req: Request): string | null {
    try {
      if (req.user && typeof req.user === 'object' && 'id' in req.user) {
        return (req.user as any).id;
      }
      return null;
    } catch (error) {
      return null;
    }
  }
}

import { Injectable, NestMiddleware } from '@nestjs/common';
import { Request, Response, NextFunction } from 'express';

interface RateLimitStore {
  [key: string]: {
    count: number;
    resetTime: number;
  };
}

@Injectable()
export class RateLimitMiddleware implements NestMiddleware {
  private store: RateLimitStore = {};

  use(req: Request, res: Response, next: NextFunction) {
    const clientIp = this.getClientIp(req);
    const route = req.route?.path || req.path;
    const method = req.method;

    // Define rate limits for different endpoints
    const rateLimits = this.getRateLimits(route, method);

    if (!rateLimits) {
      return next();
    }

    const key = `${clientIp}:${route}:${method}`;
    const now = Date.now();
    const windowMs = rateLimits.windowMs;
    const maxRequests = rateLimits.max;

    // Clean up expired entries
    this.cleanupExpiredEntries(now);

    // Get or create rate limit entry
    if (!this.store[key]) {
      this.store[key] = {
        count: 1,
        resetTime: now + windowMs,
      };
    } else {
      // Reset if window has expired
      if (now > this.store[key].resetTime) {
        this.store[key] = {
          count: 1,
          resetTime: now + windowMs,
        };
      } else {
        this.store[key].count++;
      }
    }

    const current = this.store[key];
    const remaining = Math.max(0, maxRequests - current.count);
    const resetTimeSeconds = Math.ceil((current.resetTime - now) / 1000);

    // Set rate limit headers
    res.setHeader('X-RateLimit-Limit', maxRequests);
    res.setHeader('X-RateLimit-Remaining', remaining);
    res.setHeader('X-RateLimit-Reset', resetTimeSeconds);

    // Check if rate limit exceeded
    if (current.count > maxRequests) {
      res.status(429).json({
        error: 'Too Many Requests',
        message: `Rate limit exceeded. Try again in ${resetTimeSeconds} seconds.`,
        retryAfter: resetTimeSeconds,
      });
      return;
    }

    next();
  }

  private getClientIp(req: Request): string {
    return (
      (req.headers['x-forwarded-for'] as string) ||
      (req.headers['x-real-ip'] as string) ||
      req.connection.remoteAddress ||
      req.socket.remoteAddress ||
      'unknown'
    )
      .split(',')[0]
      .trim();
  }

  private getRateLimits(
    route: string,
    method: string,
  ): { windowMs: number; max: number } | null {
    // Authentication endpoints - stricter limits
    if (route.includes('/auth/login')) {
      return { windowMs: 15 * 60 * 1000, max: 5 }; // 5 attempts per 15 minutes
    }

    if (route.includes('/auth/register')) {
      return { windowMs: 60 * 60 * 1000, max: 3 }; // 3 registrations per hour
    }

    if (route.includes('/auth/refresh')) {
      return { windowMs: 60 * 1000, max: 10 }; // 10 refresh per minute
    }

    // Content creation - moderate limits
    if (method === 'POST' && route.includes('/posts')) {
      return { windowMs: 60 * 1000, max: 5 }; // 5 posts per minute
    }

    if (method === 'POST' && route.includes('/comments')) {
      return { windowMs: 60 * 1000, max: 10 }; // 10 comments per minute
    }

    if (method === 'POST' && route.includes('/reactions')) {
      return { windowMs: 60 * 1000, max: 30 }; // 30 reactions per minute
    }

    // Follow/Unfollow actions
    if (route.includes('/follow')) {
      return { windowMs: 60 * 1000, max: 20 }; // 20 follow actions per minute
    }

    // File uploads
    if (route.includes('/cloudinary') || route.includes('/upload')) {
      return { windowMs: 60 * 1000, max: 10 }; // 10 uploads per minute
    }

    // General API - lenient limits
    if (method === 'GET') {
      return { windowMs: 60 * 1000, max: 100 }; // 100 GET requests per minute
    }

    if (method === 'POST' || method === 'PUT' || method === 'PATCH') {
      return { windowMs: 60 * 1000, max: 30 }; // 30 write operations per minute
    }

    if (method === 'DELETE') {
      return { windowMs: 60 * 1000, max: 10 }; // 10 delete operations per minute
    }

    return null; // No rate limiting
  }

  private cleanupExpiredEntries(now: number) {
    // Clean up expired entries every 5 minutes
    if (!this.lastCleanup || now - this.lastCleanup > 5 * 60 * 1000) {
      Object.keys(this.store).forEach((key) => {
        if (now > this.store[key].resetTime) {
          delete this.store[key];
        }
      });
      this.lastCleanup = now;
    }
  }

  private lastCleanup = 0;
}

