}

import { Injectable, Logger } from '@nestjs/common';
import {
  WebSocketRoom,
  WebSocketRoomType,
  WebSocketNamespace,
  IRoomManager,
} from '../interfaces/websocket.interface';

@Injectable()
export class RoomManagerService implements IRoomManager {
  private readonly logger = new Logger(RoomManagerService.name);
  private rooms = new Map<string, WebSocketRoom>(); // roomId -> WebSocketRoom
  private userRooms = new Map<string, Set<string>>(); // userId -> Set<roomId>
  private namespaceRooms = new Map<WebSocketNamespace, Set<string>>(); // namespace -> Set<roomId>

  createRoom(
    roomData: Omit<WebSocketRoom, 'participants' | 'sockets'>,
  ): WebSocketRoom {
    const room: WebSocketRoom = {
      ...roomData,
      participants: new Set(),
      sockets: new Set(),
    };

    this.rooms.set(room.id, room);

    // Track namespace rooms
    if (!this.namespaceRooms.has(room.namespace)) {
      this.namespaceRooms.set(room.namespace, new Set());
    }
    this.namespaceRooms.get(room.namespace)!.add(room.id);

    this.logger.debug(`Created room ${room.id} in namespace ${room.namespace}`);
    return room;
  }

  getRoom(roomId: string): WebSocketRoom | undefined {
    return this.rooms.get(roomId);
  }

  joinRoom(roomId: string, userId: string, socketId: string): boolean {
    const room = this.rooms.get(roomId);
    if (!room) {
      this.logger.warn(`Attempted to join non-existent room: ${roomId}`);
      return false;
    }

    // Add participant and socket to room
    room.participants.add(userId);
    room.sockets.add(socketId);
    room.metadata.lastActivity = new Date();

    // Track user rooms
    if (!this.userRooms.has(userId)) {
      this.userRooms.set(userId, new Set());
    }
    this.userRooms.get(userId)!.add(roomId);

    this.logger.debug(
      `User ${userId} joined room ${roomId} (socket: ${socketId})`,
    );
    return true;
  }

  leaveRoom(roomId: string, userId: string, socketId: string): boolean {
    const room = this.rooms.get(roomId);
    if (!room) {
      this.logger.warn(`Attempted to leave non-existent room: ${roomId}`);
      return false;
    }

    // Remove socket from room
    room.sockets.delete(socketId);
    room.metadata.lastActivity = new Date();

    // Check if user has other sockets in the room
    const userSocketsInRoom = Array.from(room.sockets).filter((socketId) => {
      // This would need connection manager to check socket ownership
      // For now, we'll assume the socket belongs to the user
      return true;
    });

    // If user has no more sockets in the room, remove them from participants
    if (userSocketsInRoom.length === 0) {
      room.participants.delete(userId);

      // Remove room from user's room list
      const userRooms = this.userRooms.get(userId);
      if (userRooms) {
        userRooms.delete(roomId);
        if (userRooms.size === 0) {
          this.userRooms.delete(userId);
        }
      }
    }

    this.logger.debug(
      `User ${userId} left room ${roomId} (socket: ${socketId})`,
    );
    return true;
  }

  deleteRoom(roomId: string): boolean {
    const room = this.rooms.get(roomId);
    if (!room) {
      return false;
    }

    // Remove room from all participants' room lists
    for (const userId of room.participants) {
      const userRooms = this.userRooms.get(userId);
      if (userRooms) {
        userRooms.delete(roomId);
        if (userRooms.size === 0) {
          this.userRooms.delete(userId);
        }
      }
    }

    // Remove from namespace rooms
    const namespaceRooms = this.namespaceRooms.get(room.namespace);
    if (namespaceRooms) {
      namespaceRooms.delete(roomId);
    }

    // Remove from rooms map
    this.rooms.delete(roomId);

    this.logger.debug(`Deleted room ${roomId}`);
    return true;
  }

  getRoomsByUser(userId: string): WebSocketRoom[] {
    const roomIds = this.userRooms.get(userId);
    if (!roomIds) {
      return [];
    }

    return Array.from(roomIds)
      .map((roomId) => this.rooms.get(roomId))
      .filter(Boolean) as WebSocketRoom[];
  }

  getRoomsByNamespace(namespace: WebSocketNamespace): WebSocketRoom[] {
    const roomIds = this.namespaceRooms.get(namespace);
    if (!roomIds) {
      return [];
    }

    return Array.from(roomIds)
      .map((roomId) => this.rooms.get(roomId))
      .filter(Boolean) as WebSocketRoom[];
  }

  cleanupEmptyRooms(): number {
    let removedCount = 0;

    for (const [roomId, room] of this.rooms.entries()) {
      if (room.participants.size === 0 && room.sockets.size === 0) {
        this.deleteRoom(roomId);
        removedCount++;
      }
    }

    if (removedCount > 0) {
      this.logger.log(`Cleaned up ${removedCount} empty rooms`);
    }

    return removedCount;
  }

  // Utility methods
  getRoomParticipantCount(roomId: string): number {
    const room = this.rooms.get(roomId);
    return room ? room.participants.size : 0;
  }

  getRoomSocketCount(roomId: string): number {
    const room = this.rooms.get(roomId);
    return room ? room.sockets.size : 0;
  }

  isUserInRoom(roomId: string, userId: string): boolean {
    const room = this.rooms.get(roomId);
    return room ? room.participants.has(userId) : false;
  }

  getRoomsByType(type: WebSocketRoomType): WebSocketRoom[] {
    return Array.from(this.rooms.values()).filter((room) => room.type === type);
  }

  // Create predefined room types
  createUserRoom(userId: string, namespace: WebSocketNamespace): WebSocketRoom {
    const roomId = `user:${userId}`;

    // Check if room already exists
    const existingRoom = this.getRoom(roomId);
    if (existingRoom) {
      return existingRoom;
    }

    return this.createRoom({
      id: roomId,
      type: WebSocketRoomType.USER,
      namespace,
      metadata: {
        createdAt: new Date(),
        lastActivity: new Date(),
        isPrivate: true,
      },
    });
  }

  createConversationRoom(
    conversationId: string,
    namespace: WebSocketNamespace,
  ): WebSocketRoom {
    const roomId = `conversation:${conversationId}`;

    const existingRoom = this.getRoom(roomId);
    if (existingRoom) {
      return existingRoom;
    }

    return this.createRoom({
      id: roomId,
      type: WebSocketRoomType.CONVERSATION,
      namespace,
      metadata: {
        createdAt: new Date(),
        lastActivity: new Date(),
        isPrivate: true,
        totalMessages: 0,
      },
    });
  }

  createPostRoom(postId: string, namespace: WebSocketNamespace): WebSocketRoom {
    const roomId = `post:${postId}`;

    const existingRoom = this.getRoom(roomId);
    if (existingRoom) {
      return existingRoom;
    }

    return this.createRoom({
      id: roomId,
      type: WebSocketRoomType.POST,
      namespace,
      metadata: {
        createdAt: new Date(),
        lastActivity: new Date(),
        isPrivate: false,
      },
    });
  }

  createTopicRoom(topic: string, namespace: WebSocketNamespace): WebSocketRoom {
    const roomId = `topic:${topic}`;

    const existingRoom = this.getRoom(roomId);
    if (existingRoom) {
      return existingRoom;
    }

    return this.createRoom({
      id: roomId,
      type: WebSocketRoomType.TOPIC,
      namespace,
      metadata: {
        createdAt: new Date(),
        lastActivity: new Date(),
        isPrivate: false,
      },
    });
  }

  createFeedRoom(userId: string, namespace: WebSocketNamespace): WebSocketRoom {
    const roomId = `feed:${userId}`;

    const existingRoom = this.getRoom(roomId);
    if (existingRoom) {
      return existingRoom;
    }

    return this.createRoom({
      id: roomId,
      type: WebSocketRoomType.FEED,
      namespace,
      metadata: {
        createdAt: new Date(),
        lastActivity: new Date(),
        isPrivate: true,
      },
    });
  }

  // Bulk operations
  removeUserFromAllRooms(userId: string): number {
    const userRooms = this.userRooms.get(userId);
    if (!userRooms) {
      return 0;
    }

    let removedFromCount = 0;

    for (const roomId of userRooms) {
      const room = this.rooms.get(roomId);
      if (room) {
        room.participants.delete(userId);
        room.metadata.lastActivity = new Date();
        removedFromCount++;
      }
    }

    this.userRooms.delete(userId);

    this.logger.debug(`Removed user ${userId} from ${removedFromCount} rooms`);
    return removedFromCount;
  }

  // Statistics and monitoring
  getRoomStats(): any {
    const totalRooms = this.rooms.size;
    const totalParticipants = this.userRooms.size;

    const roomsByType: Record<WebSocketRoomType, number> = {} as any;
    const roomsByNamespace: Record<WebSocketNamespace, number> = {} as any;

    for (const room of this.rooms.values()) {
      roomsByType[room.type] = (roomsByType[room.type] || 0) + 1;
      roomsByNamespace[room.namespace] =
        (roomsByNamespace[room.namespace] || 0) + 1;
    }

    return {
      totalRooms,
      totalParticipants,
      roomsByType,
      roomsByNamespace,
      lastUpdated: new Date(),
    };
  }

  // Health check
  healthCheck(): { healthy: boolean; details: any } {
    const stats = this.getRoomStats();

    const healthy = stats.totalRooms >= 0 && stats.totalParticipants >= 0;

    return {
      healthy,
      details: {
        ...stats,
        roomsWithoutParticipants: Array.from(this.rooms.values()).filter(
          (room) => room.participants.size === 0,
        ).length,
        timestamp: new Date(),
      },
    };
  }
}

import { Injectable, Logger } from '@nestjs/common';
import { Server, Socket } from 'socket.io';

import { ConnectionManagerService } from './connection-manager.service';
import { RoomManagerService } from './room-manager.service';
import { PresenceWebSocketService } from '../services/presence-websocket.service';
import {
  WebSocketEvent,
  WebSocketNamespace,
} from '../interfaces/websocket.interface';

@Injectable()
export class WebSocketConnectionService {
  private readonly logger = new Logger(WebSocketConnectionService.name);
  private server: Server;

  constructor(
    private readonly connectionManager: ConnectionManagerService,
    private readonly roomManager: RoomManagerService,
    private readonly presenceService: PresenceWebSocketService,
  ) {}

  setServer(server: Server): void {
    this.server = server;
  }

  async handleConnection(client: Socket): Promise<void> {
    try {
      this.logger.debug(`Client attempting connection: ${client.id}`);

      // Extract user info from handshake
      const token =
        client.handshake.auth?.token || client.handshake.query?.token;

      if (!token) {
        this.logger.warn(`No token provided for client: ${client.id}`);
        client.disconnect(true);
        return;
      }

      // Validate JWT token and extract user info
      const userId = await this.validateToken(token);

      if (!userId) {
        this.logger.warn(`Invalid token for client: ${client.id}`);
        client.disconnect(true);
        return;
      }

      // Store connection
      const connection = await this.connectionManager.addConnectionAsync(
        userId,
        client.id,
        WebSocketNamespace.SOCIAL,
        client,
      );

      if (connection) {
        await this.setupUserConnection(client, userId);
        this.logger.log(`User ${userId} connected with socket ${client.id}`);
      }
    } catch (error) {
      this.logger.error(`Connection error for client ${client.id}:`, error);
      client.disconnect(true);
    }
  }

  async handleDisconnect(client: Socket): Promise<void> {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );

      if (connection) {
        const userId = connection.userId;

        // Notify presence service about user disconnection
        await this.presenceService.handleUserDisconnected(userId, client.id);

        // Remove from all rooms
        this.roomManager.removeUserFromAllRooms(userId);

        // Remove connection
        await this.connectionManager.removeConnectionAsync(userId, client.id);

        this.logger.log(`User ${userId} disconnected (socket: ${client.id})`);
      }
    } catch (error) {
      this.logger.error(`Disconnect error for client ${client.id}:`, error);
    }
  }

  async handleJoinRoom(
    client: Socket,
    data: { roomId: string },
  ): Promise<void> {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );
      if (!connection) {
        client.emit('error', { message: 'Connection not found' });
        return;
      }

      await client.join(data.roomId);
      const joined = this.roomManager.joinRoom(
        data.roomId,
        connection.userId,
        client.id,
      );

      if (joined) {
        client.emit('room_joined', { roomId: data.roomId });
        client.to(data.roomId).emit(WebSocketEvent.USER_JOINED_ROOM, {
          userId: connection.userId,
          roomId: data.roomId,
        });
      }
    } catch (error) {
      this.logger.error(`Error joining room:`, error);
      client.emit('error', { message: 'Failed to join room' });
    }
  }

  async handleLeaveRoom(
    client: Socket,
    data: { roomId: string },
  ): Promise<void> {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );
      if (!connection) {
        client.emit('error', { message: 'Connection not found' });
        return;
      }

      await client.leave(data.roomId);
      this.roomManager.leaveRoom(data.roomId, connection.userId, client.id);

      client.emit('room_left', { roomId: data.roomId });
      client.to(data.roomId).emit(WebSocketEvent.USER_LEFT_ROOM, {
        userId: connection.userId,
        roomId: data.roomId,
      });
    } catch (error) {
      this.logger.error(`Error leaving room:`, error);
      client.emit('error', { message: 'Failed to leave room' });
    }
  }

  private async setupUserConnection(
    client: Socket,
    userId: string,
  ): Promise<void> {
    // Join user's personal room
    const userRoom = this.roomManager.createUserRoom(
      userId,
      WebSocketNamespace.SOCIAL,
    );
    await client.join(userRoom.id);
    this.roomManager.joinRoom(userRoom.id, userId, client.id);

    // Emit connection success
    client.emit(WebSocketEvent.CONNECTION_ESTABLISHED, {
      userId,
      socketId: client.id,
      timestamp: new Date(),
    });

    // Notify presence service about user connection
    await this.presenceService.handleUserConnected(userId, client.id, {
      device: client.handshake.headers['user-agent'],
    });
  }

  private async validateToken(token: string): Promise<string | null> {
    try {
      // This should use your JWT service to validate the token
      // For now, returning a mock implementation
      // You should inject and use your JWT service here

      // Example:
      // const payload = await this.jwtService.verifyAsync(token);
      // return payload.sub || payload.userId;

      // Mock implementation - replace with actual JWT validation
      return 'mock-user-id';
    } catch (error) {
      this.logger.error('Token validation error:', error);
      return null;
    }
  }
}

