import { Injectable, Logger } from '@nestjs/common';
import { Server } from 'socket.io';

import { ConnectionManagerService } from './connection-manager.service';
import { RoomManagerService } from './room-manager.service';
import {
  WebSocketEvent,
  WebSocketNamespace,
  WebSocketEventPayload,
} from '../interfaces/websocket.interface';

@Injectable()
export class WebSocketEmitterService {
  private readonly logger = new Logger(WebSocketEmitterService.name);
  private server: Server;

  constructor(
    private readonly connectionManager: ConnectionManagerService,
    private readonly roomManager: RoomManagerService,
  ) {}

  setServer(server: Server): void {
    this.server = server;
  }

  async emitToUser(
    userId: string,
    event: WebSocketEvent,
    data: any,
    namespace?: WebSocketNamespace,
  ): Promise<boolean> {
    try {
      const connections = await this.connectionManager.getUserConnectionsAsync(
        userId,
        namespace,
      );

      if (connections.length === 0) {
        this.logger.debug(
          `No connections found for user ${userId} in namespace ${namespace}`,
        );
        return false;
      }

      const payload: WebSocketEventPayload = {
        event,
        data,
        metadata: {
          timestamp: new Date(),
          eventId: `${Date.now()}-${Math.random()}`,
          version: '1.0',
        },
        namespace: namespace || WebSocketNamespace.SOCIAL,
      };

      let emitted = false;
      for (const connection of connections) {
        try {
          this.server.to(connection.socketId).emit(event, payload);
          emitted = true;
        } catch (error) {
          this.logger.error(
            `Failed to emit to socket ${connection.socketId}:`,
            error,
          );
        }
      }

      return emitted;
    } catch (error) {
      this.logger.error(`Error emitting to user ${userId}:`, error);
      return false;
    }
  }

  async emitToRoom(
    roomId: string,
    event: WebSocketEvent,
    data: any,
  ): Promise<boolean> {
    try {
      const room = this.roomManager.getRoom(roomId);
      if (!room) {
        this.logger.warn(`Room ${roomId} not found for emission`);
        return false;
      }

      const payload: WebSocketEventPayload = {
        event,
        data,
        metadata: {
          timestamp: new Date(),
          eventId: `${Date.now()}-${Math.random()}`,
          version: '1.0',
        },
        namespace: room.namespace,
      };

      this.server.to(roomId).emit(event, payload);
      return true;
    } catch (error) {
      this.logger.error(`Error emitting to room ${roomId}:`, error);
      return false;
    }
  }

  async broadcastToNamespace(
    namespace: WebSocketNamespace,
    event: WebSocketEvent,
    data: any,
  ): Promise<boolean> {
    try {
      const payload: WebSocketEventPayload = {
        event,
        data,
        metadata: {
          timestamp: new Date(),
          eventId: `${Date.now()}-${Math.random()}`,
          version: '1.0',
        },
        namespace,
      };

      this.server.emit(event, payload);
      return true;
    } catch (error) {
      this.logger.error(`Error broadcasting to namespace ${namespace}:`, error);
      return false;
    }
  }
}

import { Injectable, Logger } from '@nestjs/common';
import {
  WebSocketGateway,
  WebSocketServer,
  SubscribeMessage,
  OnGatewayConnection,
  OnGatewayDisconnect,
  ConnectedSocket,
  MessageBody,
} from '@nestjs/websockets';
import { Server, Socket } from 'socket.io';

import { WebSocketConnectionService } from './websocket-connection.service';
import { WebSocketEmitterService } from './websocket-emitter.service';
import { WebSocketUtilityService } from './websocket-utility.service';
import { MessagingWebSocketService } from '../services/messaging-websocket.service';
import { SocialWebSocketService } from '../services/social-websocket.service';
import { PresenceWebSocketService } from '../services/presence-websocket.service';
import {
  WebSocketEvent,
  WebSocketNamespace,
  IWebSocketGatewayService,
} from '../interfaces/websocket.interface';

@WebSocketGateway({
  cors: {
    origin: process.env.FRONTEND_URL || 'http://localhost:3000',
    credentials: true,
  },
  namespace: '/',
})
@Injectable()
export class WebSocketGatewayService
  implements IWebSocketGatewayService, OnGatewayConnection, OnGatewayDisconnect
{
  @WebSocketServer()
  server: Server;

  private readonly logger = new Logger(WebSocketGatewayService.name);

  constructor(
    private readonly connectionService: WebSocketConnectionService,
    private readonly emitterService: WebSocketEmitterService,
    private readonly utilityService: WebSocketUtilityService,
    private readonly messagingService: MessagingWebSocketService,
    private readonly socialService: SocialWebSocketService,
    private readonly presenceService: PresenceWebSocketService,
  ) {}

  // ============ LIFECYCLE HOOKS ============
  afterInit(server: Server) {
    this.server = server;
    this.initializeServices();
  }

  private initializeServices(): void {
    this.connectionService.setServer(this.server);
    this.emitterService.setServer(this.server);
    this.utilityService.setServer(this.server);
    this.messagingService.setServer(this.server);
    this.socialService.setServer(this.server);
    this.presenceService.setServer(this.server);
  }

  // ============ CONNECTION LIFECYCLE (DELEGATE) ============
  async handleConnection(client: Socket) {
    return this.connectionService.handleConnection(client);
  }

  async handleDisconnect(client: Socket) {
    return this.connectionService.handleDisconnect(client);
  }

  // ============ CORE MESSAGING METHODS (DELEGATE) ============
  async emitToUser(
    userId: string,
    event: WebSocketEvent,
    data: any,
    namespace?: WebSocketNamespace,
  ): Promise<boolean> {
    return this.emitterService.emitToUser(userId, event, data, namespace);
  }

  async emitToRoom(
    roomId: string,
    event: WebSocketEvent,
    data: any,
  ): Promise<boolean> {
    return this.emitterService.emitToRoom(roomId, event, data);
  }

  async broadcastToNamespace(
    namespace: WebSocketNamespace,
    event: WebSocketEvent,
    data: any,
  ): Promise<boolean> {
    return this.emitterService.broadcastToNamespace(namespace, event, data);
  }

  // ============ CORE ROOM MANAGEMENT (DELEGATE) ============
  @SubscribeMessage('join_room')
  async handleJoinRoom(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: { roomId: string },
  ) {
    return this.connectionService.handleJoinRoom(client, data);
  }

  @SubscribeMessage('leave_room')
  async handleLeaveRoom(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: { roomId: string },
  ) {
    return this.connectionService.handleLeaveRoom(client, data);
  }

  // ============ MESSAGING FEATURE HANDLERS (DELEGATE) ============
  @SubscribeMessage('join_conversation')
  async handleJoinConversation(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: { conversationId: string },
  ) {
    return this.messagingService.handleJoinConversation(client, data);
  }

  @SubscribeMessage('send_message')
  async handleSendMessage(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: any,
  ) {
    return this.messagingService.handleSendMessage(client, data);
  }

  @SubscribeMessage('typing_indicator')
  async handleTypingIndicator(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: any,
  ) {
    return this.messagingService.handleTypingIndicator(client, data);
  }

  @SubscribeMessage('mark_message_read')
  async handleMarkMessageRead(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: any,
  ) {
    return this.messagingService.handleMarkMessageRead(client, data);
  }

  // ============ SOCIAL FEATURE HANDLERS (DELEGATE) ============
  @SubscribeMessage('join_post')
  async handleJoinPost(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: { postId: string },
  ) {
    return this.socialService.handleJoinPost(client, data);
  }

  @SubscribeMessage('like_post')
  async handleLikePost(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: any,
  ) {
    return this.socialService.handleLikePost(client, data);
  }

  @SubscribeMessage('add_comment')
  async handleAddComment(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: any,
  ) {
    return this.socialService.handleAddComment(client, data);
  }

  @SubscribeMessage('follow_user')
  async handleFollowUser(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: any,
  ) {
    return this.socialService.handleFollowUser(client, data);
  }

  // ============ PRESENCE FEATURE HANDLERS (DELEGATE) ============
  @SubscribeMessage('update_status')
  async handleUpdateStatus(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: any,
  ) {
    return this.presenceService.handleUpdateStatus(client, data);
  }

  @SubscribeMessage('update_activity')
  async handleUpdateActivity(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: any,
  ) {
    return this.presenceService.handleUpdateActivity(client, data);
  }

  @SubscribeMessage('get_online_users')
  async handleGetOnlineUsers(@ConnectedSocket() client: Socket) {
    return this.presenceService.handleGetOnlineUsers(client);
  }

  @SubscribeMessage('update_presence')
  async handleUpdatePresence(
    @ConnectedSocket() client: Socket,
    @MessageBody() data: { status: string; activity?: string },
  ) {
    return this.presenceService.handleUpdateStatus(client, {
      status: data.status as 'online' | 'offline' | 'away' | 'busy',
    });
  }

  // ============ HEALTH AND MONITORING (DELEGATE) ============
  async getConnectionStats(): Promise<any> {
    return this.utilityService.getConnectionStats();
  }

  async healthCheck(): Promise<{ healthy: boolean; details: any }> {
    return this.utilityService.healthCheck();
  }
}

