import { Injectable, Logger } from '@nestjs/common';
import { Socket, Server } from 'socket.io';
import {
  WebSocketEvent,
  WebSocketNamespace,
} from '../interfaces/websocket.interface';
import { RoomManagerService } from '../core/room-manager.service';
import { ConnectionManagerService } from '../core/connection-manager.service';

export interface PresenceEventData {
  userId: string;
  status: 'online' | 'offline' | 'away' | 'busy';
  lastSeen?: Date;
  device?: string;
  location?: string;
}

export interface ActivityEventData {
  userId: string;
  activity: 'viewing_post' | 'browsing_feed' | 'in_conversation' | 'idle';
  targetId?: string; // postId, conversationId, etc.
  timestamp: Date;
}

@Injectable()
export class PresenceWebSocketService {
  private readonly logger = new Logger(PresenceWebSocketService.name);
  private server: Server;

  // Store user presence data in memory (in production, use Redis)
  private userPresence = new Map<string, PresenceEventData>();
  private userActivities = new Map<string, ActivityEventData>();

  constructor(
    private readonly connectionManager: ConnectionManagerService,
    private readonly roomManager: RoomManagerService,
  ) {}

  // Set server instance from gateway
  setServer(server: Server): void {
    this.server = server;
  }

  // ============ HELPER METHODS ============

  /**
   * Broadcast to namespace
   */
  private async broadcastToNamespace(
    namespace: WebSocketNamespace,
    event: WebSocketEvent,
    data: any,
  ): Promise<void> {
    try {
      this.server.emit(event, data);
    } catch (error) {
      this.logger.error(`Error broadcasting to namespace ${namespace}:`, error);
    }
  }

  /**
   * Emit to specific user
   */
  private async emitToUser(
    userId: string,
    event: WebSocketEvent,
    data: any,
  ): Promise<boolean> {
    try {
      const connections =
        await this.connectionManager.getUserConnectionsAsync(userId);

      if (connections.length === 0) {
        this.logger.debug(`No connections found for user ${userId}`);
        return false;
      }

      let emitted = false;
      for (const connection of connections) {
        try {
          this.server.to(connection.socketId).emit(event, data);
          emitted = true;
        } catch (error) {
          this.logger.error(
            `Failed to emit to socket ${connection.socketId}:`,
            error,
          );
        }
      }

      return emitted;
    } catch (error) {
      this.logger.error(`Error emitting to user ${userId}:`, error);
      return false;
    }
  }

  // ============ WEBSOCKET EVENT HANDLERS ============

  /**
   * Handle update status event from client
   */
  async handleUpdateStatus(
    client: Socket,
    data: { status: 'online' | 'offline' | 'away' | 'busy' },
  ) {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );
      if (!connection) return;

      const presenceData = {
        userId: connection.userId,
        status: data.status,
        timestamp: new Date(),
      };

      // Broadcast to presence namespace
      await this.broadcastToNamespace(
        WebSocketNamespace.PRESENCE,
        WebSocketEvent.USER_STATUS_CHANGE,
        presenceData,
      );

      client.emit('status_updated', presenceData);
    } catch (error) {
      this.logger.error('Error updating status:', error);
      client.emit('error', { message: 'Failed to update status' });
    }
  }

  /**
   * Handle update activity event from client
   */
  async handleUpdateActivity(
    client: Socket,
    data: {
      activity: 'viewing_post' | 'browsing_feed' | 'in_conversation' | 'idle';
      targetId?: string;
    },
  ) {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );
      if (!connection) return;

      await this.connectionManager.updateUserActivity(connection.userId);

      const activityData = {
        userId: connection.userId,
        activity: data.activity,
        targetId: data.targetId,
        timestamp: new Date(),
      };

      // Broadcast activity update
      await this.broadcastToNamespace(
        WebSocketNamespace.PRESENCE,
        WebSocketEvent.PRESENCE_UPDATED,
        activityData,
      );
    } catch (error) {
      this.logger.error('Error updating activity:', error);
    }
  }

  /**
   * Handle get online users event from client
   */
  async handleGetOnlineUsers(client: Socket) {
    try {
      const onlineUsers = this.getOnlineUsers();
      client.emit('online_users', {
        users: onlineUsers,
        timestamp: new Date(),
      });
    } catch (error) {
      this.logger.error('Error getting online users:', error);
      client.emit('error', { message: 'Failed to get online users' });
    }
  }

  // ============ BUSINESS LOGIC METHODS ============

  /**
   * Update user presence status
   */
  async updatePresence(data: PresenceEventData): Promise<void> {
    try {
      const previousPresence = this.userPresence.get(data.userId);
      this.userPresence.set(data.userId, data);

      // Only broadcast if status actually changed
      if (!previousPresence || previousPresence.status !== data.status) {
        let event: WebSocketEvent;

        switch (data.status) {
          case 'online':
            event = WebSocketEvent.USER_ONLINE;
            break;
          case 'offline':
            event = WebSocketEvent.USER_OFFLINE;
            break;
          default:
            event = WebSocketEvent.USER_STATUS_CHANGE;
        }

        // Broadcast to presence namespace
        await this.broadcastToNamespace(
          WebSocketNamespace.PRESENCE,
          event,
          data,
        );

        // Also notify friends/contacts specifically
        await this.notifyContacts(data.userId, event, data);
      }

      this.logger.debug(
        `Presence updated for user ${data.userId}: ${data.status}`,
      );
    } catch (error) {
      this.logger.error('Error updating presence:', error);
    }
  }

  /**
   * Update user activity
   */
  async updateActivity(data: ActivityEventData): Promise<void> {
    try {
      this.userActivities.set(data.userId, data);

      // Broadcast activity updates to interested parties
      await this.broadcastToNamespace(
        WebSocketNamespace.PRESENCE,
        WebSocketEvent.PRESENCE_UPDATED,
        {
          userId: data.userId,
          activity: data.activity,
          targetId: data.targetId,
          timestamp: data.timestamp,
        },
      );

      this.logger.debug(
        `Activity updated for user ${data.userId}: ${data.activity}`,
      );
    } catch (error) {
      this.logger.error('Error updating activity:', error);
    }
  }

  /**
   * Get user presence
   */
  getUserPresence(userId: string): PresenceEventData | null {
    return this.userPresence.get(userId) || null;
  }

  /**
   * Get user activity
   */
  getUserActivity(userId: string): ActivityEventData | null {
    return this.userActivities.get(userId) || null;
  }

  /**
   * Get online users
   */
  getOnlineUsers(): string[] {
    const onlineUsers: string[] = [];

    this.userPresence.forEach((presence, userId) => {
      if (presence.status === 'online') {
        onlineUsers.push(userId);
      }
    });

    return onlineUsers;
  }

  /**
   * Handle user connection
   */
  async handleUserConnected(
    userId: string,
    socketId: string,
    metadata?: any,
  ): Promise<void> {
    try {
      // Update presence to online
      await this.updatePresence({
        userId,
        status: 'online',
        device: metadata?.device,
        location: metadata?.location,
      });

      // Join presence room
      const presenceRoom = this.roomManager.createUserRoom(
        userId,
        WebSocketNamespace.PRESENCE,
      );
      this.roomManager.joinRoom(presenceRoom.id, userId, socketId);

      this.logger.debug(`User ${userId} connected to presence service`);
    } catch (error) {
      this.logger.error('Error handling user connected:', error);
    }
  }

  /**
   * Handle user disconnection
   */
  async handleUserDisconnected(
    userId: string,
    socketId: string,
  ): Promise<void> {
    try {
      // Check if user has other active connections
      // If not, mark as offline
      const hasOtherConnections = await this.checkOtherConnections(
        userId,
        socketId,
      );

      if (!hasOtherConnections) {
        await this.updatePresence({
          userId,
          status: 'offline',
          lastSeen: new Date(),
        });

        // Clean up activity data
        this.userActivities.delete(userId);
      }

      this.logger.debug(`User ${userId} disconnected from presence service`);
    } catch (error) {
      this.logger.error('Error handling user disconnected:', error);
    }
  }

  /**
   * Set user as away (after inactivity)
   */
  async setUserAway(userId: string): Promise<void> {
    try {
      const currentPresence = this.userPresence.get(userId);

      if (currentPresence && currentPresence.status === 'online') {
        await this.updatePresence({
          ...currentPresence,
          status: 'away',
        });
      }
    } catch (error) {
      this.logger.error('Error setting user away:', error);
    }
  }

  /**
   * Cleanup old presence data
   */
  cleanup(): void {
    const now = new Date();
    const threshold = 15 * 60 * 1000; // 15 minutes

    this.userPresence.forEach((presence, userId) => {
      if (presence.status === 'offline' && presence.lastSeen) {
        if (now.getTime() - presence.lastSeen.getTime() > threshold) {
          this.userPresence.delete(userId);
          this.userActivities.delete(userId);
        }
      }
    });
  }

  /**
   * Notify user's contacts about presence changes
   */
  private async notifyContacts(
    userId: string,
    event: WebSocketEvent,
    data: PresenceEventData,
  ): Promise<void> {
    try {
      // In a real app, you'd fetch the user's contacts/friends from database
      // For demo purposes, we'll just broadcast to a contacts room
      const contactsRoom = `contacts:${userId}`;
      this.server.to(contactsRoom).emit(event, data);
    } catch (error) {
      this.logger.error('Error notifying contacts:', error);
    }
  }

  /**
   * Check if user has other active connections
   */
  private async checkOtherConnections(
    userId: string,
    excludeSocketId: string,
  ): Promise<boolean> {
    try {
      // This should check with ConnectionManagerService
      // For now, return false to simulate single connection
      return false;
    } catch (error) {
      this.logger.error('Error checking other connections:', error);
      return false;
    }
  }
}

import { Injectable, Logger } from '@nestjs/common';
import { Socket, Server } from 'socket.io';
import {
  WebSocketEvent,
  WebSocketNamespace,
} from '../interfaces/websocket.interface';
import { RoomManagerService } from '../core/room-manager.service';
import { ConnectionManagerService } from '../core/connection-manager.service';

export interface SocialEventData {
  userId: string;
  targetId: string; // postId, commentId, userId (for follows)
  type: 'post' | 'comment' | 'like' | 'follow';
  action:
    | 'created'
    | 'updated'
    | 'deleted'
    | 'liked'
    | 'unliked'
    | 'followed'
    | 'unfollowed';
  data?: any;
  timestamp: Date;
}

export interface PostEventData extends SocialEventData {
  postId: string;
  authorId: string;
  content?: string;
  mediaUrls?: string[];
}

export interface CommentEventData extends SocialEventData {
  postId: string;
  commentId: string;
  authorId: string;
  content?: string;
  parentCommentId?: string;
}

export interface FollowEventData extends SocialEventData {
  followerId: string;
  followingId: string;
  isAccepted?: boolean;
}

@Injectable()
export class SocialWebSocketService {
  private readonly logger = new Logger(SocialWebSocketService.name);
  private server: Server;

  constructor(
    private readonly connectionManager: ConnectionManagerService,
    private readonly roomManager: RoomManagerService,
  ) {}

  // Set server instance from gateway
  setServer(server: Server): void {
    this.server = server;
  }

  // ============ HELPER METHODS ============

  /**
   * Emit to specific user
   */
  private async emitToUser(
    userId: string,
    event: WebSocketEvent,
    data: any,
  ): Promise<boolean> {
    try {
      const connections =
        await this.connectionManager.getUserConnectionsAsync(userId);

      if (connections.length === 0) {
        this.logger.debug(`No connections found for user ${userId}`);
        return false;
      }

      let emitted = false;
      for (const connection of connections) {
        try {
          this.server.to(connection.socketId).emit(event, data);
          emitted = true;
        } catch (error) {
          this.logger.error(
            `Failed to emit to socket ${connection.socketId}:`,
            error,
          );
        }
      }

      return emitted;
    } catch (error) {
      this.logger.error(`Error emitting to user ${userId}:`, error);
      return false;
    }
  }

  // ============ WEBSOCKET EVENT HANDLERS ============

  /**
   * Handle join post event from client
   */
  async handleJoinPost(client: Socket, data: { postId: string }) {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );
      if (!connection) {
        client.emit('error', { message: 'Connection not found' });
        return;
      }

      const postRoom = this.roomManager.createPostRoom(
        data.postId,
        WebSocketNamespace.SOCIAL,
      );
      const joined = this.roomManager.joinRoom(
        postRoom.id,
        connection.userId,
        client.id,
      );

      if (joined) {
        await client.join(postRoom.id);
        client.emit('post_joined', {
          postId: data.postId,
          roomId: postRoom.id,
        });
      }
    } catch (error) {
      this.logger.error('Error joining post:', error);
      client.emit('error', { message: 'Failed to join post' });
    }
  }

  /**
   * Handle like post event from client
   */
  async handleLikePost(
    client: Socket,
    data: { postId: string; liked: boolean },
  ) {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );
      if (!connection) return;

      const event = data.liked
        ? WebSocketEvent.POST_LIKED
        : WebSocketEvent.POST_UNLIKED;
      const postRoom = `post:${data.postId}`;

      const eventData = {
        postId: data.postId,
        userId: connection.userId,
        action: data.liked ? 'liked' : 'unliked',
        timestamp: new Date(),
      };

      // Notify users in post room
      this.server.to(postRoom).emit(event, eventData);

      client.emit('like_post_ack', eventData);
    } catch (error) {
      this.logger.error('Error handling post like:', error);
      client.emit('error', { message: 'Failed to like/unlike post' });
    }
  }

  /**
   * Handle add comment event from client
   */
  async handleAddComment(
    client: Socket,
    data: {
      postId: string;
      content: string;
      parentCommentId?: string;
    },
  ) {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );
      if (!connection) return;

      const commentData = {
        postId: data.postId,
        commentId: `comment_${Date.now()}_${Math.random()}`,
        authorId: connection.userId,
        content: data.content,
        parentCommentId: data.parentCommentId,
        timestamp: new Date(),
      };

      const postRoom = `post:${data.postId}`;
      this.server.to(postRoom).emit(WebSocketEvent.COMMENT_ADDED, commentData);

      client.emit('comment_added_ack', commentData);
    } catch (error) {
      this.logger.error('Error adding comment:', error);
      client.emit('error', { message: 'Failed to add comment' });
    }
  }

  /**
   * Handle follow user event from client
   */
  async handleFollowUser(
    client: Socket,
    data: { targetUserId: string; follow: boolean },
  ) {
    try {
      const connection = await this.connectionManager.getConnectionBySocket(
        client.id,
      );
      if (!connection) return;

      const event = data.follow
        ? WebSocketEvent.FOLLOW_REQUEST
        : 'unfollow_request';

      const followData = {
        followerId: connection.userId,
        followingId: data.targetUserId,
        action: data.follow ? 'followed' : 'unfollowed',
        timestamp: new Date(),
      };

      // Notify target user
      await this.emitToUser(
        data.targetUserId,
        event as WebSocketEvent,
        followData,
      );

      client.emit('follow_user_ack', followData);
    } catch (error) {
      this.logger.error('Error handling follow user:', error);
      client.emit('error', { message: 'Failed to follow/unfollow user' });
    }
  }

  // ============ BUSINESS LOGIC METHODS ============

  /**
   * Handle post creation/update/deletion
   */
  async handlePostEvent(data: PostEventData): Promise<void> {
    try {
      let event: WebSocketEvent;

      switch (data.action) {
        case 'created':
          event = WebSocketEvent.POST_CREATED;
          break;
        case 'updated':
          event = WebSocketEvent.POST_UPDATED;
          break;
        case 'deleted':
          event = WebSocketEvent.POST_DELETED;
          break;
        case 'liked':
          event = WebSocketEvent.POST_LIKED;
          break;
        case 'unliked':
          event = WebSocketEvent.POST_UNLIKED;
          break;
        default:
          this.logger.warn(`Unknown post action: ${data.action}`);
          return;
      }

      // Notify followers and interested users
      await this.notifyFollowers(data.authorId, event, data);

      // Also notify users in the post's room (for comments/likes on posts they're engaged with)
      if (data.action === 'liked' || data.action === 'unliked') {
        const postRoom = this.getPostRoomId(data.postId);
        this.server.to(postRoom).emit(event, data);
      }

      this.logger.debug(
        `Post ${data.action} event handled for post ${data.postId}`,
      );
    } catch (error) {
      this.logger.error('Error handling post event:', error);
    }
  }

  /**
   * Handle comment events
   */
  async handleCommentEvent(data: CommentEventData): Promise<void> {
    try {
      let event: WebSocketEvent;

      switch (data.action) {
        case 'created':
          event = WebSocketEvent.COMMENT_ADDED;
          break;
        case 'liked':
          event = WebSocketEvent.COMMENT_LIKED;
          break;
        case 'unliked':
          event = WebSocketEvent.COMMENT_UNLIKED;
          break;
        default:
          this.logger.warn(`Unknown comment action: ${data.action}`);
          return;
      }

      // Notify post room (users interested in this post)
      const postRoom = this.getPostRoomId(data.postId);
      this.server.to(postRoom).emit(event, data);

      this.logger.debug(
        `Comment ${data.action} event handled for post ${data.postId}`,
      );
    } catch (error) {
      this.logger.error('Error handling comment event:', error);
    }
  }

  /**
   * Handle follow events
   */
  async handleFollowEvent(data: FollowEventData): Promise<void> {
    try {
      let event: WebSocketEvent;

      switch (data.action) {
        case 'followed':
          event = WebSocketEvent.FOLLOW_REQUEST;
          break;
        case 'unfollowed':
          // Could add FOLLOW_REMOVED event if needed
          return;
        default:
          if (data.isAccepted === true) {
            event = WebSocketEvent.FOLLOW_ACCEPTED;
          } else if (data.isAccepted === false) {
            event = WebSocketEvent.FOLLOW_DECLINED;
          } else {
            this.logger.warn(`Unknown follow action: ${data.action}`);
            return;
          }
      }

      // Notify the target user
      await this.emitToUser(data.followingId, event, data);

      // If accepted, also notify the follower
      if (event === WebSocketEvent.FOLLOW_ACCEPTED) {
        await this.emitToUser(data.followerId, event, data);
      }

      this.logger.debug(
        `Follow ${data.action} event handled between ${data.followerId} and ${data.followingId}`,
      );
    } catch (error) {
      this.logger.error('Error handling follow event:', error);
    }
  }

  /**
   * Join user to post room (for real-time updates on post they're viewing/commenting)
   */
  async joinPostRoom(
    userId: string,
    socketId: string,
    postId: string,
  ): Promise<boolean> {
    try {
      const room = this.roomManager.createPostRoom(
        postId,
        WebSocketNamespace.SOCIAL,
      );
      return this.roomManager.joinRoom(room.id, userId, socketId);
    } catch (error) {
      this.logger.error(`Error joining post room ${postId}:`, error);
      return false;
    }
  }

  /**
   * Leave post room
   */
  async leavePostRoom(
    userId: string,
    socketId: string,
    postId: string,
  ): Promise<boolean> {
    try {
      const roomId = this.getPostRoomId(postId);
      return this.roomManager.leaveRoom(roomId, userId, socketId);
    } catch (error) {
      this.logger.error(`Error leaving post room ${postId}:`, error);
      return false;
    }
  }

  /**
   * Join user to their feed room for real-time feed updates
   */
  async joinFeedRoom(userId: string, socketId: string): Promise<boolean> {
    try {
      const room = this.roomManager.createFeedRoom(
        userId,
        WebSocketNamespace.SOCIAL,
      );
      return this.roomManager.joinRoom(room.id, userId, socketId);
    } catch (error) {
      this.logger.error(`Error joining feed room for user ${userId}:`, error);
      return false;
    }
  }

  /**
   * Notify followers about user's activity
   */
  private async notifyFollowers(
    userId: string,
    event: WebSocketEvent,
    data: any,
  ): Promise<void> {
    try {
      // In a real app, you'd fetch the user's followers from database
      // For now, we'll use a feed-based approach
      const feedRoom = this.getFeedRoomId(userId);
      this.server.to(feedRoom).emit(event, data);
    } catch (error) {
      this.logger.error('Error notifying followers:', error);
    }
  }

  /**
   * Get post room ID
   */
  private getPostRoomId(postId: string): string {
    return `post:${postId}`;
  }

  /**
   * Get feed room ID
   */
  private getFeedRoomId(userId: string): string {
    return `feed:${userId}`;
  }
}
